/* global __app_id, __firebase_config, __initial_auth_token */
import React, { useState, useEffect, useRef } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, signInWithCustomToken } from 'firebase/auth';
import { getFunctions, httpsCallable } from 'firebase/functions';
import { getFirestore, doc, onSnapshot, collection, addDoc, query, orderBy, serverTimestamp } from 'firebase/firestore';

// Component to display a single message
const Message = ({ text, from }) => (
    <div className={`flex my-2 ${from === 'user' ? 'justify-end' : 'justify-start'}`}>
        <div className={`p-3 rounded-lg max-w-xs md:max-w-md lg:max-w-lg ${from === 'user' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'}`}>
            <p>{text}</p>
        </div>
    </div>
);

// Component for the message input area
const MessageInput = ({ message, setMessage, handleSendMessage, isSending }) => {
    const handleKeyDown = (e) => {
        if (e.key === 'Enter') {
            handleSendMessage();
        }
    };

    return (
        <div className="p-4 bg-white border-t flex">
            <input
                type="text"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                onKeyDown={handleKeyDown}
                className="flex-1 p-2 border rounded-lg focus:outline-none"
                placeholder="Type your message here..."
                disabled={isSending}
            />
            <button
                onClick={handleSendMessage}
                className={`ml-2 p-2 rounded-lg text-white ${isSending ? 'bg-gray-400' : 'bg-blue-500'}`}
                disabled={isSending}
            >
                {isSending ? 'Sending...' : 'Send'}
            </button>
        </div>
    );
};

// Main Chatbot component
const AIChatbot = () => {
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState('');
    const [isSending, setIsSending] = useState(false);
    const messagesEndRef = useRef(null);

    // Initialize Firebase once and sign in
    useEffect(() => {
        if (typeof __firebase_config !== 'undefined') {
            initializeApp(__firebase_config);
        }
        const auth = getAuth();
        if (typeof __initial_auth_token !== 'undefined') {
            signInWithCustomToken(auth, __initial_auth_token).catch((error) => {
                console.error("Error signing in with custom token:", error);
            });
        } else {
            signInAnonymously(auth).catch((error) => {
                console.error("Error signing in anonymously:", error);
            });
        }
    }, []);

    // Listen for messages from Firestore
    useEffect(() => {
        if (typeof __app_id !== 'undefined') {
            const db = getFirestore();
            const q = query(collection(db, 'chats', __app_id, 'messages'), orderBy('createdAt'));
            const unsubscribe = onSnapshot(q, (snapshot) => {
                const newMessages = snapshot.docs.map(doc => doc.data());
                setMessages(newMessages);
            });
            return () => unsubscribe();
        }
    }, []);

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages]);

    const sendMessage = async () => {
        if (!message.trim() || isSending) return;

        setIsSending(true);
        const userMessage = { text: message, from: 'user', createdAt: serverTimestamp() };
        setMessages((prevMessages) => [...prevMessages, userMessage]);
        setMessage('');

        try {
            const db = getFirestore();
            await addDoc(collection(db, 'chats', __app_id, 'messages'), userMessage);
            const functions = getFunctions();
            const callChatbot = httpsCallable(functions, 'chatbot');
            await callChatbot({ messages: [...messages, userMessage] });
        } catch (error) {
            console.error("Error sending message or calling chatbot function:", error);
            const errorMessage = { text: "Sorry, I am unable to respond at this time.", from: "ai", createdAt: serverTimestamp() };
            setMessages((prevMessages) => [...prevMessages, errorMessage]);
        } finally {
            setIsSending(false);
        }
    };

    return (
        <div className="flex flex-col h-full bg-gray-100">
            <div className="flex-1 overflow-y-auto p-4">
                {messages.map((msg, index) => (
                    <Message key={index} text={msg.text} from={msg.from} />
                ))}
                <div ref={messagesEndRef} />
            </div>
            <MessageInput
                message={message}
                setMessage={setMessage}
                handleSendMessage={sendMessage}
                isSending={isSending}
            />
        </div>
    );
};

export default AIChatbot;
