/* global __firebase_config, __app_id, __initial_auth_token, marked */

import { useState, useEffect, useRef, useCallback } from 'react';
import { initializeApp } from 'firebase/app';
import {
  getAuth,
  signInWithCustomToken,
  signInAnonymously,
  onAuthStateChanged,
  sendSignInLinkToEmail,
  isSignInWithEmailLink,
  signInWithEmailLink,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut
} from 'firebase/auth';
import {
  getFirestore,
  collection,
  onSnapshot,
  addDoc,
  serverTimestamp,
  doc,
  setDoc,
  query,
  limit
} from 'firebase/firestore';
import { getStorage, ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import { createPortal } from 'react-dom';
import { Sparkles, MessageSquareMore, Image, LoaderCircle, X, ExternalLink, Menu, Plus, Volume2, Pause, Mic, Settings } from 'lucide-react';

// --- Firebase Configuration ---
// The global variables are provided by the Canvas environment.
// We fall back to a local config for development purposes if they are not defined.
const localFirebaseConfig = {
  apiKey: "AIzaSyDhJoNaFf0qgl4VTuUXj16ysJG1hycq-p8",
  authDomain: "ai-chatbot-88602.firebaseapp.com",
  projectId: "ai-chatbot-88602",
  storageBucket: "ai-chatbot-88602.firebasestorage.app",
  messagingSenderId: "167580287666",
  appId: "1:167580287666:web:9e8313ae6e667e47d47fab",
  measurementId: "G-CXKJ8QRLZ1"
};

const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : localFirebaseConfig;
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

// Utility function for generating unique IDs (like conversation IDs)
const generateId = () => {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0, v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
};

// Converts a base64 string to an ArrayBuffer
function base64ToArrayBuffer(base64) {
  const binaryString = window.atob(base64);
  const len = binaryString.length;
  const bytes = new Uint8Array(len);
  for (let i = 0; i < len; i++) {
    bytes[i] = binaryString.charCodeAt(i);
  }
  return bytes.buffer;
}

// Converts PCM audio data to a WAV blob
function pcmToWav(pcmData, sampleRate) {
  const pcm16 = new Int16Array(pcmData);
  const buffer = new ArrayBuffer(44 + pcm16.length * 2);
  const view = new DataView(buffer);
  const writeString = (view, offset, string) => {
      for (let i = 0; i < string.length; i++) {
          view.setUint8(offset + i, string.charCodeAt(i));
      }
  };

  let offset = 0;
  writeString(view, offset, 'RIFF'); offset += 4;
  view.setUint32(offset, 36 + pcm16.length * 2, true); offset += 4;
  writeString(view, offset, 'WAVE'); offset += 4;
  writeString(view, offset, 'fmt '); offset += 4;
  view.setUint32(offset, 16, true); offset += 4; // Sub-chunk size
  view.setUint16(offset, 1, true); offset += 2; // Audio format (1 = PCM)
  view.setUint16(offset, 1, true); offset += 2; // Number of channels
  view.setUint32(offset, sampleRate, true); offset += 4; // Sample rate
  view.setUint32(offset, sampleRate * 2, true); offset += 4; // Byte rate
  view.setUint16(offset, 2, true); offset += 2; // Block align
  view.setUint16(offset, 16, true); offset += 2; // Bits per sample
  writeString(view, offset, 'data'); offset += 4;
  view.setUint32(offset, pcm16.length * 2, true); offset += 4; // Data size

  for (let i = 0; i < pcm16.length; i++) {
      view.setInt16(offset, pcm16[i], true);
      offset += 2;
  }

  return new Blob([view], { type: 'audio/wav' });
}


// The main App component
function App() {
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);
  const [storage, setStorage] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailSent, setEmailSent] = useState(false);
  const [isSigningInWithLink, setIsSigningInWithLink] = useState(false);
  const [isAIGenerating, setIsAIGenerating] = useState(false);
  const [authMode, setAuthMode] = useState('email-link');

  // New states for conversation history and sidebar
  const [conversations, setConversations] = useState([]);
  const [currentConversationId, setCurrentConversationId] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isSummarizing, setIsSummarizing] = useState(false);
  const [isGeneratingImage, setIsGeneratingImage] = useState(false);
  const [currentAudio, setCurrentAudio] = useState(null);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [voiceName, setVoiceName] = useState('Kore');
  const [showVoiceModal, setShowVoiceModal] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  // New states for Gemini features
  const [isRecording, setIsRecording] = useState(false);
  const [showSettingsModal, setShowSettingsModal] = useState(false);
  const [systemInstruction, setSystemInstruction] = useState('');
  const [generationSettings, setGenerationSettings] = useState({
    temperature: 0.9,
    topK: 40,
    topP: 0.95
  });

  const messagesEndRef = useRef(null);
  const audioContextRef = useRef(null);
  const audioRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  // Scroll to bottom of messages whenever messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Exponential backoff utility for API calls
  const fetchWithExponentialBackoff = async (url, options, retries = 5, delay = 1000) => {
    try {
      const response = await fetch(url, options);
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`API error: ${errorData.error?.message || response.statusText}`);
      }
      return response;
    } catch (error) {
      if (retries > 0 && error.message.includes("API error")) {
        await new Promise(resolve => setTimeout(resolve, delay));
        return fetchWithExponentialBackoff(url, options, retries - 1, delay * 2);
      }
      throw error;
    }
  };

  // 1. Initialize Firebase and set up authentication.
  useEffect(() => {
    if (Object.keys(firebaseConfig).length === 0 || !firebaseConfig.apiKey) {
      setError('Firebase configuration is missing or incomplete. Please ensure your Firebase config is correctly set, especially the API Key.');
      setLoading(false);
      return;
    }

    try {
      const firebaseApp = initializeApp(firebaseConfig);
      const authInstance = getAuth(firebaseApp);
      const dbInstance = getFirestore(firebaseApp);
      const storageInstance = getStorage(firebaseApp);

      setAuth(authInstance);
      setDb(dbInstance);
      setStorage(storageInstance);

      const unsubscribe = onAuthStateChanged(authInstance, async (currentUser) => {
        if (currentUser) {
          setUser(currentUser);
          setIsAuthReady(true);
          setError(null);
        } else {
          if (initialAuthToken) {
             try {
               await signInWithCustomToken(authInstance, initialAuthToken);
             } catch (e) {
               console.error("Custom token sign-in failed:", e);
               setError(`Custom token sign-in failed: ${e.message}. This usually means the token is invalid or expired. Please try refreshing.`);
             }
          } else if (!isSigningInWithLink) {
            try {
              await signInAnonymously(authInstance);
            } catch (e) {
              console.error("Anonymous sign-in failed:", e);
              setError(`Anonymous sign-in failed: ${e.message}. Please ensure Anonymous Authentication is enabled in your Firebase project settings.`);
            }
          }
          setIsAuthReady(true);
        }
        setLoading(false);
      });
      return () => unsubscribe();
    } catch (e) {
      setError(`Failed to initialize Firebase: ${e.message}. Please check the console for more details.`);
      setLoading(false);
    }
  }, []);

  // 2. Handle email link sign-in.
  useEffect(() => {
    if (!auth || !isAuthReady) return;

    if (isSignInWithEmailLink(auth, window.location.href)) {
      setIsSigningInWithLink(true);
      setLoading(true);

      let storedEmail = window.localStorage.getItem('emailForSignIn');

      if (!storedEmail) {
        // Using a custom modal instead of prompt
        const modalDiv = document.createElement('div');
        modalDiv.className = 'fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50';
        modalDiv.innerHTML = `
          <div class="bg-white p-8 rounded-lg shadow-lg max-w-sm w-full">
            <h3 class="text-xl font-bold mb-4">Confirm Email</h3>
            <p class="mb-4">Please provide your email to complete sign-in.</p>
            <input id="email-confirm-input" type="email" placeholder="Enter your email" class="w-full p-3 border border-gray-300 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-indigo-500" required />
            <button id="email-confirm-button" class="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200">Confirm</button>
          </div>
        `;
        document.body.appendChild(modalDiv);

        const emailConfirmInput = modalDiv.querySelector('#email-confirm-input');
        const emailConfirmButton = modalDiv.querySelector('#email-confirm-button');

        emailConfirmButton.onclick = () => {
          storedEmail = emailConfirmInput.value;
          if (storedEmail) {
            document.body.removeChild(modalDiv);
            continueSignIn(storedEmail);
          } else {
            console.error("Email not provided. Please try again.");
          }
        };

        const continueSignIn = (emailToUse) => {
          signInWithEmailLink(auth, emailToUse, window.location.href)
            .then((result) => {
              window.localStorage.removeItem('emailForSignIn');
              setUser(result.user);
              setIsSigningInWithLink(false);
              setLoading(false);
              setError(null);
              window.history.replaceState({}, document.title, window.location.pathname);
            })
            .catch((error) => {
              setError(`Error completing sign-in with link: ${error.message}`);
              setIsSigningInWithLink(false);
              setLoading(false);
              window.history.replaceState({}, document.title, window.location.pathname);
            });
        };
        return; // Exit the effect to wait for user input
      }

      signInWithEmailLink(auth, storedEmail, window.location.href)
        .then((result) => {
          window.localStorage.removeItem('emailForSignIn');
          setUser(result.user);
          setIsSigningInWithLink(false);
          setLoading(false);
          setError(null);
          window.history.replaceState({}, document.title, window.location.pathname);
        })
        .catch((error) => {
          setError(`Error completing sign-in with link: ${error.message}`);
          setIsSigningInWithLink(false);
          setLoading(false);
          window.history.replaceState({}, document.title, window.location.pathname);
        });
    }
  }, [auth, isAuthReady]);

  // 3. Fetch conversations for the current user.
  useEffect(() => {
    if (!db || !user || !isAuthReady) return;

    const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
    const unsubscribe = onSnapshot(conversationsCollectionRef, (querySnapshot) => {
      const fetchedConversations = [];
      querySnapshot.forEach((doc) => {
        fetchedConversations.push({ id: doc.id, ...doc.data() });
      });

      fetchedConversations.sort((a, b) => (b.lastUpdated?.seconds || 0) - (a.lastUpdated?.seconds || 0));

      setConversations(fetchedConversations);

      if (!currentConversationId && fetchedConversations.length > 0) {
        setCurrentConversationId(fetchedConversations[0].id);
      } else if (fetchedConversations.length === 0) {
        setCurrentConversationId(null);
        setMessages([]);
      }
    }, (firestoreError) => {
      setError(`Failed to load conversations: ${firestoreError.message}`);
      console.error("Firestore conversations error:", firestoreError);
    });

    return () => unsubscribe();
  }, [db, user, isAuthReady, appId, currentConversationId]);

  // 4. Fetch messages for the current conversation.
  useEffect(() => {
    if (!db || !user || !currentConversationId) {
      setMessages([]); // Clear messages if no conversation is selected
      return;
    }

    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
    const unsubscribe = onSnapshot(messagesCollectionRef, (querySnapshot) => {
      const fetchedMessages = [];
      querySnapshot.forEach((doc) => {
        fetchedMessages.push({ id: doc.id, ...doc.data() });
      });

      fetchedMessages.sort((a, b) => (a.timestamp?.seconds || 0) - (b.timestamp?.seconds || 0));

      setMessages(fetchedMessages);
    }, (firestoreError) => {
      setError(`Failed to load messages for conversation: ${firestoreError.message}`);
      console.error("Firestore messages error:", firestoreError);
    });

    return () => unsubscribe();
  }, [db, user, currentConversationId, appId]);

  // 5. Start a new conversation.
  const startNewChat = () => {
    setCurrentConversationId(null);
    setMessages([]);
    setNewMessage('');
    setSelectedImage(null);
    setError(null);
    setIsAIGenerating(false);
    setIsSummarizing(false);
    setIsGeneratingImage(false);
    setIsSidebarOpen(false);
  };

  // 6. Handle sending a new message (and creating a new conversation if needed).
  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() && !selectedImage || !user || !db || isAIGenerating || isSpeaking || isRecording) return;

    let conversationIdToUse = currentConversationId;
    const userMessageText = newMessage;
    const imageToSend = selectedImage;
    setNewMessage('');
    setSelectedImage(null);

    // If no conversation is active, create a new one.
    if (!conversationIdToUse) {
      const newConvId = generateId();
      const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
      const newConversationDocRef = doc(conversationsCollectionRef, newConvId);
      const initialTitle = userMessageText ? (userMessageText.substring(0, 50) + (userMessageText.length > 50 ? '...' : '')) : 'New chat with image';

      try {
        await setDoc(newConversationDocRef, {
          title: initialTitle,
          createdAt: serverTimestamp(),
          lastUpdated: serverTimestamp(),
        });
        conversationIdToUse = newConvId;
        setCurrentConversationId(newConvId);
      } catch (createError) {
        setError(`Failed to create new conversation: ${createError.message}`);
        console.error("Error creating new conversation:", createError);
        return;
      }
    } else {
      // Otherwise, just update the lastUpdated timestamp of the current conversation.
      const conversationDocRef = doc(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse);
      try {
        await setDoc(conversationDocRef, { lastUpdated: serverTimestamp() }, { merge: true });
      } catch (updateError) {
        console.error("Error updating conversation timestamp:", updateError);
      }
    }
    
    // Upload image to Firebase Storage if it exists
    let imageUrl = null;
    if (imageToSend) {
        try {
            const storageRef = ref(storage, `artifacts/${appId}/users/${user.uid}/images/${Date.now()}-${file.name}`);
            const uploadTask = await uploadBytes(storageRef, imageToSend);
            imageUrl = await getDownloadURL(uploadTask.ref);
        } catch (uploadError) {
            setError(`Failed to upload image: ${uploadError.message}`);
            return;
        }
    }


    // Add user message to Firestore
    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse, 'messages');
      await addDoc(messagesCollectionRef, {
        text: userMessageText,
        userId: user.uid,
        userName: `User_${user.uid.substring(0, 5)}`,
        timestamp: serverTimestamp(),
        type: 'user-message',
        imageUrl: imageUrl, // Save image URL instead of base64 data
      });
    } catch (e) {
      setError(`Failed to send message: ${e.message}`);
      return;
    }

    const command = userMessageText.toLowerCase().trim();

    if (imageToSend) {
      handleImageAnalysis(userMessageText, imageUrl, conversationIdToUse);
    } else if (command === 'summarize') {
      handleSummarizeChat(conversationIdToUse);
    } else if (command.startsWith('image of')) {
      const imagePrompt = userMessageText.substring('image of'.length).trim();
      handleGenerateImage(imagePrompt, conversationIdToUse);
    } else {
      handleAIResponse(userMessageText, conversationIdToUse);
    }
  };

  // Function to handle image upload
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };


  // 7. Handle sending the sign-in email link.
  const handleSendSignInLink = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim()) {
      setError("Please enter a valid email address.");
      return;
    }
    setLoading(true);
    setError(null);
    const actionCodeSettings = {
      url: window.location.origin,
      handleCodeInApp: true,
    };
    try {
      await sendSignInLinkToEmail(auth, email, actionCodeSettings);
      setEmailSent(true);
      window.localStorage.setItem('emailForSignIn', email);
      setLoading(false);
    } catch (error) {
      setError(`Failed to send sign-in link: ${error.message}`);
      setLoading(false);
    }
  };

  // 8. Handle Email/Password Registration
  const handleEmailPasswordSignUp = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim() || !password.trim()) {
      setError("Please enter a valid email and password.");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
      setLoading(false);
    } catch (error) {
      setError(`Registration failed: ${error.message}`);
      setLoading(false);
    }
  };

  // 9. Handle Email/Password Login
  const handleEmailPasswordLogin = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim() || !password.trim()) {
      setError("Please enter a valid email and password.");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      await signInWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
      setLoading(false);
    } catch (error) {
      setError(`Login failed: ${error.message}`);
      setLoading(false);
    }
  };

  // 10. Handle Logout
  const handleLogout = async () => {
    if (!auth) return;
    try {
      await signOut(auth);
      setUser(null);
      setMessages([]);
      setConversations([]);
      setCurrentConversationId(null);
      setEmailSent(false);
      setIsSigningInWithLink(false);
      setAuthMode('email-link');
      setError(null);
    } catch (error) {
      setError(`Logout failed: ${error.message}`);
    }
  };

  // 11. Handle summarization of the current chat using the Gemini API
  const handleSummarizeChat = async (conversationId) => {
    if (!conversationId || messages.length === 0 || isAIGenerating || isSummarizing || isGeneratingImage) return;
    setIsSummarizing(true);
    setError(null);
    try {
      const chatHistoryText = messages
        .filter(msg => msg.type === 'user-message' || msg.type === 'ai-response')
        .map(msg => `${msg.userId === user.uid ? 'User' : 'AI'}: ${msg.text}`)
        .join('\n');
      const prompt = `Please summarize the following chat conversation into a concise paragraph:\n\n${chatHistoryText}`;
      const chatHistory = [{ role: "user", parts: [{ text: prompt }] }];
      const payload = {
        contents: chatHistory,
        generationConfig: {
          temperature: generationSettings.temperature,
          topK: generationSettings.topK,
          topP: generationSettings.topP,
        },
      };
      if (systemInstruction) {
        payload.systemInstruction = { parts: [{ text: systemInstruction }] };
      }
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      let responseText = "Sorry, I couldn't generate a summary.";
      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        responseText = result.candidates[0].content.parts[0].text;
      }
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
      await addDoc(messagesCollectionRef, {
        text: `**Chat Summary:**\n\n${responseText}`,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response'
      });
    } catch (error) {
      console.error("Error summarizing chat:", error);
      setError(`Summarization error: ${error.message}`);
    } finally {
      setIsSummarizing(false);
    }
  };

  // 12. Handle image generation using the Gemini API
  const handleGenerateImage = async (imagePrompt, conversationId) => {
    if (!conversationId || isAIGenerating || isSummarizing || isGeneratingImage) return;
    setIsGeneratingImage(true);
    setError(null);
    let tempImageMessageRef = null;
    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
      tempImageMessageRef = doc(messagesCollectionRef); // Add a placeholder message immediately
      await setDoc(tempImageMessageRef, {
        text: `Generating an image for: "${imagePrompt}"...`,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response',
        imageUrl: '',
      });
      const payload = { instances: { prompt: imagePrompt }, parameters: { "sampleCount": 1} };
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/imagen-3.0-generate-002:predict?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      if (result.predictions && result.predictions.length > 0 && result.predictions[0].bytesBase64Encoded) {
        const imageUrl = `data:image/png;base64,${result.predictions[0].bytesBase64Encoded}`;
        await setDoc(tempImageMessageRef, {
          text: `Here is the image you requested for "${imagePrompt}":`,
          imageUrl: imageUrl,
          userId: 'bot',
          userName: 'AI Chatbot',
          timestamp: serverTimestamp(),
          type: 'ai-response',
        });
      } else {
        await setDoc(tempImageMessageRef, {
          text: `Sorry, I couldn't generate an image for "${imagePrompt}". Please try again.`,
          userId: 'bot',
          userName: 'AI Chatbot',
          timestamp: serverTimestamp(),
          type: 'ai-response',
        });
      }
    } catch (error) {
      console.error("Error generating image:", error);
      setError(`Image generation error: ${error.message}`);
      if (tempImageMessageRef) {
        try {
          await setDoc(tempImageMessageRef, {
            text: `Error generating image: ${error.message}. Please try again.`,
            userId: 'bot',
            userName: 'AI Chatbot',
            timestamp: serverTimestamp(),
            type: 'ai-response',
          });
        } catch (updateError) {
          console.error("Error updating temporary message with error:", updateError);
        }
      }
    } finally {
      setIsGeneratingImage(false);
    }
  };

  // 13. Handle AI response for text and image analysis
  const handleAIResponse = async (userText, conversationId) => {
    if (isAIGenerating || !conversationId) return;
    setIsAIGenerating(true);
    setError(null);
    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');

    // Filter messages to create chat history for context
    const chatHistory = messages
      .filter(msg => msg.type === 'user-message' || msg.type === 'ai-response')
      .map(msg => ({
        role: msg.userId === user.uid ? 'user' : 'model',
        parts: [{ text: msg.text }],
      }));
    
    // Add the current user message to the history
    chatHistory.push({ role: 'user', parts: [{ text: userText }] });


    let tempMessageRef = null;
    try {
      tempMessageRef = doc(messagesCollectionRef);
      await setDoc(tempMessageRef, {
        text: '...',
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response'
      });
      const payload = {
        contents: chatHistory,
        generationConfig: {
          temperature: generationSettings.temperature,
          topK: generationSettings.topK,
          topP: generationSettings.topP,
        },
      };
      if (systemInstruction) {
        payload.systemInstruction = { parts: [{ text: systemInstruction }] };
      }
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      let responseText = "Sorry, I couldn't generate a response.";
      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        responseText = result.candidates[0].content.parts[0].text;
      }
      await setDoc(tempMessageRef, {
        text: responseText,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response'
      });
    } catch (error) {
      console.error("Error generating AI response:", error);
      setError(`AI response error: ${error.message}`);
      if (tempMessageRef) {
        try {
          await setDoc(tempMessageRef, {
            text: `Error generating AI response: ${error.message}. Please try again.`,
            userId: 'bot',
            userName: 'AI Chatbot',
            timestamp: serverTimestamp(),
            type: 'ai-response'
          });
        } catch (updateError) {
          console.error("Error updating temporary message with error:", updateError);
        }
      }
    } finally {
      setIsAIGenerating(false);
    }
  };

  const handleImageAnalysis = async (userText, imageUrl, conversationId) => {
    if (isAIGenerating || !conversationId) return;
    setIsAIGenerating(true);
    setError(null);
    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
    let tempMessageRef = null;
    try {
      tempMessageRef = doc(messagesCollectionRef);
      await setDoc(tempMessageRef, {
        text: 'Analyzing image...',
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response'
      });
      const imageData = imageUrl.split(',')[1];
      const chatHistory = [{
        role: "user",
        parts: [
          { text: userText },
          { inlineData: { mimeType: "image/png", data: imageData } }
        ]
      }];
      const payload = {
        contents: chatHistory,
        generationConfig: {
          temperature: generationSettings.temperature,
          topK: generationSettings.topK,
          topP: generationSettings.topP,
        },
      };
      if (systemInstruction) {
        payload.systemInstruction = { parts: [{ text: systemInstruction }] };
      }
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      let responseText = "Sorry, I couldn't analyze the image.";
      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        responseText = result.candidates[0].content.parts[0].text;
      }
      await setDoc(tempMessageRef, {
        text: responseText,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response'
      });
    } catch (error) {
      console.error("Error analyzing image:", error);
      setError(`Image analysis error: ${error.message}`);
      if (tempMessageRef) {
        try {
          await setDoc(tempMessageRef, {
            text: `Error analyzing image: ${error.message}. Please try again.`,
            userId: 'bot',
            userName: 'AI Chatbot',
            timestamp: serverTimestamp(),
            type: 'ai-response'
          });
        } catch (updateError) {
          console.error("Error updating temporary message with error:", updateError);
        }
      }
    } finally {
      setIsAIGenerating(false);
    }
  };

  const handleTextToSpeech = async (text, voice = 'Kore') => {
    if (!text || isSpeaking) return;
    setIsSpeaking(true);
    setCurrentAudio(null);
    setError(null);

    // Stop any currently playing audio
    if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current.src = "";
    }

    try {
        const payload = {
            contents: [{
                parts: [{ text: text }]
            }],
            generationConfig: {
                responseModalities: ["AUDIO"],
                speechConfig: {
                    voiceConfig: {
                        prebuiltVoiceConfig: { voiceName: voice }
                    }
                }
            },
            model: "gemini-2.5-flash-preview-tts"
        };
        const apiKey = "";
        const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-tts:generateContent?key=${apiKey}`;
        const response = await fetchWithExponentialBackoff(apiUrl, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload)
        });
        const result = await response.json();
        const part = result?.candidates?.[0]?.content?.parts?.[0];
        const audioData = part?.inlineData?.data;
        const mimeType = part?.inlineData?.mimeType;

        if (audioData && mimeType && mimeType.startsWith("audio/")) {
            const sampleRateMatch = mimeType.match(/rate=(\d+)/);
            const sampleRate = sampleRateMatch ? parseInt(sampleRateMatch[1], 10) : 16000;
            const pcmData = base64ToArrayBuffer(audioData);
            const wavBlob = pcmToWav(pcmData, sampleRate);
            const audioUrl = URL.createObjectURL(wavBlob);

            if (!audioRef.current) {
                audioRef.current = new Audio();
            }
            audioRef.current.src = audioUrl;
            audioRef.current.play();

            audioRef.current.onended = () => {
                setIsSpeaking(false);
            };

            setCurrentAudio(audioUrl);
        } else {
            console.error("TTS response is missing audio data.");
            setError("TTS response is missing audio data.");
            setIsSpeaking(false);
        }
    } catch (error) {
        console.error("Error generating TTS:", error);
        setError(`TTS generation error: ${error.message}`);
        setIsSpeaking(false);
    }
  };

  const stopAudio = () => {
    if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current.currentTime = 0;
        setIsSpeaking(false);
    }
  };

  const startRecording = async () => {
    if (isRecording) {
      stopRecording();
      return;
    }
    
    setError(null);
    setIsRecording(true);
    audioChunksRef.current = [];

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new MediaRecorder(stream);

      mediaRecorderRef.current.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorderRef.current.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/webm;codecs=opus' });
        const reader = new FileReader();
        reader.readAsDataURL(audioBlob);
        reader.onloadend = async () => {
          const base64Audio = reader.result.split(',')[1];
          try {
              // Placeholder for sending audio to a transcription service
              // Since there is no dedicated STT API, we will use a generic AI model for demonstration
              // In a real application, a dedicated STT API would be used for better performance
              const prompt = "Transcribe the following audio into text. Only provide the transcribed text and nothing else.";
              const payload = {
                contents: [{
                  parts: [
                    { text: prompt },
                    { inlineData: { mimeType: 'audio/webm', data: base64Audio } }
                  ]
                }],
              };
              const apiKey = "";
              const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
              
              const response = await fetchWithExponentialBackoff(apiUrl, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(payload)
              });

              const result = await response.json();
              const transcribedText = result?.candidates?.[0]?.content?.parts?.[0]?.text || "Transcription failed.";
              setNewMessage(transcribedText);
          } catch (error) {
            console.error("Speech-to-Text error:", error);
            setError(`Speech-to-Text error: ${error.message}`);
          }
        };
        setIsRecording(false);
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorderRef.current.start();
    } catch (err) {
      console.error('Error accessing microphone:', err);
      setError('Error accessing microphone. Please check your browser permissions.');
      setIsRecording(false);
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
      mediaRecorderRef.current.stop();
    }
  };

  // Main UI Component Structure
  return (
    <div className="flex flex-col h-screen bg-gray-100 antialiased font-sans">
      {/* Sidebar for chat history */}
      <aside
        className={`fixed top-0 left-0 h-full w-72 bg-gray-900 text-white shadow-xl transform transition-transform duration-300 ease-in-out z-40 ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'}`}
      >
        <div className="p-4 flex items-center justify-between border-b border-gray-700">
          <h2 className="text-xl font-bold">Chats</h2>
          <button onClick={() => setIsSidebarOpen(false)} className="text-gray-400 hover:text-white transition-colors">
            <X size={20} />
          </button>
        </div>
        <div className="p-4">
          <button onClick={startNewChat} className="w-full flex items-center justify-center p-3 bg-indigo-600 rounded-xl text-white font-semibold hover:bg-indigo-700 transition-colors shadow-lg">
            <Plus size={20} className="mr-2" /> New Chat
          </button>
        </div>
        <nav className="mt-4 overflow-y-auto h-[calc(100vh-140px)]">
          <ul>
            {conversations.map(conv => (
              <li key={conv.id}>
                <a
                  href="#"
                  onClick={() => setCurrentConversationId(conv.id)}
                  className={`block px-4 py-3 mx-2 rounded-xl transition-colors duration-200 ${currentConversationId === conv.id ? 'bg-indigo-700 text-white' : 'hover:bg-gray-800 text-gray-300'}`}
                >
                  {conv.title}
                </a>
              </li>
            ))}
          </ul>
        </nav>
      </aside>

      {/* Main chat area */}
      <div className={`flex flex-col flex-1 transition-all duration-300 ease-in-out ${isSidebarOpen ? 'ml-72' : 'ml-0'}`}>

        {/* Top bar */}
        <header className="flex-shrink-0 flex items-center justify-between p-4 bg-white shadow-lg z-30">
          <button onClick={() => setIsSidebarOpen(true)} className="text-gray-600 hover:text-gray-900 transition-colors focus:outline-none focus:ring-2 focus:ring-indigo-500 rounded-lg lg:hidden">
            <Menu size={24} />
          </button>
          <div className="flex-1 flex justify-center items-center">
            <h1 className="text-2xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-indigo-500 to-purple-600">
              Gemini Chat
            </h1>
          </div>
          <div className="flex items-center space-x-4">
            {user && (
              <div className="text-sm text-gray-500">
                Logged in as: <span className="font-semibold">{user.email || `User_${user.uid.substring(0, 5)}`}</span>
              </div>
            )}
            <button onClick={handleLogout} className="px-4 py-2 bg-red-500 text-white rounded-lg shadow-md hover:bg-red-600 transition-colors">
              Logout
            </button>
            <button onClick={() => setShowSettingsModal(true)} className="text-gray-600 hover:text-gray-900 transition-colors focus:outline-none focus:ring-2 focus:ring-indigo-500 rounded-lg">
                <Settings size={24} />
            </button>
          </div>
        </header>
        
        {/* Chat messages display */}
        <main className="flex-1 overflow-y-auto p-4 md:p-6 bg-gray-50 flex flex-col space-y-4">
          <div className="flex-1 overflow-y-auto p-4 space-y-4">
            {messages.length === 0 && (
              <div className="flex flex-col items-center justify-center h-full">
                <Sparkles size={64} className="text-gray-300 mb-4" />
                <h2 className="text-2xl font-semibold text-gray-500">Start a new conversation</h2>
                <p className="text-gray-400">Your AI assistant is ready to help.</p>
              </div>
            )}
            {messages.map((message, index) => (
              <div key={message.id} className={`flex ${message.userId === user?.uid ? 'justify-end' : 'justify-start'}`}>
                <div className={`flex items-start max-w-xl p-4 rounded-3xl shadow-lg ${message.userId === user?.uid ? 'bg-indigo-500 text-white' : 'bg-white text-gray-800'}`}>
                  {message.userId !== user?.uid && (
                    <div className="flex-shrink-0 mr-3">
                      <Sparkles size={24} className="text-purple-500" />
                    </div>
                  )}
                  <div>
                    {message.imageUrl && (
                      <div className="mb-2">
                        <img src={message.imageUrl} alt="Generated" className="max-w-xs rounded-xl shadow-md" />
                      </div>
                    )}
                    <div dangerouslySetInnerHTML={{ __html: marked.parse(message.text || '') }} />
                  </div>
                  {message.userId === user?.uid && (
                    <div className="flex-shrink-0 ml-3">
                      <img src={`https://placehold.co/40x40/E5E7EB/1F2937?text=${user?.uid.substring(0, 1)}`} alt="User Avatar" className="w-10 h-10 rounded-full" />
                    </div>
                  )}
                  {message.userId !== user?.uid && message.text !== '...' && (
                    <button
                        onClick={() => isSpeaking ? stopAudio() : handleTextToSpeech(message.text, voiceName)}
                        className={`ml-2 text-gray-500 hover:text-gray-900 transition-colors ${isSpeaking ? 'text-red-500' : ''}`}
                    >
                        {isSpeaking ? <Pause size={20} /> : <Volume2 size={20} />}
                    </button>
                  )}
                </div>
              </div>
            ))}

            {isAIGenerating && (
              <div className="flex justify-start">
                <div className="flex items-center max-w-xl p-4 rounded-3xl shadow-lg bg-white text-gray-800">
                  <div className="flex-shrink-0 mr-3">
                    <Sparkles size={24} className="text-purple-500" />
                  </div>
                  <div className="dot-flashing-container flex space-x-1">
                    <div className="dot-flashing"></div>
                    <div className="dot-flashing delay-150"></div>
                    <div className="dot-flashing delay-300"></div>
                  </div>
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>
        </main>

        {/* Input area */}
        <footer className="flex-shrink-0 p-4 md:p-6 bg-white shadow-inner flex items-center justify-center z-20">
            {selectedImage && (
                <div className="relative mr-4">
                    <img src={selectedImage} alt="Preview" className="h-16 w-16 object-cover rounded-xl" />
                    <button onClick={() => setSelectedImage(null)} className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full p-1">
                        <X size={12} />
                    </button>
                </div>
            )}
            <form onSubmit={handleSendMessage} className="flex w-full max-w-3xl space-x-2 md:space-x-4">
                <label className="p-3 bg-gray-200 rounded-xl cursor-pointer hover:bg-gray-300 transition-colors">
                    <Image size={24} />
                    <input type="file" accept="image/*" className="hidden" onChange={handleImageUpload} />
                </label>
                <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    placeholder="Send a message..."
                    className="flex-1 p-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-all duration-200"
                    disabled={isAIGenerating || isSummarizing || isGeneratingImage || isRecording}
                />
                <button
                    type="button"
                    onClick={startRecording}
                    className={`p-3 rounded-xl transition-colors ${isRecording ? 'bg-red-500 text-white hover:bg-red-600' : 'bg-gray-200 text-gray-600 hover:bg-gray-300'}`}
                    disabled={isAIGenerating || isSummarizing || isGeneratingImage}
                >
                    <Mic size={24} />
                </button>
                <button
                    type="submit"
                    className="p-3 bg-indigo-600 text-white rounded-xl shadow-md hover:bg-indigo-700 transition-colors"
                    disabled={!newMessage.trim() && !selectedImage || isAIGenerating || isSummarizing || isGeneratingImage}
                >
                    {isAIGenerating || isSummarizing || isGeneratingImage ? <LoaderCircle size={24} className="animate-spin" /> : <Sparkles size={24} />}
                </button>
            </form>
        </footer>
      </div>

      {/* Auth UI */}
      {(!user && !loading) && (
        <div className="fixed inset-0 bg-gray-900 bg-opacity-95 flex items-center justify-center p-4 z-50">
          <div className="w-full max-w-md bg-white p-8 rounded-2xl shadow-2xl">
            <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">Welcome</h2>
            {error && <div className="p-4 mb-4 text-sm text-red-700 bg-red-100 rounded-lg" role="alert">{error}</div>}

            <div className="flex justify-center mb-6 space-x-2">
              <button
                onClick={() => setAuthMode('email-link')}
                className={`px-4 py-2 rounded-full font-semibold transition-colors ${authMode === 'email-link' ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
              >
                Email Link
              </button>
              <button
                onClick={() => setAuthMode('email-password')}
                className={`px-4 py-2 rounded-full font-semibold transition-colors ${authMode === 'email-password' ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
              >
                Email/Password
              </button>
            </div>

            {authMode === 'email-link' && (
              <form onSubmit={handleSendSignInLink} className="space-y-4">
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Enter your email"
                  className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  required
                />
                <button
                  type="submit"
                  className="w-full bg-indigo-600 text-white p-3 rounded-lg font-semibold shadow-lg hover:bg-indigo-700 transition-colors"
                  disabled={loading}
                >
                  {loading ? 'Sending Link...' : 'Send Sign-in Link'}
                </button>
              </form>
            )}

            {authMode === 'email-password' && (
              <div className="space-y-4">
                <form onSubmit={handleEmailPasswordLogin} className="space-y-4">
                  <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Enter your email"
                    className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                    required
                  />
                  <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Enter your password"
                    className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                    required
                  />
                  <button
                    type="submit"
                    className="w-full bg-indigo-600 text-white p-3 rounded-lg font-semibold shadow-lg hover:bg-indigo-700 transition-colors"
                    disabled={loading}
                  >
                    {loading ? 'Logging In...' : 'Login'}
                  </button>
                </form>
                <div className="mt-4 text-center">
                  <p className="text-gray-600">Don't have an account?</p>
                  <button
                    onClick={handleEmailPasswordSignUp}
                    className="w-full p-3 mt-2 bg-gray-200 text-gray-800 rounded-lg font-semibold hover:bg-gray-300 transition-colors"
                    disabled={loading}
                  >
                    Sign Up
                  </button>
                </div>
              </div>
            )}
            
            {emailSent && (
              <div className="p-4 mt-4 text-sm text-green-700 bg-green-100 rounded-lg">
                <p>A sign-in link has been sent to your email. Please check your inbox!</p>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Loading overlay */}
      {(loading || isSigningInWithLink) && (
        <div className="fixed inset-0 bg-gray-900 bg-opacity-95 flex items-center justify-center p-4 z-50">
          <div className="flex items-center space-x-2 text-white">
            <LoaderCircle size={32} className="animate-spin text-indigo-400" />
            <p className="text-xl font-semibold">Loading...</p>
          </div>
        </div>
      )}
      
      {/* Settings Modal */}
      {showSettingsModal && createPortal(
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50">
          <div className="bg-white p-8 rounded-lg shadow-lg max-w-md w-full">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-xl font-bold text-gray-800">Gemini Settings</h3>
              <button onClick={() => setShowSettingsModal(false)} className="text-gray-400 hover:text-gray-700">
                <X size={24} />
              </button>
            </div>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700">System Instruction</label>
                <textarea
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm p-2"
                  rows="4"
                  value={systemInstruction}
                  onChange={(e) => setSystemInstruction(e.target.value)}
                  placeholder="Set a system-level instruction for the AI, e.g., 'You are a helpful coding assistant.'"
                ></textarea>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Temperature: {generationSettings.temperature}</label>
                <input
                  type="range"
                  min="0"
                  max="1"
                  step="0.05"
                  value={generationSettings.temperature}
                  onChange={(e) => setGenerationSettings({ ...generationSettings, temperature: parseFloat(e.target.value) })}
                  className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer range-lg dark:bg-gray-700"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Top K: {generationSettings.topK}</label>
                <input
                  type="range"
                  min="1"
                  max="50"
                  step="1"
                  value={generationSettings.topK}
                  onChange={(e) => setGenerationSettings({ ...generationSettings, topK: parseInt(e.target.value) })}
                  className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer range-lg dark:bg-gray-700"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Top P: {generationSettings.topP}</label>
                <input
                  type="range"
                  min="0"
                  max="1"
                  step="0.05"
                  value={generationSettings.topP}
                  onChange={(e) => setGenerationSettings({ ...generationSettings, topP: parseFloat(e.target.value) })}
                  className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer range-lg dark:bg-gray-700"
                />
              </div>
            </div>
          </div>
        </div>,
        document.body
      )}

      {/* Dot Flashing CSS Animation */}
      <style>{`
        @keyframes dotFlashing {
          0% { background-color: #a3a3a3; }
          50%, 100% { background-color: #f7f7f7; }
        }
        .dot-flashing-container > .dot-flashing {
          animation: dotFlashing 1.2s infinite linear alternate;
        }
        .dot-flashing-container > .dot-flashing.delay-150 { animation-delay: 0.15s; }
        .dot-flashing-container > .dot-flashing.delay-300 { animation-delay: 0.3s; }
      `}</style>
    </div>
  );
}

export default App;
