// The main App component
function App() {
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailSent, setEmailSent] = useState(false);
  const [isSigningInWithLink, setIsSigningInWithLink] = useState(false);
  const [isAIGenerating, setIsAIGenerating] = useState(false);
  const [authMode, setAuthMode] = useState('email-link');

  // New states for conversation history and sidebar
  const [conversations, setConversations] = useState([]);
  const [currentConversationId, setCurrentConversationId] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isSummarizing, setIsSummarizing] = useState(false);
  const [isGeneratingImage, setIsGeneratingImage] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [voiceName, setVoiceName] = useState('Kore');
  const [showSettingsModal, setShowSettingsModal] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  // New states for Gemini features
  const [systemInstruction, setSystemInstruction] = useState('');
  const [generationSettings, setGenerationSettings] = useState({
    temperature: 0.9,
    topK: 40,
    topP: 0.95
  });
  const [showGeminiModal, setShowGeminiModal] = useState(false);
  const [translationTargetLanguage, setTranslationTargetLanguage] = useState('en-US');
  const [textToContinue, setTextToContinue] = useState('');
  const [textToTranslate, setTextToTranslate] = useState('');

  // New state for the creative writing feature
  const [showCreativeWriterModal, setShowCreativeWriterModal] = useState(false);
  const [creativeTopic, setCreativeTopic] = useState('');
  const [creativeStyle, setCreativeStyle] = useState('Poem');
  const [creativeTone, setCreativeTone] = useState('Neutral');

  const messagesEndRef = useRef(null);
  const audioRef = useRef(null);
  const currentAudioIdRef = useRef(null);

  // Scroll to bottom of messages whenever messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Exponential backoff utility for API calls
  const fetchWithExponentialBackoff = async (url, options, retries = 5, delay = 1000) => {
    try {
      const response = await fetch(url, options);
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`API error: ${errorData.error?.message || response.statusText}`);
      }
      return response;
    } catch (error) {
      if (retries > 0 && error.message.includes("API error")) {
        await new Promise(resolve => setTimeout(resolve, delay));
        return fetchWithExponentialBackoff(url, options, retries - 1, delay * 2);
      }
      throw error;
    }
  };

  // 1. Initialize Firebase and set up authentication.
  useEffect(() => {
    if (Object.keys(firebaseConfig).length === 0 || !firebaseConfig.apiKey) {
      setError('Firebase configuration is missing or incomplete. Please ensure your Firebase config is correctly set, especially the API Key.');
      setLoading(false);
      return;
    }

    try {
      const firebaseApp = initializeApp(firebaseConfig);
      const authInstance = getAuth(firebaseApp);
      const dbInstance = getFirestore(firebaseApp);

      setAuth(authInstance);
      setDb(dbInstance);

      const unsubscribe = onAuthStateChanged(authInstance, async (currentUser) => {
        if (currentUser) {
          setUser(currentUser);
          setIsAuthReady(true);
          setError(null);
        } else {
          if (initialAuthToken) {
             try {
               await signInWithCustomToken(authInstance, initialAuthToken);
             } catch (e) {
               console.error("Custom token sign-in failed:", e);
               setError(`Custom token sign-in failed: ${e.message}. This usually means the token is invalid or expired. Please try refreshing.`);
             }
          } else if (!isSigningInWithLink) {
            try {
              await signInAnonymously(authInstance);
            } catch (e) {
              console.error("Anonymous sign-in failed:", e);
              setError(`Anonymous sign-in failed: ${e.message}. Please ensure Anonymous Authentication is enabled in your Firebase project settings.`);
            }
          }
          setIsAuthReady(true);
        }
        setLoading(false);
      });
      return () => unsubscribe();
    } catch (e) {
      setError(`Failed to initialize Firebase: ${e.message}. Please check the console for more details.`);
      setLoading(false);
    }
  }, []);

  // 2. Handle email link sign-in.
  useEffect(() => {
    if (!auth || !isAuthReady) return;

    if (isSignInWithEmailLink(auth, window.location.href)) {
      setIsSigningInWithLink(true);
      setLoading(true);

      let storedEmail = window.localStorage.getItem('emailForSignIn');

      if (!storedEmail) {
        // Using a custom modal instead of prompt
        const modalDiv = document.createElement('div');
        modalDiv.className = 'fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50';
        modalDiv.innerHTML = `
          <div class="bg-white dark:bg-gray-800 p-8 rounded-lg shadow-lg max-w-sm w-full">
            <h3 class="text-xl font-bold mb-4">Confirm Email</h3>
            <p class="mb-4">Please provide your email to complete sign-in.</p>
            <input id="email-confirm-input" type="email" placeholder="Enter your email" class="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500" required />
            <button id="email-confirm-button" class="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200">Confirm</button>
          </div>
        `;
        document.body.appendChild(modalDiv);

        const emailConfirmInput = modalDiv.querySelector('#email-confirm-input');
        const emailConfirmButton = modalDiv.querySelector('#email-confirm-button');

        emailConfirmButton.onclick = () => {
          storedEmail = emailConfirmInput.value;
          if (storedEmail) {
            document.body.removeChild(modalDiv);
            continueSignIn(storedEmail);
          } else {
            console.error("Email not provided. Please try again.");
          }
        };

        const continueSignIn = (emailToUse) => {
          signInWithEmailLink(auth, emailToUse, window.location.href)
            .then((result) => {
              window.localStorage.removeItem('emailForSignIn');
              setUser(result.user);
              setIsSigningInWithLink(false);
              setLoading(false);
              setError(null);
              window.history.replaceState({}, document.title, window.location.pathname);
            })
            .catch((error) => {
              setError(`Error completing sign-in with link: ${error.message}`);
              setIsSigningInWithLink(false);
              setLoading(false);
              window.history.replaceState({}, document.title, window.location.pathname);
            });
        };
        return; // Exit the effect to wait for user input
      }

      signInWithEmailLink(auth, storedEmail, window.location.href)
        .then((result) => {
          window.localStorage.removeItem('emailForSignIn');
          setUser(result.user);
          setIsSigningInWithLink(false);
          setLoading(false);
          setError(null);
          window.history.replaceState({}, document.title, window.location.pathname);
        })
        .catch((error) => {
          setError(`Error completing sign-in with link: ${error.message}`);
          setIsSigningInWithLink(false);
          setLoading(false);
          window.history.replaceState({}, document.title, window.location.pathname);
        });
    }
  }, [auth, isAuthReady]);

  // 3. Fetch conversations for the current user.
  useEffect(() => {
    if (!db || !user || !isAuthReady) return;

    const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
    const unsubscribe = onSnapshot(conversationsCollectionRef, (querySnapshot) => {
      const fetchedConversations = [];
      querySnapshot.forEach((doc) => {
        fetchedConversations.push({ id: doc.id, ...doc.data() });
      });

      fetchedConversations.sort((a, b) => (b.lastUpdated?.seconds || 0) - (a.lastUpdated?.seconds || 0));

      setConversations(fetchedConversations);

      if (!currentConversationId && fetchedConversations.length > 0) {
        setCurrentConversationId(fetchedConversations[0].id);
      } else if (fetchedConversations.length === 0) {
        setCurrentConversationId(null);
        setMessages([]);
      }
    }, (firestoreError) => {
      setError(`Failed to load conversations: ${firestoreError.message}`);
      console.error("Firestore conversations error:", firestoreError);
    });

    return () => unsubscribe();
  }, [db, user, isAuthReady, appId, currentConversationId]);

  // 4. Fetch messages for the current conversation.
  useEffect(() => {
    if (!db || !user || !currentConversationId) {
      setMessages([]); // Clear messages if no conversation is selected
      return;
    }

    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
    const unsubscribe = onSnapshot(messagesCollectionRef, (querySnapshot) => {
      const fetchedMessages = [];
      querySnapshot.forEach((doc) => {
        fetchedMessages.push({ id: doc.id, ...doc.data() });
      });

      fetchedMessages.sort((a, b) => (a.timestamp?.seconds || 0) - (b.timestamp?.seconds || 0));

      setMessages(fetchedMessages);
    }, (firestoreError) => {
      setError(`Failed to load messages for conversation: ${firestoreError.message}`);
      console.error("Firestore messages error:", firestoreError);
    });

    return () => unsubscribe();
  }, [db, user, currentConversationId, appId]);

  // 5. Start a new conversation.
  const startNewChat = async () => {
    if (!db || !user) {
      setError("You must be logged in to start a new chat.");
      return;
    }

    setMessages([]);
    setNewMessage('');
    setSelectedImage(null);
    setError(null);
    setIsAIGenerating(false);
    setIsSummarizing(false);
    setIsGeneratingImage(false);

    try {
      const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
      const newConversationDocRef = doc(conversationsCollectionRef);
      const newConvId = newConversationDocRef.id;

      await setDoc(newConversationDocRef, {
        title: 'New Chat',
        createdAt: serverTimestamp(),
        lastUpdated: serverTimestamp(),
      });

      setCurrentConversationId(newConvId);
      setIsSidebarOpen(false);
    } catch (e) {
      setError(`Failed to create a new chat: ${e.message}`);
      console.error("Error creating new conversation:", e);
    }
  };

  // 6. Handle sending a new message (and creating a new conversation if needed).
  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() && !selectedImage || !user || !db || isAIGenerating || isSpeaking) return;

    let conversationIdToUse = currentConversationId;
    const userMessageText = newMessage;
    const imageToSend = selectedImage;
    setNewMessage('');
    setSelectedImage(null);

    const command = userMessageText.toLowerCase().trim();

    // If no conversation is active, create a new one.
    if (!conversationIdToUse) {
      const newConvId = generateId();
      const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
      const newConversationDocRef = doc(conversationsCollectionRef, newConvId);
      const initialTitle = userMessageText ? (userMessageText.substring(0, 50) + (userMessageText.length > 50 ? '...' : '')) : 'New chat with image';

      try {
        await setDoc(newConversationDocRef, {
          title: initialTitle,
          createdAt: serverTimestamp(),
          lastUpdated: serverTimestamp(),
        });
        conversationIdToUse = newConvId;
        setCurrentConversationId(newConvId);
      } catch (createError) {
        setError(`Failed to create new conversation: ${createError.message}`);
        console.error("Error creating new conversation:", createError);
        return;
      }
    } else {
      // Otherwise, just update the lastUpdated timestamp of the current conversation.
      const conversationDocRef = doc(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse);
      try {
        await setDoc(conversationDocRef, { lastUpdated: serverTimestamp() }, { merge: true });
      } catch (updateError) {
        console.error("Error updating conversation timestamp:", updateError);
      }
    }

    // Add user message to Firestore
    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse, 'messages');
      await addDoc(messagesCollectionRef, {
        text: userMessageText,
        userId: user.uid,
        userName: `User_${user.uid.substring(0, 5)}`,
        timestamp: serverTimestamp(),
        type: 'user-message',
        imageData: imageToSend
      });
    } catch (e) {
      setError(`Failed to send message: ${e.message}`);
      return;
    }

    if (imageToSend) {
      handleImageAnalysis(userMessageText, imageToSend, conversationIdToUse);
    } else if (command.startsWith('image of')) {
      const imagePrompt = userMessageText.substring('image of'.length).trim();
      handleGenerateImage(imagePrompt, conversationIdToUse);
    } else {
      handleAIResponse(userMessageText, conversationIdToUse);
    }
  };

  // Function to handle image upload
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  // 7. Handle sending the sign-in email link.
  const handleSendSignInLink = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim()) {
      setError("Please enter a valid email address.");
      return;
    }
    setLoading(true);
    setError(null);
    const actionCodeSettings = {
      url: window.location.origin,
      handleCodeInApp: true,
    };
    try {
      await sendSignInLinkToEmail(auth, email, actionCodeSettings);
      setEmailSent(true);
      window.localStorage.setItem('emailForSignIn', email);
      setLoading(false);
    } catch (error) {
      setError(`Failed to send sign-in link: ${error.message}`);
      setLoading(false);
    }
  };

  // 8. Handle Email/Password Registration
  const handleEmailPasswordSignUp = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim() || !password.trim()) {
      setError("Please enter a valid email and password.");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
      setLoading(false);
    } catch (error) {
      setError(`Registration failed: ${error.message}`);
      setLoading(false);
    }
  };

  // 9. Handle Email/Password Login
  const handleEmailPasswordLogin = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim() || !password.trim()) {
      setError("Please enter a valid email and password.");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      await signInWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
      setLoading(false);
    } catch (error) {
      setError(`Login failed: ${error.message}`);
      setLoading(false);
    }
  };

  // 10. Handle Logout
  const handleLogout = async () => {
    if (!auth) return;
    try {
      await signOut(auth);
      setUser(null);
      setMessages([]);
      setConversations([]);
      setCurrentConversationId(null);
      setEmailSent(false);
      setIsSigningInWithLink(false);
      setAuthMode('email-link');
      setError(null);
    } catch (error) {
      setError(`Logout failed: ${error.message}`);
    }
  };

  // 11. Handle summarization of the current chat using the Gemini API
  const handleSummarizeChat = async () => {
    const conversationId = currentConversationId;
    if (!conversationId || messages.length === 0 || isAIGenerating || isSummarizing || isGeneratingImage) return;
    setIsSummarizing(true);
    setError(null);
    setShowGeminiModal(false);
    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
    const summarizeMessageRef = doc(messagesCollectionRef);
    try {
      // Add a placeholder message for the AI response
      await setDoc(summarizeMessageRef, {
        text: 'Summarizing...',
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response',
      });
      const chatHistoryText = messages
        .filter(msg => msg.type === 'user-message' || msg.type === 'ai-response')
        .map(msg => `${msg.userId === user.uid ? 'User' : 'AI'}: ${msg.text}`)
        .join('\n');
      const prompt = `Please summarize the following chat conversation into a concise paragraph:\n\n${chatHistoryText}`;
      const chatHistory = [{ role: "user", parts: [{ text: prompt }] }];
      const payload = {
        contents: chatHistory,
        generationConfig: {
          temperature: generationSettings.temperature,
          topK: generationSettings.topK,
          topP: generationSettings.topP,
        },
      };
      if (systemInstruction) {
        payload.systemInstruction = { parts: [{ text: systemInstruction }] };
      }
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      let responseText = "Sorry, I couldn't generate a summary.";
      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        responseText = result.candidates[0].content.parts[0].text;
      }
      await setDoc(summarizeMessageRef, {
        text: responseText,
        type: 'ai-response',
      }, { merge: true });
    } catch (error) {
      console.error("Error summarizing chat:", error);
      await setDoc(summarizeMessageRef, {
        text: `An error occurred while summarizing. Error: ${error.message}`,
      }, { merge: true });
      setError(`Summarization error: ${error.message}`);
    } finally {
      setIsSummarizing(false);
    }
  };

  // 12. Handle image generation from a text prompt using the Gemini API
  const handleGenerateImage = async (imagePrompt, conversationId) => {
    if (!conversationId || isAIGenerating || isGeneratingImage || isSummarizing) return;
    setIsGeneratingImage(true);
    setError(null);
    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
    const imageMessageRef = doc(messagesCollectionRef);
    try {
      // Add a placeholder message for the AI response
      await setDoc(imageMessageRef, {
        text: 'Generating image...',
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response',
      });
      const payload = { instances: { prompt: imagePrompt }, parameters: { "sampleCount": 1 } };
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/imagen-3.0-generate-002:predict?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      let imageUrl = null;
      if (result.predictions && result.predictions.length > 0 && result.predictions[0].bytesBase64Encoded) {
        imageUrl = `data:image/png;base64,${result.predictions[0].bytesBase64Encoded}`;
        await setDoc(imageMessageRef, {
          text: `Here is the image you requested: "${imagePrompt}"`,
          imageUrl: imageUrl,
          type: 'ai-image-response'
        }, { merge: true });
      } else {
        await setDoc(imageMessageRef, {
          text: "Sorry, I couldn't generate an image based on that prompt.",
          type: 'ai-response',
        }, { merge: true });
      }
    } catch (error) {
      console.error("Error generating image:", error);
      await setDoc(imageMessageRef, {
        text: `An error occurred while generating the image. Error: ${error.message}`,
      }, { merge: true });
      setError(`Image generation error: ${error.message}`);
    } finally {
      setIsGeneratingImage(false);
    }
  };

  // 13. Handle image analysis using the Gemini API
  const handleImageAnalysis = async (userPrompt, imageData, conversationId) => {
    if (!conversationId || isAIGenerating || isGeneratingImage || isSummarizing) return;
    setIsAIGenerating(true);
    setError(null);
    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
    const aiMessageRef = doc(messagesCollectionRef);
    try {
      // Add a placeholder message for the AI response
      await setDoc(aiMessageRef, {
        text: 'Analyzing image...',
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response',
      });
      const base64ImageData = imageData.split(',')[1];
      const chatHistory = [{
        role: "user",
        parts: [
          { text: userPrompt || "What is in this image?" },
          {
            inlineData: {
              mimeType: "image/jpeg", // Assuming JPEG, but could be dynamic
              data: base64ImageData
            }
          }
        ]
      }];
      const payload = {
        contents: chatHistory,
        generationConfig: {
          temperature: generationSettings.temperature,
          topK: generationSettings.topK,
          topP: generationSettings.topP,
        },
      };
      if (systemInstruction) {
        payload.systemInstruction = { parts: [{ text: systemInstruction }] };
      }
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      let responseText = "Sorry, I couldn't analyze the image.";
      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        responseText = result.candidates[0].content.parts[0].text;
      }
      await setDoc(aiMessageRef, {
        text: responseText,
        type: 'ai-response',
      }, { merge: true });
    } catch (error) {
      console.error("Error analyzing image:", error);
      await setDoc(aiMessageRef, {
        text: `An error occurred while analyzing the image. Error: ${error.message}`,
      }, { merge: true });
      setError(`Image analysis error: ${error.message}`);
    } finally {
      setIsAIGenerating(false);
    }
  };

  // 14. Handle general AI response using the Gemini API
  const handleAIResponse = async (userMessage, conversationId) => {
    if (!conversationId || !userMessage || isAIGenerating || isGeneratingImage || isSummarizing) return;
    setIsAIGenerating(true);
    setError(null);
    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
    const aiMessageRef = doc(messagesCollectionRef);
    try {
      // Add a placeholder message for the AI response
      await setDoc(aiMessageRef, {
        text: 'Thinking...',
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response',
      });
      // Get the last 10 messages for context, excluding the placeholder
      const historyQuery = query(messagesCollectionRef, limit(10));
      const historySnapshot = await getDocs(historyQuery);
      const chatHistory = historySnapshot.docs
        .map(doc => doc.data())
        .filter(msg => msg.id !== aiMessageRef.id) // Exclude the placeholder
        .sort((a, b) => (a.timestamp?.seconds || 0) - (b.timestamp?.seconds || 0))
        .map(msg => ({
          role: msg.userId === user.uid ? 'user' : 'model',
          parts: [{ text: msg.text }]
        }));

      // Add the current user message to the history
      chatHistory.push({
        role: 'user',
        parts: [{ text: userMessage }]
      });

      const payload = {
        contents: chatHistory,
        generationConfig: {
          temperature: generationSettings.temperature,
          topK: generationSettings.topK,
          topP: generationSettings.topP,
        },
      };

      if (systemInstruction) {
        payload.systemInstruction = { parts: [{ text: systemInstruction }] };
      }

      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      let responseText = "Sorry, an unexpected error occurred.";
      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        responseText = result.candidates[0].content.parts[0].text;
      }
      await setDoc(aiMessageRef, {
        text: responseText,
        type: 'ai-response',
      }, { merge: true });
    } catch (error) {
      console.error("Error generating AI response:", error);
      await setDoc(aiMessageRef, {
        text: `An error occurred. Error: ${error.message}`,
      }, { merge: true });
      setError(`AI response error: ${error.message}`);
    } finally {
      setIsAIGenerating(false);
    }
  };

  // 15. Handle text translation
  const handleTranslateText = async (text, targetLanguage, conversationId) => {
    if (!text || !targetLanguage || isAIGenerating) return;
    setIsAIGenerating(true);
    setError(null);
    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
    const aiMessageRef = doc(messagesCollectionRef);
    try {
      await setDoc(aiMessageRef, {
        text: `Translating to ${targetLanguage}...`,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response',
      });
      const prompt = `Translate the following text into ${targetLanguage}: "${text}"`;
      const chatHistory = [{ role: "user", parts: [{ text: prompt }] }];
      const payload = {
        contents: chatHistory,
        generationConfig: {
          temperature: generationSettings.temperature,
          topK: generationSettings.topK,
          topP: generationSettings.topP,
        },
      };
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      let translatedText = "Sorry, I couldn't translate that.";
      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        translatedText = result.candidates[0].content.parts[0].text;
      }
      await setDoc(aiMessageRef, {
        text: translatedText,
        type: 'ai-response',
      }, { merge: true });
    } catch (error) {
      console.error("Error translating text:", error);
      await setDoc(aiMessageRef, {
        text: `An error occurred while translating. Error: ${error.message}`,
      }, { merge: true });
      setError(`Translation error: ${error.message}`);
    } finally {
      setIsAIGenerating(false);
      setShowGeminiModal(false);
    }
  };

  // 16. Handle text continuation
  const handleContinueText = async (text, conversationId) => {
    if (!text || isAIGenerating) return;
    setIsAIGenerating(true);
    setError(null);
    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
    const aiMessageRef = doc(messagesCollectionRef);
    try {
      await setDoc(aiMessageRef, {
        text: `Continuing the text...`,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response',
      });
      const prompt = `Please continue the following text in a creative and coherent way: "${text}"`;
      const chatHistory = [{ role: "user", parts: [{ text: prompt }] }];
      const payload = {
        contents: chatHistory,
        generationConfig: {
          temperature: generationSettings.temperature,
          topK: generationSettings.topK,
          topP: generationSettings.topP,
        },
      };
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      let continuedText = "Sorry, I couldn't continue the text.";
      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        continuedText = result.candidates[0].content.parts[0].text;
      }
      await setDoc(aiMessageRef, {
        text: continuedText,
        type: 'ai-response',
      }, { merge: true });
    } catch (error) {
      console.error("Error continuing text:", error);
      await setDoc(aiMessageRef, {
        text: `An error occurred while continuing the text. Error: ${error.message}`,
      }, { merge: true });
      setError(`Text continuation error: ${error.message}`);
    } finally {
      setIsAIGenerating(false);
      setShowGeminiModal(false);
    }
  };

  // 17. Handle text generation for creative writing
  const handleCreativeWriting = async (topic, style, tone, conversationId) => {
    if (!topic || isAIGenerating) return;
    setIsAIGenerating(true);
    setError(null);
    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
    const aiMessageRef = doc(messagesCollectionRef);
    try {
      await setDoc(aiMessageRef, {
        text: `Generating a ${style.toLowerCase()} on the topic of "${topic}" with a ${tone.toLowerCase()} tone...`,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response',
      });
      const prompt = `Write a ${style.toLowerCase()} about "${topic}" in a ${tone.toLowerCase()} tone.`;
      const chatHistory = [{ role: "user", parts: [{ text: prompt }] }];
      const payload = {
        contents: chatHistory,
        generationConfig: {
          temperature: generationSettings.temperature,
          topK: generationSettings.topK,
          topP: generationSettings.topP,
        },
      };
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      let creativeText = "Sorry, I couldn't generate the creative text.";
      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        creativeText = result.candidates[0].content.parts[0].text;
      }
      await setDoc(aiMessageRef, {
        text: creativeText,
        type: 'ai-response',
      }, { merge: true });
    } catch (error) {
      console.error("Error generating creative text:", error);
      await setDoc(aiMessageRef, {
        text: `An error occurred while generating creative text. Error: ${error.message}`,
      }, { merge: true });
      setError(`Creative writing error: ${error.message}`);
    } finally {
      setIsAIGenerating(false);
      setShowCreativeWriterModal(false);
    }
  };

  // 18. Handle Text-to-Speech generation and playback
  const handleTTS = async (text, audioId, voice = 'Kore') => {
    if (isSpeaking || !text || !audioId) return;

    // If there's an audio playing, stop it first
    if (audioRef.current && !audioRef.current.paused) {
      audioRef.current.pause();
      setIsSpeaking(false);
      currentAudioIdRef.current = null;
      // If the user clicked the same message, just stop it.
      if (audioId === currentAudioIdRef.current) return;
    }

    setIsSpeaking(true);
    currentAudioIdRef.current = audioId;

    try {
      const payload = {
        contents: [{
          parts: [{ text: text }]
        }],
        generationConfig: {
          responseModalities: ["AUDIO"],
          speechConfig: {
            voiceConfig: {
              prebuiltVoiceConfig: { voiceName: voice }
            }
          }
        },
        model: "gemini-2.5-flash-preview-tts"
      };
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-tts:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      const part = result?.candidates?.[0]?.content?.parts?.[0];
      const audioData = part?.inlineData?.data;
      const mimeType = part?.inlineData?.mimeType;

      if (audioData && mimeType && mimeType.startsWith("audio/")) {
        // Extract sample rate from the mimeType string
        const sampleRateMatch = mimeType.match(/rate=(\d+)/);
        const sampleRate = sampleRateMatch ? parseInt(sampleRateMatch[1], 10) : 16000;

        const pcmData = base64ToArrayBuffer(audioData);
        const pcm16 = new Int16Array(pcmData);
        const wavBlob = pcmToWav(pcm16, sampleRate);
        const audioUrl = URL.createObjectURL(wavBlob);

        audioRef.current = new Audio(audioUrl);
        audioRef.current.play();

        audioRef.current.onended = () => {
          setIsSpeaking(false);
          currentAudioIdRef.current = null;
          URL.revokeObjectURL(audioUrl); // Clean up the URL after playback
        };
      } else {
        setError("TTS generation failed or returned no audio data.");
        setIsSpeaking(false);
        currentAudioIdRef.current = null;
      }
    } catch (error) {
      console.error("TTS error:", error);
      setError(`An error occurred during TTS: ${error.message}`);
      setIsSpeaking(false);
      currentAudioIdRef.current = null;
    }
  };

  const handleStopTTS = () => {
    if (audioRef.current) {
      audioRef.current.pause();
      audioRef.current.currentTime = 0;
      setIsSpeaking(false);
      currentAudioIdRef.current = null;
    }
  };

  // Render authentication UI if user is not authenticated
  const renderAuthUI = () => (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-800 dark:text-gray-200 p-4">
      <div className="w-full max-w-md p-8 bg-white dark:bg-gray-800 rounded-xl shadow-lg">
        <h2 className="text-3xl font-bold text-center mb-6 text-indigo-500">
          MadisonAI
        </h2>
        {error && <div className="p-4 mb-4 text-sm text-red-700 bg-red-100 rounded-lg dark:bg-red-200 dark:text-red-800" role="alert">{error}</div>}
        {user && (
          <div className="text-center mb-4">
            <p className="text-lg">Welcome, {user.uid}!</p>
            <p className="text-sm text-gray-500 dark:text-gray-400">You are signed in anonymously. To save your chats, please use email sign-in.</p>
            <button onClick={handleLogout} className="mt-4 w-full bg-red-500 text-white p-3 rounded-lg shadow-md hover:bg-red-600 transition-colors duration-200">
              Logout
            </button>
          </div>
        )}
        <div className="flex justify-center mb-4">
          <button
            onClick={() => setAuthMode('email-link')}
            className={`px-4 py-2 rounded-l-lg font-semibold transition-colors duration-200 ${
              authMode === 'email-link' ? 'bg-indigo-500 text-white' : 'bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300'
            }`}
          >
            Email Link
          </button>
          <button
            onClick={() => setAuthMode('email-password')}
            className={`px-4 py-2 rounded-r-lg font-semibold transition-colors duration-200 ${
              authMode === 'email-password' ? 'bg-indigo-500 text-white' : 'bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300'
            }`}
          >
            Email/Password
          </button>
        </div>
        {authMode === 'email-link' && (
          <form onSubmit={handleSendSignInLink} className="space-y-4">
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
              required
            />
            <button
              type="submit"
              className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200"
              disabled={emailSent}
            >
              {emailSent ? 'Link Sent! Check your email.' : 'Sign in with Email Link'}
            </button>
          </form>
        )}
        {authMode === 'email-password' && (
          <div className="space-y-4">
            <form onSubmit={handleEmailPasswordLogin} className="space-y-4">
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Email address"
                className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                required
              />
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Password"
                className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                required
              />
              <button
                type="submit"
                className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200"
              >
                Login
              </button>
            </form>
            <div className="relative flex items-center justify-center my-4">
              <div className="w-full h-px bg-gray-300 dark:bg-gray-600"></div>
              <span className="absolute px-3 text-sm text-gray-500 dark:text-gray-400 bg-white dark:bg-gray-800">OR</span>
            </div>
            <button
              onClick={handleEmailPasswordSignUp}
              className="w-full bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 p-3 rounded-lg shadow-md hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors duration-200"
            >
              Create Account
            </button>
          </div>
        )}
      </div>
    </div>
  );

  // Render a loading spinner while authenticating
  if (loading || !isAuthReady) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900">
        <LoaderCircle className="animate-spin text-indigo-500 h-10 w-10" />
      </div>
    );
  }

  // If user is not authenticated and not in the middle of an email link sign-in, show auth UI.
  if (!user && !isSigningInWithLink) {
    return renderAuthUI();
  }

  // Main chat application UI
  return (
    <div className="flex h-screen bg-gray-100 dark:bg-gray-900 text-gray-800 dark:text-gray-200 antialiased">
      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 transform ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'} transition-transform duration-300 ease-in-out md:relative md:translate-x-0 flex flex-col w-64 bg-white dark:bg-gray-800 shadow-lg z-50`}>
        <div className="flex items-center justify-between p-4 border-b border-gray-200 dark:border-gray-700">
          <div className="flex items-center space-x-2">
            <Sparkles className="h-6 w-6 text-indigo-500" />
            <span className="text-xl font-bold">MadisonAI</span>
          </div>
          <button onClick={() => setIsSidebarOpen(false)} className="md:hidden text-gray-500 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-100">
            <X className="h-6 w-6" />
          </button>
        </div>
        <div className="p-4">
          <button onClick={startNewChat} className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200 flex items-center justify-center space-x-2">
            <Plus className="h-5 w-5" />
            <span>New Chat</span>
          </button>
        </div>
        <div className="flex-1 overflow-y-auto p-4 space-y-2">
          {conversations.map((conv) => (
            <div
              key={conv.id}
              onClick={() => {
                setCurrentConversationId(conv.id);
                setIsSidebarOpen(false);
              }}
              className={`p-3 rounded-lg cursor-pointer transition-colors duration-200 ${
                currentConversationId === conv.id ? 'bg-indigo-100 dark:bg-indigo-700 text-indigo-800 dark:text-white' : 'bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600'
              }`}
            >
              <h4 className="text-sm font-semibold truncate">{conv.title}</h4>
            </div>
          ))}
        </div>
        <div className="p-4 border-t border-gray-200 dark:border-gray-700">
          <div className="flex items-center justify-between text-sm text-gray-500 dark:text-gray-400 mb-2">
            <span>Signed in as:</span>
            <span className="font-mono text-xs">{user.uid}</span>
          </div>
          <button
            onClick={() => setShowSettingsModal(true)}
            className="w-full bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 p-3 rounded-lg shadow-md hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors duration-200 flex items-center justify-center space-x-2"
          >
            <Settings className="h-5 w-5" />
            <span>Settings</span>
          </button>
          <button
            onClick={handleLogout}
            className="w-full mt-2 bg-red-500 text-white p-3 rounded-lg shadow-md hover:bg-red-600 transition-colors duration-200 flex items-center justify-center space-x-2"
          >
            <ExternalLink className="h-5 w-5" />
            <span>Logout</span>
          </button>
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="flex-1 flex flex-col transition-all duration-300 ease-in-out">
        {/* Header */}
        <header className="md:hidden flex items-center justify-between p-4 bg-white dark:bg-gray-800 border-b border-gray-200 dark:border-gray-700">
          <button onClick={() => setIsSidebarOpen(true)} className="text-gray-500 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-100">
            <Menu className="h-6 w-6" />
          </button>
          <div className="flex items-center space-x-2">
            <Sparkles className="h-6 w-6 text-indigo-500" />
            <h1 className="text-xl font-bold">MadisonAI</h1>
          </div>
          <div className="w-6 h-6"></div> {/* Placeholder for alignment */}
        </header>

        {/* Message container */}
        <main className="flex-1 overflow-y-auto p-4 md:p-6 space-y-6 bg-gray-50 dark:bg-gray-900">
          {messages.length === 0 ? (
            <div className="flex flex-col items-center justify-center h-full text-center text-gray-400 dark:text-gray-600">
              <MessageSquareMore className="h-16 w-16 mb-4" />
              <p className="text-lg font-semibold">Start a new conversation.</p>
              <p className="text-sm">Your chats will appear here.</p>
            </div>
          ) : (
            messages.map((message) => (
              <div
                key={message.id}
                className={`flex items-start space-x-4 ${message.type === 'user-message' ? 'justify-end' : 'justify-start'}`}
              >
                {message.type !== 'user-message' && (
                  <div className="flex-shrink-0 w-8 h-8 flex items-center justify-center rounded-full bg-indigo-500 text-white">
                    <Sparkles className="h-4 w-4" />
                  </div>
                )}
                <div
                  className={`p-4 rounded-3xl max-w-lg ${
                    message.type === 'user-message'
                      ? 'bg-indigo-500 text-white rounded-br-none'
                      : 'bg-white dark:bg-gray-800 text-gray-800 dark:text-gray-200 rounded-bl-none'
                  }`}
                >
                  {message.imageData && (
                    <img
                      src={message.imageData}
                      alt="Uploaded"
                      className="rounded-lg mb-2 max-h-64 w-auto"
                    />
                  )}
                  {message.imageUrl && (
                    <img
                      src={message.imageUrl}
                      alt="Generated"
                      className="rounded-lg mb-2 max-h-64 w-auto"
                    />
                  )}
                  {/* Markdown rendering for AI responses */}
                  {message.type === 'ai-response' ? (
                    <>
                      <div className="markdown-body" dangerouslySetInnerHTML={{ __html: marked.parse(message.text) }}></div>
                      {isSpeaking && currentAudioIdRef.current === message.id ? (
                        <button onClick={handleStopTTS} className="mt-2 text-red-500">
                          <Pause className="h-5 w-5" />
                        </button>
                      ) : (
                        <button onClick={() => handleTTS(message.text, message.id, voiceName)} className="mt-2 text-indigo-300 dark:text-indigo-400">
                          <Play className="h-5 w-5" />
                        </button>
                      )}
                    </>
                  ) : (
                    <div className="markdown-body" dangerouslySetInnerHTML={{ __html: marked.parse(message.text) }}></div>
                  )}
                </div>
                {message.type === 'user-message' && (
                  <div className="flex-shrink-0 w-8 h-8 flex items-center justify-center rounded-full bg-gray-300 text-gray-800 dark:bg-gray-700 dark:text-gray-200">
                    <MessageSquareMore className="h-4 w-4" />
                  </div>
                )}
              </div>
            ))
          )}
          <div ref={messagesEndRef} />
        </main>

        {/* Input area */}
        <footer className="p-4 md:p-6 bg-white dark:bg-gray-800 border-t border-gray-200 dark:border-gray-700">
          <div className="max-w-3xl mx-auto flex items-end space-x-2">
            <label className="cursor-pointer flex items-center justify-center p-3 rounded-full bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors duration-200">
              <Image className="h-5 w-5" />
              <input
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
                className="hidden"
              />
            </label>
            <div className="flex-1 relative">
              {selectedImage && (
                <div className="absolute -top-32 left-0 right-0 p-4 bg-gray-200 dark:bg-gray-700 rounded-t-lg flex items-center justify-between">
                  <img src={selectedImage} alt="Preview" className="h-24 w-auto rounded-md" />
                  <button onClick={() => setSelectedImage(null)} className="text-gray-500 dark:text-gray-400">
                    <X className="h-5 w-5" />
                  </button>
                </div>
              )}
              <textarea
                className="w-full p-4 pr-16 bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 rounded-3xl resize-none focus:outline-none focus:ring-2 focus:ring-indigo-500 overflow-hidden"
                rows={1}
                value={newMessage}
                onChange={(e) => {
                  setNewMessage(e.target.value);
                  e.target.style.height = 'auto';
                  e.target.style.height = e.target.scrollHeight + 'px';
                }}
                onKeyDown={(e) => {
                  if (e.key === 'Enter' && !e.shiftKey) {
                    e.preventDefault();
                    handleSendMessage(e);
                  }
                }}
                placeholder="Message MadisonAI..."
              />
              <button
                onClick={handleSendMessage}
                className="absolute right-3 bottom-3 p-2 bg-indigo-500 text-white rounded-full shadow-md hover:bg-indigo-600 transition-colors duration-200 disabled:bg-indigo-300 disabled:cursor-not-allowed"
                disabled={isAIGenerating || isGeneratingImage || isSummarizing || isSpeaking || (!newMessage.trim() && !selectedImage)}
              >
                <PenTool className="h-5 w-5" />
              </button>
            </div>
            <button
              onClick={() => setShowGeminiModal(true)}
              className="p-3 rounded-full bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors duration-200"
            >
              <Sparkles className="h-5 w-5" />
            </button>
            <button
              onClick={() => setShowCreativeWriterModal(true)}
              className="p-3 rounded-full bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300 hover:bg-gray-300 dark:hover:bg-gray-600 transition-colors duration-200"
            >
              <PenTool className="h-5 w-5" />
            </button>
          </div>
          {error && <div className="mt-2 text-sm text-red-500 text-center">{error}</div>}
        </footer>
        <style key="markdown-styles">{`
            .markdown-body h1 { font-size: 1.5em; }
            .markdown-body h2 { font-size: 1.25em; }
            .markdown-body h3 { font-size: 1.125em; }
            .markdown-body p {
              margin-bottom: 1rem;
              line-height: 1.5;
            }
            .markdown-body ul, .markdown-body ol {
              margin-bottom: 1rem;
              padding-left: 1.5rem;
            }
            .markdown-body li {
              margin-bottom: 0.5rem;
            }
            .markdown-body blockquote {
              border-left: 4px solid #4299e1;
              padding-left: 1rem;
              margin-left: 0;
              color: #718096;
            }
            .dark .markdown-body blockquote {
              color: #a0aec0;
            }
          `}</style>
      </div>

      {/* Settings Modal */}
      {showSettingsModal && createPortal(
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-[100]">
          <div className="relative p-8 bg-white dark:bg-gray-800 w-96 rounded-xl shadow-lg transform transition-all duration-300 scale-100 opacity-100">
            <button onClick={() => setShowSettingsModal(false)} className="absolute top-4 right-4 text-gray-500 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-100">
              <X className="h-6 w-6" />
            </button>
            <h3 className="text-2xl font-bold mb-6 text-indigo-500">Settings</h3>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">TTS Voice</label>
                <select
                  value={voiceName}
                  onChange={(e) => setVoiceName(e.target.value)}
                  className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                >
                  {prebuiltVoices.map(voice => (
                    <option key={voice} value={voice}>{voice}</option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">System Instruction</label>
                <textarea
                  value={systemInstruction}
                  onChange={(e) => setSystemInstruction(e.target.value)}
                  placeholder="e.g. 'You are a helpful assistant that always responds in a friendly, concise manner.'"
                  className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500 h-24"
                />
              </div>

              <div className="pt-2">
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Generation Settings</label>
                <div className="space-y-2 text-sm text-gray-500 dark:text-gray-400">
                  <div className="flex items-center space-x-2">
                    <span>Temperature: {generationSettings.temperature.toFixed(2)}</span>
                    <input
                      type="range"
                      min="0"
                      max="1"
                      step="0.01"
                      value={generationSettings.temperature}
                      onChange={(e) => setGenerationSettings({...generationSettings, temperature: parseFloat(e.target.value)})}
                      className="w-full"
                    />
                  </div>
                  <div className="flex items-center space-x-2">
                    <span>Top K: {generationSettings.topK}</span>
                    <input
                      type="range"
                      min="1"
                      max="40"
                      step="1"
                      value={generationSettings.topK}
                      onChange={(e) => setGenerationSettings({...generationSettings, topK: parseInt(e.target.value)})}
                      className="w-full"
                    />
                  </div>
                  <div className="flex items-center space-x-2">
                    <span>Top P: {generationSettings.topP.toFixed(2)}</span>
                    <input
                      type="range"
                      min="0"
                      max="1"
                      step="0.01"
                      value={generationSettings.topP}
                      onChange={(e) => setGenerationSettings({...generationSettings, topP: parseFloat(e.target.value)})}
                      className="w-full"
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>,
        document.body
      )}

      {/* Gemini Modal */}
      {showGeminiModal && createPortal(
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-[100]">
          <div className="relative p-8 bg-white dark:bg-gray-800 w-96 rounded-xl shadow-lg transform transition-all duration-300 scale-100 opacity-100">
            <button onClick={() => setShowGeminiModal(false)} className="absolute top-4 right-4 text-gray-500 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-100">
              <X className="h-6 w-6" />
            </button>
            <h3 className="text-2xl font-bold mb-6 text-indigo-500">Quick Actions</h3>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Summarize current chat</label>
                <button
                  onClick={() => {
                    handleSummarizeChat();
                    setShowGeminiModal(false);
                  }}
                  className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200"
                  disabled={isSummarizing || isAIGenerating || isGeneratingImage || messages.length === 0}
                >
                  {isSummarizing ? (
                    <LoaderCircle className="animate-spin h-5 w-5 mx-auto" />
                  ) : (
                    "Summarize"
                  )}
                </button>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Translate Text</label>
                <textarea
                  value={textToTranslate}
                  onChange={(e) => setTextToTranslate(e.target.value)}
                  placeholder="Enter text to translate"
                  className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  rows="2"
                />
                <select
                  value={translationTargetLanguage}
                  onChange={(e) => setTranslationTargetLanguage(e.target.value)}
                  className="w-full p-3 mt-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                >
                  <option value="en-US">English</option>
                  <option value="es-US">Spanish</option>
                  <option value="fr-FR">French</option>
                  <option value="de-DE">German</option>
                  <option value="ja-JP">Japanese</option>
                  <option value="ko-KR">Korean</option>
                  <option value="zh-CN">Chinese (Simplified)</option>
                  <option value="ar-EG">Arabic</option>
                </select>
                <button
                  onClick={() => handleTranslateText(textToTranslate, translationTargetLanguage, currentConversationId)}
                  className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200 mt-2"
                  disabled={isAIGenerating || !textToTranslate}
                >
                  Translate
                </button>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Continue Text</label>
                <textarea
                  value={textToContinue}
                  onChange={(e) => setTextToContinue(e.target.value)}
                  placeholder="Enter text to continue"
                  className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  rows="2"
                />
                <button
                  onClick={() => handleContinueText(textToContinue, currentConversationId)}
                  className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200 mt-2"
                  disabled={isAIGenerating || !textToContinue}
                >
                  Continue
                </button>
              </div>
            </div>
          </div>
        </div>,
        document.body
      )}

      {/* Creative Writer Modal */}
      {showCreativeWriterModal && createPortal(
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-[100]">
          <div className="relative p-8 bg-white dark:bg-gray-800 w-96 rounded-xl shadow-lg transform transition-all duration-300 scale-100 opacity-100">
            <button onClick={() => setShowCreativeWriterModal(false)} className="absolute top-4 right-4 text-gray-500 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-100">
              <X className="h-6 w-6" />
            </button>
            <h3 className="text-2xl font-bold mb-6 text-indigo-500">Creative Writer</h3>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Topic</label>
                <input
                  type="text"
                  value={creativeTopic}
                  onChange={(e) => setCreativeTopic(e.target.value)}
                  placeholder="e.g. 'A lonely robot on a moonless night'"
                  className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Style</label>
                <select
                  value={creativeStyle}
                  onChange={(e) => setCreativeStyle(e.target.value)}
                  className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                >
                  <option value="Poem">Poem</option>
                  <option value="Short Story">Short Story</option>
                  <option value="Song Lyrics">Song Lyrics</option>
                  <option value="Blog Post">Blog Post</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Tone</label>
                <select
                  value={creativeTone}
                  onChange={(e) => setCreativeTone(e.target.value)}
                  className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                >
                  <option value="Neutral">Neutral</option>
                  <option value="Happy">Happy</option>
                  <option value="Sad">Sad</option>
                  <option value="Excited">Excited</option>
                  <option value="Mysterious">Mysterious</option>
                </select>
              </div>

              <button
                onClick={() => handleCreativeWriting(creativeTopic, creativeStyle, creativeTone, currentConversationId)}
                className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200 mt-4"
                disabled={isAIGenerating || !creativeTopic}
              >
                Generate
              </button>
            </div>
          </div>
        </div>,
        document.body
      )}

      <audio ref={audioRef} className="hidden" />
    </div>
  );
}

export default App;
