/* global __firebase_config, __app_id, __initial_auth_token */

import React, { useState, useEffect, useRef } from 'react';
import {
  initializeApp
} from 'firebase/app';
import {
  getAuth,
  signInWithCustomToken,
  signInAnonymously,
  onAuthStateChanged,
  signOut
} from 'firebase/auth';
import {
  getFirestore,
  collection,
  onSnapshot,
  addDoc,
  serverTimestamp,
  query,
  limit,
  doc,
  setDoc,
} from 'firebase/firestore';
import {
  marked
} from 'marked';
import {
  createPortal
} from 'react-dom';
import {
  Sparkles,
  MessageSquareMore,
  Image,
  LoaderCircle,
  X,
  ExternalLink,
  Menu,
  Plus,
  Play,
  Pause,
  Mic,
  Settings,
  Volume2,
  PenTool,
  Copy,
  User,
  LogOut,
  Send
} from 'lucide-react';

// --- Firebase Configuration ---
// The global variables are provided by the Canvas environment.
// We safely check for their existence and fall back to local dev values if they are not defined.
const localFirebaseConfig = {
  // Use a placeholder or your own dev keys here if you are running this locally
  apiKey: "YOUR_API_KEY",
  authDomain: "YOUR_AUTH_DOMAIN",
  projectId: "YOUR_PROJECT_ID",
  storageBucket: "YOUR_STORAGE_BUCKET",
  messagingSenderId: "YOUR_MESSAGING_SENDER_ID",
  appId: "YOUR_APP_ID",
};

const localAppId = 'default-app-id';
const localAuthToken = 'dev-token';

const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : localFirebaseConfig;
const appId = typeof __app_id !== 'undefined' ? __app_id : localAppId;
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : localAuthToken;

// --- Initialize Firebase Services ---
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);

// Helper function for sanitizing user input and rendering markdown
const renderMarkdown = (text) => {
  const sanitizedText = text.replace(/</g, "&lt;").replace(/>/g, "&gt;");
  return {
    __html: marked.parse(sanitizedText)
  };
};

// Main App Component
const App = () => {
  const [user, setUser] = useState(null);
  const [userId, setUserId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [newMessage, setNewMessage] = useState("");
  const messagesEndRef = useRef(null);

  // --- Authentication and Initialization ---
  useEffect(() => {
    // This listener will be called when the auth state changes
    const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
      if (currentUser) {
        setUser(currentUser);
        setUserId(currentUser.uid);
      } else {
        // Handle anonymous sign-in for unauthenticated users
        try {
          await signInAnonymously(auth);
        } catch (authError) {
          setError(`Authentication Error: ${authError.message}`);
          console.error("Anonymous sign-in failed:", authError);
        }
      }
      setIsAuthReady(true);
      setLoading(false);
    });

    // Clean up the listener on component unmount
    return () => unsubscribe();
  }, []);

  // --- Firebase Firestore Data Listener ---
  useEffect(() => {
    if (isAuthReady) {
      const messagesRef = collection(db, `artifacts/${appId}/public/data/messages`);
      const q = query(messagesRef, limit(50));

      const unsubscribe = onSnapshot(q, (snapshot) => {
        const fetchedMessages = snapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setMessages(fetchedMessages);
        messagesEndRef.current?.scrollIntoView({
          behavior: "smooth"
        });
      }, (err) => {
        console.error("Failed to fetch messages:", err);
        setError("Failed to load messages. Please check your network connection.");
      });

      return () => unsubscribe();
    }
  }, [isAuthReady, appId, db]);

  // --- Handle sending new messages ---
  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (newMessage.trim() === "" || !userId) return;

    try {
      await addDoc(collection(db, `artifacts/${appId}/public/data/messages`), {
        text: newMessage,
        senderId: userId,
        timestamp: serverTimestamp(),
      });
      setNewMessage("");
    } catch (sendError) {
      console.error("Error sending message:", sendError);
      setError("Failed to send message.");
    }
  };

  // --- UI Rendering ---
  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-gray-100">
        <LoaderCircle className="animate-spin h-12 w-12 text-indigo-500" />
      </div>
    );
  }

  if (error && !user) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900 p-4">
        <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-2xl p-8 w-full max-w-md text-center">
          <p className="text-red-500 dark:text-red-400 mb-4">{error}</p>
          <button
            onClick={() => window.location.reload()}
            className="bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200"
          >
            Reload
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-gray-100">
      <header className="bg-white dark:bg-gray-800 shadow-md p-4 flex items-center justify-between">
        <h1 className="text-2xl font-bold">Chat App</h1>
        <div className="flex items-center space-x-4">
          <span className="text-sm">User ID: {userId}</span>
          <button
            onClick={() => signOut(auth)}
            className="p-2 rounded-lg text-red-500 hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors duration-200"
          >
            <LogOut size={20} />
          </button>
        </div>
      </header>

      <main className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map((msg, index) => (
          <div key={msg.id} className={`flex ${msg.senderId === userId ? 'justify-end' : 'justify-start'}`}>
            <div
              className={`max-w-md rounded-xl p-3 shadow-md ${
                msg.senderId === userId
                  ? 'bg-indigo-500 text-white rounded-br-none'
                  : 'bg-white dark:bg-gray-700 rounded-bl-none'
              }`}
            >
              <div
                dangerouslySetInnerHTML={renderMarkdown(msg.text)}
                className="prose dark:prose-invert"
              />
              <span className={`block text-xs mt-1 ${msg.senderId === userId ? 'text-indigo-200' : 'text-gray-400'}`}>
                {msg.senderId === userId ? 'You' : `User ID: ${msg.senderId.substring(0, 8)}...`}
              </span>
            </div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </main>

      <footer className="bg-white dark:bg-gray-800 p-4 shadow-md">
        <form onSubmit={handleSendMessage} className="flex space-x-2">
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            className="flex-1 p-3 rounded-lg border-2 border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors duration-200"
            placeholder="Type a message..."
          />
          <button
            type="submit"
            className="bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200"
          >
            <Send size={20} />
          </button>
        </form>
      </footer>
    </div>
  );
};

export default App;
