// This line tells ESLint to ignore the following global variables that are provided by the Canvas environment.
/* global __app_id, __firebase_config, __initial_auth_token */

import React, { useState, useEffect, useRef } from 'react';
import { initializeApp, getApps, getApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, signInAnonymously, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, collection, addDoc, serverTimestamp, query, onSnapshot } from 'firebase/firestore';
import { SendHorizonal, Bot } from 'lucide-react';

function App() {
  const [db, setDb] = useState(null);
  const [userId, setUserId] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [isAIGenerating, setIsAIGenerating] = useState(false);
  const messagesEndRef = useRef(null);
  
  // A robust approach to getting the Firebase config
  let firebaseConfig = {};
  const defaultFirebaseConfig = {
    authDomain: "ai-chatbot-88602.firebaseapp.com",
    projectId: "ai-chatbot-88602",
    storageBucket: "ai-chatbot-88602.appspot.com",
    messagingSenderId: "167580287666",
    appId: "1:167580287666:android:b057a4176a46729ad47fab"
  };

  try {
    const providedConfig = JSON.parse(typeof __firebase_config !== 'undefined' ? __firebase_config : '{}');
    if (providedConfig && providedConfig.projectId) {
      firebaseConfig = providedConfig;
    } else {
      console.warn("Provided Firebase config is empty or invalid. Using a hardcoded placeholder config.");
      firebaseConfig = defaultFirebaseConfig;
    }
  } catch (e) {
    console.error("Failed to parse firebase config from global variable. Using hardcoded placeholder config.", e);
    firebaseConfig = defaultFirebaseConfig;
  }

  const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

  useEffect(() => {
    const initAndAuth = async () => {
      console.log("[INIT] Starting Firebase initialization and authentication...");
      try {
        const app = getApps().length ? getApp() : initializeApp(firebaseConfig);
        const firestoreAuth = getAuth(app);
        const firestoreDb = getFirestore(app);

        // Set up the auth state listener to get the user ID once authentication is complete
        const unsubscribe = onAuthStateChanged(firestoreAuth, async (user) => {
          if (user) {
            setUserId(user.uid);
            console.log(`[AUTH] User state changed. Authenticated with UID: ${user.uid}`);
            setDb(firestoreDb); // Set the db once the user is authenticated
            setIsLoading(false);
          } else {
            console.log("[AUTH] No user is signed in. Attempting to sign in.");
            // If no user is signed in, attempt to sign in.
            try {
              if (initialAuthToken) {
                await signInWithCustomToken(firestoreAuth, initialAuthToken);
                console.log("[AUTH] Successfully signed in with custom token.");
              } else {
                await signInAnonymously(firestoreAuth);
                console.log("[AUTH] Successfully signed in anonymously.");
              }
            } catch (authError) {
              console.error("[AUTH ERROR] Failed to sign in.", authError);
              setError(`Authentication failed: ${authError.message}`);
              setIsLoading(false);
            }
          }
        });

        return () => {
          console.log("[INIT] Cleaning up auth state listener.");
          unsubscribe();
        };

      } catch (e) {
        console.error("[INIT ERROR] Failed to initialize Firebase:", e);
        setError(`Firebase initialization failed: ${e.message}`);
        setIsLoading(false);
      }
    };
    initAndAuth();
  }, [initialAuthToken]); // Re-run if auth token changes

  // Custom CSS for dot-flashing animation, since we can't import App.css
  const dotFlashingCss = `
    .dot-flashing {
      position: relative;
      width: 10px;
      height: 10px;
      border-radius: 5px;
      background-color: #9880ff;
      color: #9880ff;
      animation: dotFlashing 1s infinite linear alternate;
      animation-delay: .5s;
      margin-top: 4px; /* Adjust spacing */
    }

    .dot-flashing::before, .dot-flashing::after {
      content: '';
      display: inline-block;
      position: absolute;
      top: 0;
    }

    .dot-flashing::before {
      left: -15px;
      width: 10px;
      height: 10px;
      border-radius: 5px;
      background-color: #9880ff;
      color: #9880ff;
      animation: dotFlashing 1s infinite alternate;
      animation-delay: 0s;
    }

    .dot-flashing::after {
      left: 15px;
      width: 10px;
      height: 10px;
      border-radius: 5px;
      background-color: #9880ff;
      color: #9880ff;
      animation: dotFlashing 1s infinite alternate;
      animation-delay: 1s;
    }

    @keyframes dotFlashing {
      0% {
        background-color: #9880ff;
      }
      50%,
      100% {
        background-color: #ebe6ff;
      }
    }
  `;

  // Fetch messages from Firestore in real-time
  useEffect(() => {
    if (!db || !userId) {
      console.warn("Firestore or User ID not available, skipping message fetch.");
      return;
    }

    const messagesCollectionPath = `artifacts/${__app_id || 'default-app-id'}/public/data/messages`;
    const messagesCollectionRef = collection(db, messagesCollectionPath);
    const q = query(messagesCollectionRef);

    const unsubscribe = onSnapshot(q, (snapshot) => {
      console.log('[FIRESTORE] New message snapshot received.');
      const fetchedMessages = snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));
      const sortedMessages = fetchedMessages.sort((a, b) => (a.timestamp?.toMillis() || 0) - (b.timestamp?.toMillis() || 0));
      setMessages(sortedMessages);
    }, (firestoreError) => {
      console.error("[FIRESTORE ERROR] Error fetching messages:", firestoreError.message);
      setError(`Failed to load messages. Details: ${firestoreError.message}`);
    });

    return () => {
      console.log('[FIRESTORE] Unsubscribing from messages collection.');
      unsubscribe();
    };
  }, [db, userId]);

  // Use a separate useEffect to handle AI responses to user messages
  useEffect(() => {
    const lastMessage = messages[messages.length - 1];
    if (db && lastMessage && lastMessage.sender === 'user' && !isAIGenerating) {
      const getAIResponse = async () => {
        setIsAIGenerating(true);
        console.log("[AI] User sent a message. Generating AI response...");
        try {
          const chatHistory = messages.map(msg => ({
            role: msg.sender === 'user' ? 'user' : 'model',
            parts: [{ text: msg.text }]
          }));

          const prompt = {
            role: "user",
            parts: [{ text: lastMessage.text }]
          };

          const payload = { contents: [...chatHistory, prompt] };
          const apiKey = "";
          const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;

          const response = await fetch(apiUrl, {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify(payload)
          });

          const result = await response.json();
          let responseText = "Sorry, I couldn't generate a response.";
          if (result.candidates && result.candidates.length > 0 &&
              result.candidates[0].content && result.candidates[0].content.parts &&
              result.candidates[0].content.parts.length > 0) {
            responseText = result.candidates[0].content.parts[0].text;
            console.log("[AI] Generated AI response successfully.");
          }

          await addDoc(collection(db, `artifacts/${__app_id || 'default-app-id'}/public/data/messages`), {
            text: responseText,
            sender: 'bot',
            timestamp: serverTimestamp(),
            userId: "bot"
          });
        } catch (e) {
          console.error("[AI ERROR] Error getting AI response:", e);
          await addDoc(collection(db, `artifacts/${__app_id || 'default-app-id'}/public/data/messages`), {
            text: `Sorry, I am having trouble processing that request. Error: ${e.message}`,
            sender: 'bot',
            timestamp: serverTimestamp(),
            userId: "bot"
          });
        } finally {
          setIsAIGenerating(false);
        }
      };
      getAIResponse();
    }
  }, [messages, db, isAIGenerating]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async () => {
    if (input.trim() === '' || !db || !userId || isAIGenerating) {
      console.warn("[SEND] Message send blocked. Input is empty, DB/userId not ready, or AI is generating.");
      return;
    }
    try {
      const messagesCollectionPath = `artifacts/${__app_id || 'default-app-id'}/public/data/messages`;
      console.log(`[SEND] Attempting to send message to path: ${messagesCollectionPath}`);
      const messagesCollectionRef = collection(db, messagesCollectionPath);
      await addDoc(messagesCollectionRef, {
        text: input,
        sender: 'user',
        timestamp: serverTimestamp(),
        userId: userId // Add userId to the message for identification
      });
      setInput('');
      console.log("[SEND] Message sent successfully.");
    } catch (e) {
      console.error("[SEND ERROR] Error adding document: ", e);
      setError(`Failed to send message: ${e.message}`);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && input.trim() !== '' && !isAIGenerating) {
      sendMessage();
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen text-gray-500">
        Loading application...
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen text-red-500 p-4 text-center">
        <p>An error occurred: {error}</p>
      </div>
    );
  }

  return (
    <div className="App bg-gray-100 dark:bg-gray-900 min-h-screen flex items-center justify-center p-4">
      <div className="flex flex-col w-full max-w-xl h-[80vh] bg-white dark:bg-gray-800 rounded-2xl shadow-2xl">
        <style>{dotFlashingCss}</style>
        <div className="flex justify-between items-center p-4 border-b border-gray-200 dark:border-gray-700 rounded-t-2xl">
          <h1 className="text-xl font-bold text-gray-900 dark:text-white">AI Chatbot</h1>
          {userId && (
            <span className="text-xs text-gray-500 dark:text-gray-400">
              User ID: <span className="font-mono">{userId}</span>
            </span>
          )}
        </div>
        <div className="flex-grow p-4 overflow-y-auto space-y-4">
          {error ? (
            <div className="flex justify-center items-center h-full text-red-500 font-medium text-center p-4">
              <p>{error}</p>
            </div>
          ) : messages.length === 0 ? (
            <div className="flex justify-center items-center h-full">
              <span className="text-gray-500 dark:text-gray-400">Start a new conversation!</span>
            </div>
          ) : (
            messages.map((message, index) => (
              <div key={index} className={`flex items-start gap-3 ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
                {message.sender === 'bot' && (
                  <div className="flex items-center justify-center w-8 h-8 rounded-full bg-indigo-200 dark:bg-indigo-700">
                    <Bot size={16} className="text-indigo-600 dark:text-indigo-200" />
                  </div>
                )}
                <div className={`p-3 rounded-lg max-w-[70%] ${message.sender === 'user' ? 'bg-indigo-500 text-white dark:bg-indigo-600' : 'bg-gray-200 dark:bg-gray-700 text-gray-900 dark:text-white'}`}>
                  <p className="whitespace-pre-wrap">{message.text}</p>
                </div>
              </div>
            ))
          )}
          {isAIGenerating && (
            <div className="flex items-start gap-3 justify-start">
              <div className="p-3 rounded-lg bg-gray-200 dark:bg-gray-700 text-gray-900 dark:text-white max-w-[70%] flex items-center">
                <Bot size={16} className="inline-block mr-2" />
                <div className="dot-flashing"></div>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>
        <div className="p-4 border-t border-gray-200 dark:border-gray-700">
          <div className="relative flex items-center">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Type your message..."
              className="flex-grow rounded-full border border-gray-300 dark:border-gray-600 bg-gray-100 dark:bg-gray-900 px-4 py-2 text-gray-900 dark:text-white focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors"
              disabled={isAIGenerating}
            />
            <button
              onClick={sendMessage}
              disabled={isAIGenerating || input.trim() === ''}
              className="ml-3 p-2 rounded-full bg-indigo-600 text-white hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 disabled:bg-gray-400 transition-colors"
            >
              <SendHorizonal size={20} />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
