// This line tells ESLint to ignore the following global variables that are provided by the Canvas environment.
/* global __app_id, __firebase_config, __initial_auth_token */

import { useState, useEffect, useRef } from 'react';
// Import the core Firebase App functions
import { initializeApp, getApps, getApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, signInAnonymously, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, collection, query, onSnapshot, addDoc, serverTimestamp } from 'firebase/firestore';
import { Send, Bot, Volume2, Image as ImageIcon, XCircle, Wand, Download } from 'lucide-react'; // Using Lucide for a modern icon, added Volume2 for TTS
import { v4 as uuidv4 } from 'uuid'; // Import uuid for unique key generation

// This is the main App component for our chat application.
// It handles all the UI, state management, and Firebase interactions.
export default function App() {
  // State to hold the chat messages. An array of objects.
  const [messages, setMessages] = useState([]);
  // State to hold the user's input in the text field.
  const [input, setInput] = useState('');
  // State to hold the Firebase database instance.
  const [db, setDb] = useState(null);
  // State to hold the current user's ID.
  const [userId, setUserId] = useState(null);
  // State to track if an AI response is being generated.
  const [isAIGenerating, setIsAIGenerating] = useState(false);
  // State to track if the TTS audio is currently being generated or played.
  const [isSpeaking, setIsSpeaking] = useState(false);
  // State to track if an image is being generated.
  const [isImageGenerating, setIsImageGenerating] = useState(false);
  // State to hold any authentication or initialization errors.
  const [error, setError] = useState(null);
  // State to store the uploaded image file and its preview URL.
  const [imageFile, setImageFile] = useState(null);
  const [imagePreviewUrl, setImagePreviewUrl] = useState(null);
  // A ref to automatically scroll to the latest message.
  const messagesEndRef = useRef(null);

  // A fallback Firebase configuration in case the provided global variable is not available.
  const fallbackFirebaseConfig = {
    apiKey: "AIzaSyDhJoNaFf0qgl4VTuUXj16ysJG1hycq-p8",
    authDomain: "ai-chatbot-88602.firebaseapp.com",
    projectId: "ai-chatbot-88602",
    storageBucket: "ai-chatbot-88602.firebasestorage.app",
    messagingSenderId: "167580287666",
    appId: "1:167580287666:web:9e8313ae6e667e47d47fab"
  };

  // useEffect to handle Firebase initialization and authentication.
  // The empty dependency array `[]` ensures this runs only once on component mount.
  useEffect(() => {
    const initializeFirebase = async () => {
      let firebaseConfig = fallbackFirebaseConfig;
      try {
        if (typeof __firebase_config !== 'undefined' && __firebase_config) {
          firebaseConfig = JSON.parse(__firebase_config);
        }
      } catch (e) {
        console.error("Failed to parse __firebase_config:", e);
        setError("Failed to parse Firebase configuration.");
        return;
      }
  
      if (!firebaseConfig || !firebaseConfig.projectId) {
        const configError = "Firebase configuration is missing. Please provide a valid config.";
        console.error(configError);
        setError(configError);
        return;
      }
  
      try {
        // Initialize Firebase app only if it hasn't been initialized yet
        let app;
        if (!getApps().length) {
          console.log("[FIREBASE] Initializing Firebase app...");
          app = initializeApp(firebaseConfig);
        } else {
          app = getApp();
        }
        
        const auth = getAuth(app);
        const firestore = getFirestore(app);
        
        // Use a flag to ensure sign-in logic runs only once
        let isSigningIn = true;

        // Set up the auth state listener first
        onAuthStateChanged(auth, async (user) => {
          if (user) {
            console.log("[FIREBASE] User signed in with UID:", user.uid);
            setUserId(user.uid);
            setDb(firestore);
            setError(null);
          } else {
            // Only attempt to sign in if this is the initial check
            if (isSigningIn) {
              console.log("[FIREBASE] User is not signed in. Attempting sign-in...");
              try {
                const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;
                if (initialAuthToken) {
                  await signInWithCustomToken(auth, initialAuthToken);
                } else {
                  await signInAnonymously(auth);
                }
              } catch (e) {
                console.error("[FIREBASE] Sign-in failed:", e);
                setError(`Error: Sign-in failed. Reason: ${e.message}`);
              }
              isSigningIn = false; // Prevent re-triggering sign-in logic
            }
          }
        });

      } catch (e) {
        console.error("[FIREBASE] Initialization failed:", e.message);
        setError(`Error: Firebase initialization failed. Please check the console for more details. Reason: ${e.message}`);
      }
    };
  
    initializeFirebase();
  }, []);

  // useEffect to listen for real-time updates to the chat messages from Firestore.
  // This effect will run whenever `db` or `userId` changes, ensuring Firestore is ready.
  useEffect(() => {
    // We only attach the listener if Firebase is fully initialized and authenticated.
    if (db && userId) {
      const currentAppId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
      const path = `/artifacts/${currentAppId}/public/data/messages`;
      
      // Create a query to get messages from the 'messages' collection.
      const q = query(collection(db, path));
      
      const unsubscribe = onSnapshot(q, (querySnapshot) => {
        const fetchedMessages = [];
        querySnapshot.forEach((doc) => {
          const data = doc.data();
          fetchedMessages.push({ id: doc.id, ...data, type: data.type || 'text' });
        });
        // Sort the messages by timestamp to ensure they are in the correct order.
        fetchedMessages.sort((a, b) => (a.timestamp?.toMillis() || 0) - (b.timestamp?.toMillis() || 0));
        setMessages(fetchedMessages);
      }, (firestoreError) => {
        console.error("Error fetching messages:", firestoreError);
        setError("Failed to fetch messages. Check your Firestore rules and connection.");
      });
      
      return () => unsubscribe();
    }
  }, [db, userId]);

  // Use a separate useEffect to handle AI responses to user messages
  useEffect(() => {
    const lastMessage = messages[messages.length - 1];
    // Check if the last message is from the user and an AI response is not already in progress.
    if (db && lastMessage && lastMessage.userId === userId && lastMessage.type !== 'image' && !isAIGenerating) {
      const getAIResponse = async () => {
        setIsAIGenerating(true);
        console.log("[AI] User sent a message. Generating AI response...");

        try {
          // Prepare chat history for the API call.
          const chatHistory = messages.map(msg => {
            const parts = [{ text: msg.text }];
            if (msg.image) {
              parts.push({
                inlineData: {
                  mimeType: msg.image.mimeType,
                  data: msg.image.data
                }
              });
            }
            return {
              role: msg.userId === userId ? 'user' : 'model',
              parts: parts
            };
          });
          
          const payload = { contents: chatHistory };
          const apiKey = ""; // API key is automatically provided by the Canvas environment
          const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
          
          let response;
          let retryCount = 0;
          const maxRetries = 5;
          const initialDelay = 1000;

          while (retryCount < maxRetries) {
            try {
              response = await fetch(apiUrl, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(payload)
              });
              if (response.status !== 429) {
                break;
              }
              console.warn(`[API] Rate limit hit. Retrying in ${initialDelay * Math.pow(2, retryCount)}ms...`);
              await new Promise(resolve => setTimeout(resolve, initialDelay * Math.pow(2, retryCount)));
              retryCount++;
            } catch (fetchError) {
              console.error(`[API] Fetch failed. Retrying...`, fetchError);
              await new Promise(resolve => setTimeout(resolve, initialDelay * Math.pow(2, retryCount)));
              retryCount++;
            }
          }
          
          if (!response || !response.ok) {
            throw new Error(`API error: ${response?.statusText || 'Unknown error'}`);
          }

          const result = await response.json();
          let responseText = "Sorry, I couldn't generate a response.";
          if (result.candidates && result.candidates.length > 0 &&
              result.candidates[0].content && result.candidates[0].content.parts &&
              result.candidates[0].content.parts.length > 0) {
            responseText = result.candidates[0].content.parts[0].text;
            console.log("[AI] Generated AI response successfully.");
          }

          const currentAppId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
          const path = `/artifacts/${currentAppId}/public/data/messages`;
          await addDoc(collection(db, path), {
            text: responseText,
            userId: 'bot',
            type: 'text',
            timestamp: serverTimestamp(),
          });
        } catch (e) {
          console.error("[AI ERROR] Error getting AI response:", e);
          const currentAppId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
          const path = `/artifacts/${currentAppId}/public/data/messages`;
          await addDoc(collection(db, path), {
            text: `Sorry, I am having trouble processing that request. Error: ${e.message}`,
            userId: 'bot',
            type: 'text',
            timestamp: serverTimestamp(),
          });
        } finally {
          setIsAIGenerating(false);
        }
      };

      getAIResponse();
    }
  }, [messages, db, isAIGenerating, userId]);

  // Helper functions for TTS audio conversion
  const base64ToArrayBuffer = (base64) => {
    const binaryString = atob(base64);
    const len = binaryString.length;
    const bytes = new Uint8Array(len);
    for (let i = 0; i < len; i++) {
      bytes[i] = binaryString.charCodeAt(i);
    }
    return bytes.buffer;
  };

  const pcmToWav = (pcmData, sampleRate) => {
    const buffer = new ArrayBuffer(44 + pcmData.byteLength);
    const view = new DataView(buffer);

    // Write WAV file header
    writeString(view, 0, 'RIFF');
    view.setUint32(4, 36 + pcmData.byteLength, true);
    writeString(view, 8, 'WAVE');
    writeString(view, 12, 'fmt ');
    view.setUint32(16, 16, true);
    view.setUint16(20, 1, true);
    view.setUint16(22, 1, true); // Mono
    view.setUint32(24, sampleRate, true);
    view.setUint32(28, sampleRate * 2, true); // Byte rate
    view.setUint16(32, 2, true); // Block align
    view.setUint16(34, 16, true); // Bits per sample
    writeString(view, 36, 'data');
    view.setUint32(40, pcmData.byteLength, true);

    // Write PCM data
    const pcmView = new Int16Array(pcmData);
    let offset = 44;
    for (let i = 0; i < pcmView.length; i++, offset += 2) {
      view.setInt16(offset, pcmView[i], true);
    }
    return new Blob([view], { type: 'audio/wav' });
  };

  const writeString = (view, offset, string) => {
    for (let i = 0; i < string.length; i++) {
      view.setUint8(offset + i, string.charCodeAt(i));
    }
  };

  // Function to handle the text-to-speech functionality
  const handleTextToSpeech = async (text) => {
    setIsSpeaking(true);
    console.log("[TTS] Generating text-to-speech for message:", text);
    try {
      const payload = {
        contents: [{ parts: [{ text: text }] }],
        generationConfig: {
          responseModalities: ["AUDIO"],
          speechConfig: {
            voiceConfig: {
              prebuiltVoiceConfig: { voiceName: "Kore" } // Using the Kore voice
            }
          }
        }
      };
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-tts:generateContent?key=${apiKey}`;

      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      const part = result?.candidates?.[0]?.content?.parts?.[0];
      const audioData = part?.inlineData?.data;
      const mimeType = part?.inlineData?.mimeType;

      if (audioData && mimeType && mimeType.startsWith("audio/")) {
        // The API returns 'audio/L16;rate=24000' for example
        const sampleRate = parseInt(mimeType.match(/rate=(\d+)/)[1], 10);
        const pcmData = base64ToArrayBuffer(audioData);
        const pcm16 = new Int16Array(pcmData);
        const wavBlob = pcmToWav(pcm16, sampleRate);
        const audioUrl = URL.createObjectURL(wavBlob);
        const audio = new Audio(audioUrl);
        
        audio.onended = () => {
          setIsSpeaking(false);
          URL.revokeObjectURL(audioUrl);
        };
        audio.play();
      } else {
        console.error("[TTS Error] Invalid audio response from API.");
        setIsSpeaking(false);
      }
    } catch (e) {
      console.error("[TTS Error] Error generating speech:", e);
      setIsSpeaking(false);
    }
  };

  // Function to handle sending a new message.
  const handleSendMessage = async () => {
    if ((input.trim() === '' && !imageFile) || !db || !userId) {
      console.log("Cannot send empty message or Firebase not ready.");
      return;
    }

    try {
      const currentAppId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
      const path = `/artifacts/${currentAppId}/public/data/messages`;

      const messageData = {
        text: input,
        userId: userId,
        type: 'text',
        timestamp: serverTimestamp(),
      };

      if (imageFile) {
        const reader = new FileReader();
        reader.onloadend = async () => {
          const base64Data = reader.result.split(',')[1];
          messageData.image = {
            data: base64Data,
            mimeType: imageFile.type
          };
          await addDoc(collection(db, path), messageData);
          setInput('');
          setImageFile(null);
          setImagePreviewUrl(null);
        };
        reader.readAsDataURL(imageFile);
      } else {
        await addDoc(collection(db, path), messageData);
        setInput('');
      }
    } catch (error) {
      console.error("Error adding document: ", error);
      setError("Failed to send message. Please try again.");
    }
  };

  // Function to handle image file selection
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setImageFile(file);
      setImagePreviewUrl(URL.createObjectURL(file));
    }
  };

  // Function to remove the selected image
  const handleRemoveImage = () => {
    setImageFile(null);
    setImagePreviewUrl(null);
  };
  
  // Function to handle image generation using the Gemini API
  const handleGenerateImage = async () => {
    if (input.trim() === '' || !db || !userId) {
      console.log("Cannot generate image with an empty prompt.");
      return;
    }
  
    setIsImageGenerating(true);
    try {
      const currentAppId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
      const path = `/artifacts/${currentAppId}/public/data/messages`;
      
      // Add a user message to indicate the image generation request
      await addDoc(collection(db, path), {
        text: `Generate an image for: "${input}"`,
        userId: userId,
        type: 'text',
        timestamp: serverTimestamp(),
      });
      
      const payload = {
        instances: { prompt: input },
        parameters: { "sampleCount": 1 }
      };
      
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/imagen-3.0-generate-002:predict?key=${apiKey}`;
      
      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      
      const result = await response.json();
      
      if (result.predictions && result.predictions.length > 0 && result.predictions[0].bytesBase64Encoded) {
        const base64Data = result.predictions[0].bytesBase64Encoded;
        const imageUrl = `data:image/png;base64,${base64Data}`;
        
        // Add the generated image to the chat messages
        await addDoc(collection(db, path), {
          text: `Here is the image you requested for "${input}".`,
          imageUrl: imageUrl,
          userId: 'bot',
          type: 'image',
          timestamp: serverTimestamp(),
        });
        
        console.log("[AI] Generated image successfully.");
      } else {
        console.error("[AI ERROR] No image prediction found in the API response.");
        await addDoc(collection(db, path), {
          text: `Sorry, I couldn't generate an image for that prompt.`,
          userId: 'bot',
          type: 'text',
          timestamp: serverTimestamp(),
        });
      }
    } catch (e) {
      console.error("[AI ERROR] Error generating image:", e);
      const currentAppId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
      const path = `/artifacts/${currentAppId}/public/data/messages`;
      await addDoc(collection(db, path), {
        text: `Sorry, I am having trouble generating that image. Error: ${e.message}`,
        userId: 'bot',
        type: 'text',
        timestamp: serverTimestamp(),
      });
    } finally {
      setIsImageGenerating(false);
      setInput('');
    }
  };


  // Function to handle key presses in the input field.
  const handleKeyDown = (event) => {
    if (event.key === 'Enter' && input.trim() !== '' && !isAIGenerating && !isSpeaking) {
      handleSendMessage();
    }
  };

  // A useEffect to scroll the chat window to the bottom whenever new messages arrive.
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // CSS for the dot-flashing loading animation. This is in a template literal
  // because we don't have an external CSS file for React immersives.
  const dotFlashingCss = `
    .dot-flashing {
      position: relative;
      width: 5px;
      height: 5px;
      border-radius: 5px;
      background-color: #efefef;
      color: #efefef;
      animation: dotFlashing 1s infinite linear alternate;
      animation-delay: .5s;
    }
    .dot-flashing::before, .dot-flashing::after {
      content: '';
      display: inline-block;
      position: absolute;
      top: 0;
    }
    .dot-flashing::before {
      left: -10px;
      width: 5px;
      height: 5px;
      border-radius: 5px;
      background-color: #efefef;
      color: #efefef;
      animation: dotFlashing 1s infinite alternate;
    }
    .dot-flashing::after {
      left: 10px;
      width: 5px;
      height: 5px;
      border-radius: 5px;
      background-color: #efefef;
      color: #efefef;
      animation: dotFlashing 1s infinite alternate;
      animation-delay: 1s;
    }
    @keyframes dotFlashing {
      0% { background-color: #efefef; }
      50%, 100% { background-color: #9e9e9e; }
    }
  `;

  return (
    // Main container with a dark background and full viewport height/width.
    // It uses flexbox to center the chat container both horizontally and vertically.
    <div className="flex flex-col items-center justify-center min-h-screen p-4 bg-slate-900 font-inter">
      {/* Inject the custom CSS for the loading animation */}
      <style>{dotFlashingCss}</style>

      {/* The main chat container. It's a responsive box with a dark background. */}
      <div className="flex flex-col w-full max-w-sm md:max-w-xl lg:max-w-2xl bg-slate-800 rounded-2xl shadow-xl overflow-hidden h-[80vh]">
        
        {/* Header section for the chat window. */}
        <div className="p-4 bg-slate-700 text-white text-center text-lg font-semibold border-b border-slate-600 rounded-t-2xl">
          AI Chatbot
        </div>

        {/* Display area for the messages. It's a scrollable container. */}
        <div className="flex-1 p-4 overflow-y-auto space-y-4">
          {/* Display loading, error, or messages */}
          {!db && !error && (
            <div className="text-center text-slate-400 mt-10">
              Initializing Firebase...
            </div>
          )}
          {error && (
            <div className="text-center text-red-400 mt-10 p-4 bg-red-900 rounded-lg">
              Error: {error}
            </div>
          )}
          {db && !error && messages.map((message, index) => (
            // A message bubble. We use different styles for the current user's messages and the bot's.
            <div
              key={message.id || uuidv4()}
              className={`flex ${message.userId === userId ? 'justify-end' : 'justify-start'}`}
            >
              <div
                className={`max-w-[70%] p-3 rounded-2xl shadow-md ${
                  message.userId === userId
                    ? 'bg-blue-600 text-white' // User's message style
                    : 'bg-slate-700 text-slate-100' // Bot's message style
                } ${
                  message.userId === userId
                    ? 'rounded-br-none' // Make the bottom-right corner sharp for user messages
                    : 'rounded-bl-none' // Make the bottom-left corner sharp for bot messages
                }`}
              >
                {/* Conditionally render the bot icon and TTS button for bot messages */}
                {message.userId === 'bot' && (
                  <div className="flex items-center gap-2 mb-2">
                    <Bot size={16} />
                    {message.type === 'text' && (
                      <button
                        onClick={() => handleTextToSpeech(message.text)}
                        className="text-white text-xs px-2 py-1 bg-slate-600 rounded-full hover:bg-slate-500 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                        disabled={isSpeaking || isAIGenerating || isImageGenerating}
                      >
                        <Volume2 size={12} className="inline-block mr-1" />
                        Speak 
                      </button>
                    )}
                  </div>
                )}
                {/* The message content */}
                {message.type === 'text' && (
                  <p className="text-sm break-words inline-block">{message.text}</p>
                )}
                {message.type === 'image' && (
                  <div className="flex flex-col items-start gap-2">
                    <img src={message.imageUrl} alt="Generated" className="rounded-lg max-w-full h-auto" />
                    <a href={message.imageUrl} download={`gemini-image-${index}.png`} className="flex items-center gap-1 text-xs text-blue-300 hover:text-blue-200 transition-colors">
                      <Download size={12} />
                      Download Image
                    </a>
                  </div>
                )}
                {message.userId === userId && message.image && (
                  <div className="mt-2">
                    <img src={`data:${message.image.mimeType};base64,${message.image.data}`} alt="Uploaded" className="rounded-lg max-w-full h-auto" />
                  </div>
                )}
                
                {/* Optional: Display the user ID for collaborative context */}
                {message.userId !== 'bot' && (
                  <div className="text-xs text-slate-400 mt-1">
                    User: {message.userId.substring(0, 8)}...
                  </div>
                )}
              </div>
            </div>
          ))}

          {/* AI generating message indicator */}
          {(isAIGenerating || isImageGenerating) && (
            <div className="flex items-start justify-start">
              <div className="p-3 rounded-2xl bg-slate-700 text-slate-100 max-w-[70%] rounded-bl-none">
                <Bot size={16} className="inline-block mr-2" />
                <div className="dot-flashing inline-block align-middle ml-2" />
              </div>
            </div>
          )}

          {/* This empty div is our scroll anchor. */}
          <div ref={messagesEndRef} />
        </div>
        
        {/* Input area for sending messages. */}
        <div className="p-4 bg-slate-700 border-t border-slate-600 rounded-b-2xl">
          {imagePreviewUrl && (
            <div className="relative mb-2 p-2 border border-slate-600 rounded-lg bg-slate-700">
              <img src={imagePreviewUrl} alt="Preview" className="max-h-24 rounded-md" />
              <button
                onClick={handleRemoveImage}
                className="absolute top-1 right-1 p-1 bg-red-500 rounded-full text-white hover:bg-red-600 transition-colors"
              >
                <XCircle size={16} />
              </button>
            </div>
          )}
          <div className="flex items-center gap-2">
            {/* The text input field. */}
            <input
              type="text"
              className="flex-1 p-3 bg-slate-800 text-white placeholder-slate-400 rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all"
              placeholder="Type your message..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={handleKeyDown}
              // Disable input if not ready or if an error occurred
              disabled={!db || isAIGenerating || isSpeaking || isImageGenerating || !!error}
            />
            {/* Image upload button */}
            <label className="p-3 bg-purple-600 text-white rounded-full shadow-lg hover:bg-purple-700 transition-all cursor-pointer disabled:opacity-50 disabled:cursor-not-allowed">
              <input
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
                className="hidden"
                disabled={!db || isAIGenerating || isSpeaking || isImageGenerating || !!error}
              />
              <ImageIcon size={20} />
            </label>
            {/* The generate image button */}
            <button
              onClick={handleGenerateImage}
              className="p-3 bg-amber-600 text-white rounded-full shadow-lg hover:bg-amber-700 transition-all disabled:opacity-50 disabled:cursor-not-allowed"
              disabled={!db || input.trim() === '' || isAIGenerating || isSpeaking || isImageGenerating || !!error}
            >
              <Wand size={20} />
            </button>
            {/* The send button. It's disabled if the input is empty or not authenticated. */}
            <button
              onClick={handleSendMessage}
              className="p-3 bg-blue-600 text-white rounded-full shadow-lg hover:bg-blue-700 transition-all disabled:opacity-50 disabled:cursor-not-allowed"
              disabled={!db || (input.trim() === '' && !imageFile) || isAIGenerating || isSpeaking || isImageGenerating || !!error}
            >
              {/* Using the Send icon from lucide-react */}
              <Send size={20} />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
