/* global __firebase_config, __app_id, __initial_auth_token, marked */

import { useState, useEffect, useRef } from 'react';
import { initializeApp } from 'firebase/app';
import {
  getAuth,
  signInWithCustomToken,
  signInAnonymously,
  onAuthStateChanged,
  sendSignInLinkToEmail,
  isSignInWithEmailLink,
  signInWithEmailLink,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut
} from 'firebase/auth';
import {
  getFirestore,
  collection,
  onSnapshot,
  addDoc,
  serverTimestamp,
  doc,
  setDoc,
  query,
  limit
} from 'firebase/firestore';
import { createPortal } from 'react-dom';
import { Sparkles, MessageSquareMore, Image, LoaderCircle, X, ExternalLink, Menu, Plus, Volume2, Pause } from 'lucide-react';

// --- Firebase Configuration ---
// The global variables are provided by the Canvas environment.
// We fall back to a local config for development purposes if they are not defined.
const localFirebaseConfig = {
  apiKey: "AIzaSyDhJoNaFf0qgl4VTuUXj16ysJG1hycq-p8",
  authDomain: "ai-chatbot-88602.firebaseapp.com",
  projectId: "ai-chatbot-88602",
  storageBucket: "ai-chatbot-88602.firebasestorage.app",
  messagingSenderId: "167580287666",
  appId: "1:167580287666:web:9e8313ae6e667e47d47fab",
  measurementId: "G-CXKJ8QRLZ1"
};

const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : localFirebaseConfig;
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

// Utility function for generating unique IDs (like conversation IDs)
const generateId = () => {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0, v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
};

// Converts a base64 string to an ArrayBuffer
function base64ToArrayBuffer(base64) {
  const binaryString = window.atob(base64);
  const len = binaryString.length;
  const bytes = new Uint8Array(len);
  for (let i = 0; i < len; i++) {
    bytes[i] = binaryString.charCodeAt(i);
  }
  return bytes.buffer;
}

// Converts PCM audio data to a WAV blob
function pcmToWav(pcmData, sampleRate) {
  const pcm16 = new Int16Array(pcmData);
  const buffer = new ArrayBuffer(44 + pcm16.length * 2);
  const view = new DataView(buffer);
  const writeString = (view, offset, string) => {
      for (let i = 0; i < string.length; i++) {
          view.setUint8(offset + i, string.charCodeAt(i));
      }
  };

  let offset = 0;
  writeString(view, offset, 'RIFF'); offset += 4;
  view.setUint32(offset, 36 + pcm16.length * 2, true); offset += 4;
  writeString(view, offset, 'WAVE'); offset += 4;
  writeString(view, offset, 'fmt '); offset += 4;
  view.setUint32(offset, 16, true); offset += 4; // Sub-chunk size
  view.setUint16(offset, 1, true); offset += 2; // Audio format (1 = PCM)
  view.setUint16(offset, 1, true); offset += 2; // Number of channels
  view.setUint32(offset, sampleRate, true); offset += 4; // Sample rate
  view.setUint32(offset, sampleRate * 2, true); offset += 4; // Byte rate
  view.setUint16(offset, 2, true); offset += 2; // Block align
  view.setUint16(offset, 16, true); offset += 2; // Bits per sample
  writeString(view, offset, 'data'); offset += 4;
  view.setUint32(offset, pcm16.length * 2, true); offset += 4; // Data size

  for (let i = 0; i < pcm16.length; i++) {
      view.setInt16(offset, pcm16[i], true);
      offset += 2;
  }

  return new Blob([view], { type: 'audio/wav' });
}


// The main App component
function App() {
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailSent, setEmailSent] = useState(false);
  const [isSigningInWithLink, setIsSigningInWithLink] = useState(false);
  const [isAIGenerating, setIsAIGenerating] = useState(false);
  const [authMode, setAuthMode] = useState('email-link');

  // New states for conversation history and sidebar
  const [conversations, setConversations] = useState([]);
  const [currentConversationId, setCurrentConversationId] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isSummarizing, setIsSummarizing] = useState(false);
  const [isGeneratingImage, setIsGeneratingImage] = useState(false);
  const [currentAudio, setCurrentAudio] = useState(null);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [voiceName, setVoiceName] = useState('Kore');
  const [showVoiceModal, setShowVoiceModal] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  const messagesEndRef = useRef(null);
  const audioContextRef = useRef(null);
  const audioRef = useRef(null);

  // Scroll to bottom of messages whenever messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Exponential backoff utility for API calls
  const fetchWithExponentialBackoff = async (url, options, retries = 5, delay = 1000) => {
    try {
      const response = await fetch(url, options);
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`API error: ${errorData.error?.message || response.statusText}`);
      }
      return response;
    } catch (error) {
      if (retries > 0 && error.message.includes("API error")) {
        await new Promise(resolve => setTimeout(resolve, delay));
        return fetchWithExponentialBackoff(url, options, retries - 1, delay * 2);
      }
      throw error;
    }
  };

  // 1. Initialize Firebase and set up authentication.
  useEffect(() => {
    if (Object.keys(firebaseConfig).length === 0 || !firebaseConfig.apiKey) {
      setError('Firebase configuration is missing or incomplete. Please ensure your Firebase config is correctly set, especially the API Key.');
      setLoading(false);
      return;
    }

    try {
      const firebaseApp = initializeApp(firebaseConfig);
      const authInstance = getAuth(firebaseApp);
      const dbInstance = getFirestore(firebaseApp);

      setAuth(authInstance);
      setDb(dbInstance);

      const unsubscribe = onAuthStateChanged(authInstance, async (currentUser) => {
        if (currentUser) {
          setUser(currentUser);
          setIsAuthReady(true);
          setError(null);
        } else {
          if (initialAuthToken) {
             try {
               await signInWithCustomToken(authInstance, initialAuthToken);
             } catch (e) {
               console.error("Custom token sign-in failed:", e);
               setError(`Custom token sign-in failed: ${e.message}. This usually means the token is invalid or expired. Please try refreshing.`);
             }
          } else if (!isSigningInWithLink) {
            try {
              await signInAnonymously(authInstance);
            } catch (e) {
              console.error("Anonymous sign-in failed:", e);
              setError(`Anonymous sign-in failed: ${e.message}. Please ensure Anonymous Authentication is enabled in your Firebase project settings.`);
            }
          }
          setIsAuthReady(true);
        }
        setLoading(false);
      });
      return () => unsubscribe();
    } catch (e) {
      setError(`Failed to initialize Firebase: ${e.message}. Please check the console for more details.`);
      setLoading(false);
    }
  }, []);

  // 2. Handle email link sign-in.
  useEffect(() => {
    if (!auth || !isAuthReady) return;

    if (isSignInWithEmailLink(auth, window.location.href)) {
      setIsSigningInWithLink(true);
      setLoading(true);

      let storedEmail = window.localStorage.getItem('emailForSignIn');

      if (!storedEmail) {
        // Using a custom modal instead of prompt
        const modalDiv = document.createElement('div');
        modalDiv.className = 'fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50';
        modalDiv.innerHTML = `
          <div class="bg-white p-8 rounded-lg shadow-lg max-w-sm w-full">
            <h3 class="text-xl font-bold mb-4">Confirm Email</h3>
            <p class="mb-4">Please provide your email to complete sign-in.</p>
            <input id="email-confirm-input" type="email" placeholder="Enter your email" class="w-full p-3 border border-gray-300 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-indigo-500" required />
            <button id="email-confirm-button" class="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200">Confirm</button>
          </div>
        `;
        document.body.appendChild(modalDiv);

        const emailConfirmInput = modalDiv.querySelector('#email-confirm-input');
        const emailConfirmButton = modalDiv.querySelector('#email-confirm-button');

        emailConfirmButton.onclick = () => {
          storedEmail = emailConfirmInput.value;
          if (storedEmail) {
            document.body.removeChild(modalDiv);
            continueSignIn(storedEmail);
          } else {
            console.error("Email not provided. Please try again.");
          }
        };

        const continueSignIn = (emailToUse) => {
          signInWithEmailLink(auth, emailToUse, window.location.href)
            .then((result) => {
              window.localStorage.removeItem('emailForSignIn');
              setUser(result.user);
              setIsSigningInWithLink(false);
              setLoading(false);
              setError(null);
              window.history.replaceState({}, document.title, window.location.pathname);
            })
            .catch((error) => {
              setError(`Error completing sign-in with link: ${error.message}`);
              setIsSigningInWithLink(false);
              setLoading(false);
              window.history.replaceState({}, document.title, window.location.pathname);
            });
        };
        return; // Exit the effect to wait for user input
      }

      signInWithEmailLink(auth, storedEmail, window.location.href)
        .then((result) => {
          window.localStorage.removeItem('emailForSignIn');
          setUser(result.user);
          setIsSigningInWithLink(false);
          setLoading(false);
          setError(null);
          window.history.replaceState({}, document.title, window.location.pathname);
        })
        .catch((error) => {
          setError(`Error completing sign-in with link: ${error.message}`);
          setIsSigningInWithLink(false);
          setLoading(false);
          window.history.replaceState({}, document.title, window.location.pathname);
        });
    }
  }, [auth, isAuthReady]);

  // 3. Fetch conversations for the current user.
  useEffect(() => {
    if (!db || !user || !isAuthReady) return;

    const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
    const unsubscribe = onSnapshot(conversationsCollectionRef, (querySnapshot) => {
      const fetchedConversations = [];
      querySnapshot.forEach((doc) => {
        fetchedConversations.push({ id: doc.id, ...doc.data() });
      });

      fetchedConversations.sort((a, b) => (b.lastUpdated?.seconds || 0) - (a.lastUpdated?.seconds || 0));

      setConversations(fetchedConversations);

      if (!currentConversationId && fetchedConversations.length > 0) {
        setCurrentConversationId(fetchedConversations[0].id);
      } else if (fetchedConversations.length === 0) {
        setCurrentConversationId(null);
        setMessages([]);
      }
    }, (firestoreError) => {
      setError(`Failed to load conversations: ${firestoreError.message}`);
      console.error("Firestore conversations error:", firestoreError);
    });

    return () => unsubscribe();
  }, [db, user, isAuthReady, appId, currentConversationId]);

  // 4. Fetch messages for the current conversation.
  useEffect(() => {
    if (!db || !user || !currentConversationId) {
      setMessages([]); // Clear messages if no conversation is selected
      return;
    }

    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
    const unsubscribe = onSnapshot(messagesCollectionRef, (querySnapshot) => {
      const fetchedMessages = [];
      querySnapshot.forEach((doc) => {
        fetchedMessages.push({ id: doc.id, ...doc.data() });
      });

      fetchedMessages.sort((a, b) => (a.timestamp?.seconds || 0) - (b.timestamp?.seconds || 0));

      setMessages(fetchedMessages);
    }, (firestoreError) => {
      setError(`Failed to load messages for conversation: ${firestoreError.message}`);
      console.error("Firestore messages error:", firestoreError);
    });

    return () => unsubscribe();
  }, [db, user, currentConversationId, appId]);

  // 5. Start a new conversation.
  const startNewChat = () => {
    setCurrentConversationId(null);
    setMessages([]);
    setNewMessage('');
    setSelectedImage(null);
    setError(null);
    setIsAIGenerating(false);
    setIsSummarizing(false);
    setIsGeneratingImage(false);
    setIsSidebarOpen(false);
  };

  // 6. Handle sending a new message (and creating a new conversation if needed).
  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() && !selectedImage || !user || !db || isAIGenerating || isSpeaking) return;

    let conversationIdToUse = currentConversationId;
    const userMessageText = newMessage;
    const imageToSend = selectedImage;
    setNewMessage('');
    setSelectedImage(null);

    // If no conversation is active, create a new one.
    if (!conversationIdToUse) {
      const newConvId = generateId();
      const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
      const newConversationDocRef = doc(conversationsCollectionRef, newConvId);
      const initialTitle = userMessageText ? (userMessageText.substring(0, 50) + (userMessageText.length > 50 ? '...' : '')) : 'New chat with image';

      try {
        await setDoc(newConversationDocRef, {
          title: initialTitle,
          createdAt: serverTimestamp(),
          lastUpdated: serverTimestamp(),
        });
        conversationIdToUse = newConvId;
        setCurrentConversationId(newConvId);
      } catch (createError) {
        setError(`Failed to create new conversation: ${createError.message}`);
        console.error("Error creating new conversation:", createError);
        return;
      }
    } else {
      // Otherwise, just update the lastUpdated timestamp of the current conversation.
      const conversationDocRef = doc(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse);
      try {
        await setDoc(conversationDocRef, { lastUpdated: serverTimestamp() }, { merge: true });
      } catch (updateError) {
        console.error("Error updating conversation timestamp:", updateError);
      }
    }

    // Add user message to Firestore
    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse, 'messages');
      await addDoc(messagesCollectionRef, {
        text: userMessageText,
        userId: user.uid,
        userName: `User_${user.uid.substring(0, 5)}`,
        timestamp: serverTimestamp(),
        type: 'user-message',
        imageData: imageToSend
      });
    } catch (e) {
      setError(`Failed to send message: ${e.message}`);
      return;
    }

    const command = userMessageText.toLowerCase().trim();

    if (imageToSend) {
      handleImageAnalysis(userMessageText, imageToSend);
    } else if (command === 'summarize') {
      handleSummarizeChat();
    } else if (command.startsWith('image of')) {
      const imagePrompt = userMessageText.substring('image of'.length).trim();
      handleGenerateImage(imagePrompt);
    } else {
      handleAIResponse(userMessageText);
    }
  };

  // Function to handle image upload
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };


  // 7. Handle sending the sign-in email link.
  const handleSendSignInLink = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim()) {
      setError("Please enter a valid email address.");
      return;
    }
    setLoading(true);
    setError(null);
    const actionCodeSettings = {
      url: window.location.origin,
      handleCodeInApp: true,
    };
    try {
      await sendSignInLinkToEmail(auth, email, actionCodeSettings);
      setEmailSent(true);
      window.localStorage.setItem('emailForSignIn', email);
      setLoading(false);
    } catch (error) {
      setError(`Failed to send sign-in link: ${error.message}`);
      setLoading(false);
    }
  };

  // 8. Handle Email/Password Registration
  const handleEmailPasswordSignUp = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim() || !password.trim()) {
      setError("Please enter a valid email and password.");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
      setLoading(false);
    } catch (error) {
      setError(`Registration failed: ${error.message}`);
      setLoading(false);
    }
  };

  // 9. Handle Email/Password Login
  const handleEmailPasswordLogin = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim() || !password.trim()) {
      setError("Please enter a valid email and password.");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      await signInWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
      setLoading(false);
    } catch (error) {
      setError(`Login failed: ${error.message}`);
      setLoading(false);
    }
  };

  // 10. Handle Logout
  const handleLogout = async () => {
    if (!auth) return;
    try {
      await signOut(auth);
      setUser(null);
      setMessages([]);
      setConversations([]);
      setCurrentConversationId(null);
      setEmailSent(false);
      setIsSigningInWithLink(false);
      setAuthMode('email-link');
      setError(null);
    } catch (error) {
      setError(`Logout failed: ${error.message}`);
    }
  };

  // 11. Handle summarization of the current chat using the Gemini API
  const handleSummarizeChat = async () => {
    if (!currentConversationId || messages.length === 0 || isAIGenerating || isSummarizing || isGeneratingImage) return;
    setIsSummarizing(true);
    setError(null);
    try {
      const chatHistoryText = messages
        .filter(msg => msg.type === 'user-message' || msg.type === 'ai-response')
        .map(msg => `${msg.userId === user.uid ? 'User' : 'AI'}: ${msg.text}`)
        .join('\n');
      const prompt = `Please summarize the following chat conversation into a concise paragraph:\n\n${chatHistoryText}`;
      const chatHistory = [{ role: "user", parts: [{ text: prompt }] }];
      const payload = { contents: chatHistory };
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      let responseText = "Sorry, I couldn't generate a summary.";
      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        responseText = result.candidates[0].content.parts[0].text;
      }
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
      await addDoc(messagesCollectionRef, {
        text: `**Chat Summary:**\n\n${responseText}`,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response'
      });
    } catch (error) {
      console.error("Error summarizing chat:", error);
      setError(`Summarization error: ${error.message}`);
    } finally {
      setIsSummarizing(false);
    }
  };
  
  // 12. Handle image generation using the Gemini API
  const handleGenerateImage = async (imagePrompt) => {
    if (!currentConversationId || isAIGenerating || isSummarizing || isGeneratingImage) return;
    setIsGeneratingImage(true);
    setError(null);
    let tempImageMessageRef = null;
    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
      tempImageMessageRef = doc(messagesCollectionRef); // Add a placeholder message immediately
      await setDoc(tempImageMessageRef, {
        text: `Generating an image for: "${imagePrompt}"...`,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response',
        imageUrl: '',
      });
      const payload = { instances: { prompt: imagePrompt }, parameters: { "sampleCount": 1} };
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/imagen-3.0-generate-002:predict?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      if (result.predictions && result.predictions.length > 0 && result.predictions[0].bytesBase64Encoded) {
        const imageUrl = `data:image/png;base64,${result.predictions[0].bytesBase64Encoded}`;
        await setDoc(tempImageMessageRef, {
          text: `Here is the image you requested for "${imagePrompt}."`,
          userId: 'bot',
          userName: 'AI Chatbot',
          timestamp: serverTimestamp(),
          type: 'ai-response',
          imageUrl: imageUrl,
        });
      } else {
        throw new Error('Unexpected API response structure or no image data.');
      }
    } catch (error) {
      console.error("Error generating image:", error);
      if (tempImageMessageRef) {
        await setDoc(doc(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages', tempImageMessageRef.id), {
          text: `I couldn't generate the image. Please try again with a different prompt. Error: ${error.message}`,
          userId: 'bot',
          userName: 'AI Chatbot',
          timestamp: serverTimestamp(),
          type: 'ai-response',
          imageUrl: '',
        }, { merge: true });
      }
      setError(`Image generation error: ${error.message}`);
    } finally {
      setIsGeneratingImage(false);
    }
  };

  // 13. Handle text-to-speech
  const handleTTS = async (text, msgId) => {
    if (isSpeaking) {
      stopAudio();
      if (currentAudio === msgId) {
        return; // Stop and do not restart if the same audio is playing
      }
    }
    setCurrentAudio(msgId);
    setIsSpeaking(true);
    setError(null);

    try {
        const payload = {
            contents: [{
                parts: [{ text: `Say in a natural tone: ${text}` }]
            }],
            generationConfig: {
                responseModalities: ["AUDIO"],
                speechConfig: {
                    voiceConfig: {
                        prebuiltVoiceConfig: { voiceName: voiceName }
                    }
                }
            },
            model: "gemini-2.5-flash-preview-tts"
        };

        const apiKey = "";
        const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-tts:generateContent?key=${apiKey}`;

        const response = await fetchWithExponentialBackoff(apiUrl, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload)
        });
        const result = await response.json();
        const part = result?.candidates?.[0]?.content?.parts?.[0];
        const audioData = part?.inlineData?.data;
        const mimeType = part?.inlineData?.mimeType;

        if (audioData && mimeType && mimeType.startsWith("audio/")) {
            const sampleRate = parseInt(mimeType.match(/rate=(\d+)/)[1], 10);
            const pcmData = base64ToArrayBuffer(audioData);
            const pcm16 = new Int16Array(pcmData);
            const wavBlob = pcmToWav(pcm16, sampleRate);
            const audioUrl = URL.createObjectURL(wavBlob);

            if (audioRef.current) {
                audioRef.current.pause();
                audioRef.current.src = audioUrl;
                audioRef.current.play().catch(e => console.error("Audio playback failed:", e));
            } else {
                audioRef.current = new Audio(audioUrl);
                audioRef.current.play().catch(e => console.error("Audio playback failed:", e));
            }

            audioRef.current.onended = () => {
                setIsSpeaking(false);
                setCurrentAudio(null);
            };
        } else {
            throw new Error('TTS API response was malformed or missing audio data.');
        }

    } catch (error) {
        console.error("Error with TTS:", error);
        setError(`TTS error: ${error.message}`);
        setIsSpeaking(false);
        setCurrentAudio(null);
    }
  };

  const stopAudio = () => {
    if (audioRef.current) {
      audioRef.current.pause();
      audioRef.current.currentTime = 0;
    }
    setIsSpeaking(false);
    setCurrentAudio(null);
  }


  // 14. Handle AI text response using the Gemini API
  const handleAIResponse = async (userMessageText) => {
    setIsAIGenerating(true);
    setError(null);
    let tempMessageRef = null;

    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
      tempMessageRef = doc(messagesCollectionRef);
      // Add a placeholder message for the AI response
      await setDoc(tempMessageRef, {
        text: '',
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response',
      });

      const conversationHistory = messages.map(msg => ({
        role: msg.userId === user.uid ? 'user' : 'model',
        parts: [{ text: msg.text }]
      }));
      conversationHistory.push({ role: 'user', parts: [{ text: userMessageText }] });

      const payload = {
        contents: conversationHistory,
      };

      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;

      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      
      let responseText = "Sorry, I couldn't generate a response.";
      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        responseText = result.candidates[0].content.parts[0].text;
      }
      
      // Update the placeholder message with the real response
      await setDoc(tempMessageRef, { text: responseText }, { merge: true });

    } catch (error) {
      console.error("Error generating AI response:", error);
      if (tempMessageRef) {
        await setDoc(tempMessageRef, {
          text: `An error occurred while generating a response. Please try again. Error: ${error.message}`,
        }, { merge: true });
      }
      setError(`AI response error: ${error.message}`);
    } finally {
      setIsAIGenerating(false);
    }
  };

  // 15. Handle multi-modal image analysis
  const handleImageAnalysis = async (userMessageText, imageData) => {
    setIsAIGenerating(true);
    setError(null);
    let tempMessageRef = null;

    try {
        const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
        tempMessageRef = doc(messagesCollectionRef);
        await setDoc(tempMessageRef, {
            text: '',
            userId: 'bot',
            userName: 'AI Chatbot',
            timestamp: serverTimestamp(),
            type: 'ai-response',
        });

        // The API expects base64 data without the data URI prefix
        const base64ImageData = imageData.split(',')[1];
        const mimeType = imageData.split(';')[0].split(':')[1];

        const payload = {
            contents: [
                {
                    role: "user",
                    parts: [
                        { text: userMessageText || "What is this image?" },
                        {
                            inlineData: {
                                mimeType: mimeType,
                                data: base64ImageData
                            }
                        }
                    ]
                }
            ],
        };

        const apiKey = "";
        const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;

        const response = await fetchWithExponentialBackoff(apiUrl, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload)
        });
        const result = await response.json();

        let responseText = "Sorry, I couldn't analyze the image.";
        if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
            responseText = result.candidates[0].content.parts[0].text;
        }

        await setDoc(tempMessageRef, { text: responseText }, { merge: true });

    } catch (error) {
        console.error("Error generating AI response for image:", error);
        if (tempMessageRef) {
            await setDoc(tempMessageRef, {
                text: `An error occurred while analyzing the image. Please try again. Error: ${error.message}`,
            }, { merge: true });
        }
        setError(`Image analysis error: ${error.message}`);
    } finally {
        setIsAIGenerating(false);
    }
  };


  const Message = ({ msg, isUser, isAIGenerating, currentAudio, handleTTS, stopAudio }) => {
    // Check if the marked library is available before using it
    const markedHtml = window.marked ? window.marked.parse(msg.text) : msg.text;

    const isCurrentAudio = currentAudio === msg.id;

    return (
      <div className={`flex w-full mt-2 space-x-3 max-w-xs ${isUser ? 'ml-auto justify-end' : 'justify-start'}`}>
        {!isUser && (
          <div className="flex-shrink-0 h-10 w-10 rounded-full bg-gray-300 flex items-center justify-center">
            <Sparkles size={20} className="text-gray-600" />
          </div>
        )}
        <div>
          <div className={`p-3 rounded-lg ${isUser ? 'rounded-br-none bg-blue-600 text-white' : 'rounded-bl-none bg-gray-300 text-gray-800'}`}>
            {msg.imageData && (
              <div className="mb-2 rounded-lg overflow-hidden">
                <img src={msg.imageData} alt="User upload" className="max-w-full h-auto" />
              </div>
            )}
            <div dangerouslySetInnerHTML={{ __html: markedHtml }} />
            {msg.imageUrl && (
              <img src={msg.imageUrl} alt="Generated" className="mt-2 rounded-lg max-w-full h-auto" />
            )}
            {isUser && msg.type === 'user-message' && (isAIGenerating || isSummarizing || isGeneratingImage) && (
              <div className="dot-flashing mt-2"></div>
            )}
          </div>
          {!isUser && msg.type === 'ai-response' && (
            <div className="flex items-center space-x-2 mt-2">
              <span className="text-xs text-gray-500">{msg.userName}</span>
              {isCurrentAudio ? (
                <button
                  onClick={stopAudio}
                  className="p-1 rounded-full bg-gray-200 text-gray-700 hover:bg-gray-300 transition-colors duration-200"
                  aria-label="Stop audio"
                >
                  <Pause size={16} />
                </button>
              ) : (
                <button
                  onClick={() => handleTTS(msg.text, msg.id)}
                  className="p-1 rounded-full bg-gray-200 text-gray-700 hover:bg-gray-300 transition-colors duration-200"
                  aria-label="Play audio"
                >
                  <Volume2 size={16} />
                </button>
              )}
            </div>
          )}
        </div>
        {isUser && (
          <div className="flex-shrink-0 h-10 w-10 rounded-full bg-blue-500 flex items-center justify-center text-white font-bold">
            {user?.email?.charAt(0).toUpperCase() || user?.uid?.substring(0, 1).toUpperCase() || 'U'}
          </div>
        )}
      </div>
    );
  };

  const VoiceModal = ({ onClose }) => {
    const voices = [
      'Zephyr', 'Puck', 'Charon', 'Kore', 'Fenrir', 'Leda', 'Orus', 'Aoede', 'Callirrhoe', 'Autonoe', 'Enceladus', 'Iapetus', 'Umbriel', 'Algieba', 'Despina', 'Erinome', 'Algenib', 'Rasalgethi', 'Laomedeia', 'Achernar', 'Alnilam', 'Schedar', 'Gacrux', 'Pulcherrima', 'Achird', 'Zubenelgenubi', 'Vindemiatrix', 'Sadachbia', 'Sadaltager', 'Sulafat'
    ];
    return createPortal(
      <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50">
        <div className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-xl font-bold">Select a Voice</h3>
            <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
              <X size={24} />
            </button>
          </div>
          <div className="grid grid-cols-3 gap-4 max-h-80 overflow-y-auto pr-2">
            {voices.map(voice => (
              <button
                key={voice}
                onClick={() => {
                  setVoiceName(voice);
                  onClose();
                }}
                className={`p-2 rounded-lg text-sm transition-colors duration-200 ${
                  voiceName === voice
                    ? 'bg-indigo-500 text-white font-semibold shadow-md'
                    : 'bg-gray-100 text-gray-800 hover:bg-indigo-100'
                }`}
              >
                {voice}
              </button>
            ))}
          </div>
        </div>
      </div>,
      document.body
    );
  };

  return (
    <div className="min-h-screen bg-gray-100 text-gray-900 font-sans flex antialiased">
      {/* Sidebar for Conversations */}
      <aside className={`fixed inset-y-0 left-0 bg-white shadow-xl transition-transform duration-300 ease-in-out z-40 ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'} lg:relative lg:translate-x-0 lg:flex lg:flex-col w-64`}>
        <div className="flex items-center justify-between p-4 border-b">
          <h2 className="text-2xl font-bold text-gray-800">Gemini Chat</h2>
          <button onClick={() => setIsSidebarOpen(false)} className="text-gray-500 hover:text-gray-700 lg:hidden">
            <X size={24} />
          </button>
        </div>
        <div className="p-4 space-y-2 overflow-y-auto flex-grow">
          <button
            onClick={startNewChat}
            className="w-full flex items-center justify-center px-4 py-3 bg-indigo-600 text-white rounded-lg shadow-md hover:bg-indigo-700 transition-colors duration-200"
          >
            <Plus size={20} className="mr-2" /> New Chat
          </button>
          {conversations.map(conv => (
            <button
              key={conv.id}
              onClick={() => {
                setCurrentConversationId(conv.id);
                setIsSidebarOpen(false);
              }}
              className={`w-full text-left p-3 rounded-lg transition-colors duration-200 ${
                currentConversationId === conv.id ? 'bg-indigo-100 text-indigo-800 font-semibold' : 'bg-gray-50 text-gray-700 hover:bg-gray-100'
              }`}
            >
              <h3 className="truncate text-sm">{conv.title}</h3>
              <span className="text-xs text-gray-500">
                {conv.lastUpdated ? new Date(conv.lastUpdated.seconds * 1000).toLocaleDateString() : 'New'}
              </span>
            </button>
          ))}
        </div>
        <div className="p-4 border-t">
          {user && (
            <div className="text-sm">
              <p className="font-semibold truncate">User ID: {user.uid}</p>
              {user.email && <p className="truncate">Email: {user.email}</p>}
              <button
                onClick={handleLogout}
                className="mt-2 w-full text-center px-4 py-2 text-sm text-white bg-gray-700 rounded-lg hover:bg-gray-800 transition-colors duration-200"
              >
                Logout
              </button>
            </div>
          )}
        </div>
      </aside>

      {/* Main Chat Area */}
      <main className="flex-1 flex flex-col min-h-screen bg-gray-50 lg:ml-64">
        {/* Header */}
        <header className="p-4 bg-white shadow-sm flex items-center sticky top-0 z-30">
          <button onClick={() => setIsSidebarOpen(true)} className="text-gray-600 hover:text-gray-900 lg:hidden mr-4">
            <Menu size={24} />
          </button>
          <div className="flex items-center space-x-2">
            <h1 className="text-xl font-bold text-gray-800">
              {currentConversationId ? (conversations.find(c => c.id === currentConversationId)?.title || '...loading') : 'New Chat'}
            </h1>
            <div className="relative">
              <button
                onClick={() => setShowVoiceModal(true)}
                className="p-2 rounded-lg bg-gray-100 hover:bg-gray-200 transition-colors duration-200 text-gray-700"
                aria-label="Select AI Voice"
              >
                <Volume2 size={20} />
              </button>
              {showVoiceModal && <VoiceModal onClose={() => setShowVoiceModal(false)} />}
            </div>
          </div>
        </header>

        {/* Message Container */}
        <div className="flex-1 overflow-y-auto p-4 space-y-4">
          {loading && (
            <div className="flex justify-center items-center h-full">
              <div className="dot-flashing"></div>
            </div>
          )}
          {error && (
            <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 rounded-lg" role="alert">
              <p className="font-bold">Error</p>
              <p>{error}</p>
            </div>
          )}
          {messages.length === 0 && !loading && (
            <div className="text-center text-gray-500 pt-20">
              <MessageSquareMore size={48} className="mx-auto mb-4" />
              <p className="text-lg">Start a new conversation with Gemini!</p>
              <p className="text-sm mt-2">Try asking for a summary of the chat by typing `summarize` or generating an image by typing `image of [your prompt]`.</p>
            </div>
          )}
          {messages.map((msg, index) => (
            <Message
              key={msg.id || index}
              msg={msg}
              isUser={msg.userId === user?.uid}
              isAIGenerating={isAIGenerating}
              isSummarizing={isSummarizing}
              isGeneratingImage={isGeneratingImage}
              currentAudio={currentAudio}
              handleTTS={handleTTS}
              stopAudio={stopAudio}
            />
          ))}
          {(isAIGenerating || isSummarizing || isGeneratingImage) && (
            <div className="flex w-full mt-2 space-x-3 max-w-xs">
              <div className="flex-shrink-0 h-10 w-10 rounded-full bg-gray-300 flex items-center justify-center">
                <Sparkles size={20} className="text-gray-600" />
              </div>
              <div className="p-3 rounded-lg rounded-bl-none bg-gray-300 text-gray-800">
                <div className="dot-flashing"></div>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        {/* Message Input */}
        <div className="p-4 bg-white border-t sticky bottom-0 z-30">
          {selectedImage && (
            <div className="relative mb-4 p-2 border-b border-gray-200">
              <img src={selectedImage} alt="Preview" className="h-20 w-auto rounded-lg object-contain" />
              <button
                onClick={() => setSelectedImage(null)}
                className="absolute top-0 right-0 p-1 bg-red-500 text-white rounded-full text-xs"
              >
                <X size={12} />
              </button>
            </div>
          )}
          <form onSubmit={handleSendMessage} className="flex items-center space-x-2">
            <label htmlFor="image-upload" className="p-3 rounded-lg shadow-md transition-colors duration-200 bg-gray-200 hover:bg-gray-300 text-gray-700 cursor-pointer">
              <Image size={24} />
              <input
                id="image-upload"
                type="file"
                accept="image/*"
                className="hidden"
                onChange={handleImageUpload}
                disabled={isAIGenerating || isSummarizing || isGeneratingImage || !isAuthReady}
              />
            </label>
            <input
              type="text"
              className="flex-1 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              placeholder="Message Gemini..."
              disabled={isAIGenerating || isSummarizing || isGeneratingImage || !isAuthReady}
            />
            <button
              type="submit"
              className={`p-3 rounded-lg shadow-md transition-colors duration-200 ${
                isAIGenerating || isSummarizing || isGeneratingImage || !isAuthReady
                  ? 'bg-gray-400 cursor-not-allowed'
                  : 'bg-indigo-500 hover:bg-indigo-600 text-white'
              }`}
              disabled={isAIGenerating || isSummarizing || isGeneratingImage || !isAuthReady}
            >
              <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-send-horizonal">
                <path d="m3 3 3 9-3 9 19-9Z" />
                <path d="M6 12h16" />
              </svg>
            </button>
          </form>
        </div>
      </main>

      {/* Auth UI */}
      {user === null && !loading && !isSigningInWithLink && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-90 z-50 p-4">
          <div className="bg-white p-8 rounded-lg shadow-2xl max-w-lg w-full transform transition-all">
            <h2 className="text-3xl font-extrabold text-center mb-6 text-gray-800">
              Welcome to the Chat App!
            </h2>
            {error && (
              <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 rounded-lg mb-6" role="alert">
                <p>{error}</p>
              </div>
            )}
            <div className="flex space-x-2 mb-6">
              <button
                onClick={() => setAuthMode('email-link')}
                className={`flex-1 py-3 px-4 rounded-lg font-semibold transition-colors duration-200 ${
                  authMode === 'email-link' ? 'bg-indigo-500 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                }`}
              >
                Sign In with Email Link
              </button>
              <button
                onClick={() => setAuthMode('email-password')}
                className={`flex-1 py-3 px-4 rounded-lg font-semibold transition-colors duration-200 ${
                  authMode === 'email-password' ? 'bg-indigo-500 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                }`}
              >
                Email/Password
              </button>
            </div>
            {authMode === 'email-link' ? (
              <form onSubmit={handleSendSignInLink}>
                <p className="text-sm text-gray-600 mb-4 text-center">
                  Enter your email to receive a sign-in link.
                </p>
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="you@example.com"
                  className="w-full p-4 mb-4 text-gray-900 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  required
                />
                <button
                  type="submit"
                  className="w-full py-4 bg-indigo-600 text-white rounded-lg shadow-md hover:bg-indigo-700 transition-colors duration-200 font-bold text-lg"
                >
                  Send Sign-in Link
                </button>
              </form>
            ) : (
              <div className="space-y-4">
                <form onSubmit={handleEmailPasswordSignUp}>
                  <p className="text-sm text-gray-600 mb-4 text-center">
                    New user? Sign up with email and password.
                  </p>
                  <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="you@example.com"
                    className="w-full p-4 mb-2 text-gray-900 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                    required
                  />
                  <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Password"
                    className="w-full p-4 mb-4 text-gray-900 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                    required
                  />
                  <button
                    type="submit"
                    className="w-full py-4 bg-green-600 text-white rounded-lg shadow-md hover:bg-green-700 transition-colors duration-200 font-bold text-lg"
                  >
                    Sign Up
                  </button>
                </form>
                <div className="relative flex items-center justify-center my-6">
                  <span className="absolute bg-white px-3 text-gray-400">OR</span>
                  <div className="w-full border-t border-gray-300"></div>
                </div>
                <form onSubmit={handleEmailPasswordLogin}>
                  <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="you@example.com"
                    className="w-full p-4 mb-2 text-gray-900 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                    required
                  />
                  <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Password"
                    className="w-full p-4 mb-4 text-gray-900 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
                    required
                  />
                  <button
                    type="submit"
                    className="w-full py-4 bg-indigo-600 text-white rounded-lg shadow-md hover:bg-indigo-700 transition-colors duration-200 font-bold text-lg"
                  >
                    Log In
                  </button>
                </form>
              </div>
            )}
            {emailSent && (
              <div className="mt-6 bg-green-100 border-l-4 border-green-500 text-green-700 p-4 rounded-lg" role="alert">
                <p className="font-bold">Email Sent!</p>
                <p>Please check your inbox for the sign-in link.</p>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Dot Flashing CSS Animation */}
      <style>{`
        .dot-flashing {
          position: relative;
          width: 5px;
          height: 5px;
          border-radius: 5px;
          background-color: #a3a3a3;
          color: #a3a3a3;
          animation: dotFlashing 1s infinite linear alternate;
          animation-delay: 0.5s;
        }

        .dot-flashing::before, .dot-flashing::after {
          content: '';
          display: inline-block;
          position: absolute;
          top: 0;
        }

        .dot-flashing::before {
          left: -10px;
          width: 5px;
          height: 5px;
          border-radius: 5px;
          background-color: #a3a3a3;
          color: #a3a3a3;
          animation: dotFlashing 1s infinite alternate;
          animation-delay: 0s;
        }

        .dot-flashing::after {
          left: 10px;
          width: 5px;
          height: 5px;
          border-radius: 5px;
          background-color: #a3a3a3;
          color: #a3a3a3;
          animation: dotFlashing 1s infinite alternate;
          animation-delay: 1s;
        }

        @keyframes dotFlashing {
          0% {
            background-color: #a3a3a3;
          }
          50%,
          100% {
            background-color: #e0e0e0;
          }
        }
      `}</style>
    </div>
  );
}

export default App;
