/* global __firebase_config, __app_id, __initial_auth_token, marked */

import { useState, useEffect, useRef } from 'react';
import { initializeApp } from 'firebase/app';
import {
  getAuth,
  signInWithCustomToken,
  signInAnonymously,
  onAuthStateChanged,
  sendSignInLinkToEmail,
  isSignInWithEmailLink,
  signInWithEmailLink,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut
} from 'firebase/auth';
import {
  getFirestore,
  collection,
  onSnapshot,
  addDoc,
  serverTimestamp,
  doc,
  setDoc,
  query,
  limit
} from 'firebase/firestore';
import { createPortal } from 'react-dom';
import {
  Sparkles,
  MessageSquareMore,
  Image,
  LoaderCircle,
  X,
  ExternalLink,
  Menu,
  Plus,
  Volume2,
  Pause,
  Mic,
  Settings,
  CircleStop,
  User,
  Bot
} from 'lucide-react';

// --- Firebase Configuration ---
// The global variables are provided by the Canvas environment.
// We fall back to a local config for development purposes if they are not defined.
const localFirebaseConfig = {
  apiKey: "AIzaSyDhJoNaFf0qgl4VTuUXj16ysJG1hycq-p8",
  authDomain: "ai-chatbot-88602.firebaseapp.com",
  projectId: "ai-chatbot-88602",
  storageBucket: "ai-chatbot-88602.firebasestorage.app",
  messagingSenderId: "167580287666",
  appId: "1:167580287666:web:9e8313ae6e667e47d47fab",
  measurementId: "G-CXKJ8QRLZ1"
};

const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : localFirebaseConfig;
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

// Utility function for generating unique IDs (like conversation IDs)
const generateId = () => {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0, v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
};

// Converts a base64 string to an ArrayBuffer
function base64ToArrayBuffer(base64) {
  const binaryString = window.atob(base64);
  const len = binaryString.length;
  const bytes = new Uint8Array(len);
  for (let i = 0; i < len; i++) {
    bytes[i] = binaryString.charCodeAt(i);
  }
  return bytes.buffer;
}

// Converts PCM audio data to a WAV blob
function pcmToWav(pcmData, sampleRate) {
  const pcm16 = new Int16Array(pcmData);
  const buffer = new ArrayBuffer(44 + pcm16.length * 2);
  const view = new DataView(buffer);
  const writeString = (view, offset, string) => {
      for (let i = 0; i < string.length; i++) {
          view.setUint8(offset + i, string.charCodeAt(i));
      }
  };

  let offset = 0;
  writeString(view, offset, 'RIFF'); offset += 4;
  view.setUint32(offset, 36 + pcm16.length * 2, true); offset += 4;
  writeString(view, offset, 'WAVE'); offset += 4;
  writeString(view, offset, 'fmt '); offset += 4;
  view.setUint32(offset, 16, true); offset += 4; // Sub-chunk size
  view.setUint16(offset, 1, true); offset += 2; // Audio format (1 = PCM)
  view.setUint16(offset, 1, true); offset += 2; // Number of channels
  view.setUint32(offset, sampleRate, true); offset += 4; // Sample rate
  view.setUint32(offset, sampleRate * 2, true); offset += 4; // Byte rate
  view.setUint16(offset, 2, true); offset += 2; // Block align
  view.setUint16(offset, 16, true); offset += 2; // Bits per sample
  writeString(view, offset, 'data'); offset += 4;
  view.setUint32(offset, pcm16.length * 2, true); offset += 4; // Data size

  for (let i = 0; i < pcm16.length; i++) {
      view.setInt16(offset, pcm16[i], true);
      offset += 2;
  }

  return new Blob([view], { type: 'audio/wav' });
}


// The main App component
function App() {
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailSent, setEmailSent] = useState(false);
  const [isSigningInWithLink, setIsSigningInWithLink] = useState(false);
  const [isAIGenerating, setIsAIGenerating] = useState(false);
  const [authMode, setAuthMode] = useState('email-link');

  // New states for conversation history and sidebar
  const [conversations, setConversations] = useState([]);
  const [currentConversationId, setCurrentConversationId] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isSummarizing, setIsSummarizing] = useState(false);
  const [isGeneratingImage, setIsGeneratingImage] = useState(false);
  const [currentAudio, setCurrentAudio] = useState(null);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [voiceName, setVoiceName] = useState('Kore');
  const [showVoiceModal, setShowVoiceModal] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  // New states for Gemini features
  const [isRecording, setIsRecording] = useState(false);
  const [showSettingsModal, setShowSettingsModal] = useState(false);
  const [systemInstruction, setSystemInstruction] = useState('');
  const [generationSettings, setGenerationSettings] = useState({
    temperature: 0.9,
    topK: 40,
    topP: 0.95
  });

  const messagesEndRef = useRef(null);
  const audioContextRef = useRef(null);
  const audioRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  // Scroll to bottom of messages whenever messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Exponential backoff utility for API calls
  const fetchWithExponentialBackoff = async (url, options, retries = 5, delay = 1000) => {
    try {
      const response = await fetch(url, options);
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`API error: ${errorData.error?.message || response.statusText}`);
      }
      return response;
    } catch (error) {
      if (retries > 0 && error.message.includes("API error")) {
        await new Promise(resolve => setTimeout(resolve, delay));
        return fetchWithExponentialBackoff(url, options, retries - 1, delay * 2);
      }
      throw error;
    }
  };

  // 1. Initialize Firebase and set up authentication.
  useEffect(() => {
    if (Object.keys(firebaseConfig).length === 0 || !firebaseConfig.apiKey) {
      setError('Firebase configuration is missing or incomplete. Please ensure your Firebase config is correctly set, especially the API Key.');
      setLoading(false);
      return;
    }

    try {
      const firebaseApp = initializeApp(firebaseConfig);
      const authInstance = getAuth(firebaseApp);
      const dbInstance = getFirestore(firebaseApp);

      setAuth(authInstance);
      setDb(dbInstance);

      const unsubscribe = onAuthStateChanged(authInstance, async (currentUser) => {
        if (currentUser) {
          setUser(currentUser);
          setIsAuthReady(true);
          setError(null);
        } else {
          if (initialAuthToken) {
             try {
               await signInWithCustomToken(authInstance, initialAuthToken);
             } catch (e) {
               console.error("Custom token sign-in failed:", e);
               setError(`Custom token sign-in failed: ${e.message}. This usually means the token is invalid or expired. Please try refreshing.`);
             }
          } else if (!isSigningInWithLink) {
            try {
              await signInAnonymously(authInstance);
            } catch (e) {
              console.error("Anonymous sign-in failed:", e);
              setError(`Anonymous sign-in failed: ${e.message}. Please ensure Anonymous Authentication is enabled in your Firebase project settings.`);
            }
          }
          setIsAuthReady(true);
        }
        setLoading(false);
      });
      return () => unsubscribe();
    } catch (e) {
      setError(`Failed to initialize Firebase: ${e.message}. Please check the console for more details.`);
      setLoading(false);
    }
  }, []);

  // 2. Handle email link sign-in.
  useEffect(() => {
    if (!auth || !isAuthReady) return;

    if (isSignInWithEmailLink(auth, window.location.href)) {
      setIsSigningInWithLink(true);
      setLoading(true);

      let storedEmail = window.localStorage.getItem('emailForSignIn');

      if (!storedEmail) {
        // Using a custom modal instead of prompt
        const modalDiv = document.createElement('div');
        modalDiv.className = 'fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50';
        modalDiv.innerHTML = `
          <div class="bg-white p-8 rounded-lg shadow-lg max-w-sm w-full">
            <h3 class="text-xl font-bold mb-4">Confirm Email</h3>
            <p class="mb-4">Please provide your email to complete sign-in.</p>
            <input id="email-confirm-input" type="email" placeholder="Enter your email" class="w-full p-3 border border-gray-300 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-indigo-500" required />
            <button id="email-confirm-button" class="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200">Confirm</button>
          </div>
        `;
        document.body.appendChild(modalDiv);

        const emailConfirmInput = modalDiv.querySelector('#email-confirm-input');
        const emailConfirmButton = modalDiv.querySelector('#email-confirm-button');

        emailConfirmButton.onclick = () => {
          storedEmail = emailConfirmInput.value;
          if (storedEmail) {
            document.body.removeChild(modalDiv);
            continueSignIn(storedEmail);
          } else {
            console.error("Email not provided. Please try again.");
          }
        };

        const continueSignIn = (emailToUse) => {
          signInWithEmailLink(auth, emailToUse, window.location.href)
            .then((result) => {
              window.localStorage.removeItem('emailForSignIn');
              setUser(result.user);
              setIsSigningInWithLink(false);
              setLoading(false);
              setError(null);
              window.history.replaceState({}, document.title, window.location.pathname);
            })
            .catch((error) => {
              setError(`Error completing sign-in with link: ${error.message}`);
              setIsSigningInWithLink(false);
              setLoading(false);
              window.history.replaceState({}, document.title, window.location.pathname);
            });
        };
        return; // Exit the effect to wait for user input
      }

      signInWithEmailLink(auth, storedEmail, window.location.href)
        .then((result) => {
          window.localStorage.removeItem('emailForSignIn');
          setUser(result.user);
          setIsSigningInWithLink(false);
          setLoading(false);
          setError(null);
          window.history.replaceState({}, document.title, window.location.pathname);
        })
        .catch((error) => {
          setError(`Error completing sign-in with link: ${error.message}`);
          setIsSigningInWithLink(false);
          setLoading(false);
          window.history.replaceState({}, document.title, window.location.pathname);
        });
    }
  }, [auth, isAuthReady]);

  // 3. Fetch conversations for the current user.
  useEffect(() => {
    if (!db || !user || !isAuthReady) return;

    const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
    const unsubscribe = onSnapshot(conversationsCollectionRef, (querySnapshot) => {
      const fetchedConversations = [];
      querySnapshot.forEach((doc) => {
        fetchedConversations.push({ id: doc.id, ...doc.data() });
      });

      fetchedConversations.sort((a, b) => (b.lastUpdated?.seconds || 0) - (a.lastUpdated?.seconds || 0));

      setConversations(fetchedConversations);

      if (!currentConversationId && fetchedConversations.length > 0) {
        setCurrentConversationId(fetchedConversations[0].id);
      } else if (fetchedConversations.length === 0) {
        setCurrentConversationId(null);
        setMessages([]);
      }
    }, (firestoreError) => {
      setError(`Failed to load conversations: ${firestoreError.message}`);
      console.error("Firestore conversations error:", firestoreError);
    });

    return () => unsubscribe();
  }, [db, user, isAuthReady, appId, currentConversationId]);

  // 4. Fetch messages for the current conversation.
  useEffect(() => {
    if (!db || !user || !currentConversationId) {
      setMessages([]); // Clear messages if no conversation is selected
      return;
    }

    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
    const unsubscribe = onSnapshot(messagesCollectionRef, (querySnapshot) => {
      const fetchedMessages = [];
      querySnapshot.forEach((doc) => {
        fetchedMessages.push({ id: doc.id, ...doc.data() });
      });

      fetchedMessages.sort((a, b) => (a.timestamp?.seconds || 0) - (b.timestamp?.seconds || 0));

      setMessages(fetchedMessages);
    }, (firestoreError) => {
      setError(`Failed to load messages for conversation: ${firestoreError.message}`);
      console.error("Firestore messages error:", firestoreError);
    });

    return () => unsubscribe();
  }, [db, user, currentConversationId, appId]);

  // 5. Start a new conversation.
  const startNewChat = () => {
    setCurrentConversationId(null);
    setMessages([]);
    setNewMessage('');
    setSelectedImage(null);
    setError(null);
    setIsAIGenerating(false);
    setIsSummarizing(false);
    setIsGeneratingImage(false);
    setIsSidebarOpen(false);
  };

  // 6. Handle sending a new message (and creating a new conversation if needed).
  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() && !selectedImage || !user || !db || isAIGenerating || isSpeaking || isRecording) return;

    let conversationIdToUse = currentConversationId;
    const userMessageText = newMessage;
    const imageToSend = selectedImage;
    setNewMessage('');
    setSelectedImage(null);

    // If no conversation is active, create a new one.
    if (!conversationIdToUse) {
      const newConvId = generateId();
      const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
      const newConversationDocRef = doc(conversationsCollectionRef, newConvId);
      const initialTitle = userMessageText ? (userMessageText.substring(0, 50) + (userMessageText.length > 50 ? '...' : '')) : 'New chat with image';

      try {
        await setDoc(newConversationDocRef, {
          title: initialTitle,
          createdAt: serverTimestamp(),
          lastUpdated: serverTimestamp(),
        });
        conversationIdToUse = newConvId;
        setCurrentConversationId(newConvId);
      } catch (createError) {
        setError(`Failed to create new conversation: ${createError.message}`);
        console.error("Error creating new conversation:", createError);
        return;
      }
    } else {
      // Otherwise, just update the lastUpdated timestamp of the current conversation.
      const conversationDocRef = doc(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse);
      try {
        await setDoc(conversationDocRef, { lastUpdated: serverTimestamp() }, { merge: true });
      } catch (updateError) {
        console.error("Error updating conversation timestamp:", updateError);
      }
    }

    // Add user message to Firestore
    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse, 'messages');
      await addDoc(messagesCollectionRef, {
        text: userMessageText,
        userId: user.uid,
        userName: `User_${user.uid.substring(0, 5)}`,
        timestamp: serverTimestamp(),
        type: 'user-message',
        imageData: imageToSend
      });
    } catch (e) {
      setError(`Failed to send message: ${e.message}`);
      return;
    }

    const command = userMessageText.toLowerCase().trim();

    if (imageToSend) {
      handleImageAnalysis(userMessageText, imageToSend, conversationIdToUse);
    } else if (command === 'summarize') {
      handleSummarizeChat(conversationIdToUse);
    } else if (command.startsWith('image of')) {
      const imagePrompt = userMessageText.substring('image of'.length).trim();
      handleGenerateImage(imagePrompt, conversationIdToUse);
    } else {
      handleAIResponse(userMessageText, conversationIdToUse);
    }
  };

  // Function to handle image upload
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };


  // 7. Handle sending the sign-in email link.
  const handleSendSignInLink = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim()) {
      setError("Please enter a valid email address.");
      return;
    }
    setLoading(true);
    setError(null);
    const actionCodeSettings = {
      url: window.location.origin,
      handleCodeInApp: true,
    };
    try {
      await sendSignInLinkToEmail(auth, email, actionCodeSettings);
      setEmailSent(true);
      window.localStorage.setItem('emailForSignIn', email);
      setLoading(false);
    } catch (error) {
      setError(`Failed to send sign-in link: ${error.message}`);
      setLoading(false);
    }
  };

  // 8. Handle Email/Password Registration
  const handleEmailPasswordSignUp = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim() || !password.trim()) {
      setError("Please enter a valid email and password.");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
      setLoading(false);
    } catch (error) {
      setError(`Registration failed: ${error.message}`);
      setLoading(false);
    }
  };

  // 9. Handle Email/Password Login
  const handleEmailPasswordLogin = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim() || !password.trim()) {
      setError("Please enter a valid email and password.");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      await signInWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
      setLoading(false);
    } catch (error) {
      setError(`Login failed: ${error.message}`);
      setLoading(false);
    }
  };

  // 10. Handle Logout
  const handleLogout = async () => {
    if (!auth) return;
    try {
      await signOut(auth);
      setUser(null);
      setMessages([]);
      setConversations([]);
      setCurrentConversationId(null);
      setEmailSent(false);
      setIsSigningInWithLink(false);
      setAuthMode('email-link');
      setError(null);
    } catch (error) {
      setError(`Logout failed: ${error.message}`);
    }
  };
  
  // 11. Handle AI Response generation
  const handleAIResponse = async (userMessageText, conversationId, additionalContent = []) => {
    if (!conversationId || isAIGenerating || isSummarizing || isGeneratingImage) return;

    setIsAIGenerating(true);
    setError(null);
    let aiMessageDocRef = null;

    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');

      // Add a placeholder message for the AI's response
      aiMessageDocRef = await addDoc(messagesCollectionRef, {
        text: '...',
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response'
      });

      // Prepare chat history for the API call
      const chatHistory = messages
        .filter(msg => msg.type === 'user-message' || msg.type === 'ai-response' || msg.type === 'image-analysis')
        .map(msg => ({
          role: msg.userId === user.uid ? 'user' : 'model',
          parts: [{ text: msg.text }]
        }));

      // Add the current user message and any additional content (like image data)
      const parts = [{ text: userMessageText }];
      if (additionalContent.length > 0) {
        parts.push(...additionalContent);
      }
      chatHistory.push({ role: 'user', parts });

      const payload = {
        contents: chatHistory,
        generationConfig: {
          temperature: generationSettings.temperature,
          topK: generationSettings.topK,
          topP: generationSettings.topP,
        },
      };

      if (systemInstruction) {
        payload.systemInstruction = { parts: [{ text: systemInstruction }] };
      }

      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;

      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();

      let responseText = "Sorry, I couldn't generate a response.";
      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        responseText = result.candidates[0].content.parts[0].text;
      }

      // Update the placeholder message with the actual response
      await setDoc(doc(messagesCollectionRef, aiMessageDocRef.id), {
        text: responseText,
        timestamp: serverTimestamp(),
      }, { merge: true });

    } catch (e) {
      console.error("Error generating AI response:", e);
      setError(`AI response error: ${e.message}`);
      if (aiMessageDocRef) {
        await setDoc(doc(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages', aiMessageDocRef.id), {
          text: `I apologize, an error occurred: ${e.message}`,
          timestamp: serverTimestamp(),
        }, { merge: true });
      }
    } finally {
      setIsAIGenerating(false);
    }
  };

  // 12. Handle summarization of the current chat using the Gemini API
  const handleSummarizeChat = async (conversationId) => {
    if (!conversationId || messages.length === 0 || isAIGenerating || isSummarizing || isGeneratingImage) return;
    setIsSummarizing(true);
    setError(null);
    try {
      const chatHistoryText = messages
        .filter(msg => msg.type === 'user-message' || msg.type === 'ai-response')
        .map(msg => `${msg.userId === user.uid ? 'User' : 'AI'}: ${msg.text}`)
        .join('\n');
      const prompt = `Please summarize the following chat conversation into a concise paragraph:\n\n${chatHistoryText}`;
      const chatHistory = [{ role: "user", parts: [{ text: prompt }] }];
      const payload = {
        contents: chatHistory,
        generationConfig: {
          temperature: generationSettings.temperature,
          topK: generationSettings.topK,
          topP: generationSettings.topP,
        },
      };
      if (systemInstruction) {
        payload.systemInstruction = { parts: [{ text: systemInstruction }] };
      }
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      let responseText = "Sorry, I couldn't generate a summary.";
      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        responseText = result.candidates[0].content.parts[0].text;
      }
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
      await addDoc(messagesCollectionRef, {
        text: `**Chat Summary:**\n\n${responseText}`,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response'
      });
    } catch (error) {
      console.error("Error summarizing chat:", error);
      setError(`Summarization error: ${error.message}`);
    } finally {
      setIsSummarizing(false);
    }
  };

  // 13. Handle image generation using the Gemini API
  const handleGenerateImage = async (imagePrompt, conversationId) => {
    if (!conversationId || isAIGenerating || isSummarizing || isGeneratingImage) return;
    setIsGeneratingImage(true);
    setError(null);
    let tempImageMessageRef = null;
    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
      tempImageMessageRef = doc(messagesCollectionRef); // Add a placeholder message immediately
      await setDoc(tempImageMessageRef, {
        text: `Generating an image for: "${imagePrompt}"...`,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response',
        imageUrl: '',
      });
      const payload = {
        instances: { prompt: imagePrompt },
        parameters: { "sampleCount": 1}
      };
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/imagen-3.0-generate-002:predict?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      if (result.predictions && result.predictions.length > 0 && result.predictions[0].bytesBase64Encoded) {
        const imageUrl = `data:image/png;base64,${result.predictions[0].bytesBase64Encoded}`;
        await setDoc(tempImageMessageRef, {
          text: `Here is the image you requested for "${imagePrompt}."`,
          userId: 'bot',
          userName: 'AI Chatbot',
          timestamp: serverTimestamp(),
          type: 'ai-response',
          imageUrl: imageUrl,
        });
      } else {
        throw new Error('Unexpected API response structure or no image data.');
      }
    } catch (error) {
      console.error("Error generating image:", error);
      if (tempImageMessageRef) {
        await setDoc(doc(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages', tempImageMessageRef.id), {
          text: `I couldn't generate the image. Please try again with a different prompt. Error: ${error.message}`,
          userId: 'bot',
          userName: 'AI Chatbot',
          timestamp: serverTimestamp(),
          type: 'ai-response',
          imageUrl: '',
        }, { merge: true });
      }
      setError(`Image generation error: ${error.message}`);
    } finally {
      setIsGeneratingImage(false);
    }
  };

  // 14. Handle text-to-speech
  const handleTTS = async (text, msgId, speakerName = 'User') => {
    if (isSpeaking) {
      stopAudio();
      if (currentAudio === msgId) {
        return; // Stop and do not restart if the same audio is playing
      }
    }
    setCurrentAudio(msgId);
    setIsSpeaking(true);
    setError(null);
    try {
      const payload = {
        contents: [{ parts: [{ text: `Say in a natural tone: ${text}` }] }],
        generationConfig: {
          responseModalities: ["AUDIO"],
          speechConfig: {
            voiceConfig: {
              prebuiltVoiceConfig: { voiceName: voiceName }
            }
          }
        },
        model: "gemini-2.5-flash-preview-tts"
      };
      
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-tts:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      const part = result?.candidates?.[0]?.content?.parts?.[0];
      const audioData = part?.inlineData?.data;
      const mimeType = part?.inlineData?.mimeType;

      if (audioData && mimeType && mimeType.startsWith("audio/")) {
        const sampleRateMatch = mimeType.match(/rate=(\d+)/);
        const sampleRate = sampleRateMatch ? parseInt(sampleRateMatch[1], 10) : 16000;
        const pcmData = base64ToArrayBuffer(audioData);
        const wavBlob = pcmToWav(new Int16Array(pcmData), sampleRate);
        const audioUrl = URL.createObjectURL(wavBlob);
        
        audioRef.current = new Audio(audioUrl);
        audioRef.current.play();
        audioRef.current.onended = () => {
          setIsSpeaking(false);
          setCurrentAudio(null);
        };
      } else {
        throw new Error('Unexpected TTS API response structure.');
      }
    } catch (error) {
      console.error("Error generating TTS:", error);
      setError(`Text-to-speech error: ${error.message}`);
      setIsSpeaking(false);
      setCurrentAudio(null);
    }
  };
  
  // 15. Stop current audio playback
  const stopAudio = () => {
    if (audioRef.current) {
      audioRef.current.pause();
      audioRef.current.currentTime = 0;
      audioRef.current = null;
    }
    setIsSpeaking(false);
    setCurrentAudio(null);
  };

  // 16. Start audio recording from the microphone
  const handleStartRecording = async () => {
    if (isRecording) return;
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      audioContextRef.current = new AudioContext();
      mediaRecorderRef.current = new MediaRecorder(stream, { mimeType: 'audio/webm' });
      audioChunksRef.current = [];

      mediaRecorderRef.current.ondataavailable = (event) => {
        audioChunksRef.current.push(event.data);
      };

      mediaRecorderRef.current.onstop = () => {
        handleProcessVoice(new Blob(audioChunksRef.current, { type: 'audio/webm' }));
      };

      mediaRecorderRef.current.start();
      setIsRecording(true);
      setError(null);
    } catch (error) {
      console.error("Error starting media recorder:", error);
      setError(`Failed to start recording: ${error.message}. Please check your microphone permissions.`);
    }
  };

  // 17. Stop audio recording
  const handleStopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
      mediaRecorderRef.current.stop();
      mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
      setIsRecording(false);
    }
  };

  // 18. Process the recorded voice blob
  const handleProcessVoice = async (audioBlob) => {
    if (!audioBlob || !user || !db || isAIGenerating) return;
    setIsAIGenerating(true);
    setError(null);

    let conversationIdToUse = currentConversationId;
    
    try {
      // Create a placeholder user message
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse, 'messages');
      const userMessageDocRef = await addDoc(messagesCollectionRef, {
        text: '...',
        userId: user.uid,
        userName: `User_${user.uid.substring(0, 5)}`,
        timestamp: serverTimestamp(),
        type: 'user-message'
      });

      // Transcribe the audio
      const audioBase64 = await new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.onloadend = () => resolve(reader.result.split(',')[1]);
        reader.onerror = reject;
        reader.readAsDataURL(audioBlob);
      });

      const transcriptionPayload = {
        contents: [
          {
            role: 'user',
            parts: [
              {
                inlineData: {
                  mimeType: 'audio/webm',
                  data: audioBase64
                }
              },
              { text: 'transcribe the given audio' }
            ]
          }
        ]
      };

      const apiKey = "";
      const transcriptionApiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-audio-transcription:generateContent?key=${apiKey}`;

      const transcriptionResponse = await fetchWithExponentialBackoff(transcriptionApiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(transcriptionPayload)
      });
      const transcriptionResult = await transcriptionResponse.json();
      const transcribedText = transcriptionResult?.candidates?.[0]?.content?.parts?.[0]?.text || 'Could not transcribe audio.';

      // Update the user message with the transcribed text
      await setDoc(doc(messagesCollectionRef, userMessageDocRef.id), {
        text: transcribedText,
        timestamp: serverTimestamp(),
      }, { merge: true });

      // Get AI response to the transcribed text
      await handleAIResponse(transcribedText, conversationIdToUse);

    } catch (error) {
      console.error("Error processing voice input:", error);
      setError(`Voice processing failed: ${error.message}`);
      setIsAIGenerating(false);
    }
  };
  
  // 19. Handle image analysis
  const handleImageAnalysis = async (userMessage, base64ImageData, conversationId) => {
    if (!conversationId || !base64ImageData || isAIGenerating || isSummarizing || isGeneratingImage) return;

    setIsAIGenerating(true);
    setError(null);
    let aiMessageDocRef = null;

    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');

      aiMessageDocRef = await addDoc(messagesCollectionRef, {
        text: '...',
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response'
      });

      const parts = [
        { text: userMessage },
        {
          inlineData: {
            mimeType: "image/png",
            data: base64ImageData.split(',')[1]
          }
        }
      ];

      const chatHistory = [{ role: "user", parts }];

      const payload = {
        contents: chatHistory,
        generationConfig: {
          temperature: generationSettings.temperature,
          topK: generationSettings.topK,
          topP: generationSettings.topP,
        },
      };
      
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;

      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();

      let responseText = "Sorry, I couldn't analyze the image.";
      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        responseText = result.candidates[0].content.parts[0].text;
      }

      await setDoc(doc(messagesCollectionRef, aiMessageDocRef.id), {
        text: responseText,
        timestamp: serverTimestamp(),
      }, { merge: true });

    } catch (e) {
      console.error("Error analyzing image:", e);
      setError(`Image analysis error: ${e.message}`);
      if (aiMessageDocRef) {
        await setDoc(doc(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages', aiMessageDocRef.id), {
          text: `I apologize, an error occurred during image analysis: ${e.message}`,
          timestamp: serverTimestamp(),
        }, { merge: true });
      }
    } finally {
      setIsAIGenerating(false);
    }
  };

  const getVoiceDisplayName = (voiceName) => {
    const voices = {
      'Kore': 'Kore (Firm)',
      'Puck': 'Puck (Upbeat)',
      'Zephyr': 'Zephyr (Bright)',
      'Charon': 'Charon (Informative)',
      'Fenrir': 'Fenrir (Excitable)',
      'Leda': 'Leda (Youthful)',
      'Orus': 'Orus (Firm)',
      'Aoede': 'Aoede (Breezy)',
      'Callirrhoe': 'Callirrhoe (Easy-going)',
      'Autonoe': 'Autonoe (Bright)',
      'Enceladus': 'Enceladus (Breathy)',
      'Iapetus': 'Iapetus (Clear)',
      'Umbriel': 'Umbriel (Easy-going)',
      'Algieba': 'Algieba (Smooth)',
      'Despina': 'Despina (Smooth)',
      'Erinome': 'Erinome (Clear)',
      'Algenib': 'Algenib (Gravelly)',
      'Rasalgethi': 'Rasalgethi (Informative)',
      'Laomedeia': 'Laomedeia (Upbeat)',
      'Achernar': 'Achernar (Soft)',
      'Alnilam': 'Alnilam (Firm)',
      'Schedar': 'Schedar (Even)',
      'Gacrux': 'Gacrux (Mature)',
      'Pulcherrima': 'Pulcherrima (Forward)',
      'Achird': 'Achird (Friendly)',
      'Zubenelgenubi': 'Zubenelgenubi (Casual)',
      'Vindemiatrix': 'Vindemiatrix (Gentle)',
      'Sadachbia': 'Sadachbia (Lively)',
      'Sadaltager': 'Sadaltager (Knowledgeable)',
      'Sulafat': 'Sulafat (Warm)',
    };
    return voices[voiceName] || voiceName;
  };
  
  const voiceOptions = [
    'Kore', 'Puck', 'Zephyr', 'Charon', 'Fenrir', 'Leda', 'Orus', 'Aoede', 'Callirrhoe', 'Autonoe', 'Enceladus', 'Iapetus', 'Umbriel', 'Algieba', 'Despina', 'Erinome', 'Algenib', 'Rasalgethi', 'Laomedeia', 'Achernar', 'Alnilam', 'Schedar', 'Gacrux', 'Pulcherrima', 'Achird', 'Zubenelgenubi', 'Vindemiatrix', 'Sadachbia', 'Sadaltager', 'Sulafat'
  ];

  const VoiceSettingsModal = () => {
    return (
      <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50 p-4">
        <div className="bg-white p-6 rounded-2xl shadow-2xl w-full max-w-md transform transition-all duration-300 scale-100">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-xl font-bold text-gray-800">Choose a Voice</h3>
            <button onClick={() => setShowVoiceModal(false)} className="text-gray-500 hover:text-gray-800 transition-colors duration-200">
              <X size={24} />
            </button>
          </div>
          <div className="grid grid-cols-2 sm:grid-cols-3 gap-3 max-h-96 overflow-y-auto pr-2">
            {voiceOptions.map((v) => (
              <button
                key={v}
                onClick={() => {
                  setVoiceName(v);
                  setShowVoiceModal(false);
                }}
                className={`py-2 px-4 rounded-xl text-sm font-medium transition-all duration-200 ${
                  voiceName === v
                    ? 'bg-indigo-600 text-white shadow-md transform scale-105'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                {getVoiceDisplayName(v)}
              </button>
            ))}
          </div>
        </div>
      </div>
    );
  };
  
  const SettingsModal = () => {
    return (
      <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50 p-4">
        <div className="bg-white p-6 rounded-2xl shadow-2xl w-full max-w-lg transform transition-all duration-300 scale-100">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-xl font-bold text-gray-800">Generation Settings</h3>
            <button onClick={() => setShowSettingsModal(false)} className="text-gray-500 hover:text-gray-800 transition-colors duration-200">
              <X size={24} />
            </button>
          </div>
          <div className="space-y-4">
            <div>
              <label htmlFor="systemInstruction" className="block text-sm font-medium text-gray-700 mb-1">
                System Instruction
              </label>
              <textarea
                id="systemInstruction"
                rows="4"
                className="w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors duration-200"
                placeholder="e.g., 'You are a helpful assistant that always responds in markdown format.' "
                value={systemInstruction}
                onChange={(e) => setSystemInstruction(e.target.value)}
              />
            </div>
            <div>
              <label htmlFor="temperature" className="block text-sm font-medium text-gray-700 mb-1">
                Temperature: {generationSettings.temperature.toFixed(2)}
              </label>
              <input
                type="range"
                id="temperature"
                min="0"
                max="1"
                step="0.05"
                value={generationSettings.temperature}
                onChange={(e) => setGenerationSettings({ ...generationSettings, temperature: parseFloat(e.target.value) })}
                className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
              />
              <p className="text-xs text-gray-500 mt-1">Controls the randomness of the output. Lower values are more deterministic.</p>
            </div>
            <div>
              <label htmlFor="topK" className="block text-sm font-medium text-gray-700 mb-1">
                Top-K: {generationSettings.topK}
              </label>
              <input
                type="range"
                id="topK"
                min="1"
                max="100"
                step="1"
                value={generationSettings.topK}
                onChange={(e) => setGenerationSettings({ ...generationSettings, topK: parseInt(e.target.value, 10) })}
                className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
              />
              <p className="text-xs text-gray-500 mt-1">Filters the top K most likely tokens to sample from.</p>
            </div>
            <div>
              <label htmlFor="topP" className="block text-sm font-medium text-gray-700 mb-1">
                Top-P: {generationSettings.topP.toFixed(2)}
              </label>
              <input
                type="range"
                id="topP"
                min="0"
                max="1"
                step="0.05"
                value={generationSettings.topP}
                onChange={(e) => setGenerationSettings({ ...generationSettings, topP: parseFloat(e.target.value) })}
                className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
              />
              <p className="text-xs text-gray-500 mt-1">Nucleus sampling. Filters the smallest set of tokens whose cumulative probability exceeds P.</p>
            </div>
          </div>
        </div>
      </div>
    );
  };
  
  // Custom Modal component
  const Modal = ({ title, children, onClose }) => {
    return (
      <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50 p-4">
        <div className="bg-white p-6 rounded-2xl shadow-2xl w-full max-w-sm transform transition-all duration-300 scale-100">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-xl font-bold text-gray-800">{title}</h3>
            <button onClick={onClose} className="text-gray-500 hover:text-gray-800 transition-colors duration-200">
              <X size={24} />
            </button>
          </div>
          {children}
        </div>
      </div>
    );
  };
  

  const renderMessageContent = (message) => {
    if (message.imageUrl) {
      return (
        <div className="flex flex-col items-start gap-2">
          <p className="text-gray-900 font-medium">{message.text}</p>
          <img
            src={message.imageUrl}
            alt="Generated content"
            className="rounded-lg shadow-md max-h-96 object-contain"
            onError={(e) => {
              e.target.src = "https://placehold.co/400x300/e2e8f0/64748b?text=Image+Load+Error";
            }}
          />
        </div>
      );
    }
    
    // Check for "Generating image" placeholder
    if (message.text.startsWith('Generating an image')) {
      return (
        <div className="flex items-center space-x-2 text-gray-500 italic">
          <LoaderCircle className="animate-spin" size={18} />
          <span>{message.text}</span>
        </div>
      );
    }
    
    // Check for "AI is typing" placeholder
    if (message.text === '...') {
      return (
        <div className="flex items-center space-x-2 text-gray-500 italic">
          <div className="dot-flashing"></div>
        </div>
      );
    }

    // Render markdown for other messages
    return <div dangerouslySetInnerHTML={{ __html: marked(message.text || '') }} className="prose prose-sm max-w-none text-gray-900" />;
  };


  // Main UI render
  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100 text-gray-600">
        <LoaderCircle className="animate-spin mr-2" size={24} />
        <p>Loading...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen bg-red-50 p-4 text-red-700">
        <p className="font-bold text-lg mb-2">Error</p>
        <p className="text-center">{error}</p>
      </div>
    );
  }

  if (!user) {
    // Auth UI
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center p-4">
        {emailSent ? (
          <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-md text-center">
            <h2 className="text-2xl font-bold text-gray-800 mb-4">Check your email</h2>
            <p className="text-gray-600 mb-6">
              A sign-in link has been sent to {email}. Please check your inbox and click the link to continue.
            </p>
            <button
              onClick={() => setEmailSent(false)}
              className="w-full bg-indigo-500 text-white p-3 rounded-xl shadow-md hover:bg-indigo-600 transition-colors duration-200"
            >
              Go Back
            </button>
          </div>
        ) : (
          <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-md">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-2xl font-bold text-gray-800">Sign In</h2>
              {authMode !== 'email-link' && (
                <button
                  onClick={() => setAuthMode('email-link')}
                  className="text-indigo-500 hover:text-indigo-700 transition-colors duration-200 font-medium"
                >
                  Use Sign-in Link
                </button>
              )}
            </div>
            
            {authMode === 'email-link' ? (
              <form onSubmit={handleSendSignInLink} className="space-y-4">
                <p className="text-sm text-gray-500 text-center">
                  Enter your email to receive a magic sign-in link.
                </p>
                <input
                  type="email"
                  placeholder="Email Address"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors duration-200"
                  required
                />
                <button
                  type="submit"
                  className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200"
                >
                  Send Sign-in Link
                </button>
                <button
                  type="button"
                  onClick={() => setAuthMode('email-password')}
                  className="w-full mt-4 text-indigo-500 hover:text-indigo-700 transition-colors duration-200 font-medium"
                >
                  Use Email & Password
                </button>
              </form>
            ) : (
              <form className="space-y-4">
                <p className="text-sm text-gray-500 text-center">
                  Use email and password to create an account or sign in.
                </p>
                <input
                  type="email"
                  placeholder="Email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors duration-200"
                  required
                />
                <input
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="w-full p-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors duration-200"
                  required
                />
                <button
                  onClick={handleEmailPasswordLogin}
                  className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200"
                >
                  Sign In
                </button>
                <button
                  onClick={handleEmailPasswordSignUp}
                  className="w-full mt-2 bg-gray-200 text-gray-800 p-3 rounded-lg shadow-md hover:bg-gray-300 transition-colors duration-200"
                >
                  Register
                </button>
              </form>
            )}
          </div>
        )}
      </div>
    );
  }

  // Chat UI
  return (
    <div className="relative flex h-screen bg-gray-100 font-sans">
      {/* Sidebar */}
      <div
        className={`fixed inset-y-0 left-0 bg-white shadow-xl transition-all duration-300 transform ${
          isSidebarOpen ? 'translate-x-0' : '-translate-x-full'
        } lg:relative lg:translate-x-0 w-64 flex flex-col z-40`}
      >
        <div className="flex-shrink-0 p-4 border-b border-gray-200">
          <div className="flex justify-between items-center">
            <h1 className="text-lg font-bold text-gray-800">Conversations</h1>
            <button
              onClick={startNewChat}
              className="p-2 bg-indigo-500 text-white rounded-full shadow-md hover:bg-indigo-600 transition-colors duration-200"
              title="Start a new chat"
            >
              <Plus size={20} />
            </button>
          </div>
        </div>
        <div className="flex-grow overflow-y-auto p-2 space-y-2">
          {conversations.map((conv) => (
            <button
              key={conv.id}
              onClick={() => {
                setCurrentConversationId(conv.id);
                setIsSidebarOpen(false);
              }}
              className={`block w-full text-left p-3 rounded-xl transition-all duration-200 ${
                currentConversationId === conv.id
                  ? 'bg-indigo-100 text-indigo-800 font-semibold'
                  : 'bg-gray-50 hover:bg-gray-200 text-gray-700'
              }`}
            >
              <div className="text-sm truncate">{conv.title}</div>
            </button>
          ))}
        </div>
        <div className="flex-shrink-0 p-4 border-t border-gray-200">
          <div className="flex items-center justify-between text-sm text-gray-600">
            <div className="flex items-center space-x-2">
              <span className="font-medium">User ID:</span>
              <span className="font-mono text-xs overflow-hidden text-ellipsis">
                {user.uid}
              </span>
            </div>
            <button
              onClick={handleLogout}
              className="text-red-500 hover:text-red-700 transition-colors duration-200 font-medium"
            >
              Logout
            </button>
          </div>
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="flex-1 flex flex-col bg-gray-50">
        {/* Mobile Sidebar Toggle */}
        <div className="lg:hidden flex justify-between items-center p-4 bg-white shadow-md">
          <button onClick={() => setIsSidebarOpen(true)} className="text-gray-600 hover:text-gray-800">
            <Menu size={24} />
          </button>
          <div className="font-semibold text-lg text-gray-800 truncate">
            {conversations.find(c => c.id === currentConversationId)?.title || 'New Chat'}
          </div>
          <div className="w-6"></div> {/* Placeholder for alignment */}
        </div>

        {/* Messages Container */}
        <div className="flex-1 overflow-y-auto p-4 space-y-6 lg:p-8 custom-scrollbar">
          {!currentConversationId && messages.length === 0 && (
            <div className="flex flex-col items-center justify-center h-full text-center text-gray-400">
              <Sparkles size={48} className="mb-4" />
              <p className="text-lg font-medium">Start a new conversation with Gemini</p>
              <p className="text-sm max-w-xs mt-2">
                Type a message, ask a question, upload an image, or use your voice to get started.
              </p>
            </div>
          )}
          {messages.map((msg) => (
            <div
              key={msg.id}
              className={`flex items-start gap-4 ${
                msg.userId === user.uid ? 'justify-end' : 'justify-start'
              }`}
            >
              <div className="flex-shrink-0">
                {msg.userId === user.uid ? (
                  <User size={24} className="text-indigo-500" />
                ) : (
                  <Bot size={24} className="text-green-500" />
                )}
              </div>
              <div
                className={`max-w-[75%] rounded-xl p-4 shadow-md ${
                  msg.userId === user.uid
                    ? 'bg-indigo-500 text-white rounded-tr-none'
                    : 'bg-white text-gray-800 rounded-tl-none'
                }`}
              >
                {renderMessageContent(msg)}
                <div className="flex items-center mt-2 space-x-2">
                  {msg.text && (msg.userId === user.uid || msg.text !== '...') && (
                    isSpeaking && currentAudio === msg.id ? (
                      <button onClick={stopAudio} className="text-white hover:text-gray-200 transition-colors duration-200" title="Stop Audio">
                        <Pause size={18} />
                      </button>
                    ) : (
                      <button onClick={() => handleTTS(msg.text, msg.id, msg.userName)} className="text-white hover:text-gray-200 transition-colors duration-200" title="Play Audio">
                        <Volume2 size={18} />
                      </button>
                    )
                  )}
                </div>
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        {/* Input area */}
        <form
          onSubmit={handleSendMessage}
          className="flex-shrink-0 p-4 border-t border-gray-200 bg-white"
        >
          {selectedImage && (
            <div className="relative mb-4 p-2 bg-gray-100 rounded-lg shadow-inner">
              <img src={selectedImage} alt="Preview" className="max-h-40 object-contain rounded-lg mx-auto" />
              <button
                onClick={() => setSelectedImage(null)}
                className="absolute top-0 right-0 m-2 text-white bg-red-500 rounded-full p-1 shadow-md hover:bg-red-600 transition-colors duration-200"
              >
                <X size={16} />
              </button>
            </div>
          )}
          <div className="flex items-center space-x-3">
            <button
              type="button"
              onClick={() => document.getElementById('image-upload').click()}
              className="p-3 text-gray-600 hover:text-indigo-500 transition-colors duration-200 disabled:opacity-50"
              title="Upload Image"
              disabled={isAIGenerating || isRecording || !user || !currentConversationId}
            >
              <Image size={24} />
            </button>
            <input
              id="image-upload"
              type="file"
              accept="image/*"
              className="hidden"
              onChange={handleImageUpload}
              disabled={isAIGenerating || isRecording}
            />

            <input
              type="text"
              placeholder={isRecording ? "Listening..." : "Message Gemini..."}
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              className="flex-1 p-3 border border-gray-300 rounded-full shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors duration-200 disabled:bg-gray-100"
              disabled={isAIGenerating || isRecording}
            />
            
            {isRecording ? (
              <button
                type="button"
                onClick={handleStopRecording}
                className="p-3 bg-red-500 text-white rounded-full shadow-lg hover:bg-red-600 transition-colors duration-200"
                title="Stop Recording"
              >
                <CircleStop size={24} />
              </button>
            ) : (
              <button
                type="button"
                onClick={handleStartRecording}
                className="p-3 bg-indigo-500 text-white rounded-full shadow-lg hover:bg-indigo-600 transition-colors duration-200 disabled:opacity-50"
                title="Start Voice Input"
                disabled={isAIGenerating || !user || !currentConversationId}
              >
                <Mic size={24} />
              </button>
            )}

            <button
              type="submit"
              className="p-3 bg-indigo-500 text-white rounded-full shadow-lg hover:bg-indigo-600 transition-colors duration-200 disabled:opacity-50"
              title="Send Message"
              disabled={(!newMessage.trim() && !selectedImage) || isAIGenerating || isRecording || !user}
            >
              <MessageSquareMore size={24} />
            </button>

            <button
              type="button"
              onClick={() => setShowSettingsModal(true)}
              className="p-3 text-gray-600 hover:text-indigo-500 transition-colors duration-200"
              title="Generation Settings"
            >
              <Settings size={24} />
            </button>
          </div>
        </form>
      </div>
      
      {showVoiceModal && createPortal(<VoiceSettingsModal />, document.body)}
      {showSettingsModal && createPortal(<SettingsModal />, document.body)}

      {/* Dot Flashing CSS Animation */}
      <style>{`
        .custom-scrollbar::-webkit-scrollbar {
            width: 8px;
        }

        .custom-scrollbar::-webkit-scrollbar-track {
            background: #f1f5f9;
        }

        .custom-scrollbar::-webkit-scrollbar-thumb {
            background-color: #cbd5e1;
            border-radius: 4px;
        }

        .custom-scrollbar::-webkit-scrollbar-thumb:hover {
            background-color: #94a3b8;
        }
        
        .dot-flashing {
          position: relative;
          width: 5px;
          height: 5px;
          border-radius: 5px;
          background-color: #a3a3a3;
          color: #a3a3a3;
          animation: dotFlashing 1s infinite linear alternate;
          animation-delay: 0.5s;
        }

        .dot-flashing::before, .dot-flashing::after {
          content: '';
          display: inline-block;
          position: absolute;
          top: 0;
        }

        .dot-flashing::before {
          left: -10px;
          width: 5px;
          height: 5px;
          border-radius: 5px;
          background-color: #a3a3a3;
          color: #a3a3a3;
          animation: dotFlashing 1s infinite alternate;
          animation-delay: 0s;
        }

        .dot-flashing::after {
          left: 10px;
          width: 5px;
          height: 5px;
          border-radius: 5px;
          background-color: #a3a3a3;
          color: #a3a3a3;
          animation: dotFlashing 1s infinite alternate;
          animation-delay: 1s;
        }

        @keyframes dotFlashing {
          0% {
            background-color: #a3a3a3;
          }
          50%,
          100% {
            background-color: #e5e5e5;
          }
        }
      `}</style>
    </div>
  );
}

export default App;
