      }

      // Update the placeholder message with the real response
      await setDoc(tempMessageRef, {
        text: responseText
      }, { merge: true });
    } catch (error) {
      console.error("Error getting AI response:", error);
      if (tempMessageRef) {
        await setDoc(tempMessageRef, {
          text: `An error occurred. Error: ${error.message}`,
        }, { merge: true });
      }
      setError(`AI response error: ${error.message}`);
