      setIsAIGenerating(false);
    }
  };

  // 15. Handle Text-to-Speech using the Gemini API
  const handleTextToSpeech = async (text, messageId) => {
    if (isSpeaking) {
      // If already speaking, pause it
      if (audioRef.current && currentAudioIdRef.current === messageId) {
        audioRef.current.pause();
        setIsSpeaking(false);
        currentAudioIdRef.current = null;
      }
      return;
    }

    setIsSpeaking(true);
    currentAudioIdRef.current = messageId;
    setError(null);

    try {
        const payload = {
            contents: [{
                parts: [{ text: text }]
            }],
            generationConfig: {
                responseModalities: ["AUDIO"],
                speechConfig: {
                    voiceConfig: {
                        prebuiltVoiceConfig: { voiceName: voiceName }
                    }
                }
            },
            model: "gemini-2.5-flash-preview-tts"
        };
        const apiKey = "AIzaSyCBBfnCqQlfrOCmSPzNuyC8F4jT0OHUX7g";
        const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-tts:generateContent?key=${apiKey}`;
        const response = await fetchWithExponentialBackoff(apiUrl, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload)
        });
        const result = await response.json();
        const part = result?.candidates?.[0]?.content?.parts?.[0];
        const audioData = part?.inlineData?.data;
        const mimeType = part?.inlineData?.mimeType;

        if (audioData && mimeType && mimeType.startsWith("audio/")) {
            const sampleRateMatch = mimeType.match(/rate=(\d+)/);
            const sampleRate = sampleRateMatch ? parseInt(sampleRateMatch[1], 10) : 16000;
            const pcmData = base64ToArrayBuffer(audioData);
            const wavBlob = pcmToWav(pcmData, sampleRate);
            const audioUrl = URL.createObjectURL(wavBlob);
            if (audioRef.current) {
                audioRef.current.src = audioUrl;
                audioRef.current.play();
                audioRef.current.onended = () => {
                    setIsSpeaking(false);
                    currentAudioIdRef.current = null;
                    URL.revokeObjectURL(audioUrl);
                };
            }
        } else {
            console.error("Invalid audio data from API:", result);
            setError("Failed to get audio data from API.");
            setIsSpeaking(false);
            currentAudioIdRef.current = null;
        }
    } catch (error) {
        console.error("Error generating speech:", error);
        setError(`Speech generation error: ${error.message}`);
        setIsSpeaking(false);
        currentAudioIdRef.current = null;
    }
  };

  // 16. Handle text continuation using Gemini API
  const handleContinueText = async (promptText) => {
