      }, { merge: true });
      setError(`Translation error: ${error.message}`);
    } finally {
      setIsAIGenerating(false);
    }
  };

  // 18. New feature: Handle creative writing (poems, stories) using Gemini API
  const handleCreativeWrite = async (topic, style, tone) => {
    const conversationId = currentConversationId;
    if (!conversationId || isAIGenerating || isSummarizing || isGeneratingImage) return;
    setIsAIGenerating(true);
