  // 19. UI Components
  const AuthUI = ({ authMode, setAuthMode, handleSendSignInLink, handleEmailPasswordLogin, handleEmailPasswordSignUp, email, setEmail, password, setPassword, emailSent, loading, error, isSigningInWithLink }) => (
