    // Check for "Say in a smooth voice" prefix to extract the actual text for TTS
    const speechPrefix = "Say in a smooth voice: ";
    const ttsText = message.text && message.text.startsWith(speechPrefix) ? message.text.substring(speechPrefix.length) : message.text;


    return (
      <div className={`flex flex-col mb-4 max-w-[80%] ${isUser ? 'items-end' : 'items-start'}`}>
        <div className={`p-4 rounded-3xl shadow-md ${messageClasses} group flex flex-col`}>
