      </div>

      {/* Settings Modal */}
      {showSettingsModal && createPortal(
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-gray-600 bg-opacity-50">
          <div className="bg-white dark:bg-gray-800 p-8 rounded-xl shadow-lg max-w-lg w-full m-4">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-2xl font-bold">Settings</h3>
              <button onClick={() => setShowSettingsModal(false)} className="p-2 rounded-md hover:bg-gray-200 dark:hover:bg-gray-700">
                <X size={24} />
              </button>
            </div>
            <div className="space-y-4">
              <div>
                <label htmlFor="system-instruction" className="block text-sm font-medium text-gray-700 dark:text-gray-300">System Instruction</label>
                <textarea
                  id="system-instruction"
                  value={systemInstruction}
                  onChange={(e) => setSystemInstruction(e.target.value)}
                  rows="4"
                  className="mt-1 block w-full rounded-md border-gray-300 dark:border-gray-600 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 bg-gray-50 dark:bg-gray-700 p-2"
                  placeholder="e.g., 'You are a friendly pirate chatbot. All your responses must be in pirate language.'"
                />
              </div>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <label htmlFor="temperature" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Temperature: {generationSettings.temperature}</label>
                  <input
                    id="temperature"
                    type="range"
                    min="0"
                    max="1"
                    step="0.05"
                    value={generationSettings.temperature}
                    onChange={(e) => setGenerationSettings({ ...generationSettings, temperature: parseFloat(e.target.value) })}
                    className="mt-1 w-full"
                  />
                </div>
                <div>
                  <label htmlFor="topK" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Top K: {generationSettings.topK}</label>
                  <input
                    id="topK"
                    type="range"
                    min="1"
                    max="100"
                    step="1"
                    value={generationSettings.topK}
                    onChange={(e) => setGenerationSettings({ ...generationSettings, topK: parseInt(e.target.value) })}
                    className="mt-1 w-full"
                  />
                </div>
                <div>
                  <label htmlFor="topP" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Top P: {generationSettings.topP}</label>
                  <input
                    id="topP"
                    type="range"
                    min="0"
                    max="1"
                    step="0.05"
                    value={generationSettings.topP}
                    onChange={(e) => setGenerationSettings({ ...generationSettings, topP: parseFloat(e.target.value) })}
                    className="mt-1 w-full"
                  />
                </div>
              </div>
              <div>
                <label htmlFor="voice-select" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Select Voice for TTS</label>
                <select
                  id="voice-select"
                  value={voiceName}
                  onChange={(e) => setVoiceName(e.target.value)}
                  className="mt-1 block w-full rounded-md border-gray-300 dark:border-gray-600 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 bg-gray-50 dark:bg-gray-700 p-2"
                >
                  {prebuiltVoices.map(voice => (
                    <option key={voice} value={voice}>{voice}</option>
                  ))}
                </select>
              </div>
            </div>
          </div>
        </div>,
        document.body
      )}

      {/* Gemini Feature Modal */}
      {showGeminiModal && createPortal(
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-gray-600 bg-opacity-50">
          <div className="bg-white dark:bg-gray-800 p-8 rounded-xl shadow-lg max-w-lg w-full m-4">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-2xl font-bold flex items-center">
                <Sparkles className="mr-2 text-indigo-500" />
                Gemini Actions
              </h3>
              <button onClick={() => setShowGeminiModal(false)} className="p-2 rounded-md hover:bg-gray-200 dark:hover:bg-gray-700">
                <X size={24} />
              </button>
            </div>
            <div className="space-y-4">
              {/* Summarize button */}
              <button
                onClick={handleSummarizeChat}
                className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
                disabled={!currentConversationId || messages.length === 0 || isAIGenerating}
              >
                <Sparkles size={20} className="mr-2" />
                Summarize Chat
              </button>

              {/* Continue Text */}
              <div className="space-y-2">
                <label htmlFor="continue-text-input" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Continue Text</label>
                <textarea
                  id="continue-text-input"
                  value={textToContinue}
                  onChange={(e) => setTextToContinue(e.target.value)}
                  rows="2"
                  className="w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  placeholder="Enter text to continue..."
                />
                <button
                  onClick={() => handleContinueText(textToContinue)}
                  className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
                  disabled={!textToContinue || isAIGenerating}
                >
                  <Sparkles size={20} className="mr-2" />
                  Continue
                </button>
              </div>

              {/* Translate Text */}
              <div className="space-y-2">
                <label htmlFor="translate-text-input" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Translate Text</label>
                <textarea
                  id="translate-text-input"
                  value={textToTranslate}
                  onChange={(e) => setTextToTranslate(e.target.value)}
                  rows="2"
                  className="w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  placeholder="Enter text to translate..."
                />
                <select
                  value={translationTargetLanguage}
                  onChange={(e) => setTranslationTargetLanguage(e.target.value)}
                  className="w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                >
                  <option value="English">English</option>
                  <option value="Spanish">Spanish</option>
                  <option value="French">French</option>
                  <option value="German">German</option>
                  <option value="Japanese">Japanese</option>
                  <option value="Chinese">Chinese</option>
                </select>
                <button
                  onClick={() => handleTranslateText(textToTranslate, translationTargetLanguage)}
                  className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
                  disabled={!textToTranslate || isAIGenerating}
                >
                  <Sparkles size={20} className="mr-2" />
                  Translate
                </button>
              </div>
            </div>
          </div>
        </div>,
        document.body
      )}

      {/* Creative Writer Modal */}
      {showCreativeWriterModal && createPortal(
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-gray-600 bg-opacity-50">
          <div className="bg-white dark:bg-gray-800 p-8 rounded-xl shadow-lg max-w-lg w-full m-4">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-2xl font-bold flex items-center">
                <PenTool className="mr-2 text-indigo-500" />
                Creative Writer
              </h3>
              <button onClick={() => setShowCreativeWriterModal(false)} className="p-2 rounded-md hover:bg-gray-200 dark:hover:bg-gray-700">
                <X size={24} />
              </button>
            </div>
            <div className="space-y-4">
              <div className="space-y-2">
                <label htmlFor="creative-topic-input" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Topic</label>
                <input
                  id="creative-topic-input"
                  type="text"
                  value={creativeTopic}
                  onChange={(e) => setCreativeTopic(e.target.value)}
                  className="w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  placeholder="e.g., 'a lonely astronaut'"
                />
              </div>
              <div className="space-y-2">
                <label htmlFor="creative-style-select" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Style</label>
                <select
                  id="creative-style-select"
                  value={creativeStyle}
                  onChange={(e) => setCreativeStyle(e.target.value)}
                  className="w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                >
                  <option value="Poem">Poem</option>
                  <option value="Haiku">Haiku</option>
                  <option value="Short Story">Short Story</option>
                  <option value="Fairy Tale">Fairy Tale</option>
                </select>
              </div>
              <div className="space-y-2">
                <label htmlFor="creative-tone-select" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Tone</label>
                <select
                  id="creative-tone-select"
                  value={creativeTone}
                  onChange={(e) => setCreativeTone(e.target.value)}
                  className="w-full p-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                >
                  <option value="Neutral">Neutral</option>
                  <option value="Whimsical">Whimsical</option>
                  <option value="Serious">Serious</option>
                  <option value="Mysterious">Mysterious</option>
                  <option value="Adventurous">Adventurous</option>
                </select>
              </div>
              <button
                onClick={() => handleCreativeWrite(creativeTopic, creativeStyle, creativeTone)}
                className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
                disabled={!creativeTopic || isAIGenerating}
              >
                <Sparkles size={20} className="mr-2" />
                Write
              </button>
            </div>
          </div>
        </div>,
        document.body
      )}

      {/* Dot Flashing CSS Animation */}
      <style>{`
        .dot-flashing {
          position: relative;
          width: 5px;
          height: 5px;
          border-radius: 5px;
          background-color: #a3a3a3;
          color: #a3a3a3;
          animation: dotFlashing 1s infinite linear alternate;
          animation-delay: 0.5s;
        }

        .dot-flashing::before, .dot-flashing::after {
          content: '';
          display: inline-block;
          position: absolute;
          top: 0;
        }

        .dot-flashing::before {
          left: -10px;
          width: 5px;
          height: 5px;
          border-radius: 5px;
          background-color: #a3a3a3;
          color: #a3a3a3;
          animation: dotFlashing 1s infinite alternate;
          animation-delay: 0s;
        }

        .dot-flashing::after {
          left: 10px;
          width: 5px;
          height: 5px;
          border-radius: 5px;
          background-color: #a3a3a3;
          color: #a3a3a3;
          animation: dotFlashing 1s infinite alternate;
          animation-delay: 1s;
        }

        @keyframes dotFlashing {
          0% {
            background-color: #a3a3a3;
          }
          50%,
          100% {
            background-color: #e0e0e0;
          }
        }
      `}</style>
      
      {/* Markdown styling */}
      <style>{`
        .markdown-body {
          color: #4a5568;
        }
        .dark .markdown-body {
          color: #e2e8f0;
        }
        .markdown-body pre {
          background-color: #f7fafc;
          border-radius: 0.5rem;
          padding: 1rem;
          overflow-x: auto;
          white-space: pre-wrap;
          word-wrap: break-word;
        }
        .dark .markdown-body pre {
          background-color: #2d3748;
        }
        .markdown-body code {
          background-color: #edf2f7;
          border-radius: 0.25rem;
          padding: 0.2rem 0.4rem;
          font-family: monospace;
        }
        .dark .markdown-body code {
          background-color: #4a5568;
        }
        .markdown-body pre code {
          background-color: transparent;
          padding: 0;
        }
        .markdown-body h1, .markdown-body h2, .markdown-body h3 {
          font-weight: bold;
          margin-top: 1.5rem;
          margin-bottom: 1rem;
        }
        .markdown-body h1 { font-size: 1.5em; }
        .markdown-body h2 { font-size: 1.25em; }
        .markdown-body h3 { font-size: 1.125em; }
        .markdown-body p {
          margin-bottom: 1rem;
          line-height: 1.5;
        }
        .markdown-body ul, .markdown-body ol {
          margin-bottom: 1rem;
          padding-left: 1.5rem;
        }
        .markdown-body li {
          margin-bottom: 0.5rem;
        }
        .markdown-body blockquote {
          border-left: 4px solid #4299e1;
          padding-left: 1rem;
          margin-left: 0;
          color: #718096;
        }
        .dark .markdown-body blockquote {
          color: #a0aec0;
        }
      `}</style>

    </div>
  );

  if (loading || !isAuthReady) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900">
        <LoaderCircle className="animate-spin text-indigo-500 h-10 w-10" />
      </div>
    );
  }

  return user ? <MainUI /> : <AuthUI
    authMode={authMode}
    setAuthMode={setAuthMode}
    handleSendSignInLink={handleSendSignInLink}
    handleEmailPasswordLogin={handleEmailPasswordLogin}
    handleEmailPasswordSignUp={handleEmailPasswordSignUp}
    email={email}
    setEmail={setEmail}
    password={password}
    setPassword={setPassword}
    emailSent={emailSent}
    loading={loading}
    error={error}
    isSigningInWithLink={isSigningInWithLink}
  />;
}

export default App;
