/* global __app_id, __firebase_config, __initial_auth_token */
import React, { useState, useEffect, useRef } from 'react';
// Import Firebase modules at the top level for broader access
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, signInWithCustomToken, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, collection, query, orderBy, onSnapshot, addDoc, serverTimestamp } from 'firebase/firestore';
import './index.css';

// Define the main App component
function App() {
    // State to store chat messages
    const [messages, setMessages] = useState([]);
    // State for the current input text
    const [inputText, setInputText] = useState('');
    // State to manage loading indicator during API calls
    const [isLoading, setIsLoading] = useState(false);
    // State for Firebase authentication and Firestore
    const [db, setDb] = useState(null);
    const [auth, setAuth] = useState(null);
    const [userId, setUserId] = useState(null);
    // Reference for the chat messages container to enable auto-scrolling
    const messagesEndRef = useRef(null);

    // Function to scroll to the bottom of the chat
    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    // Initialize Firebase and set up authentication listener
    useEffect(() => {
        const initializeFirebase = async () => {
            try {
                // Global variables provided by the Canvas environment
                const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
                const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {};
                const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

                const app = initializeApp(firebaseConfig);
                const firestoreDb = getFirestore(app);
                const firebaseAuth = getAuth(app);

                setDb(firestoreDb);
                setAuth(firebaseAuth);

                // Sign in with custom token or anonymously
                if (initialAuthToken) {
                    await signInWithCustomToken(firebaseAuth, initialAuthToken);
                } else {
                    await signInAnonymously(firebaseAuth);
                }

                // Listen for auth state changes
                onAuthStateChanged(firebaseAuth, (user) => {
                    if (user) {
                        setUserId(user.uid);
                    } else {
                        // If no user, generate a random ID for anonymous use
                        setUserId(crypto.randomUUID());
                    }
                });

            } catch (error) {
                console.error("Error initializing Firebase:", error);
            }
        };

        initializeFirebase();
    }, []); // Run only once on component mount

    // Listen for real-time chat messages from Firestore
    useEffect(() => {
        if (!db || !userId) return; // Ensure db and userId are available

        // Define the collection path for public data
        const chatCollectionRef = collection(db, `artifacts/${__app_id}/public/data/messages`);
        // Create a query to order messages by timestamp
        const q = query(chatCollectionRef, orderBy('timestamp', 'asc'));

        // Set up real-time listener
        const unsubscribe = onSnapshot(q, (snapshot) => {
            const fetchedMessages = snapshot.docs.map(doc => ({
                id: doc.id,
                ...doc.data()
            }));
            setMessages(fetchedMessages);
        }, (error) => {
            console.error("Error fetching messages:", error);
        });

        // Clean up the listener on component unmount
        return () => unsubscribe();
    }, [db, userId]); // Re-run if db or userId changes

    // Scroll to bottom whenever messages update
    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    // Function to send a message to the Gemini API
    const sendMessage = async () => {
        if (!inputText.trim() || isLoading || !db || !userId) return;

        const userMessage = {
            text: inputText,
            sender: userId, // Use the authenticated user ID
            timestamp: serverTimestamp(), // Firestore server timestamp
            isBot: false,
        };

        // Add user message to Firestore
        try {
            await addDoc(collection(db, `artifacts/${__app_id}/public/data/messages`), userMessage);
        } catch (error) {
            console.error("Error adding user message to Firestore:", error);
            // Optionally, add a local message to indicate failure
            setMessages(prev => [...prev, { ...userMessage, text: "Failed to send message.", id: Date.now(), timestamp: new Date() }]);
            setInputText('');
            return;
        }

        setInputText(''); // Clear input immediately
        setIsLoading(true); // Show loading indicator

        try {
            let chatHistory = messages.map(msg => ({
                role: msg.isBot ? "model" : "user",
                parts: [{ text: msg.text }]
            }));
            // Add the current user message to chat history for the API call
            chatHistory.push({ role: "user", parts: [{ text: userMessage.text }] });

            const payload = { contents: chatHistory };
            const apiKey = ""; // Canvas will provide this at runtime
            const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;

            let response;
            let result;
            let retries = 0;
            const maxRetries = 5;
            const baseDelay = 1000; // 1 second

            // Implement exponential backoff for API calls
            while (retries < maxRetries) {
                try {
                    response = await fetch(apiUrl, {
                        method: 'POST',
                        headers: { 'Content-Type': 'application/json' },
                        body: JSON.stringify(payload)
                    });

                    if (response.ok) {
                        result = await response.json();
                        break; // Exit loop if successful
                    } else if (response.status === 429) { // Too Many Requests
                        const delay = baseDelay * Math.pow(2, retries) + Math.random() * 1000; // Add jitter
                        console.warn(`Rate limit hit. Retrying in ${delay / 1000} seconds...`);
                        await new Promise(res => setTimeout(res, delay));
                        retries++;
                    } else {
                        throw new Error(`API error: ${response.status} ${response.statusText}`);
                    }
                } catch (error) {
                    console.error("Fetch error during retry:", error);
                    if (retries === maxRetries - 1) throw error; // Re-throw if last retry
                    const delay = baseDelay * Math.pow(2, retries) + Math.random() * 1000;
                    console.warn(`Fetch failed. Retrying in ${delay / 1000} seconds...`);
                    await new Promise(res => setTimeout(res, delay));
                    retries++;
                }
            }

            let botResponseText = "Sorry, I couldn't get a response from the AI.";
            if (result && result.candidates && result.candidates.length > 0 &&
                result.candidates[0].content && result.candidates[0].content.parts &&
                result.candidates[0].content.parts.length > 0) {
                botResponseText = result.candidates[0].content.parts[0].text;
            }

            const botMessage = {
                text: botResponseText,
                sender: 'bot',
                timestamp: serverTimestamp(),
                isBot: true,
            };

            // Add bot message to Firestore
            await addDoc(collection(db, `artifacts/${__app_id}/public/data/messages`), botMessage);

        } catch (error) {
            console.error("Error calling Gemini API or saving bot message:", error);
            // Add an error message to the chat if API call fails
            const errorMessage = {
                text: "Oops! Something went wrong while getting a response. Please try again.",
                sender: 'bot',
                timestamp: serverTimestamp(),
                isBot: true,
            };
            await addDoc(collection(db, `artifacts/${__app_id}/public/data/messages`), errorMessage);
        } finally {
            setIsLoading(false); // Hide loading indicator
        }
    };

    // Handle Enter key press
    const handleKeyPress = (e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault(); // Prevent new line in textarea
            sendMessage();
        }
    };

    return (
        <div className="flex flex-col h-screen bg-gray-100 font-inter antialiased">
            {/* Header */}
            <header className="bg-gradient-to-r from-purple-600 to-indigo-700 text-white p-4 shadow-lg rounded-b-xl">
                <h1 className="text-3xl font-bold text-center">AI Chatbot</h1>
                {userId && (
                    <p className="text-sm text-center mt-1 opacity-80">
                        Your User ID: <span className="font-mono bg-white bg-opacity-20 px-2 py-1 rounded-md">{userId}</span>
                    </p>
                )}
            </header>

            {/* Chat Messages Area */}
            <div className="flex-1 overflow-y-auto p-4 space-y-4 pb-20"> {/* Added pb-20 to ensure space for input */}
                {messages.length === 0 && !isLoading && (
                    <div className="text-center text-gray-500 mt-10">
                        Start a conversation! Type your message below.
                    </div>
                )}
                {messages.map((msg) => (
                    <div
                        key={msg.id}
                        className={`flex ${msg.isBot ? 'justify-start' : 'justify-end'}`}
                    >
                        <div
                            className={`max-w-xs md:max-w-md lg:max-w-lg p-3 rounded-xl shadow-md ${
                                msg.isBot
                                    ? 'bg-white text-gray-800 rounded-bl-none'
                                    : 'bg-indigo-500 text-white rounded-br-none'
                            }`}
                        >
                            <p className="text-sm break-words">{msg.text}</p>
                            <span className="text-xs opacity-75 mt-1 block">
                                {msg.timestamp?.toDate ? msg.timestamp.toDate().toLocaleTimeString() : new Date().toLocaleTimeString()}
                            </span>
                        </div>
                    </div>
                ))}
                {isLoading && (
                    <div className="flex justify-start">
                        <div className="max-w-xs md:max-w-md lg:max-w-lg p-3 rounded-xl shadow-md bg-white text-gray-800 rounded-bl-none">
                            <div className="flex items-center">
                                <span className="dot-flashing"></span>
                                <span className="ml-2 text-sm">Typing...</span>
                            </div>
                        </div>
                    </div>
                )}
                <div ref={messagesEndRef} /> {/* Scroll target */}
            </div>

            {/* Input Area */}
            <div className="fixed bottom-0 left-0 right-0 bg-white p-4 shadow-lg border-t border-gray-200 flex items-center space-x-3 rounded-t-xl">
                <textarea
                    className="flex-1 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 resize-none"
                    rows="1"
                    placeholder="Type your message..."
                    value={inputText}
                    onChange={(e) => setInputText(e.target.value)}
                    onKeyPress={handleKeyPress}
                    disabled={isLoading}
                ></textarea>
                <button
                    className="bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 px-6 rounded-lg shadow-md transition duration-300 ease-in-out transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed"
                    onClick={sendMessage}
                    disabled={!inputText.trim() || isLoading}
                >
                    Send
                </button>
            </div>

            {/* CSS for dot-flashing loading indicator */}
            <style>{`
                @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;600;700&display=swap');
                body {
                    font-family: 'Inter', sans-serif;
                }
                .dot-flashing {
                    position: relative;
                    width: 10px;
                    height: 10px;
                    border-radius: 5px;
                    background-color: #9880ff;
                    color: #9880ff;
                    animation: dotFlashing 1s infinite linear alternate;
                    animation-delay: .5s;
                }
                .dot-flashing::before, .dot-flashing::after {
                    content: '';
                    display: inline-block;
                    position: absolute;
                    top: 0;
                    left: -15px;
                    width: 10px;
                    height: 10px;
                    border-radius: 5px;
                    background-color: #9880ff;
                    color: #9880ff;
                    animation: dotFlashing 1s infinite linear alternate;
                }
                .dot-flashing::after {
                    left: 15px;
                    animation-delay: 1s;
                }
                @keyframes dotFlashing {
                    0% {
                        background-color: #9880ff;
                    }
                    50%, 100% {
                        background-color: #ede9fe;
                    }
                }
            `}</style>
        </div>
    );
}

export default App;
