/* eslint-disable no-undef */
import React, { useState, useEffect, useRef } from 'react';
import { initializeApp } from 'firebase/app';
import {
  getAuth,
  signInWithCustomToken,
  signInAnonymously,
  onAuthStateChanged,
  sendSignInLinkToEmail,
  isSignInWithEmailLink,
  signInWithEmailLink,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut
} from 'firebase/auth';
import {
  getFirestore,
  collection,
  onSnapshot,
  addDoc,
  serverTimestamp,
  doc,
  setDoc,
  query,
  limit,
  getDocs
} from 'firebase/firestore';
import { PanelLeft, Plus, MessageSquare, Code, Sparkles, MessageSquareMore, Image, LoaderCircle, X, ExternalLink, Menu, Play, Pause, Mic, Settings, Volume2, PenTool } from 'lucide-react';
import { marked } from 'marked';
import { createPortal } from 'react-dom';

/* global __firebase_config, __app_id, __initial_auth_token */

// Initialize Firebase App
const app = initializeApp(__firebase_config);
const auth = getAuth(app);
const db = getFirestore(app);
const appId = __app_id;
const initialAuthToken = __initial_auth_token;

// Mock data for the sidebar to replicate the look of the image
const mockChats = [
  { id: '1', name: 'Wave App Name Ideas' },
  { id: '2', name: 'Git config pull push commit' },
  { id: '3', name: 'Social media app setup' },
  { id: '4', name: 'Fix TextEncoder error' },
  { id: '5', name: 'Wave app structure setup' },
  { id: '6', name: 'Install TailwindCSS Shadcn Supa' },
  { id: '7', name: 'Connect local to remote repo' },
];

// The main App component
const App = () => {
  const [messages, setMessages] = useState([]);
  const [user, setUser] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [isCodeMode, setIsCodeMode] = useState(false); // New state to toggle code mode
  const messageInputRef = useRef(null);
  const messagesEndRef = useRef(null);

  // Auth Effect: Handles user authentication
  useEffect(() => {
    const unsubscribeAuth = onAuthStateChanged(auth, async (currentUser) => {
      if (currentUser) {
        setUser(currentUser);
      } else {
        try {
          // If no user, sign in anonymously.
          if (initialAuthToken) {
            await signInWithCustomToken(auth, initialAuthToken);
          } else {
            await signInAnonymously(auth);
          }
        } catch (error) {
          console.error("Error with authentication: ", error);
        }
      }
      setIsAuthReady(true);
    });

    // Cleanup the auth listener on component unmount
    return () => unsubscribeAuth();
  }, []);

  // Firestore Effect: Fetches messages in real-time
  useEffect(() => {
    // Only subscribe to messages after authentication is ready
    if (!isAuthReady || !user) return;

    const messagesCollectionRef = collection(db, `artifacts/${appId}/public/data/messages`);
    const q = query(messagesCollectionRef);

    // Set up a real-time listener for the messages collection
    const unsubscribeSnapshot = onSnapshot(q, (snapshot) => {
      const newMessages = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setMessages(newMessages);
      scrollToBottom();
    }, (error) => {
      console.error("Error fetching messages: ", error);
    });

    // Cleanup the snapshot listener on component unmount
    return () => unsubscribeSnapshot();
  }, [isAuthReady, user, db, appId]);

  // Utility function to scroll to the bottom of the message list
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  // Handler for sending a new message
  const handleSendMessage = async (e) => {
    e.preventDefault();
    const messageText = messageInputRef.current.value;
    if (messageText.trim() === '') return;

    if (user) {
      try {
        // Add the new message to the 'messages' collection in Firestore, with an 'isCode' flag
        await addDoc(collection(db, `artifacts/${appId}/public/data/messages`), {
          text: messageText,
          uid: user.uid,
          timestamp: serverTimestamp(),
          isCode: isCodeMode, // Add the new flag
        });
        // Clear the input field after sending
        messageInputRef.current.value = '';
      } catch (error) {
        console.error("Error adding document: ", error);
      }
    } else {
      console.error("No user is signed in. Message not sent.");
    }
  };

  if (!isAuthReady) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-900 text-white">
        Loading...
      </div>
    );
  }

  return (
    <div className="flex w-full min-h-screen bg-gray-950 text-gray-200 font-sans antialiased">
      {/* Sidebar - static for now, matching the image's style */}
      <aside className="hidden md:flex flex-col w-64 bg-gray-900 p-4 border-r border-gray-800">
        <div className="flex items-center justify-between p-2 mb-4">
          <div className="flex items-center space-x-2">
            <span className="text-xl font-bold">Chat App</span>
          </div>
          <button className="p-2 rounded-lg bg-gray-800 text-gray-400 hover:bg-gray-700 transition-colors">
            <Plus size={18} />
          </button>
        </div>
        <nav className="flex-1 overflow-y-auto">
          {mockChats.map((chat) => (
            <a
              key={chat.id}
              href="#"
              className="flex items-center p-3 mb-2 space-x-3 rounded-lg text-sm text-gray-400 hover:bg-gray-800 transition-colors"
            >
              <MessageSquare size={16} />
              <span className="truncate">{chat.name}</span>
            </a>
          ))}
        </nav>
        <div className="mt-auto pt-4 border-t border-gray-800">
          {user && (
            <div className="flex items-center space-x-3 p-3 bg-gray-800 rounded-lg">
              <div className="flex-shrink-0 h-8 w-8 rounded-full bg-gray-600 flex items-center justify-center">
                <span className="text-gray-300 text-xs">U</span>
              </div>
              <div className="flex-1">
                <p className="text-sm font-medium truncate">User ID: {user.uid}</p>
              </div>
            </div>
          )}
        </div>
      </aside>

      {/* Main Chat Area */}
      <main className="flex flex-col flex-1">
        <header className="flex items-center p-4 border-b border-gray-800 bg-gray-900 shadow-md md:hidden">
          <button className="p-2 rounded-lg text-gray-400 hover:bg-gray-800 transition-colors">
            <PanelLeft size={24} />
          </button>
          <h1 className="text-xl font-semibold mx-auto">Chat App</h1>
        </header>

        {/* Message List */}
        <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-gray-800">
          {messages.map(msg => (
            <div
              key={msg.id}
              className={`flex ${msg.uid === user?.uid ? 'justify-end' : 'justify-start'}`}
            >
              {/* Conditional rendering for regular text vs. code block */}
              {msg.isCode ? (
                <pre className={`max-w-xl p-3 rounded-lg overflow-x-auto ${
                  msg.uid === user?.uid
                    ? 'bg-blue-800 text-white'
                    : 'bg-gray-900 text-gray-300'
                }`}>
                  <code className="text-sm font-mono whitespace-pre-wrap">
                    {msg.text}
                  </code>
                </pre>
              ) : (
                <div
                  className={`max-w-xl p-3 rounded-lg ${
                    msg.uid === user?.uid
                      ? 'bg-blue-600 text-white'
                      : 'bg-gray-700 text-gray-200'
                  }`}
                >
                  <p className="text-sm">{msg.text}</p>
                </div>
              )}
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        {/* Input Form */}
        <form onSubmit={handleSendMessage} className="p-4 bg-gray-900 border-t border-gray-800 flex items-center space-x-2">
          {/* New button to toggle code mode */}
          <button
            type="button"
            onClick={() => setIsCodeMode(!isCodeMode)}
            className={`p-3 rounded-lg text-gray-400 transition-colors ${
              isCodeMode ? 'bg-blue-600 text-white' : 'bg-gray-800 hover:bg-gray-700'
            }`}
          >
            <Code size={20} />
          </button>
          <input
            ref={messageInputRef}
            type="text"
            placeholder={isCodeMode ? "Enter your code snippet..." : "Type a message..."}
            className="flex-1 p-3 rounded-lg bg-gray-800 text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-600 transition-colors"
          />
          <button
            type="submit"
            className="p-3 rounded-lg bg-blue-600 text-white hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-600 transition-colors"
          >
            Send
          </button>
        </form>
      </main>
    </div>
  );
};

export default App;
