// This line tells ESLint to ignore the following global variables that are provided by the Canvas environment.
/* global __app_id, __firebase_config, __initial_auth_token */

import React, { useState, useEffect, useRef } from 'react';
import { initializeApp, getApps, getApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, signInAnonymously, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, collection, addDoc, serverTimestamp, query, onSnapshot } from 'firebase/firestore';

// Main App component for the chatbot application.
function App() {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [db, setDb] = useState(null);
  const [userId, setUserId] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [isAIGenerating, setIsAIGenerating] = useState(false);
  const [error, setError] = useState(null); // New state for storing errors
  const messagesEndRef = useRef(null);

  // Use the global variables provided by the environment
  const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
  let firebaseConfig = {};
  try {
    firebaseConfig = JSON.parse(typeof __firebase_config !== 'undefined' ? __firebase_config : '{}');
  } catch (e) {
    console.error("Failed to parse firebase config from global variable.");
  }
  const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

  // Initialize Firebase and handle authentication
  useEffect(() => {
    const initFirebase = async () => {
      console.log("Initializing Firebase...");
      try {
        const app = getApps().length ? getApp() : initializeApp(firebaseConfig);
        const firestoreDb = getFirestore(app);
        const firestoreAuth = getAuth(app);
        
        setDb(firestoreDb);

        onAuthStateChanged(firestoreAuth, async (user) => {
          if (user) {
            setUserId(user.uid);
            console.log(`[Firebase Auth] Successfully authenticated with user ID: ${user.uid}`);
          } else {
            console.log("[Firebase Auth] No user signed in. Attempting to sign in.");
            if (initialAuthToken) {
              try {
                await signInWithCustomToken(firestoreAuth, initialAuthToken);
                console.log("[Firebase Auth] Signed in with custom token.");
              } catch (error) {
                console.error("[Firebase Auth] Error signing in with custom token. Falling back to anonymous auth:", error);
                await signInAnonymously(firestoreAuth);
                console.log("[Firebase Auth] Signed in anonymously.");
              }
            } else {
              await signInAnonymously(firestoreAuth);
              console.log("[Firebase Auth] Signed in anonymously.");
            }
          }
          setIsAuthReady(true);
        });
      } catch (e) {
        console.error("Error initializing Firebase:", e);
        setError(`Firebase initialization failed: ${e.message}`);
      }
    };
    initFirebase();
  }, [initialAuthToken, firebaseConfig]);

  // Fetch messages from Firestore in real-time
  useEffect(() => {
    if (db && isAuthReady && userId) {
      console.log('[Firestore] Attempting to fetch messages. User is authenticated:', !!userId);
      setIsLoading(true);
      const messagesCollectionRef = collection(db, `artifacts/${appId}/public/data/messages`);
      const q = query(messagesCollectionRef);

      const unsubscribe = onSnapshot(q, (snapshot) => {
        console.log('[Firestore] Received new snapshot of messages.');
        const fetchedMessages = snapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        const sortedMessages = fetchedMessages.sort((a, b) => (a.timestamp?.toMillis() || 0) - (b.timestamp?.toMillis() || 0));
        setMessages(sortedMessages);
        setIsLoading(false);
        setError(null); // Clear any previous errors on successful fetch
      }, (firestoreError) => {
        console.error("[Firestore ERROR] Failed to fetch messages:", firestoreError.message);
        setIsLoading(false);
        setError(`Failed to load messages. Please check Firestore security rules and your connection. Details: ${firestoreError.message}`);
      });
      
      return () => {
        console.log('[Firestore] Unsubscribing from messages collection.');
        unsubscribe();
      }
    }
  }, [db, userId, isAuthReady, appId]);

  // Use a separate useEffect to handle AI responses to user messages
  useEffect(() => {
    const lastMessage = messages[messages.length - 1];
    if (db && lastMessage && lastMessage.sender === 'user' && !isAIGenerating) {
      const getAIResponse = async () => {
        setIsAIGenerating(true);
        try {
          const prompt = `You are a helpful AI assistant. Please respond to the following user message: "${lastMessage.text}"`;
          const chatHistory = [];
          chatHistory.push({ role: "user", parts: [{ text: prompt }] });
          const payload = { contents: chatHistory };
          const apiKey = "";
          const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
          const response = await fetch(apiUrl, {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify(payload)
          });

          const result = await response.json();
          let responseText = "Sorry, I couldn't generate a response.";
          if (result.candidates && result.candidates.length > 0 &&
              result.candidates[0].content && result.candidates[0].content.parts &&
              result.candidates[0].content.parts.length > 0) {
            responseText = result.candidates[0].content.parts[0].text;
          }

          await addDoc(collection(db, `artifacts/${appId}/public/data/messages`), {
            text: responseText,
            sender: 'bot',
            timestamp: serverTimestamp(),
          });
        } catch (e) {
          console.error("Error getting AI response:", e);
          await addDoc(collection(db, `artifacts/${appId}/public/data/messages`), {
            text: `Sorry, I am having trouble processing that request. Error: ${e.message}`,
            sender: 'bot',
            timestamp: serverTimestamp(),
          });
        } finally {
          setIsAIGenerating(false);
        }
      };
      getAIResponse();
    }
  }, [messages, db, isAIGenerating, appId]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };
  
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async () => {
    if (input.trim() === '' || !db || !userId || isAIGenerating) return;
    try {
      const messagesCollectionRef = collection(db, `artifacts/${appId}/public/data/messages`);
      await addDoc(messagesCollectionRef, { 
        text: input, 
        sender: 'user', 
        timestamp: serverTimestamp(),
        userId: userId // Add userId to the message for identification
      });
      setInput('');
    } catch (e) {
      console.error("Error adding document: ", e);
      setError(`Failed to send message: ${e.message}`);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && input.trim() !== '' && !isAIGenerating) {
      sendMessage();
    }
  };

  return (
    <div className="App bg-gray-100 dark:bg-gray-900 min-h-screen flex items-center justify-center p-4">
      <div className="flex flex-col w-full max-w-xl h-[80vh] bg-white dark:bg-gray-800 rounded-2xl shadow-2xl">
        <div className="flex justify-between items-center p-4 border-b border-gray-200 dark:border-gray-700 rounded-t-2xl">
          <h1 className="text-xl font-bold text-gray-900 dark:text-white">AI Chatbot</h1>
          {userId && (
            <span className="text-xs text-gray-500 dark:text-gray-400">
              User ID: <span className="font-mono">{userId}</span>
            </span>
          )}
        </div>
        <div className="flex-grow p-4 overflow-y-auto space-y-4">
          {error ? (
            <div className="flex justify-center items-center h-full text-red-500 font-medium text-center p-4">
              <p>{error}</p>
            </div>
          ) : isLoading ? (
            <div className="flex justify-center items-center h-full">
              <span className="text-gray-500 dark:text-gray-400">Loading messages...</span>
            </div>
          ) : messages.length === 0 ? (
            <div className="flex justify-center items-center h-full">
              <span className="text-gray-500 dark:text-gray-400">Start a new conversation!</span>
            </div>
          ) : (
            messages.map((message, index) => (
              <div key={index} className={`flex items-start gap-3 ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
                {message.sender === 'bot' && (
                  <div className="flex items-center justify-center w-8 h-8 rounded-full bg-indigo-200 dark:bg-indigo-700">
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="text-indigo-600 dark:text-indigo-200">
                      <path d="M12 8V4H8"/><rect width="16" height="12" x="4" y="8" rx="2"/><path d="M2 14h2"/><path d="M20 14h2"/><path d="M15 13v2"/><path d="M9 13v2"/>
                    </svg>
                  </div>
                )}
                <div className={`p-3 rounded-lg max-w-[70%] ${message.sender === 'user' ? 'bg-indigo-500 text-white dark:bg-indigo-600' : 'bg-gray-200 dark:bg-gray-700 text-gray-900 dark:text-white'}`}>
                  <p className="whitespace-pre-wrap">{message.text}</p>
                </div>
              </div>
            ))
          )}
          {isAIGenerating && (
            <div className="flex items-start gap-3 justify-start">
              <div className="p-3 rounded-lg bg-gray-200 dark:bg-gray-700 text-gray-900 dark:text-white max-w-[70%]">
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="inline-block mr-2"><path d="M12 8V4H8"/><rect width="16" height="12" x="4" y="8" rx="2"/><path d="M2 14h2"/><path d="M20 14h2"/><path d="M15 13v2"/><path d="M9 13v2"/></svg>
                <div className="dot-flashing"></div>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        <div className="p-4 border-t border-gray-200 dark:border-gray-700">
          <div className="relative flex items-center">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Type your message..."
              className="flex-grow rounded-full border border-gray-300 dark:border-gray-600 bg-gray-100 dark:bg-gray-900 px-4 py-2 text-gray-900 dark:text-white focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors"
              disabled={isAIGenerating}
            />
            <button
              onClick={sendMessage}
              disabled={isAIGenerating || input.trim() === '' || !userId}
              className="ml-3 p-2 rounded-full bg-indigo-600 text-white hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 disabled:bg-gray-400 transition-colors"
            >
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M5 12h14"/><path d="m12 5 7 7-7 7"/>
              </svg>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
