/* global __firebase_config, __app_id, __initial_auth_token */

import React, { useState, useEffect, useRef } from 'react';
import { initializeApp } from 'firebase/app';
import {
  getAuth,
  signInWithCustomToken,
  signInAnonymously,
  onAuthStateChanged,
  sendSignInLinkToEmail,
  isSignInWithEmailLink,
  signInWithEmailLink,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut
} from 'firebase/auth';
import {
  getFirestore,
  collection,
  onSnapshot,
  addDoc,
  serverTimestamp,
  doc,
  setDoc,
  query,
  limit,
  getDocs
} from 'firebase/firestore';
import { marked } from 'marked';
import { createPortal } from 'react-dom';
import { Sparkles, MessageSquareMore, Image, LoaderCircle, X, ExternalLink, Menu, Plus, Play, Pause, Mic, Settings, Volume2, PenTool, Copy } from 'lucide-react';

// --- Firebase Configuration ---
// The global variables are provided by the Canvas environment.
// We fall back to a local config for development purposes if they are not defined.
const localFirebaseConfig = {
  apiKey: "YOUR_FIREBASE_API_KEY", // FIX: Replace with your actual Firebase API key.
  authDomain: "ai-chatbot-88602.firebaseapp.com",
  projectId: "ai-chatbot-88602",
  storageBucket: "ai-chatbot-88602.firebasestorage.app",
  messagingSenderId: "167580287666",
  appId: "1:167580287666:web:9e8313ae6e667e47d47fab",
  measurementId: "G-CXKJ8QRLZ1"
};

const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : localFirebaseConfig;
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

// Utility function for generating unique IDs (like conversation IDs)
const generateId = () => {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
    const r = Math.random() * 16 | 0, v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
};

// Converts a base64 string to an ArrayBuffer
function base64ToArrayBuffer(base64) {
  const binaryString = window.atob(base64);
  const len = binaryString.length;
  const bytes = new Uint8Array(len);
  for (let i = 0; i < len; i++) {
    bytes[i] = binaryString.charCodeAt(i);
  }
  return bytes.buffer;
}

// Converts PCM audio data to a WAV blob
function pcmToWav(pcmData, sampleRate) {
  const pcm16 = new Int16Array(pcmData);
  const buffer = new ArrayBuffer(44 + pcm16.length * 2);
  const view = new DataView(buffer);
  const writeString = (view, offset, string) => {
    for (let i = 0; i < string.length; i++) {
      view.setUint8(offset + i, string.charCodeAt(i));
    }
  };

  let offset = 0;
  writeString(view, offset, 'RIFF'); offset += 4;
  view.setUint32(offset, 36 + pcm16.length * 2, true); offset += 4;
  writeString(view, offset, 'WAVE'); offset += 4;
  writeString(view, offset, 'fmt '); offset += 4;
  view.setUint32(offset, 16, true); offset += 4; // Sub-chunk size
  view.setUint16(offset, 1, true); offset += 2; // Audio format (1 = PCM)
  view.setUint16(offset, 1, true); offset += 2; // Number of channels
  view.setUint32(offset, sampleRate, true); offset += 4; // Sample rate
  view.setUint32(offset, sampleRate * 2, true); offset += 4; // Byte rate
  view.setUint16(offset, 2, true); offset += 2; // Block align
  view.setUint16(offset, 16, true); offset += 2; // Bits per sample
  writeString(view, offset, 'data'); offset += 4;
  view.setUint32(offset, pcm16.length * 2, true); offset += 4; // Data size

  for (let i = 0; i < pcm16.length; i++) {
    view.setInt16(offset, pcm16[i], true);
    offset += 2;
  }

  return new Blob([view], { type: 'audio/wav' });
}

// Prebuilt voices available for TTS
const prebuiltVoices = [
  'Kore', 'Puck', 'Charon', 'Fenrir', 'Leda', 'Orus', 'Aoede', 'Callirrhoe', 'Autonoe',
  'Enceladus', 'Iapetus', 'Umbriel', 'Algieba', 'Despina', 'Erinome', 'Algenib',
  'Rasalgethi', 'Laomedeia', 'Achernar', 'Alnilam', 'Schedar', 'Gacrux',
  'Pulcherrima', 'Achird', 'Zubenelgenubi', 'Vindemiatrix', 'Sadachbia', 'Sadaltager', 'Sulafat'
];

// The main App component
function App() {
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailSent, setEmailSent] = useState(false);
  const [isSigningInWithLink, setIsSigningInWithLink] = useState(false);
  const [isAIGenerating, setIsAIGenerating] = useState(false);
  const [authMode, setAuthMode] = useState('email-link');

  // New states for conversation history and sidebar
  const [conversations, setConversations] = useState([]);
  const [currentConversationId, setCurrentConversationId] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isSummarizing, setIsSummarizing] = useState(false);
  const [isGeneratingImage, setIsGeneratingImage] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [voiceName, setVoiceName] = useState('Kore');
  const [showSettingsModal, setShowSettingsModal] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  // New states for Gemini features
  const [systemInstruction, setSystemInstruction] = useState('');
  const [generationSettings, setGenerationSettings] = useState({
    temperature: 0.9,
    topK: 40,
    topP: 0.95
  });
  const [showGeminiModal, setShowGeminiModal] = useState(false);
  const [translationTargetLanguage, setTranslationTargetLanguage] = useState('en-US');
  const [textToContinue, setTextToContinue] = useState('');
  const [textToTranslate, setTextToTranslate] = useState('');

  // New states for the creative writing feature
  const [showCreativeWriterModal, setShowCreativeWriterModal] = useState(false);
  const [creativeTopic, setCreativeTopic] = useState('');
  const [creativeStyle, setCreativeStyle] = useState('Poem');
  const [creativeTone, setCreativeTone] = useState('Neutral');

  // New states for the coding feature
  const [showCodeGeneratorModal, setShowCodeGeneratorModal] = useState(false);
  const [codeGenerationPrompt, setCodeGenerationPrompt] = useState('');
  const [codeLanguage, setCodeLanguage] = useState('JavaScript');

  const messagesEndRef = useRef(null);
  const audioRef = useRef(null);
  const currentAudioIdRef = useRef(null);
  const inputRef = useRef(null); // Added this ref for the input element

  // Scroll to bottom of messages whenever messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Exponential backoff utility for API calls
  const fetchWithExponentialBackoff = async (url, options, retries = 5, delay = 1000) => {
    try {
      const response = await fetch(url, options);
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`API error: ${errorData.error?.message || response.statusText}`);
      }
      return response;
    } catch (error) {
      if (retries > 0 && error.message.includes("API error")) {
        await new Promise(resolve => setTimeout(resolve, delay));
        return fetchWithExponentialBackoff(url, options, retries - 1, delay * 2);
      }
      throw error;
    }
  };

  // 1. Initialize Firebase and set up authentication.
  useEffect(() => {
    if (Object.keys(firebaseConfig).length === 0 || !firebaseConfig.apiKey) {
      setError('Firebase configuration is missing or incomplete. Please ensure your Firebase config is correctly set, especially the API Key.');
      setLoading(false);
      return;
    }

    try {
      const firebaseApp = initializeApp(firebaseConfig);
      const authInstance = getAuth(firebaseApp);
      const dbInstance = getFirestore(firebaseApp);

      setAuth(authInstance);
      setDb(dbInstance);

      const unsubscribe = onAuthStateChanged(authInstance, async (currentUser) => {
        if (currentUser) {
          setUser(currentUser);
          setIsAuthReady(true);
          setError(null);
        } else {
          if (initialAuthToken) {
            try {
              await signInWithCustomToken(authInstance, initialAuthToken);
            } catch (e) {
              console.error("Custom token sign-in failed:", e);
              setError(`Custom token sign-in failed: ${e.message}. This usually means the token is invalid or expired. Please try refreshing.`);
            }
          } else if (!isSigningInWithLink) {
            try {
              await signInAnonymously(authInstance);
            } catch (e) {
              console.error("Anonymous sign-in failed:", e);
              setError(`Anonymous sign-in failed: ${e.message}. Please ensure Anonymous Authentication is enabled in your Firebase project settings.`);
            }
          }
          setIsAuthReady(true);
        }
        setLoading(false);
      });
      return () => unsubscribe();
    } catch (e) {
      setError(`Failed to initialize Firebase: ${e.message}. Please check the console for more details.`);
      setLoading(false);
    }
  }, []);

  // 2. Handle email link sign-in.
  useEffect(() => {
    if (!auth || !isAuthReady) return;

    if (isSignInWithEmailLink(auth, window.location.href)) {
      setIsSigningInWithLink(true);
      setLoading(true);

      let storedEmail = window.localStorage.getItem('emailForSignIn');

      if (!storedEmail) {
        // Using a custom modal instead of prompt
        const modalDiv = document.createElement('div');
        modalDiv.className = 'fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50';
        modalDiv.innerHTML = `
          <div class="bg-white dark:bg-gray-800 p-8 rounded-lg shadow-lg max-w-sm w-full">
            <h3 class="text-xl font-bold mb-4">Confirm Email</h3>
            <p class="mb-4">Please provide your email to complete sign-in.</p>
            <input id="email-confirm-input" type="email" placeholder="Enter your email" class="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500" required />
            <button id="email-confirm-button" class="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200">Confirm</button>
          </div>
        `;
        document.body.appendChild(modalDiv);

        const emailConfirmInput = modalDiv.querySelector('#email-confirm-input');
        const emailConfirmButton = modalDiv.querySelector('#email-confirm-button');

        emailConfirmButton.onclick = () => {
          storedEmail = emailConfirmInput.value;
          if (storedEmail) {
            document.body.removeChild(modalDiv);
            continueSignIn(storedEmail);
          } else {
            console.error("Email not provided. Please try again.");
          }
        };

        const continueSignIn = (emailToUse) => {
          signInWithEmailLink(auth, emailToUse, window.location.href)
            .then((result) => {
              window.localStorage.removeItem('emailForSignIn');
              setUser(result.user);
              setIsSigningInWithLink(false);
              setLoading(false);
              setError(null);
              window.history.replaceState({}, document.title, window.location.pathname);
            })
            .catch((error) => {
              setError(`Error completing sign-in with link: ${error.message}`);
              setIsSigningInWithLink(false);
              setLoading(false);
              window.history.replaceState({}, document.title, window.location.pathname);
            });
        };
        return; // Exit the effect to wait for user input
      }

      signInWithEmailLink(auth, storedEmail, window.location.href)
        .then((result) => {
          window.localStorage.removeItem('emailForSignIn');
          setUser(result.user);
          setIsSigningInWithLink(false);
          setLoading(false);
          setError(null);
          window.history.replaceState({}, document.title, window.location.pathname);
        })
        .catch((error) => {
          setError(`Error completing sign-in with link: ${error.message}`);
          setIsSigningInWithLink(false);
          setLoading(false);
          window.history.replaceState({}, document.title, window.location.pathname);
        });
    }
  }, [auth, isAuthReady]);

  // 3. Fetch conversations for the current user.
  useEffect(() => {
    if (!db || !user || !isAuthReady) return;

    const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
    const unsubscribe = onSnapshot(conversationsCollectionRef, (querySnapshot) => {
      const fetchedConversations = [];
      querySnapshot.forEach((doc) => {
        fetchedConversations.push({ id: doc.id, ...doc.data() });
      });

      fetchedConversations.sort((a, b) => (b.lastUpdated?.seconds || 0) - (a.lastUpdated?.seconds || 0));

      setConversations(fetchedConversations);

      if (!currentConversationId && fetchedConversations.length > 0) {
        setCurrentConversationId(fetchedConversations[0].id);
      } else if (fetchedConversations.length === 0) {
        setCurrentConversationId(null);
        setMessages([]);
      }
    }, (firestoreError) => { // FIX: Add error handling to onSnapshot
      setError(`Failed to load conversations: ${firestoreError.message}`);
      console.error("Firestore conversations error:", firestoreError);
    });

    return () => unsubscribe();
  }, [db, user, isAuthReady, appId, currentConversationId]);

  // 4. Fetch messages for the current conversation.
  useEffect(() => {
    if (!db || !user || !currentConversationId) {
      setMessages([]); // Clear messages if no conversation is selected
      return;
    }

    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
    const unsubscribe = onSnapshot(messagesCollectionRef, (querySnapshot) => {
      const fetchedMessages = [];
      querySnapshot.forEach((doc) => {
        fetchedMessages.push({ id: doc.id, ...doc.data() });
      });

      fetchedMessages.sort((a, b) => (a.timestamp?.seconds || 0) - (b.timestamp?.seconds || 0));

      setMessages(fetchedMessages);
    }, (firestoreError) => { // FIX: Add error handling to onSnapshot
      setError(`Failed to load messages for conversation: ${firestoreError.message}`);
      console.error("Firestore messages error:", firestoreError);
    });

    return () => unsubscribe();
  }, [db, user, currentConversationId, appId]);

  // 5. Start a new conversation.
  const startNewChat = async () => {
    if (!db || !user) {
      setError("You must be logged in to start a new chat.");
      return;
    }

    // FIX: Clear the current conversation ID before creating a new one
    setCurrentConversationId(null);
    setMessages([]);
    setNewMessage('');
    setSelectedImage(null);
    setError(null);
    setIsAIGenerating(false);
    setIsSummarizing(false);
    setIsGeneratingImage(false);

    try {
      const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
      const newConversationDocRef = doc(conversationsCollectionRef);
      const newConvId = newConversationDocRef.id;

      await setDoc(newConversationDocRef, {
        title: 'New Chat',
        createdAt: serverTimestamp(),
        lastUpdated: serverTimestamp(),
      });
      setCurrentConversationId(newConvId);
      setIsSidebarOpen(false);
      // Immediately focus the input after starting a new chat
      setTimeout(() => {
        inputRef.current?.focus();
      }, 0);
    } catch (e) {
      setError(`Failed to create a new chat: ${e.message}`);
      console.error("Error creating new conversation:", e);
    }
  };

  // 6. Handle sending a new message (and creating a new conversation if needed).
  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() && !selectedImage || !user || !db || isAIGenerating || isSpeaking) return;

    let conversationIdToUse = currentConversationId;
    const userMessageText = newMessage;
    const imageToSend = selectedImage;

    setNewMessage('');
    setSelectedImage(null);

    // After sending the message, immediately refocus the input field
    setTimeout(() => {
      inputRef.current?.focus();
    }, 0);

    const command = userMessageText.toLowerCase().trim();

    // If no conversation is active, create a new one.
    if (!conversationIdToUse) {
      const newConvId = generateId();
      const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
      const newConversationDocRef = doc(conversationsCollectionRef, newConvId);
      const initialTitle = userMessageText ? (userMessageText.substring(0, 50) + (userMessageText.length > 50 ? '...' : '')) : 'New chat with image';

      try {
        await setDoc(newConversationDocRef, {
          title: initialTitle,
          createdAt: serverTimestamp(),
          lastUpdated: serverTimestamp(),
        });
        conversationIdToUse = newConvId;
        setCurrentConversationId(newConvId);
      } catch (createError) {
        setError(`Failed to create new conversation: ${createError.message}`);
        console.error("Error creating new conversation:", createError);
        return;
      }
    } else {
      // Otherwise, just update the lastUpdated timestamp of the current conversation.
      const conversationDocRef = doc(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse);
      try {
        await setDoc(conversationDocRef, { lastUpdated: serverTimestamp() }, { merge: true });
      } catch (updateError) {
        console.error("Error updating conversation timestamp:", updateError);
      }
    }

    // Add user message to Firestore
    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse, 'messages');
      await addDoc(messagesCollectionRef, {
        text: userMessageText,
        userId: user.uid,
        userName: `User_${user.uid.substring(0, 5)}`,
        timestamp: serverTimestamp(),
        type: 'user-message',
        imageData: imageToSend
      });
    } catch (e) {
      setError(`Failed to send message: ${e.message}`);
      return;
    }

    if (imageToSend) {
      handleImageAnalysis(userMessageText, imageToSend, conversationIdToUse);
    } else if (command.startsWith('image of')) {
      const imagePrompt = userMessageText.substring('image of'.length).trim();
      handleGenerateImage(imagePrompt, conversationIdToUse);
    } else {
      handleAIResponse(userMessageText, conversationIdToUse);
    }
  };

  // Function to handle image upload
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  // 7. Handle sending the sign-in email link.
  const handleSendSignInLink = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim()) {
      setError("Please enter a valid email address.");
      return;
    }
    setLoading(true);
    setError(null);
    const actionCodeSettings = {
      url: window.location.origin,
      handleCodeInApp: true,
    };
    try {
      await sendSignInLinkToEmail(auth, email, actionCodeSettings);
      setEmailSent(true);
      window.localStorage.setItem('emailForSignIn', email);
      setLoading(false);
    } catch (error) {
      setError(`Failed to send sign-in link: ${error.message}`);
      setLoading(false);
    }
  };

  // 8. Handle Email/Password Registration
  const handleEmailPasswordSignUp = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim() || !password.trim()) {
      setError("Please enter a valid email and password.");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
      setLoading(false);
    } catch (error) {
      setError(`Failed to sign up: ${error.message}`);
      setLoading(false);
    }
  };

  // 9. Handle Email/Password Login
  const handleEmailPasswordSignIn = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim() || !password.trim()) {
      setError("Please enter a valid email and password.");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      await signInWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
      setLoading(false);
    } catch (error) {
      setError(`Failed to sign in: ${error.message}`);
      setLoading(false);
    }
  };

  // 10. Handle sign-out.
  const handleSignOut = async () => {
    if (!auth) return;
    try {
      await signOut(auth);
      // Reset states
      setUser(null);
      setConversations([]);
      setMessages([]);
      setCurrentConversationId(null);
      setError(null);
    } catch (error) {
      setError(`Failed to sign out: ${error.message}`);
      console.error("Sign out error:", error);
    }
  };

  // 11. Handle AI response using Gemini API.
  const handleAIResponse = async (userPrompt, conversationId) => {
    if (!user || !db || !conversationId) return;
    setIsAIGenerating(true);
    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
    // Get the conversation history to provide context to the AI
    let chatHistory = [];
    try {
      // FIX: Use a more robust query for history
      const q = query(messagesCollectionRef, limit(20));
      const querySnapshot = await getDocs(q);
      querySnapshot.forEach(doc => {
        const data = doc.data();
        if (data.type === 'user-message') {
          chatHistory.push({ role: 'user', parts: [{ text: data.text }] });
        } else if (data.type === 'ai-message') {
          chatHistory.push({ role: 'model', parts: [{ text: data.text }] });
        }
      });
      // Add the latest user prompt
      chatHistory.push({ role: "user", parts: [{ text: userPrompt }] });
    } catch (e) {
      console.error("Error fetching chat history:", e);
      // If fetching history fails, just use the current prompt
      chatHistory = [{ role: "user", parts: [{ text: userPrompt }] }];
    }
    const payload = {
      contents: chatHistory,
      generationConfig: {
        temperature: generationSettings.temperature,
        topK: generationSettings.topK,
        topP: generationSettings.topP,
      },
      systemInstruction: systemInstruction ? { parts: [{ text: systemInstruction }] } : undefined,
    };
    const apiKey = "YOUR_GEMINI_API_KEY"; // FIX: Replace with your actual Gemini API key.
    const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
    try {
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      const aiResponseText = result.candidates?.[0]?.content?.parts?.[0]?.text || 'No response from AI.';
      // Add AI response to Firestore
      await addDoc(messagesCollectionRef, {
        text: aiResponseText,
        userId: 'ai',
        userName: 'Gemini',
        timestamp: serverTimestamp(),
        type: 'ai-message'
      });
    } catch (e) {
      console.error("Gemini API error:", e);
      setError(`Gemini API error: ${e.message}`);
    } finally {
      setIsAIGenerating(false);
    }
  };

  // 12. Handle image analysis with Gemini Vision API
  const handleImageAnalysis = async (userPrompt, imageData, conversationId) => {
    if (!user || !db || !conversationId) return;
    setIsAIGenerating(true);
    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
    // Remove the data URL prefix "data:image/png;base64,"
    const base64ImageData = imageData.split(',')[1];
    const payload = {
      contents: [
        {
          role: "user",
          parts: [
            { text: userPrompt || 'What is in this image?' },
            {
              inlineData: {
                mimeType: "image/png", // Assuming PNG, but should be dynamic if possible
                data: base64ImageData
              }
            }
          ]
        }
      ],
    };
    const apiKey = "YOUR_GEMINI_API_KEY"; // FIX: Replace with your actual Gemini API key.
    const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
    try {
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      const aiResponseText = result.candidates?.[0]?.content?.parts?.[0]?.text || 'No response from AI.';

      await addDoc(messagesCollectionRef, {
        text: aiResponseText,
        userId: 'ai',
        userName: 'Gemini',
        timestamp: serverTimestamp(),
        type: 'ai-message'
      });
    } catch (e) {
      console.error("Gemini Vision API error:", e);
      setError(`Gemini Vision API error: ${e.message}`);
    } finally {
      setIsAIGenerating(false);
    }
  };

  // 13. Handle image generation with Gemini API
  const handleGenerateImage = async (imagePrompt, conversationId) => {
    if (!user || !db || !conversationId) return;
    setIsGeneratingImage(true);

    let conversationIdToUse = currentConversationId;
    if (!conversationIdToUse) {
      const newConvId = generateId();
      const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
      const newConversationDocRef = doc(conversationsCollectionRef, newConvId);
      try {
        await setDoc(newConversationDocRef, {
          title: imagePrompt.substring(0, 50) + '...',
          createdAt: serverTimestamp(),
          lastUpdated: serverTimestamp(),
        });
        conversationIdToUse = newConvId;
        setCurrentConversationId(newConvId);
      } catch (createError) {
        setError(`Failed to create new conversation: ${createError.message}`);
        console.error("Error creating new conversation:", createError);
        return;
      }
    }

    // Add user prompt to Firestore
    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse, 'messages');
      await addDoc(messagesCollectionRef, {
        text: imagePrompt,
        userId: user.uid,
        userName: `User_${user.uid.substring(0, 5)}`,
        timestamp: serverTimestamp(),
        type: 'image-prompt-message'
      });
    } catch (e) {
      setError(`Failed to send message: ${e.message}`);
      return;
    }

    const payload = {
      instances: { prompt: imagePrompt },
      parameters: { "sampleCount": 1 }
    };
    const apiKey = "YOUR_GEMINI_API_KEY"; // FIX: Replace with your actual Gemini API key.
    const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/imagen-3.0-generate-002:predict?key=${apiKey}`;

    try {
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      const base64Data = result?.predictions?.[0]?.bytesBase64Encoded;

      if (!base64Data) {
        throw new Error('Image generation failed: No image data received.');
      }

      const imageUrl = `data:image/png;base64,${base64Data}`;

      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse, 'messages');
      await addDoc(messagesCollectionRef, {
        imageUrl: imageUrl,
        userId: 'ai',
        userName: 'Gemini',
        timestamp: serverTimestamp(),
        type: 'image-message'
      });

    } catch (e) {
      console.error("Gemini Image generation API error:", e);
      setError(`Gemini Image generation API error: ${e.message}`);
    } finally {
      setIsGeneratingImage(false);
    }
  };

  // 14. Handle TTS generation and playback.
  const handleTTS = async (textToSpeak) => {
    if (isSpeaking) {
      audioRef.current?.pause();
      setIsSpeaking(false);
      return;
    }
    setIsSpeaking(true);
    const utteranceId = generateId();
    currentAudioIdRef.current = utteranceId;

    const payload = {
      contents: [{
        parts: [{ text: textToSpeak }]
      }],
      generationConfig: {
        responseModalities: ["AUDIO"],
        speechConfig: {
          voiceConfig: {
            prebuiltVoiceConfig: { voiceName: voiceName }
          }
        }
      },
      model: "gemini-2.5-flash-preview-tts"
    };

    const apiKey = "YOUR_GEMINI_API_KEY"; // FIX: Replace with your actual Gemini API key.
    const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-tts:generateContent?key=${apiKey}`;

    try {
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      const part = result?.candidates?.[0]?.content?.parts?.[0];
      const audioData = part?.inlineData?.data;
      const mimeType = part?.inlineData?.mimeType;

      if (audioData && mimeType && mimeType.startsWith("audio/")) {
        const sampleRate = parseInt(mimeType.match(/rate=(\d+)/)[1], 10);
        const pcmData = base64ToArrayBuffer(audioData);
        const wavBlob = pcmToWav(pcmData, sampleRate);
        const audioUrl = URL.createObjectURL(wavBlob);

        if (currentAudioIdRef.current !== utteranceId) {
          URL.revokeObjectURL(audioUrl);
          return;
        }

        if (audioRef.current) {
          audioRef.current.src = audioUrl;
          audioRef.current.onended = () => {
            setIsSpeaking(false);
            URL.revokeObjectURL(audioUrl);
          };
          audioRef.current.play();
        }
      } else {
        throw new Error("No audio data received from API.");
      }
    } catch (e) {
      console.error("TTS API error:", e);
      setError(`TTS API error: ${e.message}`);
      setIsSpeaking(false);
    }
  };

  // 15. Handle code generation with Gemini API.
  const handleGenerateCode = async () => {
    if (!user || !db || !codeGenerationPrompt || isAIGenerating) return;
    setIsAIGenerating(true);
    setShowCodeGeneratorModal(false);

    let conversationIdToUse = currentConversationId;
    const userPrompt = codeGenerationPrompt;
    const language = codeLanguage;

    // Create a new conversation if one doesn't exist
    if (!conversationIdToUse) {
      const newConvId = generateId();
      const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
      const newConversationDocRef = doc(conversationsCollectionRef, newConvId);
      try {
        await setDoc(newConversationDocRef, {
          title: userPrompt.substring(0, 50) + '...',
          createdAt: serverTimestamp(),
          lastUpdated: serverTimestamp(),
        });
        conversationIdToUse = newConvId;
        setCurrentConversationId(newConvId);
      } catch (createError) {
        setError(`Failed to create new conversation: ${createError.message}`);
        console.error("Error creating new conversation:", createError);
        setIsAIGenerating(false);
        return;
      }
    }

    // Add user's code request to Firestore
    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse, 'messages');
      await addDoc(messagesCollectionRef, {
        text: `Generate a complete code snippet in ${language} for the following request: \n\n${userPrompt}`,
        userId: user.uid,
        userName: `User_${user.uid.substring(0, 5)}`,
        timestamp: serverTimestamp(),
        type: 'code-request'
      });
    } catch (e) {
      setError(`Failed to send code request: ${e.message}`);
      setIsAIGenerating(false);
      return;
    }

    // Prepare the prompt for the Gemini API
    const finalPrompt = `Generate a complete, well-commented code snippet in ${language} for the following request: ${userPrompt}. The output must be a single markdown code block.`;
    const payload = {
      contents: [{ role: "user", parts: [{ text: finalPrompt }] }],
      generationConfig: {
        temperature: 0.7,
        topK: 40,
        topP: 0.95,
      }
    };

    const apiKey = "YOUR_GEMINI_API_KEY"; // FIX: Replace with your actual Gemini API key.
    const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;

    try {
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      const aiResponseText = result.candidates?.[0]?.content?.parts?.[0]?.text || 'No code generated.';

      // Add AI's code response to Firestore
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse, 'messages');
      await addDoc(messagesCollectionRef, {
        text: aiResponseText,
        userId: 'ai',
        userName: 'Gemini',
        timestamp: serverTimestamp(),
        type: 'code-response',
        language: language,
      });

    } catch (e) {
      console.error("Gemini Code generation API error:", e);
      setError(`Gemini Code generation API error: ${e.message}`);
    } finally {
      setIsAIGenerating(false);
      setCodeGenerationPrompt('');
    }
  };

  // Copy to clipboard handler
  const handleCopyToClipboard = (text) => {
    navigator.clipboard.writeText(text).then(() => {
      // You could add a small visual cue here
      console.log('Code copied to clipboard!');
    }).catch(err => {
      console.error('Failed to copy text: ', err);
    });
  };

  const renderMessage = (message, isLastMessage) => {
    const isUser = message.userId === user?.uid;
    const isAi = message.userId === 'ai';
    const isCodeResponse = message.type === 'code-response';

    const messageClasses = isUser
      ? 'bg-indigo-500 text-white self-end rounded-br-none'
      : isAi
        ? 'bg-gray-200 dark:bg-gray-700 text-gray-900 dark:text-gray-100 self-start rounded-bl-none'
        : '';
    const nameClasses = isUser
      ? 'text-indigo-400 text-right'
      : 'text-gray-500 dark:text-gray-400';

    const messageContent = () => {
      if (message.imageData) {
        return (
          <div className="flex flex-col items-start space-y-2">
            <p className="text-sm font-medium text-gray-700 dark:text-gray-300">
              {message.text}
            </p>
            <img
              src={message.imageData}
              alt="User uploaded"
              className="max-w-xs rounded-lg shadow-md"
              style={{ maxHeight: '300px' }}
            />
          </div>
        );
      } else if (message.imageUrl) {
        return (
          <img
            src={message.imageUrl}
            alt="AI generated"
            className="max-w-full rounded-lg shadow-md"
            style={{ maxHeight: '400px' }}
          />
        );
      } else if (isCodeResponse) {
        // Find the code block and extract the content
        const codeBlockMatch = message.text.match(/```(?:\w+)?\n([\s\S]*?)```/);
        const codeContent = codeBlockMatch ? codeBlockMatch[1] : message.text;

        return (
          <div className="flex flex-col w-full">
            <div className="p-4 bg-gray-800 text-white rounded-t-xl overflow-x-auto relative">
              <button
                onClick={() => handleCopyToClipboard(codeContent)}
                className="absolute top-2 right-2 p-1 text-gray-400 hover:text-white bg-gray-700 rounded-md transition-colors"
                title="Copy to clipboard"
              >
                <Copy size={16} />
              </button>
              <div dangerouslySetInnerHTML={{ __html: marked.parse(message.text) }} />
            </div>
            {message.text.length > 500 && (
              <p className="text-xs text-gray-500 dark:text-gray-400 mt-2">
                Scroll to view the full code snippet.
              </p>
            )}
          </div>
        );
      }
      return <div dangerouslySetInnerHTML={{ __html: marked.parse(message.text) }} />;
    };

    return (
      <div
        key={message.id}
        className={`flex flex-col p-4 space-y-1 ${isUser ? 'items-end' : 'items-start'
          }`}
      >
        <span className={`text-xs font-semibold ${nameClasses}`}>
          {isUser ? 'You' : 'Gemini'}
        </span>
        <div
          className={`max-w-3xl p-3 rounded-xl shadow-md whitespace-pre-wrap ${messageClasses} ${isCodeResponse ? 'w-full' : 'max-w-3xl'}`}
        >
          {messageContent()}
        </div>
        <div className="flex items-center space-x-2 mt-1">
          {isAi && message.text && !isCodeResponse && (
            <button
              onClick={() => handleTTS(message.text)}
              className="text-gray-500 dark:text-gray-400 hover:text-indigo-500 transition-colors"
            >
              {/* FIX: Simplified condition to check if any audio is playing */}
              {isSpeaking && currentAudioIdRef.current === message.id ? <Pause size={16} /> : <Play size={16} />}
            </button>
          )}
          <span className="text-xs text-gray-500 dark:text-gray-400">
            {message.timestamp?.toDate().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
          </span>
        </div>
      </div>
    );
  };

  const MemoizedMessages = React.memo(({ messages }) => {
    if (messages.length === 0) {
      return (
        <div className="flex flex-col items-center justify-center h-full text-center text-gray-400 dark:text-gray-600 p-4">
          <MessageSquareMore className="h-16 w-16 mb-4" />
          <h2 className="text-xl font-bold mb-2">Start a new conversation</h2>
          <p className="max-w-sm">
            Ask me anything, generate images, or get help writing code.
          </p>
        </div>
      );
    }
    return (
      <>
        {messages.map((message, index) =>
          renderMessage(message, index === messages.length - 1)
        )}
        <div ref={messagesEndRef} />
      </>
    );
  });

  const AuthUI = () => (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-gray-100 p-4">
      <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-2xl p-8 w-full max-w-md">
        <h1 className="text-3xl font-bold text-center mb-6">Welcome</h1>
        {error && (
          <div className="p-4 mb-4 text-sm text-red-700 bg-red-100 rounded-lg dark:bg-red-200 dark:text-red-800">
            {error}
          </div>
        )}
        <div className="flex space-x-2 mb-6">
          <button
            onClick={() => setAuthMode('email-link')}
            className={`flex-1 p-3 rounded-xl font-semibold transition-colors ${authMode === 'email-link' ? 'bg-indigo-500 text-white' : 'bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200'
              }`}
          >
            Magic Link
          </button>
          <button
            onClick={() => setAuthMode('email-password')}
            className={`flex-1 p-3 rounded-xl font-semibold transition-colors ${authMode === 'email-password' ? 'bg-indigo-500 text-white' : 'bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-gray-200'
              }`}
          >
            Email/Password
          </button>
        </div>
        {authMode === 'email-link' ? (
          <form onSubmit={handleSendSignInLink} className="space-y-4">
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              required
              className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />
            <button
              type="submit"
              className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200"
              disabled={loading}
            >
              {loading ? <LoaderCircle className="animate-spin h-5 w-5 mx-auto" /> : 'Send Sign-in Link'}
            </button>
          </form>
        ) : (
          <div className="space-y-4">
            <form onSubmit={handleEmailPasswordSignIn} className="space-y-4">
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                required
                className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
              />
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                required
                className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
              />
              <button
                type="submit"
                className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200"
                disabled={loading}
              >
                {loading ? <LoaderCircle className="animate-spin h-5 w-5 mx-auto" /> : 'Sign In'}
              </button>
            </form>
            <button
              onClick={handleEmailPasswordSignUp}
              className="w-full text-indigo-500 p-3 rounded-lg hover:bg-indigo-100 dark:hover:bg-indigo-900 transition-colors duration-200"
            >
              Don't have an account? Sign Up
            </button>
          </div>
        )}
        {emailSent && (
          <p className="mt-4 text-center text-sm text-green-600 dark:text-green-400">
            A sign-in link has been sent to your email!
          </p>
        )}
      </div>
    </div>
  );

  const UserModal = ({ isOpen, onClose, title, children }) => {
    if (!isOpen) return null;
    return createPortal(
      <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-white dark:bg-gray-900 rounded-xl shadow-lg p-6 w-full max-w-md mx-4">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-bold">{title}</h2>
            <button onClick={onClose} className="p-1 rounded-full text-gray-500 hover:bg-gray-200 dark:hover:bg-gray-700">
              <X className="h-5 w-5" />
            </button>
          </div>
          {children}
        </div>
      </div>,
      document.body
    );
  };

  const CodeGeneratorModal = ({ isOpen, onClose, onGenerate }) => {
    if (!isOpen) return null;
    return (
      <UserModal isOpen={isOpen} onClose={onClose} title="Code Generator">
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
              Language
            </label>
            <select
              value={codeLanguage}
              onChange={(e) => setCodeLanguage(e.target.value)}
              className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
            >
              <option value="JavaScript">JavaScript</option>
              <option value="Python">Python</option>
              <option value="HTML">HTML</option>
              <option value="CSS">CSS</option>
              <option value="React">React</option>
              <option value="Java">Java</option>
              <option value="C++">C++</option>
              <option value="Go">Go</option>
              <option value="Swift">Swift</option>
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
              Code Prompt
            </label>
            <textarea
              value={codeGenerationPrompt}
              onChange={(e) => setCodeGenerationPrompt(e.target.value)}
              placeholder="e.g., 'A function that reverses a string' or 'A simple HTML layout with a header and footer'"
              rows="5"
              className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
            ></textarea>
          </div>
          <button
            onClick={onGenerate}
            className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200"
            disabled={isAIGenerating || !codeGenerationPrompt}
          >
            {isAIGenerating ? (
              <LoaderCircle className="animate-spin h-5 w-5 mx-auto" />
            ) : (
              'Generate Code'
            )}
          </button>
        </div>
      </UserModal>
    );
  };

  const ChatWindow = () => (
    <div className="flex flex-col h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-gray-100">
      <div className={`fixed inset-y-0 left-0 transform ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'} lg:translate-x-0 lg:static lg:inset-y-0 w-64 bg-white dark:bg-gray-800 transition-transform duration-300 ease-in-out z-40 flex flex-col`}>
        <div className="p-4 flex items-center justify-between border-b dark:border-gray-700">
          <h2 className="text-xl font-bold">Conversations</h2>
          <button
            onClick={startNewChat}
            className="p-2 rounded-full bg-indigo-500 text-white shadow-md hover:bg-indigo-600 transition-colors"
            title="New Chat"
          >
            <Plus className="h-5 w-5" />
          </button>
        </div>
        <div className="flex-1 overflow-y-auto p-2">
          {conversations.length > 0 ? (
            conversations.map((conv) => (
              <div
                key={conv.id}
                onClick={() => { setCurrentConversationId(conv.id); setIsSidebarOpen(false); }}
                className={`p-3 my-2 rounded-xl cursor-pointer hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors truncate ${currentConversationId === conv.id ? 'bg-indigo-100 dark:bg-indigo-900 font-semibold' : 'bg-gray-100 dark:bg-gray-800'
                  }`}
                title={conv.title}
              >
                {conv.title}
              </div>
            ))
          ) : (
            <p className="text-center text-sm text-gray-400 mt-4">No conversations yet.</p>
          )}
        </div>
        <div className="p-4 border-t dark:border-gray-700">
          <p className="text-sm font-semibold text-gray-500">
            User ID: <span className="text-xs break-all">{user.uid}</span>
          </p>
          <button
            onClick={handleSignOut}
            className="mt-4 w-full bg-red-500 text-white p-3 rounded-lg shadow-md hover:bg-red-600 transition-colors duration-200"
          >
            Sign Out
          </button>
        </div>
      </div>

      <div className="flex-1 flex flex-col lg:ml-64">
        <header className="bg-white dark:bg-gray-800 shadow-md p-4 flex items-center justify-between lg:hidden">
          <button onClick={() => setIsSidebarOpen(!isSidebarOpen)} className="p-2 text-gray-600 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700 rounded-lg">
            <Menu className="h-6 w-6" />
          </button>
          <h1 className="text-lg font-bold truncate">
            {conversations.find(c => c.id === currentConversationId)?.title || 'New Chat'}
          </h1>
          <button onClick={() => setShowSettingsModal(true)} className="p-2 text-gray-600 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700 rounded-lg">
            <Settings className="h-6 w-6" />
          </button>
        </header>

        <main className="flex-1 overflow-y-auto p-4 space-y-4">
          <MemoizedMessages messages={messages} />
          {(isAIGenerating || isGeneratingImage) && (
            <div className={`flex flex-col p-4 space-y-1 items-start`}>
              <span className={`text-xs font-semibold text-gray-500 dark:text-gray-400`}>
                Gemini
              </span>
              <div className={`max-w-3xl p-3 rounded-xl shadow-md whitespace-pre-wrap bg-gray-200 dark:bg-gray-700 text-gray-900 dark:text-gray-100 rounded-bl-none`}>
                <div className="flex items-center space-x-2">
                  <LoaderCircle className="animate-spin" size={20} />
                  <span>{isGeneratingImage ? 'Generating image...' : 'Thinking...'}</span>
                </div>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </main>

        <footer className="bg-white dark:bg-gray-800 p-4 border-t dark:border-gray-700">
          {error && (
            <div className="p-3 mb-4 text-sm text-red-700 bg-red-100 rounded-lg dark:bg-red-200 dark:text-red-800 flex items-center justify-between">
              <span>{error}</span>
              <button onClick={() => setError(null)} className="text-red-700 dark:text-red-800 hover:text-red-900">
                <X size={16} />
              </button>
            </div>
          )}
          <form onSubmit={handleSendMessage} className="flex space-x-2">
            <button
              type="button"
              onClick={() => setShowCodeGeneratorModal(true)}
              className="p-3 bg-gray-200 dark:bg-gray-700 text-gray-600 dark:text-gray-300 rounded-xl hover:bg-indigo-500 hover:text-white transition-colors"
              title="Generate Code"
            >
              <PenTool size={24} />
            </button>
            <label className="p-3 bg-gray-200 dark:bg-gray-700 text-gray-600 dark:text-gray-300 rounded-xl hover:bg-indigo-500 hover:text-white transition-colors cursor-pointer">
              <Image size={24} />
              <input type="file" className="hidden" onChange={handleImageUpload} accept="image/*" />
            </label>
            <div className="relative flex-1">
              <textarea
                ref={inputRef}
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                onKeyDown={(e) => {
                  if (e.key === 'Enter' && !e.shiftKey) {
                    e.preventDefault();
                    handleSendMessage(e);
                  }
                }}
                className="w-full p-3 pr-12 border border-gray-300 dark:border-gray-600 rounded-xl bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500 resize-none"
                rows={1}
                placeholder={selectedImage ? 'Ask about the image...' : 'Message Gemini...'}
                disabled={isAIGenerating || isGeneratingImage}
                style={{ maxHeight: '150px' }}
              />
              {selectedImage && (
                <div className="absolute top-2 right-2 p-1 bg-white dark:bg-gray-800 rounded-full shadow">
                  <button type="button" onClick={() => setSelectedImage(null)} className="text-gray-500 hover:text-gray-700">
                    <X size={16} />
                  </button>
                </div>
              )}
            </div>
            <button
              type="submit"
              className="p-3 bg-indigo-500 text-white rounded-xl shadow-md hover:bg-indigo-600 transition-colors"
              disabled={isAIGenerating || isGeneratingImage || (!newMessage.trim() && !selectedImage)}
            >
              <Sparkles size={24} />
            </button>
          </form>
        </footer>
      </div>

      <UserModal isOpen={showSettingsModal} onClose={() => setShowSettingsModal(false)} title="App Settings">
        <div className="space-y-4">
          <div>
            <h3 className="text-lg font-semibold mb-2">TTS Settings</h3>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">Voice</label>
            <select
              value={voiceName}
              onChange={(e) => setVoiceName(e.target.value)}
              className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg bg-gray-50 dark:bg-gray-700 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500"
            >
              {prebuiltVoices.map((voice) => (
                <option key={voice} value={voice}>{voice}</option>
              ))}
            </select>
          </div>
        </div>
      </UserModal>

      <CodeGeneratorModal isOpen={showCodeGeneratorModal} onClose={() => setShowCodeGeneratorModal(false)} onGenerate={handleGenerateCode} />

    </div>
  );

  if (loading || !isAuthReady) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-gray-100">
        <LoaderCircle className="animate-spin h-12 w-12 text-indigo-500" />
      </div>
    );
  }

  if (error && !user) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900 p-4">
        <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-2xl p-8 w-full max-w-md text-center">
          <p className="text-red-500 dark:text-red-400 mb-4">{error}</p>
          <button
            onClick={() => window.location.reload()}
            className="bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200"
          >
            Refresh Page
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-gray-100 font-sans">
      {!user ? <AuthUI /> : <ChatWindow />}
      <audio ref={audioRef} className="hidden" />
    </div>
  );
}

export default App;
