// Utility function for generating unique IDs (like conversation IDs)
const generateId = () => {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0, v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
};

// Converts a base64 string to an ArrayBuffer
function base64ToArrayBuffer(base64) {
  const binaryString = window.atob(base64);
  const len = binaryString.length;
  const bytes = new Uint8Array(len);
  for (let i = 0; i < len; i++) {
    bytes[i] = binaryString.charCodeAt(i);
  }
  return bytes.buffer;
}

// Converts PCM audio data to a WAV blob
function pcmToWav(pcmData, sampleRate) {
  const pcm16 = new Int16Array(pcmData);
  const buffer = new ArrayBuffer(44 + pcm16.length * 2);
  const view = new DataView(buffer);
  const writeString = (view, offset, string) => {
      for (let i = 0; i < string.length; i++) {
          view.setUint8(offset + i, string.charCodeAt(i));
      }
  };

  let offset = 0;
  writeString(view, offset, 'RIFF'); offset += 4;
  view.setUint32(offset, 36 + pcm16.length * 2, true); offset += 4;
  writeString(view, offset, 'WAVE'); offset += 4;
  writeString(view, offset, 'fmt '); offset += 4;
  view.setUint32(offset, 16, true); offset += 4; // Sub-chunk size
  view.setUint16(offset, 1, true); offset += 2; // Audio format (1 = PCM)
  view.setUint16(offset, 1, true); offset += 2; // Number of channels
  view.setUint32(offset, sampleRate, true); offset += 4; // Sample rate
  view.setUint32(offset, sampleRate * 2, true); offset += 4; // Byte rate
  view.setUint16(offset, 2, true); offset += 2; // Block align
  view.setUint16(offset, 16, true); offset += 2; // Bits per sample
  writeString(view, offset, 'data'); offset += 4;
  view.setUint32(offset, pcm16.length * 2, true); offset += 4; // Data size

  for (let i = 0; i < pcm16.length; i++) {
      view.setInt16(offset, pcm16[i], true);
      offset += 2;
  }

  return new Blob([view], { type: 'audio/wav' });
}


// A simple Markdown renderer component
const MarkdownRenderer = ({ markdownText }) => {
  if (typeof marked !== 'undefined') {
    const html = marked.parse(markdownText);
    return <div className="prose dark:prose-invert max-w-none break-words" dangerouslySetInnerHTML={{ __html: html }} />;
  }
  return <div className="prose dark:prose-invert max-w-none break-words">{markdownText}</div>;
};

// Main App component
function App() {
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailSent, setEmailSent] = useState(false);
  const [isSigningInWithLink, setIsSigningInWithLink] = useState(false);
  const [isAIGenerating, setIsAIGenerating] = useState(false);
  const [authMode, setAuthMode] = useState('email-link');

  // New states for conversation history and sidebar
  const [conversations, setConversations] = useState([]);
  const [currentConversationId, setCurrentConversationId] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isSummarizing, setIsSummarizing] = useState(false);
  const [isGeneratingImage, setIsGeneratingImage] = useState(false);
  const [currentAudio, setCurrentAudio] = useState(null);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [voiceName, setVoiceName] = useState('Kore');
  const [showVoiceModal, setShowVoiceModal] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  // New states for Gemini features
  const [isRecording, setIsRecording] = useState(false);
  const [showSettingsModal, setShowSettingsModal] = useState(false);
  const [systemInstruction, setSystemInstruction] = useState('');
  const [generationSettings, setGenerationSettings] = useState({
    temperature: 0.9,
    topK: 40,
    topP: 0.95
  });

  const messagesEndRef = useRef(null);
  const audioContextRef = useRef(null);
  const audioRef = useRef(null);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  // Scroll to bottom of messages whenever messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Exponential backoff utility for API calls
  const fetchWithExponentialBackoff = async (url, options, retries = 5, delay = 1000) => {
    try {
      const response = await fetch(url, options);
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`API error: ${errorData.error?.message || response.statusText}`);
      }
      return response;
    } catch (error) {
      if (retries > 0 && error.message.includes("API error")) {
        await new Promise(resolve => setTimeout(resolve, delay));
        return fetchWithExponentialBackoff(url, options, retries - 1, delay * 2);
      }
      throw error;
    }
  };

  // 1. Initialize Firebase and set up authentication.
  useEffect(() => {
    if (Object.keys(firebaseConfig).length === 0 || !firebaseConfig.apiKey) {
      setError('Firebase configuration is missing or incomplete. Please ensure your Firebase config is correctly set, especially the API Key.');
      setLoading(false);
      return;
    }

    try {
      const firebaseApp = initializeApp(firebaseConfig);
      const authInstance = getAuth(firebaseApp);
      const dbInstance = getFirestore(firebaseApp);

      setAuth(authInstance);
      setDb(dbInstance);

      const unsubscribe = onAuthStateChanged(authInstance, async (currentUser) => {
        if (currentUser) {
          setUser(currentUser);
          setIsAuthReady(true);
          setError(null);
        } else {
          if (initialAuthToken) {
             try {
               await signInWithCustomToken(authInstance, initialAuthToken);
             } catch (e) {
               console.error("Custom token sign-in failed:", e);
               setError(`Custom token sign-in failed: ${e.message}. This usually means the token is invalid or expired. Please try refreshing.`);
             }
          } else if (!isSigningInWithLink) {
            try {
              await signInAnonymously(authInstance);
            } catch (e) {
              console.error("Anonymous sign-in failed:", e);
              setError(`Anonymous sign-in failed: ${e.message}. Please ensure Anonymous Authentication is enabled in your Firebase project settings.`);
            }
          }
          setIsAuthReady(true);
        }
        setLoading(false);
      });
      return () => unsubscribe();
    } catch (e) {
      setError(`Failed to initialize Firebase: ${e.message}. Please check the console for more details.`);
      setLoading(false);
    }
  }, []);

  // 2. Handle email link sign-in.
  useEffect(() => {
    if (!auth || !isAuthReady) return;

    if (isSignInWithEmailLink(auth, window.location.href)) {
      setIsSigningInWithLink(true);
      setLoading(true);

      let storedEmail = window.localStorage.getItem('emailForSignIn');

      if (!storedEmail) {
        // Using a custom modal instead of prompt
        const modalDiv = document.createElement('div');
        modalDiv.className = 'fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50';
        modalDiv.innerHTML = `
          <div class="bg-white p-8 rounded-lg shadow-lg max-w-sm w-full">
            <h3 class="text-xl font-bold mb-4">Confirm Email</h3>
            <p class="mb-4">Please provide your email to complete sign-in.</p>
            <input id="email-confirm-input" type="email" placeholder="Enter your email" class="w-full p-3 border border-gray-300 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-indigo-500" required />
            <button id="email-confirm-button" class="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200">Confirm</button>
          </div>
        `;
        document.body.appendChild(modalDiv);

        const emailConfirmInput = modalDiv.querySelector('#email-confirm-input');
        const emailConfirmButton = modalDiv.querySelector('#email-confirm-button');

        emailConfirmButton.onclick = () => {
          storedEmail = emailConfirmInput.value;
          if (storedEmail) {
            document.body.removeChild(modalDiv);
            continueSignIn(storedEmail);
          } else {
            console.error("Email not provided. Please try again.");
          }
        };

        const continueSignIn = (emailToUse) => {
          signInWithEmailLink(auth, emailToUse, window.location.href)
            .then((result) => {
              window.localStorage.removeItem('emailForSignIn');
              setUser(result.user);
              setIsSigningInWithLink(false);
              setLoading(false);
              setError(null);
              window.history.replaceState({}, document.title, window.location.pathname);
            })
            .catch((error) => {
              setError(`Error completing sign-in with link: ${error.message}`);
              setIsSigningInWithLink(false);
              setLoading(false);
              window.history.replaceState({}, document.title, window.location.pathname);
            });
        };
        return; // Exit the effect to wait for user input
      }

      signInWithEmailLink(auth, storedEmail, window.location.href)
        .then((result) => {
          window.localStorage.removeItem('emailForSignIn');
          setUser(result.user);
          setIsSigningInWithLink(false);
          setLoading(false);
          setError(null);
          window.history.replaceState({}, document.title, window.location.pathname);
        })
        .catch((error) => {
          setError(`Error completing sign-in with link: ${error.message}`);
          setIsSigningInWithLink(false);
          setLoading(false);
          window.history.replaceState({}, document.title, window.location.pathname);
        });
    }
  }, [auth, isAuthReady]);

  // 3. Fetch conversations for the current user.
  useEffect(() => {
    if (!db || !user || !isAuthReady) return;

    const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
    const unsubscribe = onSnapshot(conversationsCollectionRef, (querySnapshot) => {
      const fetchedConversations = [];
      querySnapshot.forEach((doc) => {
        fetchedConversations.push({ id: doc.id, ...doc.data() });
      });

      fetchedConversations.sort((a, b) => (b.lastUpdated?.seconds || 0) - (a.lastUpdated?.seconds || 0));

      setConversations(fetchedConversations);

      if (!currentConversationId && fetchedConversations.length > 0) {
        setCurrentConversationId(fetchedConversations[0].id);
      } else if (fetchedConversations.length === 0) {
        setCurrentConversationId(null);
        setMessages([]);
      }
    }, (firestoreError) => {
      setError(`Failed to load conversations: ${firestoreError.message}`);
      console.error("Firestore conversations error:", firestoreError);
    });

    return () => unsubscribe();
  }, [db, user, isAuthReady, appId, currentConversationId]);

  // 4. Fetch messages for the current conversation.
  useEffect(() => {
    if (!db || !user || !currentConversationId) {
      setMessages([]); // Clear messages if no conversation is selected
      return;
    }

    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
    const unsubscribe = onSnapshot(messagesCollectionRef, (querySnapshot) => {
      const fetchedMessages = [];
      querySnapshot.forEach((doc) => {
        fetchedMessages.push({ id: doc.id, ...doc.data() });
      });

      fetchedMessages.sort((a, b) => (a.timestamp?.seconds || 0) - (b.timestamp?.seconds || 0));

      setMessages(fetchedMessages);
    }, (firestoreError) => {
      setError(`Failed to load messages for conversation: ${firestoreError.message}`);
      console.error("Firestore messages error:", firestoreError);
    });

    return () => unsubscribe();
  }, [db, user, currentConversationId, appId]);

  // 5. Start a new conversation.
  const startNewChat = () => {
    setCurrentConversationId(null);
    setMessages([]);
    setNewMessage('');
    setSelectedImage(null);
    setError(null);
    setIsAIGenerating(false);
    setIsSummarizing(false);
    setIsGeneratingImage(false);
    setIsSidebarOpen(false);
  };

  // 6. Handle sending a new message (and creating a new conversation if needed).
  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() && !selectedImage || !user || !db || isAIGenerating || isSpeaking || isRecording) return;

    let conversationIdToUse = currentConversationId;
    const userMessageText = newMessage;
    const imageToSend = selectedImage;
    setNewMessage('');
    setSelectedImage(null);

    // If no conversation is active, create a new one.
    if (!conversationIdToUse) {
      const newConvId = generateId();
      const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
      const newConversationDocRef = doc(conversationsCollectionRef, newConvId);
      const initialTitle = userMessageText ? (userMessageText.substring(0, 50) + (userMessageText.length > 50 ? '...' : '')) : 'New chat with image';

      try {
        await setDoc(newConversationDocRef, {
          title: initialTitle,
          createdAt: serverTimestamp(),
          lastUpdated: serverTimestamp(),
        });
        conversationIdToUse = newConvId;
        setCurrentConversationId(newConvId);
      } catch (createError) {
        setError(`Failed to create new conversation: ${createError.message}`);
        console.error("Error creating new conversation:", createError);
        return;
      }
    } else {
      // Otherwise, just update the lastUpdated timestamp of the current conversation.
      const conversationDocRef = doc(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse);
      try {
        await setDoc(conversationDocRef, { lastUpdated: serverTimestamp() }, { merge: true });
      } catch (updateError) {
        console.error("Error updating conversation timestamp:", updateError);
      }
    }

    // Add user message to Firestore
    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse, 'messages');
      await addDoc(messagesCollectionRef, {
        text: userMessageText,
        userId: user.uid,
        userName: `User_${user.uid.substring(0, 5)}`,
        timestamp: serverTimestamp(),
        type: 'user-message',
        imageData: imageToSend
      });
    } catch (e) {
      setError(`Failed to send message: ${e.message}`);
      return;
    }

    const command = userMessageText.toLowerCase().trim();

    if (imageToSend) {
      handleImageAnalysis(userMessageText, imageToSend, conversationIdToUse);
    } else if (command === 'summarize') {
      handleSummarizeChat(conversationIdToUse);
    } else if (command.startsWith('image of')) {
      const imagePrompt = userMessageText.substring('image of'.length).trim();
      handleGenerateImage(imagePrompt, conversationIdToUse);
    } else {
      handleAIResponse(userMessageText, conversationIdToUse);
    }
  };

  // Function to handle image upload
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };


  // 7. Handle sending the sign-in email link.
  const handleSendSignInLink = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim()) {
      setError("Please enter a valid email address.");
      return;
    }
    setLoading(true);
    setError(null);
    const actionCodeSettings = {
      url: window.location.origin,
      handleCodeInApp: true,
    };
    try {
      await sendSignInLinkToEmail(auth, email, actionCodeSettings);
      setEmailSent(true);
      window.localStorage.setItem('emailForSignIn', email);
      setLoading(false);
    } catch (error) {
      setError(`Failed to send sign-in link: ${error.message}`);
      setLoading(false);
    }
  };

  // 8. Handle Email/Password Registration
  const handleEmailPasswordSignUp = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim() || !password.trim()) {
      setError("Please enter a valid email and password.");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
      setLoading(false);
    } catch (error) {
      setError(`Registration failed: ${error.message}`);
      setLoading(false);
    }
  };

  // 9. Handle Email/Password Login
  const handleEmailPasswordLogin = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim() || !password.trim()) {
      setError("Please enter a valid email and password.");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      await signInWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
      setLoading(false);
    } catch (error) {
      setError(`Login failed: ${error.message}`);
      setLoading(false);
    }
  };

  // 10. Handle Logout
  const handleLogout = async () => {
    if (!auth) return;
    try {
      await signOut(auth);
      setUser(null);
      setMessages([]);
      setConversations([]);
      setCurrentConversationId(null);
      setEmailSent(false);
      setIsSigningInWithLink(false);
      setAuthMode('email-link');
      setError(null);
    } catch (error) {
      setError(`Logout failed: ${error.message}`);
    }
  };

  // 11. Handle summarization of the current chat using the Gemini API
  const handleSummarizeChat = async (conversationId) => {
    if (!conversationId || messages.length === 0 || isAIGenerating || isSummarizing || isGeneratingImage) return;
    setIsSummarizing(true);
    setError(null);
    try {
      const chatHistoryText = messages
        .filter(msg => msg.type === 'user-message' || msg.type === 'ai-response')
        .map(msg => `${msg.userId === user.uid ? 'User' : 'AI'}: ${msg.text}`)
        .join('\n');
      const prompt = `Please summarize the following chat conversation into a concise paragraph:\n\n${chatHistoryText}`;
      const chatHistory = [{ role: "user", parts: [{ text: prompt }] }];
      const payload = {
        contents: chatHistory,
        generationConfig: {
          temperature: generationSettings.temperature,
          topK: generationSettings.topK,
          topP: generationSettings.topP,
        },
      };
      if (systemInstruction) {
        payload.systemInstruction = { parts: [{ text: systemInstruction }] };
      }
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      let responseText = "Sorry, I couldn't generate a summary.";
      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        responseText = result.candidates[0].content.parts[0].text;
      }
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
      await addDoc(messagesCollectionRef, {
        text: `**Chat Summary:**\n\n${responseText}`,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response'
      });
    } catch (error) {
      console.error("Error summarizing chat:", error);
      setError(`Summarization error: ${error.message}`);
    } finally {
      setIsSummarizing(false);
    }
  };

  // 12. Handle image generation using the Gemini API
  const handleGenerateImage = async (imagePrompt, conversationId) => {
    if (!conversationId || isAIGenerating || isSummarizing || isGeneratingImage) return;
    setIsGeneratingImage(true);
    setError(null);
    let tempImageMessageRef = null;
    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
      tempImageMessageRef = doc(messagesCollectionRef); // Add a placeholder message immediately
      await setDoc(tempImageMessageRef, {
        text: `Generating an image for: "${imagePrompt}"...`,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response',
        imageUrl: '',
      });
      const payload = { instances: { prompt: imagePrompt }, parameters: { "sampleCount": 1} };
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/imagen-3.0-generate-002:predict?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      if (result.predictions && result.predictions.length > 0 && result.predictions[0].bytesBase64Encoded) {
        const imageUrl = `data:image/png;base64,${result.predictions[0].bytesBase64Encoded}`;
        await setDoc(tempImageMessageRef, {
          text: `Here is the image you requested for "${imagePrompt}":`,
          imageUrl: imageUrl,
          type: 'ai-response',
          timestamp: serverTimestamp(), // Update timestamp
          userId: 'bot',
          userName: 'AI Chatbot',
        });
      } else {
        await setDoc(tempImageMessageRef, {
          text: "Sorry, I couldn't generate an image. Please try again with a different prompt.",
          imageUrl: null,
          type: 'ai-response',
          timestamp: serverTimestamp(),
          userId: 'bot',
          userName: 'AI Chatbot',
        });
        throw new Error("Image generation failed with an unexpected response format.");
      }
    } catch (error) {
      console.error("Error generating image:", error);
      if (tempImageMessageRef) {
        await setDoc(tempImageMessageRef, {
          text: `An error occurred while generating the image: ${error.message}`,
          imageUrl: null,
          type: 'ai-response',
          timestamp: serverTimestamp(),
          userId: 'bot',
          userName: 'AI Chatbot',
        });
      }
    } finally {
      setIsGeneratingImage(false);
    }
  };

  // 13. Handle text-based AI response using the Gemini API
  const handleAIResponse = async (userMessage, conversationId) => {
    if (!conversationId || !userMessage || isAIGenerating || isSummarizing || isGeneratingImage) return;
    setIsAIGenerating(true);
    setError(null);
    let tempMessageRef = null;

    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
      tempMessageRef = doc(messagesCollectionRef); // Create a document reference for the placeholder

      await setDoc(tempMessageRef, {
        text: '...',
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response'
      });

      const chatHistory = messages.slice(-10).map(msg => ({
        role: msg.userId === user.uid ? "user" : "model",
        parts: [{ text: msg.text }]
      }));
      chatHistory.push({ role: "user", parts: [{ text: userMessage }] });

      const payload = {
        contents: chatHistory,
        generationConfig: {
          temperature: generationSettings.temperature,
          topK: generationSettings.topK,
          topP: generationSettings.topP,
        },
      };
      if (systemInstruction) {
        payload.systemInstruction = { parts: [{ text: systemInstruction }] };
      }

      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      let responseText = "Sorry, I couldn't get a response. Please try again.";

      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        responseText = result.candidates[0].content.parts[0].text;
      }

      await setDoc(tempMessageRef, {
        text: responseText,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response'
      });

    } catch (e) {
      console.error("Error generating AI response:", e);
      setError(`AI response error: ${e.message}`);
      if (tempMessageRef) {
        await setDoc(tempMessageRef, {
          text: `An error occurred: ${e.message}`,
          userId: 'bot',
          userName: 'AI Chatbot',
          timestamp: serverTimestamp(),
          type: 'ai-response'
        });
      }
    } finally {
      setIsAIGenerating(false);
    }
  };

  // 14. Handle image analysis with the Gemini API
  const handleImageAnalysis = async (userMessage, imageData, conversationId) => {
    if (!conversationId || !imageData || isAIGenerating || isSummarizing || isGeneratingImage) return;
    setIsAIGenerating(true);
    setError(null);
    let tempMessageRef = null;

    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationId, 'messages');
      tempMessageRef = doc(messagesCollectionRef);

      await setDoc(tempMessageRef, {
        text: '...',
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response'
      });

      const base64ImageData = imageData.split(',')[1];
      const payload = {
        contents: [
          {
            role: "user",
            parts: [
              { text: userMessage },
              {
                inlineData: {
                  mimeType: "image/png",
                  data: base64ImageData
                }
              }
            ]
          }
        ],
        generationConfig: {
          temperature: generationSettings.temperature,
          topK: generationSettings.topK,
          topP: generationSettings.topP,
        },
      };
      if (systemInstruction) {
        payload.systemInstruction = { parts: [{ text: systemInstruction }] };
      }

      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      let responseText = "Sorry, I couldn't analyze the image. Please try again.";

      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        responseText = result.candidates[0].content.parts[0].text;
      }

      await setDoc(tempMessageRef, {
        text: responseText,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response'
      });
    } catch (e) {
      console.error("Error analyzing image:", e);
      setError(`Image analysis error: ${e.message}`);
      if (tempMessageRef) {
        await setDoc(tempMessageRef, {
          text: `An error occurred while analyzing the image: ${e.message}`,
          userId: 'bot',
          userName: 'AI Chatbot',
          timestamp: serverTimestamp(),
          type: 'ai-response'
        });
      }
    } finally {
      setIsAIGenerating(false);
    }
  };


  // 15. Handle Text-to-Speech
  const handleTextToSpeech = async (text, voice = 'Kore') => {
    if (isSpeaking) {
      if (currentAudio) {
        currentAudio.pause();
        currentAudio.currentTime = 0;
      }
      setIsSpeaking(false);
      return;
    }

    setIsSpeaking(true);
    setError(null);
    setCurrentAudio(null);

    const payload = {
      contents: [{ parts: [{ text }] }],
      generationConfig: {
        responseModalities: ["AUDIO"],
        speechConfig: {
          voiceConfig: {
            prebuiltVoiceConfig: { voiceName: voice }
          }
        }
      },
      model: "gemini-2.5-flash-preview-tts"
    };

    const apiKey = "";
    const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-tts:generateContent?key=${apiKey}`;

    try {
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });

      const result = await response.json();
      const part = result?.candidates?.[0]?.content?.parts?.[0];
      const audioData = part?.inlineData?.data;
      const mimeType = part?.inlineData?.mimeType;

      if (audioData && mimeType && mimeType.startsWith("audio/")) {
        const sampleRate = parseInt(mimeType.match(/rate=(\d+)/)[1], 10);
        const pcmData = base64ToArrayBuffer(audioData);
        const wavBlob = pcmToWav(pcmData, sampleRate);
        const audioUrl = URL.createObjectURL(wavBlob);
        
        const newAudio = new Audio(audioUrl);
        newAudio.onended = () => {
          setIsSpeaking(false);
        };
        newAudio.play();
        setCurrentAudio(newAudio);
      } else {
        throw new Error("Invalid audio response from API.");
      }
    } catch (e) {
      console.error("Error with Text-to-Speech:", e);
      setError(`TTS error: ${e.message}`);
      setIsSpeaking(false);
    }
  };

  // 16. Handle Speech-to-Text
  const handleStartRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      audioContextRef.current = new AudioContext();
      mediaRecorderRef.current = new MediaRecorder(stream);
      audioChunksRef.current = [];
      mediaRecorderRef.current.ondataavailable = event => {
        audioChunksRef.current.push(event.data);
      };
      mediaRecorderRef.current.onstop = async () => {
        const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
        const reader = new FileReader();
        reader.onloadend = async () => {
          const base64Audio = reader.result.split(',')[1];
          await handleSpeechToText(base64Audio);
        };
        reader.readAsDataURL(audioBlob);
        stream.getTracks().forEach(track => track.stop());
      };
      mediaRecorderRef.current.start();
      setIsRecording(true);
    } catch (e) {
      console.error("Error starting audio recording:", e);
      setError(`Recording error: ${e.message}. Please allow microphone access.`);
    }
  };

  const handleStopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
    }
  };

  const handleSpeechToText = async (base64Audio) => {
    setIsAIGenerating(true);
    setError(null);

    try {
      const payload = {
        contents: [
          {
            role: "user",
            parts: [
              { text: "Transcribe the following audio." },
              {
                inlineData: {
                  mimeType: "audio/webm",
                  data: base64Audio
                }
              }
            ]
          }
        ],
      };
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      let transcribedText = "Could not transcribe audio. Please try again.";

      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        transcribedText = result.candidates[0].content.parts[0].text;
      }
      setNewMessage(transcribedText);
    } catch (e) {
      console.error("Error with Speech-to-Text:", e);
      setError(`STT error: ${e.message}`);
    } finally {
      setIsAIGenerating(false);
    }
  };

  // Chat message component to handle different message types and actions
  const ChatMessage = ({ message }) => {
    const isUser = message.userId === user?.uid;
    const isBot = message.userId === 'bot';

    const renderMessageContent = () => {
      if (message.imageUrl) {
        return (
          <div className="flex flex-col items-center">
            <img src={message.imageUrl} alt="Generated" className="rounded-lg max-w-full h-auto mb-4" />
            <span className="text-sm italic text-gray-500">{message.text}</span>
          </div>
        );
      }
      return <MarkdownRenderer markdownText={message.text} />;
    };

    return (
      <div className={`flex items-start mb-4 ${isUser ? 'justify-end' : 'justify-start'}`}>
        <div className={`relative flex items-end ${isUser ? 'flex-row-reverse' : 'flex-row'}`}>
          <div className={`flex items-center justify-center w-8 h-8 rounded-full flex-shrink-0 ${isUser ? 'bg-indigo-500 text-white ml-2' : 'bg-gray-200 text-gray-800 mr-2'}`}>
            {isUser ? <User size={16} /> : <Bot size={16} />}
          </div>
          <div className={`flex flex-col p-4 rounded-xl max-w-2xl shadow-sm ${isUser ? 'bg-indigo-500 text-white rounded-tr-none' : 'bg-gray-100 text-gray-800 rounded-tl-none'}`}>
            {renderMessageContent()}
            {isBot && message.text && message.text.length > 0 && (
              <button
                onClick={() => handleTextToSpeech(message.text, voiceName)}
                className="mt-2 p-1 self-end text-sm text-gray-500 bg-gray-200 rounded-full hover:bg-gray-300 focus:outline-none"
              >
                {isSpeaking && currentAudio ? <Pause size={16} /> : <Volume2 size={16} />}
              </button>
            )}
          </div>
        </div>
      </div>
    );
  };

  // Settings modal component
  const SettingsModal = ({ show, onClose }) => {
    if (!show) return null;

    const handleSave = (e) => {
      e.preventDefault();
      onClose();
    };

    return createPortal(
      <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50">
        <div className="relative bg-white dark:bg-gray-800 rounded-lg shadow-xl max-w-lg w-full p-8 m-4">
          <div className="flex justify-between items-center pb-3 border-b border-gray-200 dark:border-gray-700">
            <h3 className="text-xl font-bold text-gray-900 dark:text-white">AI Generation Settings</h3>
            <button onClick={onClose} className="p-1 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700 text-gray-500 dark:text-gray-400">
              <X size={20} />
            </button>
          </div>
          <form onSubmit={handleSave} className="mt-4 space-y-4">
            <div>
              <label htmlFor="systemInstruction" className="block text-sm font-medium text-gray-700 dark:text-gray-300">System Instruction</label>
              <textarea
                id="systemInstruction"
                value={systemInstruction}
                onChange={(e) => setSystemInstruction(e.target.value)}
                rows="4"
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 dark:bg-gray-700 dark:border-gray-600 dark:text-white"
                placeholder="e.g., You are a helpful assistant that responds in a pirate voice."
              ></textarea>
            </div>
            <div>
              <label htmlFor="temperature" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Temperature: {generationSettings.temperature}</label>
              <input
                type="range"
                id="temperature"
                min="0"
                max="1"
                step="0.05"
                value={generationSettings.temperature}
                onChange={(e) => setGenerationSettings({...generationSettings, temperature: parseFloat(e.target.value)})}
                className="mt-1 w-full"
              />
              <p className="text-xs text-gray-500 dark:text-gray-400">Controls randomness. Lower values produce more predictable results.</p>
            </div>
            <div>
              <label htmlFor="topP" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Top P: {generationSettings.topP}</label>
              <input
                type="range"
                id="topP"
                min="0"
                max="1"
                step="0.05"
                value={generationSettings.topP}
                onChange={(e) => setGenerationSettings({...generationSettings, topP: parseFloat(e.target.value)})}
                className="mt-1 w-full"
              />
              <p className="text-xs text-gray-500 dark:text-gray-400">Controls nucleus sampling. A value of 0.95 means the model considers the top 95% of the probability mass.</p>
            </div>
            <div>
              <label htmlFor="topK" className="block text-sm font-medium text-gray-700 dark:text-gray-300">Top K: {generationSettings.topK}</label>
              <input
                type="range"
                id="topK"
                min="1"
                max="100"
                step="1"
                value={generationSettings.topK}
                onChange={(e) => setGenerationSettings({...generationSettings, topK: parseInt(e.target.value)})}
                className="mt-1 w-full"
              />
              <p className="text-xs text-gray-500 dark:text-gray-400">Controls token consideration. A value of 40 means the model considers the 40 most likely next tokens.</p>
            </div>
            <div className="flex justify-end pt-4">
              <button
                type="submit"
                className="bg-indigo-600 text-white font-semibold py-2 px-4 rounded-lg shadow-md hover:bg-indigo-700 transition-colors duration-200"
              >
                Save Settings
              </button>
            </div>
          </form>
        </div>
      </div>,
      document.body
    );
  };


  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900">
        <div className="flex flex-col items-center space-y-4">
          <LoaderCircle className="h-12 w-12 text-indigo-500 animate-spin" />
          <p className="text-gray-600 dark:text-gray-400">Loading...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 dark:bg-gray-900 p-4">
        <div className="bg-red-100 dark:bg-red-900 border border-red-400 text-red-700 dark:text-red-300 px-4 py-3 rounded-lg shadow-md max-w-xl text-center">
          <p className="font-bold">An error occurred!</p>
          <p className="text-sm mt-2">{error}</p>
          <p className="text-xs mt-4">If the problem persists, please check your Firebase settings or refresh the page.</p>
        </div>
      </div>
    );
  }

  // Main application view
  return (
    <div className="flex flex-col h-screen antialiased bg-gray-50 dark:bg-gray-900 text-gray-800 dark:text-gray-200 font-inter">

      {/* Settings Modal */}
      <SettingsModal show={showSettingsModal} onClose={() => setShowSettingsModal(false)} />

      {/* Sidebar and Main Content */}
      <div className="flex-1 flex overflow-hidden">
        {/* Sidebar */}
        <div className={`transition-all duration-300 ease-in-out ${isSidebarOpen ? 'w-64' : 'w-0'} overflow-x-hidden border-r dark:border-gray-700 flex flex-col bg-gray-100 dark:bg-gray-800`}>
          <div className="flex items-center justify-between p-4 border-b dark:border-gray-700 flex-shrink-0">
            <h1 className="text-lg font-bold">Chats</h1>
            <button
              onClick={startNewChat}
              className="p-1 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700 text-gray-600 dark:text-gray-400"
              title="Start a new chat"
            >
              <Plus size={20} />
            </button>
          </div>
          <div className="flex-1 overflow-y-auto p-2">
            {conversations.map((conv) => (
              <div
                key={conv.id}
                onClick={() => setCurrentConversationId(conv.id)}
                className={`p-3 rounded-lg cursor-pointer transition-colors duration-200 mb-2 ${currentConversationId === conv.id ? 'bg-indigo-500 text-white' : 'hover:bg-gray-200 dark:hover:bg-gray-700'}`}
              >
                <p className="text-sm truncate">{conv.title}</p>
                <p className="text-xs text-gray-400 dark:text-gray-500 mt-1">
                  {new Date(conv.lastUpdated?.seconds * 1000).toLocaleString()}
                </p>
              </div>
            ))}
          </div>
          {user && (
            <div className="p-4 border-t dark:border-gray-700 flex-shrink-0">
              <p className="text-xs text-gray-500 dark:text-gray-400 mb-2">User ID: {user.uid}</p>
              <button
                onClick={handleLogout}
                className="w-full text-center py-2 px-4 bg-red-500 text-white rounded-lg shadow-md hover:bg-red-600 transition-colors duration-200"
              >
                Logout
              </button>
            </div>
          )}
        </div>

        {/* Main Chat Area */}
        <div className="flex-1 flex flex-col bg-white dark:bg-gray-900">
          {/* Header */}
          <div className="flex items-center p-4 border-b dark:border-gray-800 bg-gray-50 dark:bg-gray-900 flex-shrink-0">
            <button onClick={() => setIsSidebarOpen(!isSidebarOpen)} className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-800 mr-2 lg:hidden">
              <Menu size={20} />
            </button>
            <h2 className="text-lg font-bold flex-1">
              {currentConversationId ? (conversations.find(c => c.id === currentConversationId)?.title || '...loading') : 'New Chat'}
            </h2>
            <div className="flex items-center space-x-2">
              <button onClick={() => setShowSettingsModal(true)} className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-800" title="Settings">
                <Settings size={20} />
              </button>
              {/* Other header actions can go here */}
            </div>
          </div>

          {/* Chat Messages */}
          <div className="flex-1 overflow-y-auto p-4 space-y-4">
            {messages.length === 0 && (
              <div className="flex flex-col items-center justify-center h-full text-center text-gray-400 dark:text-gray-600">
                <Sparkles size={48} className="mb-4" />
                <p className="text-lg">Start a new conversation</p>
              </div>
            )}
            {messages.map(msg => (
              <ChatMessage key={msg.id} message={msg} />
            ))}
            {(isAIGenerating || isSummarizing || isGeneratingImage) && (
              <div className="flex justify-start">
                <div className="relative flex items-end">
                  <div className="flex items-center justify-center w-8 h-8 rounded-full bg-gray-200 text-gray-800 mr-2 flex-shrink-0">
                    <Bot size={16} />
                  </div>
                  <div className="p-4 rounded-xl rounded-tl-none bg-gray-100 dark:bg-gray-800 shadow-sm">
                    <div className="dot-flashing" />
                  </div>
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>

          {/* Chat Input */}
          <form onSubmit={handleSendMessage} className="p-4 border-t dark:border-gray-800 flex-shrink-0 bg-gray-50 dark:bg-gray-900">
            {selectedImage && (
              <div className="relative mb-4">
                <img src={selectedImage} alt="Preview" className="h-32 rounded-lg object-cover" />
                <button
                  type="button"
                  onClick={() => setSelectedImage(null)}
                  className="absolute top-2 right-2 p-1 bg-black bg-opacity-50 text-white rounded-full hover:bg-opacity-75"
                >
                  <X size={16} />
                </button>
              </div>
            )}
            <div className="flex items-center space-x-2">
              <label htmlFor="image-upload" className="p-2 cursor-pointer rounded-full hover:bg-gray-200 dark:hover:bg-gray-800">
                <Image size={20} className="text-gray-500 dark:text-gray-400" />
                <input id="image-upload" type="file" accept="image/*" className="hidden" onChange={handleImageUpload} />
              </label>
              <button
                type="button"
                onClick={isRecording ? handleStopRecording : handleStartRecording}
                className={`p-2 rounded-full transition-colors duration-200 ${isRecording ? 'bg-red-500 text-white' : 'hover:bg-gray-200 dark:hover:bg-gray-800 text-gray-500 dark:text-gray-400'}`}
                title={isRecording ? 'Stop Recording' : 'Start Recording'}
              >
                <Mic size={20} />
              </button>
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder={isRecording ? 'Listening...' : 'Type a message...'}
                className="flex-1 p-3 rounded-xl border border-gray-300 dark:border-gray-700 bg-white dark:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors duration-200"
                disabled={isAIGenerating || isRecording || isSpeaking}
              />
              <button
                type="submit"
                className={`p-3 rounded-xl transition-colors duration-200 font-semibold ${
                  (isAIGenerating || isRecording || isSpeaking) ? 'bg-gray-300 dark:bg-gray-700 text-gray-500 cursor-not-allowed' : 'bg-indigo-500 text-white hover:bg-indigo-600'
                }`}
                disabled={isAIGenerating || isRecording || isSpeaking}
              >
                Send
              </button>
            </div>
          </form>
        </div>

        {/* Login Modal */}
        {!user && (
          <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50">
            <div className="bg-white dark:bg-gray-800 p-8 rounded-lg shadow-lg max-w-sm w-full">
              <h2 className="text-2xl font-bold mb-6 text-center text-gray-900 dark:text-white">Sign In</h2>
              {authMode === 'email-link' && (
                <>
                  <p className="text-sm text-gray-600 dark:text-gray-400 mb-4">
                    Enter your email to receive a sign-in link.
                  </p>
                  <form onSubmit={handleSendSignInLink} className="space-y-4">
                    <div>
                      <input
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        className="w-full p-3 border border-gray-300 dark:border-gray-700 rounded-lg bg-gray-100 dark:bg-gray-700 text-gray-900 dark:text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
                        required
                      />
                    </div>
                    <button
                      type="submit"
                      className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200"
                      disabled={loading}
                    >
                      {loading ? <LoaderCircle className="animate-spin h-5 w-5 mx-auto" /> : 'Send Sign-in Link'}
                    </button>
                  </form>
                  {emailSent && (
                    <div className="mt-4 p-3 bg-green-100 dark:bg-green-900 text-green-700 dark:text-green-300 rounded-lg">
                      Sign-in link sent to your email! Please check your inbox.
                    </div>
                  )}
                  <p className="mt-4 text-center text-sm text-gray-600 dark:text-gray-400">
                    Or <button type="button" onClick={() => setAuthMode('email-password')} className="text-indigo-500 hover:underline">sign in with password</button>
                  </p>
                </>
              )}
              {authMode === 'email-password' && (
                <>
                  <form onSubmit={handleEmailPasswordLogin} className="space-y-4">
                    <div>
                      <input
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        className="w-full p-3 border border-gray-300 dark:border-gray-700 rounded-lg bg-gray-100 dark:bg-gray-700 text-gray-900 dark:text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
                        required
                      />
                    </div>
                    <div>
                      <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        className="w-full p-3 border border-gray-300 dark:border-gray-700 rounded-lg bg-gray-100 dark:bg-gray-700 text-gray-900 dark:text-white focus:outline-none focus:ring-2 focus:ring-indigo-500"
                        required
                      />
                    </div>
                    <button
                      type="submit"
                      className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200"
                      disabled={loading}
                    >
                      {loading ? <LoaderCircle className="animate-spin h-5 w-5 mx-auto" /> : 'Sign In'}
                    </button>
                  </form>
                  <p className="mt-4 text-center text-sm text-gray-600 dark:text-gray-400">
                    Don't have an account? <button type="button" onClick={handleEmailPasswordSignUp} className="text-indigo-500 hover:underline">Sign up</button>
                  </p>
                  <p className="mt-2 text-center text-sm text-gray-600 dark:text-gray-400">
                    Or <button type="button" onClick={() => setAuthMode('email-link')} className="text-indigo-500 hover:underline">use a sign-in link</button>
                  </p>
                </>
              )}
            </div>
          </div>
        )}
      </div>

      {/* Dot Flashing CSS Animation */}
      <style>{`
        .dot-flashing {
          position: relative;
          width: 5px;
          height: 5px;
          border-radius: 5px;
          background-color: #a3a3a3;
          color: #a3a3a3;
          animation: dotFlashing 1s infinite linear alternate;
          animation-delay: 0.5s;
        }

        .dot-flashing::before, .dot-flashing::after {
          content: '';
          display: inline-block;
          position: absolute;
          top: 0;
        }

        .dot-flashing::before {
          left: -10px;
          width: 5px;
          height: 5px;
          border-radius: 5px;
          background-color: #a3a3a3;
          color: #a3a3a3;
          animation: dotFlashing 1s infinite alternate;
          animation-delay: 0s;
        }

        .dot-flashing::after {
          left: 10px;
          width: 5px;
          height: 5px;
          border-radius: 5px;
          background-color: #a3a3a3;
          color: #a3a3a3;
          animation: dotFlashing 1s infinite alternate;
          animation-delay: 1s;
        }

        @keyframes dotFlashing {
          0% {
            background-color: #a3a3a3;
          }
          50%,
          100% {
            background-color: #e6e6e6;
          }
        }
      `}</style>
    </div>
  );
}

export default App;
