/* global __firebase_config, __app_id, __initial_auth_token */

import { useState, useEffect, useRef } from 'react';
import { initializeApp } from 'firebase/app';
import {
  getAuth,
  signInWithCustomToken,
  signInAnonymously,
  onAuthStateChanged,
  sendSignInLinkToEmail,
  isSignInWithEmailLink,
  signInWithEmailLink,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut
} from 'firebase/auth';
import {
  getFirestore,
  collection,
  onSnapshot,
  addDoc,
  serverTimestamp,
  doc,
  setDoc,
  query,
  // Removed orderBy here because of potential runtime errors with indexes.
  limit,
  getDocs,
  getDoc
} from 'firebase/firestore';

// --- Firebase Configuration ---
const localFirebaseConfig = {
  apiKey: "AIzaSyDhJoNaFf0qgl4VTuUXj16ysJG1hycq-p8",
  authDomain: "ai-chatbot-88602.firebaseapp.com",
  projectId: "ai-chatbot-88602",
  storageBucket: "ai-chatbot-88602.firebasestorage.app",
  messagingSenderId: "167580287666",
  appId: "1:167580287666:web:9e8313ae6e667e47d47fab",
  measurementId: "G-CXKJ8QRLZ1"
};

const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : localFirebaseConfig;
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

// Utility function for generating unique IDs (like conversation IDs)
const generateId = () => {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0, v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
};

function App() {
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailSent, setEmailSent] = useState(false);
  const [isSigningInWithLink, setIsSigningInWithLink] = useState(false);
  const [isAIGenerating, setIsAIGenerating] = useState(false);
  const [authMode, setAuthMode] = useState('email-link');

  // New states for conversation history and sidebar
  const [conversations, setConversations] = useState([]);
  const [currentConversationId, setCurrentConversationId] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true); // New state for sidebar
  const [isSummarizing, setIsSummarizing] = useState(false);
  const [isPlayingAudio, setIsPlayingAudio] = useState(false);

  const messagesEndRef = useRef(null); // Ref for scrolling to bottom of messages
  const audioContextRef = useRef(null);

  // Scroll to bottom of messages whenever messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // 1. Initialize Firebase and set up authentication.
  useEffect(() => {
    if (Object.keys(firebaseConfig).length === 0 || !firebaseConfig.apiKey) {
      setError('Firebase configuration is missing or incomplete. Please ensure your Firebase config is correctly set, especially the API Key.');
      setLoading(false);
      return;
    }

    try {
      const firebaseApp = initializeApp(firebaseConfig);
      const authInstance = getAuth(firebaseApp);
      const dbInstance = getFirestore(firebaseApp);

      setAuth(authInstance);
      setDb(dbInstance);

      const unsubscribe = onAuthStateChanged(authInstance, async (currentUser) => {
        if (currentUser) {
          setUser(currentUser);
          setIsAuthReady(true);
          setError(null);
        } else {
          if (initialAuthToken) {
             try {
               await signInWithCustomToken(authInstance, initialAuthToken);
             } catch (e) {
               console.error("Custom token sign-in failed:", e);
               setError(`Custom token sign-in failed: ${e.message}. This usually means the token is invalid or expired. Please try refreshing.`);
             }
          } else if (!isSigningInWithLink) {
            try {
              await signInAnonymously(authInstance);
            } catch (e) {
              console.error("Anonymous sign-in failed:", e);
              setError(`Anonymous sign-in failed: ${e.message}. Please ensure Anonymous Authentication is enabled in your Firebase project settings.`);
            }
          }
          setIsAuthReady(true);
        }
        setLoading(false);
      });
      return () => unsubscribe();
    } catch (e) {
      setError(`Failed to initialize Firebase: ${e.message}. Please check the console for more details.`);
      setLoading(false);
    }
  }, []);

  // 2. Handle email link sign-in.
  useEffect(() => {
    if (!auth || !isAuthReady) return;

    if (isSignInWithEmailLink(auth, window.location.href)) {
      setIsSigningInWithLink(true);
      setLoading(true);

      let storedEmail = window.localStorage.getItem('emailForSignIn');

      if (!storedEmail) {
        // Using a custom modal instead of prompt
        const emailInput = document.createElement('input');
        emailInput.type = 'email';
        emailInput.placeholder = 'Please provide your email for confirmation';
        emailInput.className = 'w-full p-3 border border-gray-300 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-indigo-500';

        const modalDiv = document.createElement('div');
        modalDiv.className = 'fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50';
        modalDiv.innerHTML = `
          <div class="bg-white p-8 rounded-lg shadow-lg max-w-sm w-full">
            <h3 class="text-xl font-bold mb-4">Confirm Email</h3>
            <p class="mb-4">Please provide your email to complete sign-in.</p>
            <input id="email-confirm-input" type="email" placeholder="Enter your email" class="w-full p-3 border border-gray-300 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-indigo-500" required />
            <button id="email-confirm-button" class="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200">Confirm</button>
          </div>
        `;
        document.body.appendChild(modalDiv);

        const emailConfirmInput = modalDiv.querySelector('#email-confirm-input');
        const emailConfirmButton = modalDiv.querySelector('#email-confirm-button');

        emailConfirmButton.onclick = () => {
          storedEmail = emailConfirmInput.value;
          if (storedEmail) {
            document.body.removeChild(modalDiv);
            continueSignIn(storedEmail);
          } else {
            // Replaced alert with console.error as per instructions.
            console.error("Email not provided. Please try again.");
          }
        };

        const continueSignIn = (emailToUse) => {
          signInWithEmailLink(auth, emailToUse, window.location.href)
            .then((result) => {
              window.localStorage.removeItem('emailForSignIn');
              setUser(result.user);
              setIsSigningInWithLink(false);
              setLoading(false);
              setError(null);
              window.history.replaceState({}, document.title, window.location.pathname);
            })
            .catch((error) => {
              setError(`Error completing sign-in with link: ${error.message}`);
              setIsSigningInWithLink(false);
              setLoading(false);
              window.history.replaceState({}, document.title, window.location.pathname);
            });
        };
        return; // Exit the effect to wait for user input
      }

      signInWithEmailLink(auth, storedEmail, window.location.href)
        .then((result) => {
          window.localStorage.removeItem('emailForSignIn');
          setUser(result.user);
          setIsSigningInWithLink(false);
          setLoading(false);
          setError(null);
          window.history.replaceState({}, document.title, window.location.pathname);
        })
        .catch((error) => {
          setError(`Error completing sign-in with link: ${error.message}`);
          setIsSigningInWithLink(false);
          setLoading(false);
          window.history.replaceState({}, document.title, window.location.pathname);
        });
    }
  }, [auth, isAuthReady]);

  // 3. Fetch conversations for the current user.
  useEffect(() => {
    if (!db || !user || !isAuthReady) return;

    const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
    // NOTE: Sorting is now done in memory to avoid index creation requirements on Firestore.
    const unsubscribe = onSnapshot(conversationsCollectionRef, (querySnapshot) => {
      const fetchedConversations = [];
      querySnapshot.forEach((doc) => {
        fetchedConversations.push({ id: doc.id, ...doc.data() });
      });

      // Sort conversations by lastUpdated timestamp in descending order
      fetchedConversations.sort((a, b) => (b.lastUpdated?.seconds || 0) - (a.lastUpdated?.seconds || 0));

      setConversations(fetchedConversations);

      // If no conversation is selected, and there are conversations, select the most recent one
      if (!currentConversationId && fetchedConversations.length > 0) {
        setCurrentConversationId(fetchedConversations[0].id);
      } else if (fetchedConversations.length === 0) {
        // If there are no conversations, clear current conversation
        setCurrentConversationId(null);
        setMessages([]);
      }
    }, (firestoreError) => {
      setError(`Failed to load conversations: ${firestoreError.message}`);
      console.error("Firestore conversations error:", firestoreError);
    });

    return () => unsubscribe();
  }, [db, user, isAuthReady, appId, currentConversationId]); // Added currentConversationId to re-evaluate default selection

  // 4. Fetch messages for the current conversation.
  useEffect(() => {
    if (!db || !user || !currentConversationId) {
      setMessages([]); // Clear messages if no conversation is selected
      return;
    }

    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
    // NOTE: Sorting is now done in memory to avoid index creation requirements on Firestore.
    const unsubscribe = onSnapshot(messagesCollectionRef, (querySnapshot) => {
      const fetchedMessages = [];
      querySnapshot.forEach((doc) => {
        fetchedMessages.push({ id: doc.id, ...doc.data() });
      });

      // Sort messages by timestamp in ascending order
      fetchedMessages.sort((a, b) => (a.timestamp?.seconds || 0) - (b.timestamp?.seconds || 0));

      setMessages(fetchedMessages);
    }, (firestoreError) => {
      setError(`Failed to load messages for conversation: ${firestoreError.message}`);
      console.error("Firestore messages error:", firestoreError);
    });

    return () => unsubscribe();
  }, [db, user, currentConversationId, appId]);

  // 5. Start a new conversation.
  const startNewChat = () => {
    setCurrentConversationId(null); // Clear current conversation
    setMessages([]); // Clear displayed messages
    setNewMessage(''); // Clear input
    setError(null); // Clear any errors
    setIsAIGenerating(false); // Reset AI loading state
    setIsSummarizing(false);
    setIsSidebarOpen(true);
  };

  // 6. Handle sending a new message (and creating a new conversation if needed).
  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !user || !db || isAIGenerating) return;

    let conversationIdToUse = currentConversationId;

    // If no conversation is active, create a new one
    if (!conversationIdToUse) {
      const newConvId = generateId();
      const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
      const newConversationDocRef = doc(conversationsCollectionRef, newConvId);
      const initialTitle = newMessage.substring(0, 50) + (newMessage.length > 50 ? '...' : '');

      try {
        await setDoc(newConversationDocRef, {
          title: initialTitle,
          createdAt: serverTimestamp(),
          lastUpdated: serverTimestamp(),
        });
        conversationIdToUse = newConvId;
        setCurrentConversationId(newConvId); // Set the new conversation as current
      } catch (createError) {
        setError(`Failed to create new conversation: ${createError.message}`);
        console.error("Error creating new conversation:", createError);
        return;
      }
    } else {
      // Update lastUpdated for existing conversation
      const conversationDocRef = doc(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse);
      try {
        await setDoc(conversationDocRef, { lastUpdated: serverTimestamp() }, { merge: true });
      } catch (updateError) {
        console.error("Error updating conversation timestamp:", updateError);
      }
    }

    // Add message to the conversation's messages subcollection
    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse, 'messages');
      await addDoc(messagesCollectionRef, {
        text: newMessage,
        userId: user.uid,
        userName: `User_${user.uid.substring(0, 5)}`,
        timestamp: serverTimestamp(),
        type: 'user-message' // Explicitly mark user messages
      });
      setNewMessage('');
    } catch (e) {
      setError(`Failed to send message: ${e.message}`);
    }
  };

  // 7. Handle sending the sign-in email link.
  const handleSendSignInLink = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim()) {
      setError("Please enter a valid email address.");
      return;
    }
    setLoading(true);
    setError(null);
    const actionCodeSettings = {
      url: window.location.origin,
      handleCodeInApp: true,
      iOS: { bundleId: 'com.example.ios' },
      android: { packageName: 'com.example.android', installApp: true, minimumVersion: '12' }
    };
    try {
      await sendSignInLinkToEmail(auth, email, actionCodeSettings);
      setEmailSent(true);
      window.localStorage.setItem('emailForSignIn', email);
      setLoading(false);
    } catch (error) {
      setError(`Failed to send sign-in link: ${error.message}`);
      setLoading(false);
    }
  };

  // 8. Handle Email/Password Registration
  const handleEmailPasswordSignUp = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim() || !password.trim()) {
      setError("Please enter a valid email and password.");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
      setLoading(false);
    } catch (error) {
      setError(`Registration failed: ${error.message}`);
      setLoading(false);
    }
  };

  // 9. Handle Email/Password Login
  const handleEmailPasswordLogin = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim() || !password.trim()) {
      setError("Please enter a valid email and password.");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      await signInWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
      setLoading(false);
    } catch (error) {
      setError(`Login failed: ${error.message}`);
      setLoading(false);
    }
  };

  // 10. Handle Logout
  const handleLogout = async () => {
    if (!auth) return;
    try {
      await signOut(auth);
      setUser(null);
      setMessages([]);
      setConversations([]); // Clear conversations on logout
      setCurrentConversationId(null); // Clear current conversation
      setEmailSent(false);
      setIsSigningInWithLink(false);
      setAuthMode('email-link');
      setError(null);
    } catch (error) {
      setError(`Logout failed: ${error.message}`);
    }
  };

  // 11. New: Handle summarization of the current chat using the Gemini API
  const handleSummarizeChat = async () => {
    if (!currentConversationId || messages.length === 0 || isAIGenerating || isSummarizing) return;
    
    setIsSummarizing(true);
    setIsAIGenerating(true);
    setError(null);

    try {
      const chatHistoryText = messages
        .map(msg => `${msg.userId === user.uid ? 'User' : 'AI'}: ${msg.text}`)
        .join('\n');
      
      const prompt = `Please summarize the following chat conversation:\n\n${chatHistoryText}`;

      const chatHistory = [{ role: "user", parts: [{ text: prompt }] }];
      const payload = { contents: chatHistory };
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;

      const fetchWithExponentialBackoff = async (url, options, retries = 5, delay = 1000) => {
        try {
          const response = await fetch(url, options);
          if (!response.ok) {
            const errorData = await response.json();
            throw new Error(`API error: ${errorData.error?.message || response.statusText}`);
          }
          return response;
        } catch (error) {
          if (retries > 0 && error.message.includes("API error")) {
            await new Promise(resolve => setTimeout(resolve, delay));
            return fetchWithExponentialBackoff(url, options, retries - 1, delay * 2);
          }
          throw error;
        }
      };

      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });

      const result = await response.json();
      let responseText = "Sorry, I couldn't generate a summary.";

      if (result.candidates && result.candidates.length > 0 &&
          result.candidates[0].content && result.candidates[0].content.parts &&
          result.candidates[0].content.parts.length > 0) {
        responseText = result.candidates[0].content.parts[0].text;
      }
      
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
      await addDoc(messagesCollectionRef, {
        text: responseText,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response'
      });

    } catch (error) {
      console.error("Error summarizing chat:", error);
      setError(`Summarization error: ${error.message}`);
    } finally {
      setIsAIGenerating(false);
      setIsSummarizing(false);
    }
  };

  // 12. New: Handle Text-to-Speech using the Gemini API
  const handleTextToSpeech = async (text) => {
    if (isPlayingAudio) return;

    setIsPlayingAudio(true);
    
    // Convert base64 to ArrayBuffer
    const base64ToArrayBuffer = (base64) => {
        const binaryString = window.atob(base64);
        const len = binaryString.length;
        const bytes = new Uint8Array(len);
        for (let i = 0; i < len; i++) {
            bytes[i] = binaryString.charCodeAt(i);
        }
        return bytes.buffer;
    };

    // Convert PCM to WAV format
    const pcmToWav = (pcmData, sampleRate) => {
        const pcm16 = new Int16Array(pcmData);
        const buffer = new ArrayBuffer(44 + pcm16.length * 2);
        const view = new DataView(buffer);
        const writeString = (view, offset, string) => {
            for (let i = 0; i < string.length; i++) {
                view.setUint8(offset + i, string.charCodeAt(i));
            }
        };

        let offset = 0;
        writeString(view, offset, 'RIFF'); offset += 4;
        view.setUint32(offset, 36 + pcm16.length * 2, true); offset += 4;
        writeString(view, offset, 'WAVE'); offset += 4;
        writeString(view, offset, 'fmt '); offset += 4;
        view.setUint32(offset, 16, true); offset += 4; // Sub-chunk size
        view.setUint16(offset, 1, true); offset += 2; // Audio format (1 = PCM)
        view.setUint16(offset, 1, true); offset += 2; // Number of channels
        view.setUint32(offset, sampleRate, true); offset += 4; // Sample rate
        view.setUint32(offset, sampleRate * 2, true); offset += 4; // Byte rate
        view.setUint16(offset, 2, true); offset += 2; // Block align
        view.setUint16(offset, 16, true); offset += 2; // Bits per sample
        writeString(view, offset, 'data'); offset += 4;
        view.setUint32(offset, pcm16.length * 2, true); offset += 4; // Data size
        
        for (let i = 0; i < pcm16.length; i++) {
            view.setInt16(offset, pcm16[i], true);
            offset += 2;
        }

        return new Blob([view], { type: 'audio/wav' });
    };

    try {
        const payload = {
            contents: [{
                parts: [{ text: text }]
            }],
            generationConfig: {
                responseModalities: ["AUDIO"],
                speechConfig: {
                    voiceConfig: {
                        prebuiltVoiceConfig: { voiceName: "Puck" } // Using the 'Puck' voice
                    }
                }
            },
            model: "gemini-2.5-flash-preview-tts"
        };
        const apiKey = "";
        const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-tts:generateContent?key=${apiKey}`;

        const fetchWithExponentialBackoff = async (url, options, retries = 5, delay = 1000) => {
            try {
                const response = await fetch(url, options);
                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(`API error: ${errorData.error?.message || response.statusText}`);
                }
                return response;
            } catch (error) {
                if (retries > 0 && error.message.includes("API error")) {
                    await new Promise(resolve => setTimeout(resolve, delay));
                    return fetchWithExponentialBackoff(url, options, retries - 1, delay * 2);
                }
                throw error;
            }
        };

        const response = await fetchWithExponentialBackoff(apiUrl, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload)
        });

        const result = await response.json();
        const part = result?.candidates?.[0]?.content?.parts?.[0];
        const audioData = part?.inlineData?.data;
        const mimeType = part?.inlineData?.mimeType;

        if (audioData && mimeType && mimeType.startsWith("audio/")) {
            const sampleRate = parseInt(mimeType.match(/rate=(\d+)/)[1], 10);
            const pcmData = base64ToArrayBuffer(audioData);
            const wavBlob = pcmToWav(pcmData, sampleRate);
            const audioUrl = URL.createObjectURL(wavBlob);
            const audio = new Audio(audioUrl);
            audio.play();

            audio.onended = () => {
                URL.revokeObjectURL(audioUrl);
                setIsPlayingAudio(false);
            };
        } else {
            console.error("Unexpected API response structure for TTS:", result);
            setError("Failed to get audio data from API.");
            setIsPlayingAudio(false);
        }
    } catch (error) {
        console.error("Error generating TTS:", error);
        setError(`TTS error: ${error.message}`);
        setIsPlayingAudio(false);
    }
};

  // 13. AI Response Logic - New useEffect to trigger AI response
  useEffect(() => {
    if (!db || !isAuthReady || !user || messages.length === 0 || isAIGenerating || !currentConversationId) {
      return;
    }

    const lastMessage = messages[messages.length - 1];

    if (lastMessage.userId === user.uid && lastMessage.type === 'user-message') {
      const getAIResponse = async () => {
        setIsAIGenerating(true);
        setError(null);

        try {
          // Filter chat history to only include user and AI messages for the model
          const chatHistory = messages
            .filter(msg => msg.type === 'user-message' || msg.type === 'ai-response')
            .map(msg => ({
              role: msg.userId === user.uid ? 'user' : 'model',
              parts: [{ text: msg.text }]
            }));

          const payload = { contents: chatHistory };
          // NOTE: The API key is set to an empty string to allow Canvas to provide it at runtime.
          const apiKey = "";
          const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;

          const fetchWithExponentialBackoff = async (url, options, retries = 5, delay = 1000) => {
            try {
              const response = await fetch(url, options);
              if (!response.ok) {
                const errorData = await response.json();
                throw new Error(`API error: ${errorData.error?.message || response.statusText}`);
              }
              return response;
            } catch (error) {
              if (retries > 0 && error.message.includes("API error")) {
                await new Promise(resolve => setTimeout(resolve, delay));
                return fetchWithExponentialBackoff(url, options, retries - 1, delay * 2);
              }
              throw error;
            }
          };

          const response = await fetchWithExponentialBackoff(apiUrl, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload)
          });

          const result = await response.json();
          let responseText = "Sorry, I couldn't generate a response.";

          if (result.candidates && result.candidates.length > 0 &&
              result.candidates[0].content && result.candidates[0].content.parts &&
              result.candidates[0].content.parts.length > 0) {
            responseText = result.candidates[0].content.parts[0].text;
          }

          const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
          await addDoc(messagesCollectionRef, {
            text: responseText,
            userId: 'bot',
            userName: 'AI Chatbot',
            timestamp: serverTimestamp(),
            type: 'ai-response'
          });

        } catch (error) {
          console.error("Error getting AI response:", error);
          setError(`AI response error: ${error.message}`);
          const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
          await addDoc(messagesCollectionRef, {
            text: `(AI Error: ${error.message})`,
            userId: 'bot',
            userName: 'AI Chatbot',
            timestamp: serverTimestamp(),
            type: 'ai-response'
          });
        } finally {
          setIsAIGenerating(false);
        }
      };
      getAIResponse();
    }
  }, [messages, db, isAuthReady, user, appId, isAIGenerating, currentConversationId]);


  return (
    <div className="min-h-screen bg-gray-100 flex font-sans">
      <div className="w-full max-w-full bg-white rounded-xl shadow-xl overflow-hidden flex h-screen">

        {/* Sidebar */}
        {user && (
          <div className={`flex flex-col bg-gray-800 text-white rounded-none transition-all duration-300 ease-in-out ${isSidebarOpen ? 'w-1/4 p-4' : 'w-16 p-2'}`}>
            <div className="flex items-center justify-between mb-4">
              {isSidebarOpen && <h3 className="text-xl font-bold">Conversations</h3>}
              <button
                onClick={() => setIsSidebarOpen(!isSidebarOpen)}
                className="p-2 rounded-full hover:bg-gray-700 transition-colors"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 transform transition-transform" fill="none" viewBox="0 0 24 24" stroke="currentColor" style={{ transform: isSidebarOpen ? 'rotate(0deg)' : 'rotate(180deg)' }}>
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                </svg>
              </button>
            </div>
            {isSidebarOpen && (
              <>
                <button
                  onClick={startNewChat}
                  className="w-full bg-indigo-600 text-white py-2 px-4 rounded-lg mb-4 hover:bg-indigo-700 transition-colors"
                >
                  + New Chat
                </button>
                <div className="flex-1 overflow-y-auto space-y-2">
                  {conversations.length === 0 ? (
                    <p className="text-gray-400 text-sm">No conversations yet.</p>
                  ) : (
                    conversations.map((conv) => (
                      <button
                        key={conv.id}
                        onClick={() => setCurrentConversationId(conv.id)}
                        className={`w-full text-left p-3 rounded-lg transition-colors ${
                          currentConversationId === conv.id ? 'bg-indigo-700' : 'bg-gray-700 hover:bg-gray-600'
                        }`}
                      >
                        <p className="font-semibold text-sm truncate">{conv.title || "New Chat"}</p>
                        {conv.lastUpdated && (
                          <p className="text-xs text-gray-400">
                            {new Date(conv.lastUpdated.seconds * 1000).toLocaleDateString()}
                          </p>
                        )}
                      </button>
                    ))
                  )}
                </div>
              </>
            )}
          </div>
        )}

        {/* Main Chat Area */}
        <div className={`flex-1 flex flex-col ${user && isSidebarOpen ? 'rounded-r-none' : 'rounded-xl'}`}>
          {/* Header */}
          <div className="bg-gradient-to-r from-purple-600 to-indigo-600 text-white p-4 text-center text-2xl font-bold rounded-t-xl">
            AI Chatbot App
          </div>

          {/* User ID and Status */}
          <div className="bg-gray-200 text-gray-700 text-sm p-2 flex justify-between items-center">
              <span className="font-semibold">User ID:</span>
              <span className="font-mono text-xs p-1 bg-gray-300 rounded-md truncate max-w-[70%]">
                  {user ? user.uid : 'Not signed in'}
              </span>
              {user && (
                <button
                  onClick={handleLogout}
                  className="ml-2 px-3 py-1 bg-red-500 text-white rounded-md text-xs hover:bg-red-600 transition-colors"
                >
                  Logout
                </button>
              )}
          </div>

          {/* Loading and Error Display */}
          {loading && (
            <div className="flex-1 flex items-center justify-center p-4">
              <div className="text-center text-lg text-gray-500">
                <svg xmlns="http://www.w3.org/2000/svg" className="animate-spin h-8 w-8 text-indigo-500 mx-auto mb-2" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                {isSigningInWithLink ? 'Completing sign-in with email link...' : 'Initializing Firebase...'}
              </div>
            </div>
          )}

          {error && (
            <div className="flex-1 flex items-center justify-center p-4">
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg relative max-w-sm text-center">
                <strong className="font-bold block mb-1">Error!</strong>
                <span className="block sm:inline">{error}</span>
              </div>
            </div>
          )}

          {/* Conditional Rendering: Authentication Forms or Chat */}
          {!loading && !error && !user && !isSigningInWithLink && (
            <div className="flex-1 flex flex-col items-center justify-center p-4">
              <div className="flex mb-6">
                <button
                  onClick={() => setAuthMode('email-link')}
                  className={`px-4 py-2 rounded-l-lg font-semibold ${authMode === 'email-link' ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
                >
                  Email Link
                </button>
                <button
                  onClick={() => setAuthMode('email-password-login')}
                  className={`px-4 py-2 font-semibold ${authMode === 'email-password-login' ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
                >
                  Login
                </button>
                <button
                  onClick={() => setAuthMode('email-password-signup')}
                  className={`px-4 py-2 rounded-r-lg font-semibold ${authMode === 'email-password-signup' ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'}`}
                >
                  Sign Up
                </button>
              </div>

              {authMode === 'email-link' && (
                <>
                  <h2 className="text-xl font-semibold mb-4 text-gray-700">Sign In with Email Link</h2>
                  {!emailSent ? (
                    <form onSubmit={handleSendSignInLink} className="w-full max-w-sm flex flex-col items-center">
                      <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Enter your email"
                        className="w-full p-3 border border-gray-300 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                        required
                      />
                      <button
                        type="submit"
                        className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200"
                      >
                        Send Sign-in Link
                      </button>
                    </form>
                  ) : (
                    <div className="text-center">
                      <h2 className="text-xl font-semibold mb-4 text-gray-700">Email Sent!</h2>
                      <p className="text-gray-600 mb-4">
                        Please check your inbox at <span className="font-semibold text-indigo-600">{email}</span> for your sign-in link.
                      </p>
                      <button
                        onClick={() => setEmailSent(false)}
                        className="mt-6 bg-gray-200 text-gray-700 p-2 rounded-lg hover:bg-gray-300 transition-colors duration-200"
                      >
                        Go Back
                      </button>
                    </div>
                  )}
                </>
              )}

              {(authMode === 'email-password-login' || authMode === 'email-password-signup') && (
                <form onSubmit={authMode === 'email-password-signup' ? handleEmailPasswordSignUp : handleEmailPasswordLogin} className="w-full max-w-sm flex flex-col items-center">
                  <h2 className="text-xl font-semibold mb-4 text-gray-700">
                    {authMode === 'email-password-signup' ? 'Sign Up' : 'Log In'} with Email & Password
                  </h2>
                  <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Email"
                    className="w-full p-3 border border-gray-300 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                    required
                  />
                  <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Password"
                    className="w-full p-3 border border-gray-300 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                    required
                  />
                  <button
                    type="submit"
                    className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200"
                  >
                    {authMode === 'email-password-signup' ? 'Sign Up' : 'Log In'}
                  </button>
                </form>
              )}
            </div>
          )}

          {/* Main Chat Area */}
          {!loading && !error && user && (
            <>
              {currentConversationId && (
                <div className="p-4 bg-gray-200 border-b border-gray-300 flex items-center justify-between">
                  <h4 className="font-semibold text-lg text-gray-800">
                    {conversations.find(c => c.id === currentConversationId)?.title || "Current Chat"}
                  </h4>
                  <button
                    onClick={handleSummarizeChat}
                    disabled={isSummarizing || isAIGenerating || messages.length === 0}
                    className={`ml-4 px-4 py-2 text-sm font-semibold rounded-lg shadow-md transition-colors duration-200 ${
                        isSummarizing || isAIGenerating || messages.length === 0
                        ? 'bg-gray-400 text-gray-600 cursor-not-allowed'
                        : 'bg-indigo-500 text-white hover:bg-indigo-600'
                    }`}
                  >
                    {isSummarizing ? 'Summarizing...' : '✨ Summarize Chat'}
                  </button>
                </div>
              )}
              <div className="flex-1 p-4 overflow-y-auto space-y-4 bg-gray-50">
                {messages.length === 0 && (
                  <div className="text-center text-gray-400 mt-20">
                    {currentConversationId ? 'This conversation is empty. Send a message to start!' : 'Start a new chat to begin!'}
                  </div>
                )}
                {messages.map((msg) => (
                  <div key={msg.id} className={`flex ${msg.userId === user.uid ? 'justify-end' : 'justify-start'}`}>
                    <div className={`p-3 rounded-xl shadow-md max-w-xs sm:max-w-md break-words ${
                      msg.userId === user.uid
                        ? 'bg-blue-500 text-white rounded-br-none'
                        : 'bg-gray-300 text-gray-800 rounded-bl-none'
                    }`}>
                      <div className="font-bold text-xs mb-1">
                        {msg.userId === user.uid ? 'You' : msg.userName}
                      </div>
                      {msg.text}
                      <div className="text-right text-xs text-opacity-80 mt-1 flex items-center justify-between">
                        <div className="flex-1">
                          {msg.timestamp ? new Date(msg.timestamp.seconds * 1000).toLocaleTimeString() : '...'}
                        </div>
                        {msg.userId !== user.uid && msg.text && (
                            <button
                                onClick={() => handleTextToSpeech(msg.text)}
                                disabled={isPlayingAudio}
                                className={`ml-2 p-1 rounded-full transition-colors ${isPlayingAudio ? 'bg-gray-400 cursor-not-allowed' : 'bg-indigo-500 text-white hover:bg-indigo-600'}`}
                            >
                                🔊
                            </button>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
                {/* AI Generating Indicator */}
                {(isAIGenerating && !isSummarizing) && (
                  <div className="flex justify-start">
                    <div className="p-3 rounded-xl shadow-md bg-gray-300 text-gray-800 rounded-bl-none">
                      <div className="flex items-center">
                        <span className="mr-2">AI is typing</span>
                        <div className="dot-flashing"></div>
                      </div>
                    </div>
                  </div>
                )}
                <div ref={messagesEndRef} /> {/* Scroll to this element */}
              </div>

              {/* Message Input */}
              <form onSubmit={handleSendMessage} className="p-4 bg-gray-100 flex items-center">
                <input
                  type="text"
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  placeholder="Type your message..."
                  className="flex-1 p-3 border border-gray-300 rounded-full focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  disabled={isAIGenerating || isSummarizing}
                />
                <button
                  type="submit"
                  className="ml-3 p-3 bg-indigo-500 text-white rounded-full shadow-lg hover:bg-indigo-600 transition-colors duration-200"
                  disabled={isAIGenerating || isSummarizing || !newMessage.trim()}
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                  </svg>
                </button>
              </form>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
