// This line tells ESLint to ignore the following global variables that are provided by the Canvas environment.
/* global __app_id, __firebase_config, __initial_auth_token */

import React, { useState, useEffect, useRef } from 'react';
import { initializeApp, getApps, getApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, signInAnonymously, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, collection, addDoc, serverTimestamp, query, onSnapshot } from 'firebase/firestore';
import { SendHorizonal, Bot } from 'lucide-react';

// Use the global variables provided by the environment
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
// Use the Canvas provided config, or an empty object if not available (for local testing)
let firebaseConfig = {};
try {
  firebaseConfig = JSON.parse(typeof __firebase_config !== 'undefined' ? __firebase_config : '{}');
} catch (e) {
  console.error("Failed to parse firebase config from global variable, using local placeholder.");
  // Placeholder for local testing. Replace with your actual Firebase config.
  firebaseConfig = {
    apiKey: "YOUR_API_KEY",
    authDomain: "YOUR_AUTH_DOMAIN",
    projectId: "ai-chatbot-88602",
    storageBucket: "YOUR_STORAGE_BUCKET",
    messagingSenderId: "YOUR_MESSAGING_SENDER_ID",
    appId: "1:167580287666:android:b057a4176a46729ad47fab"
  };
}

const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

// The AIChatbot component with enhanced styling and functionality
const AIChatbot = () => {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [db, setDb] = useState(null);
  const [userId, setUserId] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [isAIGenerating, setIsAIGenerating] = useState(false); // New state to track AI response generation
  const messagesEndRef = useRef(null);

  // Initialize Firebase and handle authentication
  useEffect(() => {
    const initFirebase = async () => {
      try {
        // Check if firebaseConfig is not empty
        if (!firebaseConfig || Object.keys(firebaseConfig).length === 0 || firebaseConfig.apiKey === "YOUR_API_KEY") {
          console.error("Firebase configuration is missing or using placeholder values. Please provide your firebase config.");
          setIsLoading(false);
          return;
        }

        // Initialize the Firebase app instance
        const app = getApps().length ? getApp() : initializeApp(firebaseConfig);
        const firestoreDb = getFirestore(app);
        const firestoreAuth = getAuth(app);
        
        setDb(firestoreDb);

        // Set up an authentication state change listener. This will fire when the auth state changes.
        const unsubscribe = onAuthStateChanged(firestoreAuth, async (user) => {
          console.log("onAuthStateChanged fired. User:", user);
          if (user) {
            setUserId(user.uid);
            console.log("User signed in with UID:", user.uid);
          } else {
            console.log("No user signed in. Attempting anonymous sign-in...");
            // Sign in with the provided custom token or anonymously if not available
            if (initialAuthToken) {
              await signInWithCustomToken(firestoreAuth, initialAuthToken);
            } else {
              await signInAnonymously(firestoreAuth);
            }
          }
          setIsAuthReady(true);
        });
        
        // Return the unsubscribe function for cleanup when the component unmounts
        return () => unsubscribe();
      } catch (e) {
        console.error("Error initializing Firebase:", e);
        setIsLoading(false);
      }
    };
    initFirebase();
  }, []);

  // Fetch messages from Firestore in real-time after authentication is ready
  useEffect(() => {
    // Only proceed if Firestore is initialized and the user is authenticated
    if (db && userId && isAuthReady) {
      console.log("Auth is ready, fetching messages for user:", userId);
      setIsLoading(true);
      // Construct the Firestore path for public data, ensuring it matches the security rules
      const messagesCollectionRef = collection(db, `artifacts/${appId}/public/data/messages`);
      const q = query(messagesCollectionRef);

      // Set up a real-time listener for the messages collection
      const unsubscribe = onSnapshot(q, (snapshot) => {
        const fetchedMessages = snapshot.docs.map(doc => doc.data());
        // Sort messages by timestamp on the client-side to avoid index issues
        const sortedMessages = fetchedMessages.sort((a, b) => a.timestamp?.toMillis() - b.timestamp?.toMillis());
        setMessages(sortedMessages);
        setIsLoading(false);
      }, (error) => {
        console.error("Error fetching messages: ", error);
        setIsLoading(false);
      });
      
      return () => unsubscribe();
    }
  }, [db, userId, isAuthReady]);

  // Use a separate useEffect to handle AI responses to user messages
  useEffect(() => {
    // Check if the last message is from the user and we have a valid database connection and the AI isn't already generating a response
    const lastMessage = messages[messages.length - 1];
    if (db && lastMessage && lastMessage.sender === 'user' && !isAIGenerating) {
      const userMessage = lastMessage.text;
      
      const getAIResponse = async () => {
        setIsAIGenerating(true);
        console.log("Generating AI response for message:", userMessage);
        
        try {
          const payload = {
            contents: [{ role: "user", parts: [{ text: userMessage }] }]
          };
          const apiKey = "";
          const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
          
          const response = await fetch(apiUrl, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload)
          });
          
          if (!response.ok) {
            console.error(`Gemini API call failed with status: ${response.status}`);
            throw new Error(`API call failed with status: ${response.status}`);
          }
          
          const result = await response.json();
          
          if (!result || !result.candidates || result.candidates.length === 0 || !result.candidates[0].content || !result.candidates[0].content.parts || result.candidates[0].content.parts.length === 0) {
            console.error("Gemini API returned an unexpected response structure:", result);
            throw new Error("Invalid response from AI model.");
          }
          
          const botResponse = result.candidates[0].content.parts[0].text;
          
          // Add the bot response to the database
          await addDoc(collection(db, `artifacts/${appId}/public/data/messages`), {
            text: botResponse,
            sender: 'bot',
            timestamp: serverTimestamp(),
          });
          
        } catch (error) {
          console.error("Error generating AI response:", error);
          // Add an error message to the chat
          await addDoc(collection(db, `artifacts/${appId}/public/data/messages`), {
            text: "Sorry, I am unable to respond right now. Please try again later.",
            sender: 'bot',
            timestamp: serverTimestamp(),
          });
        } finally {
          setIsAIGenerating(false);
        }
      };
      
      getAIResponse();
    }
  }, [messages, db, isAIGenerating]);

  // Scroll to the bottom of the message list whenever new messages arrive
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Send a new message to Firestore
  const sendMessage = async () => {
    if (input.trim() === '' || !db || !userId) return;

    try {
      // Use the correct Firestore path for public data when adding a new document
      const messagesCollectionRef = collection(db, `artifacts/${appId}/public/data/messages`);
      await addDoc(messagesCollectionRef, {
        text: input,
        sender: 'user',
        timestamp: serverTimestamp(),
      });
      setInput('');
    } catch (e) {
      console.error("Error adding document: ", e);
    }
  };

  return (
    <div className="flex flex-col w-full max-w-xl h-[80vh] bg-white dark:bg-gray-800 rounded-2xl shadow-2xl overflow-hidden transition-all duration-300">
      {/* Chat Header */}
      <div className="p-5 bg-gradient-to-r from-blue-500 to-purple-600 text-white rounded-t-2xl shadow-md flex items-center justify-between">
        <div className="flex items-center space-x-3">
          <Bot className="w-8 h-8 text-white" />
          <h1 className="text-2xl font-extrabold tracking-wide">AI Chatbot</h1>
        </div>
        <div className="text-sm font-medium">User ID: <span className="font-mono text-xs">{userId || 'Loading...'}</span></div>
      </div>

      {/* Message Area */}
      <div className="flex-1 p-5 overflow-y-auto space-y-4 bg-gray-50 dark:bg-gray-900 transition-colors duration-300">
        {isLoading ? (
          <div className="flex items-center justify-center h-full">
            <div className="animate-spin rounded-full h-12 w-12 border-4 border-t-4 border-blue-500 dark:border-blue-400"></div>
          </div>
        ) : (
          messages.map((msg, index) => (
            <div key={index} className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
              <div className={`p-4 rounded-3xl max-w-xs transition-all duration-300 transform ${
                msg.sender === 'user'
                  ? 'bg-blue-600 text-white shadow-lg rounded-br-none'
                  : 'bg-gray-200 dark:bg-gray-700 dark:text-gray-200 text-gray-800 shadow-lg rounded-bl-none'
              }`}>
                {msg.text}
              </div>
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Input and Send Area */}
      <div className="p-4 bg-gray-100 dark:bg-gray-800 rounded-b-2xl border-t border-gray-200 dark:border-gray-700 flex items-center space-x-3">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
          className="flex-1 p-3 rounded-full border border-gray-300 dark:border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white transition-all duration-300"
          placeholder="Type a message..."
        />
        <button
          onClick={sendMessage}
          className="p-3 bg-blue-600 text-white rounded-full shadow-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-300 disabled:bg-gray-400 disabled:cursor-not-allowed"
          disabled={input.trim() === '' || !userId || isLoading || isAIGenerating}
        >
          <SendHorizonal className="w-6 h-6" />
        </button>
      </div>
    </div>
  );
};


// The main App component that renders the AIChatbot
export default function App() {
  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-50 dark:bg-gray-900 font-sans p-4">
      <AIChatbot />
    </div>
  );
}
