/* global __firebase_config, __app_id, __initial_auth_token, marked */

import { useState, useEffect, useRef } from 'react';
import { initializeApp } from 'firebase/app';
import {
  getAuth,
  signInWithCustomToken,
  signInAnonymously,
  onAuthStateChanged,
  sendSignInLinkToEmail,
  isSignInWithEmailLink,
  signInWithEmailLink,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut
} from 'firebase/auth';
import {
  getFirestore,
  collection,
  onSnapshot,
  addDoc,
  serverTimestamp,
  doc,
  setDoc,
  query,
  limit,
  updateDoc
} from 'firebase/firestore';
import { createPortal } from 'react-dom';
import { Sparkles, MessageSquareMore, Image, LoaderCircle, X, ExternalLink, Menu, Plus, Volume2 } from 'lucide-react';

// --- Firebase Configuration ---
// The global variables are provided by the Canvas environment.
// We fall back to a local config for development purposes if they are not defined.
const localFirebaseConfig = {
  apiKey: "AIzaSyDhJoNaFf0qgl4VTuUXj16ysJG1hycq-p8",
  authDomain: "ai-chatbot-88602.firebaseapp.com",
  projectId: "ai-chatbot-88602",
  storageBucket: "ai-chatbot-88602.firebasestorage.app",
  messagingSenderId: "167580287666",
  appId: "1:167580287666:web:9e8313ae6e667e47d47fab",
  measurementId: "G-CXKJ8QRLZ1"
};

const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : localFirebaseConfig;
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

// Utility function for generating unique IDs (like conversation IDs)
const generateId = () => {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0, v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
};

// Converts a base64 string to an ArrayBuffer
function base64ToArrayBuffer(base64) {
  const binaryString = window.atob(base64);
  const len = binaryString.length;
  const bytes = new Uint8Array(len);
  for (let i = 0; i < len; i++) {
    bytes[i] = binaryString.charCodeAt(i);
  }
  return bytes.buffer;
}

// Converts PCM audio data to a WAV blob
function pcmToWav(pcmData, sampleRate) {
  const pcm16 = new Int16Array(pcmData);
  const buffer = new ArrayBuffer(44 + pcm16.length * 2);
  const view = new DataView(buffer);
  const writeString = (view, offset, string) => {
      for (let i = 0; i < string.length; i++) {
          view.setUint8(offset + i, string.charCodeAt(i));
      }
  };

  let offset = 0;
  writeString(view, offset, 'RIFF'); offset += 4;
  view.setUint32(offset, 36 + pcm16.length * 2, true); offset += 4;
  writeString(view, offset, 'WAVE'); offset += 4;
  writeString(view, offset, 'fmt '); offset += 4;
  view.setUint32(offset, 16, true); offset += 4; // Sub-chunk size
  view.setUint16(offset, 1, true); offset += 2; // Audio format (1 = PCM)
  view.setUint16(offset, 1, true); offset += 2; // Number of channels
  view.setUint32(offset, sampleRate, true); offset += 4; // Sample rate
  view.setUint32(offset, sampleRate * 2, true); offset += 4; // Byte rate
  view.setUint16(offset, 2, true); offset += 2; // Block align
  view.setUint16(offset, 16, true); offset += 2; // Bits per sample
  writeString(view, offset, 'data'); offset += 4;
  view.setUint32(offset, pcm16.length * 2, true); offset += 4; // Data size

  for (let i = 0; i < pcm16.length; i++) {
      view.setInt16(offset, pcm16[i], true);
      offset += 2;
  }

  return new Blob([view], { type: 'audio/wav' });
}


function App() {
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailSent, setEmailSent] = useState(false);
  const [isSigningInWithLink, setIsSigningInWithLink] = useState(false);
  const [isAIGenerating, setIsAIGenerating] = useState(false);
  const [authMode, setAuthMode] = useState('email-link');

  // New states for conversation history and sidebar
  const [conversations, setConversations] = useState([]);
  const [currentConversationId, setCurrentConversationId] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isSummarizing, setIsSummarizing] = useState(false);
  const [isGeneratingImage, setIsGeneratingImage] = useState(false);
  const [currentAudio, setCurrentAudio] = useState(null);
  const [isSpeaking, setIsSpeaking] = useState(false);

  const messagesEndRef = useRef(null);
  const audioContextRef = useRef(null);

  // Scroll to bottom of messages whenever messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Exponential backoff utility for API calls
  const fetchWithExponentialBackoff = async (url, options, retries = 5, delay = 1000) => {
    try {
      const response = await fetch(url, options);
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`API error: ${errorData.error?.message || response.statusText}`);
      }
      return response;
    } catch (error) {
      if (retries > 0 && error.message.includes("API error")) {
        await new Promise(resolve => setTimeout(resolve, delay));
        return fetchWithExponentialBackoff(url, options, retries - 1, delay * 2);
      }
      throw error;
    }
  };

  // 1. Initialize Firebase and set up authentication.
  useEffect(() => {
    if (Object.keys(firebaseConfig).length === 0 || !firebaseConfig.apiKey) {
      setError('Firebase configuration is missing or incomplete. Please ensure your Firebase config is correctly set, especially the API Key.');
      setLoading(false);
      return;
    }

    try {
      const firebaseApp = initializeApp(firebaseConfig);
      const authInstance = getAuth(firebaseApp);
      const dbInstance = getFirestore(firebaseApp);

      setAuth(authInstance);
      setDb(dbInstance);

      const unsubscribe = onAuthStateChanged(authInstance, async (currentUser) => {
        if (currentUser) {
          setUser(currentUser);
          setIsAuthReady(true);
          setError(null);
        } else {
          if (initialAuthToken) {
             try {
               await signInWithCustomToken(authInstance, initialAuthToken);
             } catch (e) {
               console.error("Custom token sign-in failed:", e);
               setError(`Custom token sign-in failed: ${e.message}. This usually means the token is invalid or expired. Please try refreshing.`);
             }
          } else if (!isSigningInWithLink) {
            try {
              await signInAnonymously(authInstance);
            } catch (e) {
              console.error("Anonymous sign-in failed:", e);
              setError(`Anonymous sign-in failed: ${e.message}. Please ensure Anonymous Authentication is enabled in your Firebase project settings.`);
            }
          }
          setIsAuthReady(true);
        }
        setLoading(false);
      });
      return () => unsubscribe();
    } catch (e) {
      setError(`Failed to initialize Firebase: ${e.message}. Please check the console for more details.`);
      setLoading(false);
    }
  }, []);

  // 2. Handle email link sign-in.
  useEffect(() => {
    if (!auth || !isAuthReady) return;

    if (isSignInWithEmailLink(auth, window.location.href)) {
      setIsSigningInWithLink(true);
      setLoading(true);

      let storedEmail = window.localStorage.getItem('emailForSignIn');

      if (!storedEmail) {
        // Using a custom modal instead of prompt
        const modalDiv = document.createElement('div');
        modalDiv.className = 'fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full flex items-center justify-center z-50';
        modalDiv.innerHTML = `
          <div class="bg-white p-8 rounded-lg shadow-lg max-w-sm w-full">
            <h3 class="text-xl font-bold mb-4">Confirm Email</h3>
            <p class="mb-4">Please provide your email to complete sign-in.</p>
            <input id="email-confirm-input" type="email" placeholder="Enter your email" class="w-full p-3 border border-gray-300 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-indigo-500" required />
            <button id="email-confirm-button" class="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200">Confirm</button>
          </div>
        `;
        document.body.appendChild(modalDiv);

        const emailConfirmInput = modalDiv.querySelector('#email-confirm-input');
        const emailConfirmButton = modalDiv.querySelector('#email-confirm-button');

        emailConfirmButton.onclick = () => {
          storedEmail = emailConfirmInput.value;
          if (storedEmail) {
            document.body.removeChild(modalDiv);
            continueSignIn(storedEmail);
          } else {
            console.error("Email not provided. Please try again.");
          }
        };

        const continueSignIn = (emailToUse) => {
          signInWithEmailLink(auth, emailToUse, window.location.href)
            .then((result) => {
              window.localStorage.removeItem('emailForSignIn');
              setUser(result.user);
              setIsSigningInWithLink(false);
              setLoading(false);
              setError(null);
              window.history.replaceState({}, document.title, window.location.pathname);
            })
            .catch((error) => {
              setError(`Error completing sign-in with link: ${error.message}`);
              setIsSigningInWithLink(false);
              setLoading(false);
              window.history.replaceState({}, document.title, window.location.pathname);
            });
        };
        return; // Exit the effect to wait for user input
      }

      signInWithEmailLink(auth, storedEmail, window.location.href)
        .then((result) => {
          window.localStorage.removeItem('emailForSignIn');
          setUser(result.user);
          setIsSigningInWithLink(false);
          setLoading(false);
          setError(null);
          window.history.replaceState({}, document.title, window.location.pathname);
        })
        .catch((error) => {
          setError(`Error completing sign-in with link: ${error.message}`);
          setIsSigningInWithLink(false);
          setLoading(false);
          window.history.replaceState({}, document.title, window.location.pathname);
        });
    }
  }, [auth, isAuthReady]);

  // 3. Fetch conversations for the current user.
  useEffect(() => {
    if (!db || !user || !isAuthReady) return;

    const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
    const unsubscribe = onSnapshot(conversationsCollectionRef, (querySnapshot) => {
      const fetchedConversations = [];
      querySnapshot.forEach((doc) => {
        fetchedConversations.push({ id: doc.id, ...doc.data() });
      });

      fetchedConversations.sort((a, b) => (b.lastUpdated?.seconds || 0) - (a.lastUpdated?.seconds || 0));

      setConversations(fetchedConversations);

      if (!currentConversationId && fetchedConversations.length > 0) {
        setCurrentConversationId(fetchedConversations[0].id);
      } else if (fetchedConversations.length === 0) {
        setCurrentConversationId(null);
        setMessages([]);
      }
    }, (firestoreError) => {
      setError(`Failed to load conversations: ${firestoreError.message}`);
      console.error("Firestore conversations error:", firestoreError);
    });

    return () => unsubscribe();
  }, [db, user, isAuthReady, appId, currentConversationId]);

  // 4. Fetch messages for the current conversation.
  useEffect(() => {
    if (!db || !user || !currentConversationId) {
      setMessages([]); // Clear messages if no conversation is selected
      return;
    }

    const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
    const unsubscribe = onSnapshot(messagesCollectionRef, (querySnapshot) => {
      const fetchedMessages = [];
      querySnapshot.forEach((doc) => {
        fetchedMessages.push({ id: doc.id, ...doc.data() });
      });

      fetchedMessages.sort((a, b) => (a.timestamp?.seconds || 0) - (b.timestamp?.seconds || 0));

      setMessages(fetchedMessages);
    }, (firestoreError) => {
      setError(`Failed to load messages for conversation: ${firestoreError.message}`);
      console.error("Firestore messages error:", firestoreError);
    });

    return () => unsubscribe();
  }, [db, user, currentConversationId, appId]);

  // 5. Start a new conversation.
  // This function is updated to create a new conversation document in Firestore
  const startNewChat = async () => {
    if (!db || !user || !isAuthReady) return;

    const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
    const newConvId = generateId();
    const newConversationDocRef = doc(conversationsCollectionRef, newConvId);

    try {
      await setDoc(newConversationDocRef, {
        title: 'New Chat',
        createdAt: serverTimestamp(),
        lastUpdated: serverTimestamp(),
      });
      // The Firestore listener will pick up this new conversation and update the state
      setCurrentConversationId(newConvId);
      setMessages([]);
      setNewMessage('');
      setError(null);
      setIsAIGenerating(false);
      setIsSummarizing(false);
      setIsGeneratingImage(false);
      setIsSidebarOpen(false);
    } catch (e) {
      setError(`Failed to create new conversation: ${e.message}`);
      console.error("Error creating new conversation:", e);
    }
  };

  // 6. Handle sending a new message (and creating a new conversation if needed).
  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !user || !db || isAIGenerating) return;

    let conversationIdToUse = currentConversationId;
    const userMessageText = newMessage;
    setNewMessage('');

    // Check if the current conversation is a new one with the default title
    const isNewConversation = conversations.find(conv => conv.id === currentConversationId)?.title === 'New Chat';

    // If it's a new conversation, update the title with the first message
    if (isNewConversation) {
      const conversationDocRef = doc(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId);
      const initialTitle = userMessageText.substring(0, 50) + (userMessageText.length > 50 ? '...' : '');
      try {
        await updateDoc(conversationDocRef, {
          title: initialTitle,
          lastUpdated: serverTimestamp(),
        });
      } catch (updateError) {
        console.error("Error updating conversation title:", updateError);
      }
    } else if (conversationIdToUse) {
      // Otherwise, just update the lastUpdated timestamp of the current conversation.
      const conversationDocRef = doc(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse);
      try {
        await updateDoc(conversationDocRef, { lastUpdated: serverTimestamp() });
      } catch (updateError) {
        console.error("Error updating conversation timestamp:", updateError);
      }
    } else {
        // Fallback in case there's no conversation selected, create one
        const newConvId = generateId();
        const conversationsCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations');
        const newConversationDocRef = doc(conversationsCollectionRef, newConvId);
        const initialTitle = userMessageText.substring(0, 50) + (userMessageText.length > 50 ? '...' : '');

        try {
          await setDoc(newConversationDocRef, {
            title: initialTitle,
            createdAt: serverTimestamp(),
            lastUpdated: serverTimestamp(),
          });
          conversationIdToUse = newConvId;
          setCurrentConversationId(newConvId);
        } catch (createError) {
          setError(`Failed to create new conversation: ${createError.message}`);
          console.error("Error creating new conversation:", createError);
          return;
        }
    }

    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', conversationIdToUse, 'messages');
      await addDoc(messagesCollectionRef, {
        text: userMessageText,
        userId: user.uid,
        userName: `User_${user.uid.substring(0, 5)}`,
        timestamp: serverTimestamp(),
        type: 'user-message'
      });
    } catch (e) {
      setError(`Failed to send message: ${e.message}`);
      return;
    }

    const command = userMessageText.toLowerCase().trim();

    if (command === 'summarize') {
      handleSummarizeChat();
    } else if (command.startsWith('image of')) {
      const imagePrompt = userMessageText.substring('image of'.length).trim();
      handleGenerateImage(imagePrompt);
    } else {
      handleAIResponse(userMessageText);
    }
  };

  // 7. Handle sending the sign-in email link.
  const handleSendSignInLink = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim()) {
      setError("Please enter a valid email address.");
      return;
    }
    setLoading(true);
    setError(null);
    const actionCodeSettings = {
      url: window.location.origin,
      handleCodeInApp: true,
    };
    try {
      await sendSignInLinkToEmail(auth, email, actionCodeSettings);
      setEmailSent(true);
      window.localStorage.setItem('emailForSignIn', email);
      setLoading(false);
    } catch (error) {
      setError(`Failed to send sign-in link: ${error.message}`);
      setLoading(false);
    }
  };

  // 8. Handle Email/Password Registration
  const handleEmailPasswordSignUp = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim() || !password.trim()) {
      setError("Please enter a valid email and password.");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
      setLoading(false);
    } catch (error) {
      setError(`Registration failed: ${error.message}`);
      setLoading(false);
    }
  };

  // 9. Handle Email/Password Login
  const handleEmailPasswordLogin = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim() || !password.trim()) {
      setError("Please enter a valid email and password.");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      await signInWithEmailAndPassword(auth, email, password);
      setEmail('');
      setPassword('');
      setLoading(false);
    } catch (error) {
      setError(`Login failed: ${error.message}`);
      setLoading(false);
    }
  };

  // 10. Handle Logout
  const handleLogout = async () => {
    if (!auth) return;
    try {
      await signOut(auth);
      setUser(null);
      setMessages([]);
      setConversations([]);
      setCurrentConversationId(null);
      setEmailSent(false);
      setIsSigningInWithLink(false);
      setAuthMode('email-link');
      setError(null);
    } catch (error) {
      setError(`Logout failed: ${error.message}`);
    }
  };

  // 11. Handle summarization of the current chat using the Gemini API
  const handleSummarizeChat = async () => {
    if (!currentConversationId || messages.length === 0 || isAIGenerating || isSummarizing || isGeneratingImage) return;
    setIsSummarizing(true);
    setError(null);
    try {
      const chatHistoryText = messages
        .filter(msg => msg.type === 'user-message' || msg.type === 'ai-response')
        .map(msg => `${msg.userId === user.uid ? 'User' : 'AI'}: ${msg.text}`)
        .join('\n');
      const prompt = `Please summarize the following chat conversation into a concise paragraph:\n\n${chatHistoryText}`;

      const chatHistory = [{ role: "user", parts: [{ text: prompt }] }];
      const payload = { contents: chatHistory };
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      let responseText = "Sorry, I couldn't generate a summary.";
      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        responseText = result.candidates[0].content.parts[0].text;
      }
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
      await addDoc(messagesCollectionRef, {
        text: `**Chat Summary:**\n\n${responseText}`,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response'
      });
    } catch (error) {
      console.error("Error summarizing chat:", error);
      setError(`Summarization error: ${error.message}`);
    } finally {
      setIsSummarizing(false);
    }
  };

  // 12. Handle image generation using the Gemini API
  const handleGenerateImage = async (imagePrompt) => {
    if (!currentConversationId || isAIGenerating || isSummarizing || isGeneratingImage) return;
    setIsGeneratingImage(true);
    setError(null);
    let tempImageMessageRef = null;
    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
      tempImageMessageRef = doc(messagesCollectionRef); // Add a placeholder message immediately
      await setDoc(tempImageMessageRef, {
        text: `Generating an image for: "${imagePrompt}"...`,
        userId: 'bot',
        userName: 'AI Chatbot',
        timestamp: serverTimestamp(),
        type: 'ai-response',
        imageUrl: '',
      });
      const payload = { instances: { prompt: imagePrompt }, parameters: { "sampleCount": 1} };
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/imagen-3.0-generate-002:predict?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      if (result.predictions && result.predictions.length > 0 && result.predictions[0].bytesBase64Encoded) {
        const imageUrl = `data:image/png;base64,${result.predictions[0].bytesBase64Encoded}`;
        await setDoc(tempImageMessageRef, {
          text: `Here is the image you requested for "${imagePrompt}."`,
          userId: 'bot',
          userName: 'AI Chatbot',
          timestamp: serverTimestamp(),
          type: 'ai-response',
          imageUrl: imageUrl,
        });
      } else {
        throw new Error('Unexpected API response structure or no image data.');
      }
    } catch (error) {
      console.error("Error generating image:", error);
      if (tempImageMessageRef) {
        await setDoc(doc(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages', tempImageMessageRef.id), {
          text: `I couldn't generate the image. Please try again with a different prompt. Error: ${error.message}`,
          userId: 'bot',
          userName: 'AI Chatbot',
          timestamp: serverTimestamp(),
          type: 'ai-response',
          imageUrl: '',
        }, { merge: true });
      }
      setError(`Image generation error: ${error.message}`);
    } finally {
      setIsGeneratingImage(false);
    }
  };

  // 13. Handle text-to-speech
  const handleTTS = async (text, msgId) => {
    if (isSpeaking) return;
    setIsSpeaking(true);
    try {
      const payload = {
        contents: [{
          parts: [{ text: `Say in a natural tone: ${text}` }]
        }],
        generationConfig: {
          responseModalities: ["AUDIO"],
          speechConfig: {
            voiceConfig: {
              prebuiltVoiceConfig: { voiceName: "Puck" }
            }
          }
        },
        model: "gemini-2.5-flash-preview-tts"
      };
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-tts:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const result = await response.json();
      const part = result?.candidates?.[0]?.content?.parts?.[0];
      const audioData = part?.inlineData?.data;
      const mimeType = part?.inlineData?.mimeType;
      if (audioData && mimeType && mimeType.startsWith("audio/")) {
        const match = mimeType.match(/rate=(\d+)/);
        const sampleRate = match ? parseInt(match[1], 10) : 16000;
        const pcmData = base64ToArrayBuffer(audioData);
        const wavBlob = pcmToWav(pcmData, sampleRate);
        const audioUrl = URL.createObjectURL(wavBlob);
        const audio = new Audio(audioUrl);
        setCurrentAudio(audio);
        audio.play();
        audio.onended = () => {
          setIsSpeaking(false);
          setCurrentAudio(null);
        };
      } else {
        throw new Error("No audio data received from API.");
      }
    } catch (error) {
      console.error("Error generating or playing TTS:", error);
      setError(`TTS failed: ${error.message}`);
      setIsSpeaking(false);
    }
  };

  const stopTTS = () => {
    if (currentAudio) {
      currentAudio.pause();
      currentAudio.currentTime = 0;
      setCurrentAudio(null);
      setIsSpeaking(false);
    }
  };

  // 14. Handle AI response for general chat
  const handleAIResponse = async (userMessage) => {
    setIsAIGenerating(true);
    try {
      const chatHistory = messages.map(msg => ({ role: (msg.type === 'user-message' ? "user" : "model"), parts: [{ text: msg.text }] }));
      chatHistory.push({ role: "user", parts: [{ text: userMessage }] });

      const payload = { contents: chatHistory };
      const apiKey = "";
      const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-05-20:generateContent?key=${apiKey}`;
      const response = await fetchWithExponentialBackoff(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });

      const result = await response.json();
      let responseText = "Sorry, I couldn't get a response from the AI. Please try again.";
      if (result.candidates && result.candidates.length > 0 && result.candidates[0].content && result.candidates[0].content.parts && result.candidates[0].content.parts.length > 0) {
        responseText = result.candidates[0].content.parts[0].text;
      }

      if (currentConversationId) {
        const messagesCollectionRef = collection(db, 'artifacts', appId, 'users', user.uid, 'conversations', currentConversationId, 'messages');
        await addDoc(messagesCollectionRef, {
          text: responseText,
          userId: 'bot',
          userName: 'AI Chatbot',
          timestamp: serverTimestamp(),
          type: 'ai-response'
        });
      }
    } catch (error) {
      console.error("Error fetching AI response:", error);
      setError(`AI response error: ${error.message}`);
    } finally {
      setIsAIGenerating(false);
    }
  };


  // 15. The main component render
  if (loading || !isAuthReady) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100 text-gray-700">
        <div className="text-center">
          <LoaderCircle className="w-12 h-12 text-indigo-500 animate-spin mx-auto" />
          <p className="mt-4 text-lg font-semibold">Loading...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-red-100 text-red-800 p-4">
        <div className="text-center">
          <p className="text-xl font-bold">Error</p>
          <p className="mt-2">{error}</p>
          <button
            onClick={() => window.location.reload()}
            className="mt-4 px-4 py-2 bg-red-600 text-white rounded-lg shadow-md hover:bg-red-700 transition-colors"
          >
            Refresh Page
          </button>
        </div>
      </div>
    );
  }

  // Auth UI
  const AuthUI = () => {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
        <div className="w-full max-w-md bg-white p-8 rounded-xl shadow-2xl">
          <h2 className="text-3xl font-extrabold text-center text-gray-800 mb-6">
            Welcome to AI Chat
          </h2>
          <p className="text-center text-gray-600 mb-8">
            Please sign in to continue.
          </p>

          <div className="mb-8">
            <div className="flex justify-center mb-6">
              <button
                onClick={() => setAuthMode('email-link')}
                className={`px-6 py-2 rounded-l-lg font-semibold transition-colors duration-200 ${
                  authMode === 'email-link' ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                }`}
              >
                Magic Link
              </button>
              <button
                onClick={() => setAuthMode('email-password')}
                className={`px-6 py-2 rounded-r-lg font-semibold transition-colors duration-200 ${
                  authMode === 'email-password' ? 'bg-indigo-600 text-white' : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                }`}
              >
                Email/Password
              </button>
            </div>

            {authMode === 'email-link' && (
              <form onSubmit={handleSendSignInLink} className="space-y-4">
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Enter your email"
                  className="w-full p-4 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-shadow duration-200"
                  required
                />
                <button
                  type="submit"
                  className="w-full bg-indigo-600 text-white p-4 rounded-lg font-bold shadow-lg hover:bg-indigo-700 transition-colors duration-200"
                >
                  {emailSent ? 'Link Sent! Check your inbox' : 'Send Sign-in Link'}
                </button>
              </form>
            )}

            {authMode === 'email-password' && (
              <div className="space-y-4">
                <form onSubmit={handleEmailPasswordLogin} className="space-y-4">
                  <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="Email"
                    className="w-full p-4 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-shadow duration-200"
                    required
                  />
                  <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Password"
                    className="w-full p-4 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-shadow duration-200"
                    required
                  />
                  <button
                    type="submit"
                    className="w-full bg-green-600 text-white p-4 rounded-lg font-bold shadow-lg hover:bg-green-700 transition-colors duration-200"
                  >
                    Log In
                  </button>
                </form>
                <div className="flex items-center my-4">
                  <div className="flex-grow border-t border-gray-300"></div>
                  <span className="flex-shrink mx-4 text-gray-500">OR</span>
                  <div className="flex-grow border-t border-gray-300"></div>
                </div>
                <button
                  onClick={handleEmailPasswordSignUp}
                  className="w-full bg-gray-500 text-white p-4 rounded-lg font-bold shadow-lg hover:bg-gray-600 transition-colors duration-200"
                >
                  Create Account
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    );
  };


  if (!user) {
    return <AuthUI />;
  }

  // Main chat UI
  return (
    <div className="flex min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-800 dark:text-gray-200 font-sans">
      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 transform ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'} transition-transform duration-300 ease-in-out bg-white dark:bg-gray-800 shadow-lg z-40 md:relative md:translate-x-0 md:flex md:flex-col w-64`}>
        <div className="p-4 flex items-center justify-between border-b dark:border-gray-700">
          <h2 className="text-xl font-bold">Chats</h2>
          <button
            onClick={() => startNewChat()}
            className="p-2 bg-indigo-500 text-white rounded-full shadow-md hover:bg-indigo-600 transition-colors"
            aria-label="Start a new chat"
          >
            <Plus size={20} />
          </button>
          <button
            onClick={() => setIsSidebarOpen(false)}
            className="md:hidden p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors"
            aria-label="Close sidebar"
          >
            <X size={20} />
          </button>
        </div>
        <div className="flex-grow overflow-y-auto p-2">
          {conversations.length > 0 ? (
            conversations.map((conv) => (
              <button
                key={conv.id}
                onClick={() => setCurrentConversationId(conv.id)}
                className={`w-full text-left p-3 my-1 rounded-lg transition-colors duration-200 ${
                  currentConversationId === conv.id
                    ? 'bg-indigo-500 text-white shadow-md'
                    : 'hover:bg-gray-200 dark:hover:bg-gray-700'
                }`}
              >
                <div className="font-semibold">{conv.title}</div>
                {conv.lastUpdated && (
                  <div className="text-sm opacity-75">
                    {new Date(conv.lastUpdated.seconds * 1000).toLocaleDateString()}
                  </div>
                )}
              </button>
            ))
          ) : (
            <div className="text-center text-gray-500 dark:text-gray-400 mt-4">No conversations yet.</div>
          )}
        </div>
        <div className="p-4 border-t dark:border-gray-700">
          <p className="text-sm text-gray-500 dark:text-gray-400">
            User: <span className="font-mono text-xs">{user.uid}</span>
          </p>
          <button
            onClick={handleLogout}
            className="mt-2 w-full bg-red-500 text-white p-2 rounded-lg shadow-md hover:bg-red-600 transition-colors"
          >
            Sign Out
          </button>
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="flex-grow flex flex-col">
        {/* Top bar for mobile */}
        <div className="md:hidden flex items-center justify-between p-4 bg-white dark:bg-gray-800 shadow-md">
          <button onClick={() => setIsSidebarOpen(true)} className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors">
            <Menu size={24} />
          </button>
          <h1 className="text-xl font-bold">AI Chatbot</h1>
          <button
            onClick={() => startNewChat()}
            className="p-2 bg-indigo-500 text-white rounded-full shadow-md hover:bg-indigo-600 transition-colors"
            aria-label="Start a new chat"
          >
            <Plus size={20} />
          </button>
        </div>

        {/* Message area */}
        <div className="flex-grow overflow-y-auto p-4 md:p-8 space-y-4">
          {messages.map((msg, index) => (
            <div
              key={msg.id}
              className={`flex items-start gap-4 ${
                msg.type === 'user-message' ? 'justify-end' : 'justify-start'
              }`}
            >
              {msg.type === 'ai-response' && (
                <div className="flex-shrink-0 w-8 h-8 flex items-center justify-center bg-gray-300 dark:bg-gray-700 rounded-full">
                  <Sparkles size={16} className="text-indigo-500" />
                </div>
              )}
              <div
                className={`p-4 rounded-xl max-w-md shadow-md ${
                  msg.type === 'user-message'
                    ? 'bg-indigo-500 text-white rounded-br-none'
                    : 'bg-white dark:bg-gray-800 text-gray-800 dark:text-gray-200 rounded-bl-none'
                }`}
              >
                {/* Render content based on message type */}
                {msg.imageUrl ? (
                  <a href={msg.imageUrl} target="_blank" rel="noopener noreferrer">
                    <img src={msg.imageUrl} alt="Generated" className="rounded-lg max-w-full h-auto" />
                    <span className="text-sm flex items-center mt-2 text-indigo-200 hover:underline">
                      View full image <ExternalLink size={14} className="ml-1" />
                    </span>
                  </a>
                ) : (
                  <div className="prose dark:prose-invert">
                    <div dangerouslySetInnerHTML={{ __html: marked.parse(msg.text || '') }} />
                  </div>
                )}
              </div>
              {msg.type === 'user-message' && (
                <div className="flex-shrink-0 w-8 h-8 flex items-center justify-center bg-indigo-500 text-white rounded-full font-bold">
                  {user.displayName ? user.displayName[0] : 'U'}
                </div>
              )}
            </div>
          ))}
          {(isAIGenerating || isSummarizing || isGeneratingImage) && (
            <div className="flex items-start gap-4 justify-start">
              <div className="flex-shrink-0 w-8 h-8 flex items-center justify-center bg-gray-300 dark:bg-gray-700 rounded-full">
                <Sparkles size={16} className="text-indigo-500" />
              </div>
              <div className="p-4 rounded-xl max-w-md shadow-md bg-white dark:bg-gray-800 rounded-bl-none">
                <div className="dot-flashing"></div>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        {/* Input area */}
        <div className="p-4 md:p-8 bg-white dark:bg-gray-800 border-t dark:border-gray-700">
          <form onSubmit={handleSendMessage} className="flex items-center gap-2">
            <input
              type="text"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              disabled={isAIGenerating || isSummarizing || isGeneratingImage}
              placeholder="Type a message..."
              className="flex-grow p-4 bg-gray-100 dark:bg-gray-700 border border-gray-300 dark:border-gray-600 rounded-xl focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors"
            />
            <button
              type="submit"
              disabled={!newMessage.trim() || isAIGenerating || isSummarizing || isGeneratingImage}
              className="p-4 bg-indigo-500 text-white rounded-xl shadow-md hover:bg-indigo-600 transition-colors disabled:bg-gray-400 disabled:cursor-not-allowed"
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="24"
                height="24"
                viewBox="0 0 24 24"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
                className="lucide lucide-send"
              >
                <path d="m22 2-7 14-7 7-3-10z" />
                <path d="M22 2 11 13" />
              </svg>
            </button>
            <button
              type="button"
              onClick={() => handleTTS(messages[messages.length - 1]?.text || "No message to read.", messages[messages.length - 1]?.id)}
              disabled={isSpeaking || isAIGenerating || isSummarizing || isGeneratingImage || messages.length === 0}
              className="p-4 bg-gray-200 text-gray-700 rounded-xl shadow-md hover:bg-gray-300 transition-colors disabled:bg-gray-400 disabled:cursor-not-allowed"
              aria-label="Read last message aloud"
            >
              <Volume2 size={24} />
            </button>
            <button
              type="button"
              onClick={stopTTS}
              disabled={!isSpeaking}
              className="p-4 bg-red-500 text-white rounded-xl shadow-md hover:bg-red-600 transition-colors disabled:bg-gray-400 disabled:cursor-not-allowed"
              aria-label="Stop reading"
            >
              <X size={24} />
            </button>
          </form>
        </div>
      </div>

      {/* Dot Flashing CSS Animation */}
      <style>{`
        .dot-flashing {
          position: relative;
          width: 5px;
          height: 5px;
          border-radius: 5px;
          background-color: #a3a3a3;
          color: #a3a3a3;
          animation: dotFlashing 1s infinite linear alternate;
          animation-delay: 0.5s;
        }

        .dot-flashing::before, .dot-flashing::after {
          content: '';
          display: inline-block;
          position: absolute;
          top: 0;
        }

        .dot-flashing::before {
          left: -10px;
          width: 5px;
          height: 5px;
          border-radius: 5px;
          background-color: #a3a3a3;
          color: #a3a3a3;
          animation: dotFlashing 1s infinite alternate;
          animation-delay: 0s;
        }

        .dot-flashing::after {
          left: 10px;
          width: 5px;
          height: 5px;
          border-radius: 5px;
          background-color: #a3a3a3;
          color: #a3a3a3;
          animation: dotFlashing 1s infinite alternate;
          animation-delay: 1s;
        }

        @keyframes dotFlashing {
          0% { background-color: #a3a3a3; }
          50%, 100% { background-color: #e6e6e6; }
        }
      `}</style>
    </div>
  );
}

export default App;
