/* global __firebase_config, __app_id, __initial_auth_token */

import { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInWithCustomToken, signInAnonymously, onAuthStateChanged, sendSignInLinkToEmail, isSignInWithEmailLink, signInWithEmailLink } from 'firebase/auth';
import { getFirestore, collection, onSnapshot, doc, addDoc, serverTimestamp } from 'firebase/firestore';

// --- Firebase Configuration ---
// This configuration will be used when running the app locally.
// In the Canvas environment, the __firebase_config variable will override this.
const localFirebaseConfig = {
  apiKey: "AIzaSyDhJoNaFf0qgl4VTuUXj16ysJG1hycq-p8",
  authDomain: "ai-chatbot-88602.firebaseapp.com",
  projectId: "ai-chatbot-88602",
  storageBucket: "ai-chatbot-88602.firebasestorage.app",
  messagingSenderId: "167580287666",
  appId: "1:167580287666:web:9e8313ae6e667e47d47fab",
  measurementId: "G-CXKJ8QRLZ1"
};

// Determine which Firebase config to use: Canvas environment's or local fallback.
const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : localFirebaseConfig;

// Determine app ID: Canvas environment's or local fallback.
const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';

// Determine initial auth token: Canvas environment's or local fallback.
const initialAuthToken = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;

// The main application component.
function App() {
  const [db, setDb] = useState(null);
  const [auth, setAuth] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [email, setEmail] = useState(''); // State for email input
  const [emailSent, setEmailSent] = useState(false); // State to track if email link was sent
  const [isSigningInWithLink, setIsSigningInWithLink] = useState(false); // State for link sign-in process

  // 1. Initialize Firebase and set up authentication.
  useEffect(() => {
    // Check if firebaseConfig is available
    if (Object.keys(firebaseConfig).length === 0) {
      setError('Firebase configuration is missing. Please ensure your Firebase config is correctly set.');
      setLoading(false);
      return;
    }

    try {
      const firebaseApp = initializeApp(firebaseConfig);
      const authInstance = getAuth(firebaseApp);
      const dbInstance = getFirestore(firebaseApp);

      setAuth(authInstance);
      setDb(dbInstance);

      // Listen for auth state changes
      const unsubscribe = onAuthStateChanged(authInstance, (currentUser) => {
        if (currentUser) {
          // A user is signed in.
          setUser(currentUser);
          setIsAuthReady(true);
          setError(null);
        } else {
          // No user is signed in. Attempt to sign in.
          // This will be handled by the email link flow or anonymous sign-in
          // if email link is not being processed.
          setIsAuthReady(true); // Mark auth as ready even if no user is signed in yet
        }
        setLoading(false);
      });
      return () => unsubscribe();
    } catch (e) {
      // Catch any initialization errors
      setError('Failed to initialize Firebase. Please check the configuration.');
      setLoading(false);
    }
  }, []);

  // Function to sign in the user, handling network errors gracefully.
  const signInUser = async (authInstance) => {
    try {
      // The Canvas environment provides a custom token, so we prioritize that.
      if (initialAuthToken) {
        await signInWithCustomToken(authInstance, initialAuthToken);
      } else {
        // If not in Canvas and no email link is being processed, sign in anonymously
        if (!isSigningInWithLink) {
          await signInAnonymously(authInstance);
        }
      }
    } catch (e) {
      // Handle the specific 'auth/network-request-failed' error.
      if (e.code === 'auth/network-request-failed') {
        setError("Network error: Failed to connect to Firebase. Please check your internet connection and try again.");
      } else {
        setError(`An authentication error occurred: ${e.message}`);
      }
    }
  };

  // 2. Handle email link sign-in when the component mounts or auth state changes.
  useEffect(() => {
    if (!auth || !isAuthReady) return;

    // Confirm the link is a sign-in with email link.
    if (isSignInWithEmailLink(auth, window.location.href)) {
      setIsSigningInWithLink(true);
      setLoading(true);

      // Get the email if available. This should be available if the user completes
      // the flow on the same device where they started it.
      let storedEmail = window.localStorage.getItem('emailForSignIn');

      // If no email is stored, prompt the user for it.
      if (!storedEmail) {
        // IMPORTANT: In a real app, you'd use a custom modal for this prompt.
        // For this example, we'll use window.prompt for simplicity.
        storedEmail = window.prompt('Please provide your email for confirmation');
      }

      if (!storedEmail) {
        setError("Email not provided for sign-in link. Please restart the process.");
        setIsSigningInWithLink(false);
        setLoading(false);
        // Clear the URL parameters to prevent re-processing the link
        window.history.replaceState({}, document.title, window.location.pathname);
        return;
      }

      // The client SDK will parse the code from the link for you.
      signInWithEmailLink(auth, storedEmail, window.location.href)
        .then((result) => {
          // Clear email from storage.
          window.localStorage.removeItem('emailForSignIn');
          setUser(result.user); // Set the signed-in user
          setIsSigningInWithLink(false);
          setLoading(false);
          setError(null);
          // Clear the URL parameters to prevent re-processing the link
          window.history.replaceState({}, document.title, window.location.pathname);
        })
        .catch((error) => {
          setError(`Error completing sign-in: ${error.message}`);
          setIsSigningInWithLink(false);
          setLoading(false);
          // Clear the URL parameters to prevent re-processing the link
          window.history.replaceState({}, document.title, window.location.pathname);
        });
    } else if (!user && !initialAuthToken && !emailSent) {
      // If no user is signed in, no custom token, and no email link sent,
      // then attempt anonymous sign-in to allow initial access.
      signInUser(auth);
    }
  }, [auth, isAuthReady, user, initialAuthToken, emailSent]); // Added emailSent to dependencies

  // 3. Fetch messages from Firestore in real-time.
  useEffect(() => {
    // Only fetch data if Firebase is initialized and the user is authenticated.
    if (!db || !isAuthReady || !user) return; // Ensure user is authenticated before fetching messages

    // We'll store messages in a public collection so all users can see them.
    const messagesCollectionRef = collection(db, 'artifacts', appId, 'public', 'data', 'messages');

    // Create a real-time listener for the messages.
    const unsubscribe = onSnapshot(messagesCollectionRef, (querySnapshot) => {
      const messagesData = [];
      querySnapshot.forEach((doc) => {
        messagesData.push({ id: doc.id, ...doc.data() });
      });
      // Sort messages by timestamp after fetching to avoid orderBy errors
      messagesData.sort((a, b) => (a.timestamp?.seconds || 0) - (b.timestamp?.seconds || 0));
      setMessages(messagesData);
    });

    return () => unsubscribe();
  }, [db, isAuthReady, user]); // Added user to dependencies

  // 4. Send a new message to Firestore.
  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !user) return;

    try {
      const messagesCollectionRef = collection(db, 'artifacts', appId, 'public', 'data', 'messages');
      await addDoc(messagesCollectionRef, {
        text: newMessage,
        userId: user.uid,
        userName: `User_${user.uid.substring(0, 5)}`, // Simple user name for display
        timestamp: serverTimestamp(),
      });
      setNewMessage('');
    } catch (e) {
      setError(`Failed to send message: ${e.message}`);
    }
  };

  // 5. Handle sending the sign-in email link.
  const handleSendSignInLink = async (e) => {
    e.preventDefault();
    if (!auth || !email.trim()) {
      setError("Please enter a valid email address.");
      return;
    }

    setLoading(true);
    setError(null);

    // ActionCodeSettings object for email link authentication
    const actionCodeSettings = {
      // URL you want to redirect back to. The domain for this URL must be
      // in the authorized domains list in the Firebase Console.
      // For local development, this will be http://localhost:3000 (or your port).
      url: window.location.origin,
      handleCodeInApp: true, // This must be true for sign-in operations
      iOS: {
        bundleId: 'com.example.ios' // Placeholder
      },
      android: {
        packageName: 'com.example.android', // Placeholder
        installApp: true,
        minimumVersion: '12'
      }
      // linkDomain can be omitted to use default Firebase Hosting domain
    };

    try {
      await sendSignInLinkToEmail(auth, email, actionCodeSettings);
      setEmailSent(true);
      window.localStorage.setItem('emailForSignIn', email); // Save email locally
      setLoading(false);
    } catch (error) {
      setError(`Failed to send sign-in link: ${error.message}`);
      setLoading(false);
    }
  };

  // UI rendering based on application state.
  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center p-4 font-sans">
      <div className="w-full max-w-xl bg-white rounded-2xl shadow-xl overflow-hidden flex flex-col h-[80vh]">
        
        {/* Header */}
        <div className="bg-gradient-to-r from-purple-600 to-indigo-600 text-white p-4 text-center text-2xl font-bold">
          AI Chatbot App
        </div>

        {/* User ID and Status */}
        <div className="bg-gray-200 text-gray-700 text-sm p-2 flex justify-between items-center">
            <span className="font-semibold">User ID:</span> 
            <span className="font-mono text-xs p-1 bg-gray-300 rounded-md truncate max-w-[70%]">
                {user ? user.uid : 'Not signed in'}
            </span>
        </div>

        {/* Loading and Error Display */}
        {loading && (
          <div className="flex-1 flex items-center justify-center p-4">
            <div className="text-center text-lg text-gray-500">
              <svg xmlns="http://www.w3.org/2000/svg" className="animate-spin h-8 w-8 text-indigo-500 mx-auto mb-2" fill="none" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
              {isSigningInWithLink ? 'Completing sign-in with email link...' : 'Initializing Firebase...'}
            </div>
          </div>
        )}

        {error && (
          <div className="flex-1 flex items-center justify-center p-4">
            <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg relative max-w-sm text-center">
              <strong className="font-bold block mb-1">Error!</strong>
              <span className="block sm:inline">{error}</span>
            </div>
          </div>
        )}

        {/* Conditional Rendering: Email Input or Chat */}
        {!loading && !error && !user && !emailSent && !isSigningInWithLink && (
          <div className="flex-1 flex flex-col items-center justify-center p-4">
            <h2 className="text-xl font-semibold mb-4 text-gray-700">Sign In with Email Link</h2>
            <form onSubmit={handleSendSignInLink} className="w-full max-w-sm flex flex-col items-center">
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                className="w-full p-3 border border-gray-300 rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                required
              />
              <button
                type="submit"
                className="w-full bg-indigo-500 text-white p-3 rounded-lg shadow-md hover:bg-indigo-600 transition-colors duration-200"
              >
                Send Sign-in Link
              </button>
            </form>
            <p className="text-sm text-gray-500 mt-4 text-center">
              You will receive an email with a link to sign in.
            </p>
          </div>
        )}

        {!loading && !error && !user && emailSent && !isSigningInWithLink && (
          <div className="flex-1 flex flex-col items-center justify-center p-4 text-center">
            <h2 className="text-xl font-semibold mb-4 text-gray-700">Email Sent!</h2>
            <p className="text-gray-600 mb-4">
              Please check your inbox at <span className="font-semibold text-indigo-600">{email}</span> for your sign-in link.
              Click the link to complete the authentication process.
            </p>
            <p className="text-sm text-gray-500">
              (If you don't see it, check your spam folder.)
            </p>
            <button
              onClick={() => setEmailSent(false)}
              className="mt-6 bg-gray-200 text-gray-700 p-2 rounded-lg hover:bg-gray-300 transition-colors duration-200"
            >
              Go Back
            </button>
          </div>
        )}

        {/* Main Chat Area */}
        {!loading && !error && user && (
          <>
            <div className="flex-1 p-4 overflow-y-auto space-y-4 bg-gray-50">
              {messages.length === 0 && (
                <div className="text-center text-gray-400 mt-20">
                  No messages yet. Send one to start the conversation!
                </div>
              )}
              {messages.map((msg) => (
                <div key={msg.id} className={`flex ${msg.userId === user.uid ? 'justify-end' : 'justify-start'}`}>
                  <div className={`p-3 rounded-xl shadow-md max-w-xs sm:max-w-md break-words ${
                    msg.userId === user.uid
                      ? 'bg-blue-500 text-white rounded-br-none'
                      : 'bg-gray-300 text-gray-800 rounded-bl-none'
                  }`}>
                    <div className="font-bold text-xs mb-1">
                      {msg.userId === user.uid ? 'You' : msg.userName}
                    </div>
                    {msg.text}
                    <div className="text-right text-xs text-opacity-80 mt-1">
                      {msg.timestamp ? new Date(msg.timestamp.seconds * 1000).toLocaleTimeString() : '...'}
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {/* Message Input */}
            <form onSubmit={handleSendMessage} className="p-4 bg-gray-100 flex items-center">
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type your message..."
                className="flex-1 p-3 border border-gray-300 rounded-full focus:outline-none focus:ring-2 focus:ring-indigo-500"
              />
              <button
                type="submit"
                className="ml-3 p-3 bg-indigo-500 text-white rounded-full shadow-lg hover:bg-indigo-600 transition-colors duration-200"
              >
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                </svg>
              </button>
            </form>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
